return [ 'project-id-version' => 'fluid-checkout','mime-version' => '1.0','content-type' => 'text/plain; charset=utf-8','content-transfer-encoding' => '8bit','language-team' => 'Fluid Checkout','last-translator' => 'Fluid Checkout','pot-creation-date' => '2025-03-05 23:46+0000','report-msgid-bugs-to' => 'https://fluidcheckout.com/support','x-poedit-basepath' => '..','x-poedit-keywordslist' => '__;_e;_ex:1,2c;_n:1,2;_n_noop:1,2;_nx:1,2,4c;_nx_noop:1,2,3c;_x:1,2c;esc_attr__;esc_attr_e;esc_attr_x:1,2c;esc_html__;esc_html_e;esc_html_x:1,2c','x-poedit-searchpath-0' => '.','x-poedit-searchpathexcluded-0' => '*.js','x-poedit-sourcecharset' => 'UTF-8','plural-forms' => 'nplurals=2; plural=(n != 1);','messages' => ['WooCommerce' => 'WooCommerce','Go to Plugin Search' => 'Gå til Plugin-søgning','Activate %s' => 'Aktiver %s','<strong>%1$s</strong> requires <strong>%2$s</strong> to be installed and activated. <a href="%4$s">%3$s</a>' => '<strong>%1$s</strong> kræver, at <strong>%2$s</strong> er installeret og aktiveret. <a href="%4$s">%3$s</a>','Enter your code here' => 'Indtast din kode her','Add %s' => 'Tilføj %s','Remove' => 'Fjerne','Order number and receipt will be sent to this email address.' => 'Ordrenummer og kvittering sendes til denne e-mailadresse.','Apartment, unit, building, floor, etc.' => 'Lejlighed, enhed, bygning, etage osv.','Shipping phone' => 'Telefon til forsendelse','Only used for shipping-related questions.' => 'Bruges kun til forsendelsesrelaterede spørgsmål.','Multi-step' => 'Flere trin','Single step' => 'Et enkelt trin','Click to go to the order summary' => 'Klik for at gå til ordreoversigten','Cart total:' => 'Samlet vogn:','Proceed to %s' => 'Fortsæt til %s','Proceed to contact' => 'Fortsæt med at kontakte','Proceed to shipping' => 'Fortsæt til forsendelse','Proceed to billing' => 'Fortsæt til fakturering','Proceed to payment' => 'Fortsæt til betaling','My contact' => 'Min kontaktperson','Shipping to' => 'Forsendelse til','Shipping method' => 'Forsen