return [ 'project-id-version' => 'fluid-checkout','mime-version' => '1.0','content-type' => 'text/plain; charset=utf-8','content-transfer-encoding' => '8bit','language-team' => 'Fluid Checkout','last-translator' => 'Fluid Checkout','pot-creation-date' => '2025-03-05 23:46+0000','report-msgid-bugs-to' => 'https://fluidcheckout.com/support','x-poedit-basepath' => '..','x-poedit-keywordslist' => '__;_e;_ex:1,2c;_n:1,2;_n_noop:1,2;_nx:1,2,4c;_nx_noop:1,2,3c;_x:1,2c;esc_attr__;esc_attr_e;esc_attr_x:1,2c;esc_html__;esc_html_e;esc_html_x:1,2c','x-poedit-searchpath-0' => '.','x-poedit-searchpathexcluded-0' => '*.js','x-poedit-sourcecharset' => 'UTF-8','plural-forms' => 'nplurals=2; plural=(n != 1);','messages' => ['WooCommerce' => 'WooCommerce','Go to Plugin Search' => 'Iru al Kromprogramo Serĉu','Activate %s' => 'Aktivigu %s','<strong>%1$s</strong> requires <strong>%2$s</strong> to be installed and activated. <a href="%4$s">%3$s</a>' => '<strong>%1$s</strong> postulas ke <strong>%2$s</strong> estu instalita kaj aktivigita. <a href="%4$s">%3$s</a>','Enter your code here' => 'Enigu vian kodon ĉi tie','Add %s' => 'Aldonu %s','Remove' => 'Forigi','Order number and receipt will be sent to this email address.' => 'Mendnumero kaj kvitanco estos senditaj al ĉi tiu retadreso.','Apartment, unit, building, floor, etc.' => 'Apartamento, unuo, konstruaĵo, etaĝo ktp.','Shipping phone' => 'Sendotelefono','Only used for shipping-related questions.' => 'Nur uzata por send-rilataj demandoj.','Multi-step' => 'Plurpaŝa','Single step' => 'Ununura paŝo','Click to go to the order summary' => 'Klaku por iri al la menda resumo','Cart total:' => 'Tuta ĉaro:','Proceed to %s' => 'Iru al %s','Proceed to contact' => 'Sekvu kontakti','Proceed to shipping' => 'Procedu al sendado','Proceed to billing' => 'Procedu al fakturado','Proceed to payment' => 'Procedu al pago','My contact' => 'Mia kontakto','Shipping to' => 'Sendado al','Shipping method' => 'Sendado metodo','Additional notes' => 'Pliaj notoj','Billing to' => 'Fakturado al','Payment method' => 'Pagmetodo','Step %1$s of %2$s' => 'Paŝo %1$s de %2$s','Change: %s' => 'Ŝanĝo: %s','Not selected yet.' => 'Ankoraŭ ne elektita.','Same as shipping address' => 'Sama kiel senda adreso','Same as billing address' => 'Sama kiel faktura adreso','Order summary' => 'Mendo resumo','Edit cart' => 'Redaktu ĉaron','Did you mean %s?' => 'Ĉu vi volis diri %s?','Change email address to: {suggestion-value}' => 'Ŝanĝu retadreson al: {suggestion-value}','This is a required field.' => 'Ĉi tio estas postulata kampo.','This is not a valid email address.' => 'Ĉi tio ne estas valida retadreso.','This field does not match the related field value.' => 'Ĉi tiu kampo ne kongruas kun la rilata kampa valoro.','The CPF number "{cpf_number}" is invalid.' => 'La CPF-numero &quot;{cpf_number}&quot; estas malvalida.','The CNPJ number "{cnpj_number}" is invalid.' => 'La CNPJ-numero &quot;{cnpj_number}&quot; estas malvalida.','(Required)' => '(Bezonata)','Checkout Header - Desktop' => 'Checkout Header - Labortablo','Display widgets on the checkout header for large screens. Only displayed if using the plugin\'s checkout header.' => 'Montru fenestraĵojn sur la kasa kaplinio por grandaj ekranoj. Nur montrata se vi uzas la kasan kaplinion de la kromaĵo.','Checkout Header - Mobile' => 'Paga Kapo - Poŝtelefono','Display widgets below the checkout header for mobile devices. Only displayed if using the plugin\'s checkout header.' => 'Montru fenestraĵojn sub la kasa kaplinio por porteblaj aparatoj. Nur montrata se vi uzas la kasan kaplinion de la kromaĵo.','Checkout Sidebar' => 'Checkout Flankbreto','Display widgets on the checkout sidebar after the order summary.' => 'Montru fenestraĵojn sur la kasa flanka kolumno post la mendo-resumo.','Checkout Order Summary' => 'Elĉerpa Mendo Resumo','Display widgets inside the order summary at the checkout page.' => 'Montru fenestraĵojn en la mendo-resumo ĉe la pagpaĝo.','Checkout Below Place Order' => 'Kontrolu Sube Faru Mendon','Display widgets below the place order button at the checkout page.' => 'Montru fenestraĵojn sub la butono de mendo ĉe la pagpaĝo.','Checkout Footer' => 'Checkout Footer','Display widgets on the checkout footer. Only displayed if using the plugin\'s checkout footer.' => 'Montru fenestraĵojn sur la pagpiedo. Nur montrata se vi uzas la pagpaĝon de la kromaĵo.','Classic' => 'Klasika','Boxed' => 'Boxed','Split' => 'Split','Minimalist' => 'Minimalisto','Fluid Checkout and Delivery Date plugins by CodeRockz' => 'Fluid Checkout kaj Livera Dato kromaĵojn de CodeRockz','<p>We detected that you are using the plugins Delivery & Pickup Date Time for WooCommerce (free and PRO) by CodeRockz. Integration with these plugins have been moved to Fluid Checkout PRO.</p>' => '<p>Ni detektis, ke vi uzas la kromaĵojn Livero kaj Kolekto Dato Tempo por WooCommerce (senpaga kaj PRO) de CodeRockz. Integriĝo kun ĉi tiuj kromaĵoj estis movita al Fluid Checkout PRO.</p>','Upgrade to PRO' => 'Ĝisdatigu al PRO','Fluid Checkout database updated successfully' => 'Fluid Checkout-datumbazo sukcese ĝisdatigita','The database changes where applied successfully.' => 'La datumbazo ŝanĝiĝas kie aplikita sukcese.','Ok' => 'Bone','Dismiss' => 'Forĵeti','Fluid Checkout database update' => 'Fluid Checkout datumbaza ĝisdatigo','Some changes to the database are required. As with any update, we recommend you to <strong>take a full backup of your website before proceeding</strong>.' => 'Kelkaj ŝanĝoj al la datumbazo estas postulataj. Kiel kun ĉiu ĝisdatigo, ni rekomendas al vi <strong>fari plenan sekurkopion de via retejo antaŭ ol daŭrigi</strong> .','Update database' => 'Ĝisdatigu datumbazon','Fluid Checkout is not compatible with the Divi Builder checkout layout' => 'Fluid Checkout ne kongruas kun la aranĝo de Divi Builder checkout','<p>Fluid Checkout needs the classic WooCommerce shortcode-based checkout form to work. When using the Divi Builder to customize the look and feel of the checkout page, the classic shortcodes are replaced with custom widgets from the Divi Builder for each part of the checkout page. These custom widgets are not compatible with Fluid Checkout.</p><p>Please edit your checkout page replacing the Divi Builder widgets with the a text widget containing the shortcode <code>[woocommerce_checkout]</code>.</p>' => '<p>Fluid Checkout bezonas la klasikan WooCommerce-mallongkodon-bazitan kasan formularon por funkcii. Kiam vi uzas la Divi Builder por personecigi la aspekton de la pagpaĝo, la klasikaj mallongaj kodoj estas anstataŭigitaj per kutimaj fenestraĵoj de la Divi Builder por ĉiu parto de la pagpaĝo. Ĉi tiuj kutimaj fenestraĵoj ne kongruas kun Fluid Checkout.</p><p> Bonvolu redakti vian pagpaĝon anstataŭigante la fenestraĵojn de Divi Builder per la teksta fenestraĵo enhavanta la mallongkodon <code>[woocommerce_checkout]</code> .</p>','Edit the checkout page' => 'Redaktu la pagpaĝon','Read the documentation.' => 'Legu la dokumentadon.','Germanized PRO multistep checkout feature needs to be disabled when using Fluid Checkout' => 'Germanigita PRO plurpaŝa kasa funkcio devas esti malŝaltita kiam vi uzas Fluid Checkout','When using Fluid Checkout, the multistep feature from the Germanized PRO plugin becomes unnecessary and can cause <strong>critical errors while processing new orders</strong>. Please disable the Germanized PRO multistep checkout feature in the plugin settings.' => 'Kiam vi uzas Fluid Checkout, la plurpaŝa funkcio de la Germanized PRO kromaĵo fariĝas nenecesa kaj povas kaŭzi <strong>kritikajn erarojn dum prilaborado de novaj mendoj</strong> . Bonvolu malŝalti la Germanigitan PRO-plurpaŝan pagfunkcion en la aldonaĵagordoj.','Go to Germanized settings' => 'Iru al germanigitaj agordoj','Thanks for choosing Fluid Checkout!' => 'Dankon pro la elekto de Fluid Checkout!','You have been using the plugin for a while now. How do you like it so far? <br>Please give us a quick rating, we appreciate every bit of feedback and it encourages us to keep improving the plugin :)' => 'Vi uzas la kromprogramon dum iom da tempo. Kiel vi ŝatas ĝin ĝis nun?<br> Bonvolu doni al ni rapidan takson, ni dankas ĉiun reagon kaj ĝi instigas nin daŭre plibonigi la kromprogramon :)','Rate the plugin' => 'Taksi la kromprogramon','I need help!' => 'Mi bezonas helpon!','Some features of WooCommerce Checkout Manager by Quadlayers might not work as expected when using it with Fluid Checkout. Read <a href="%s">our documentation</a> for more information and alternative solutions.' => 'Iuj funkcioj de WooCommerce Checkout Manager de Quadlayers eble ne funkcias kiel atendite kiam vi uzas ĝin kun Fluid Checkout. Legu <a href="%s">nian dokumentaron</a> por pliaj informoj kaj alternativaj solvoj.','Read documentation' => 'Legu dokumentadon','Don\'t show this again' => 'Ne montru ĉi tion denove','Special offers' => 'Specialaj ofertoj','Extend all the goodness of Fluid Checkout to your cart page and thank you pages.' => 'Etendi la tutan bonon de Fluid Checkout al via ĉarpaĝo kaj dankpaĝoj.','Fluid Checkout PRO' => 'Fluid Checkout PRO','Design templates, optimized cart and order received pages, account matching, and much more.' => 'Dezajnu ŝablonojn, optimumigitan ĉaron kaj mendu ricevitajn paĝojn, kongruon kaj multe pli.','The best tools to further <strong>improve your WooCommerce checkout conversion rate</strong> and make the purchase experience consistent on the entire journey.' => 'La plej bonaj iloj por plu <strong>plibonigi vian WooCommerce-pagkonvertan indicon</strong> kaj fari la aĉetan sperton konsekvencan dum la tuta vojaĝo.','More design templates' => 'Pli da dezajnaj ŝablonoj','Optimized cart page' => 'Optimumigita ĉarpaĝo','Optimized order received / thank you page' => 'Optimumigita mendo ricevita/dankopaĝo','Edit cart contents at checkout page' => 'Redaktu la enhavon de ĉaro ĉe la pagpaĝo','Account matching / user matching' => 'Konto kongruo / uzantkongruo','Express Checkout buttons from supported payment methods' => 'Express Checkout-butonoj de subtenataj pagmetodoj','Trust symbols on the cart and thank you pages' => 'Fidu simbolojn sur la ĉaro kaj dankon-paĝojn','More positions for the coupon code on the checkout page' => 'Pli da pozicioj por la kuponkodo sur la pagpaĝo','Integrated coupon code field on the cart page' => 'Integrita kuponkoda kampo sur la ĉarpaĝo','International phone numbers' => 'Internaciaj telefonnumeroj','Gift messages and packing slips' => 'Donacaj mesaĝoj kaj pakaj biletoj','Local pickup and in-store collection' => 'Loka kolektado kaj kolektado en vendejo','Upgrade to PRO &mdash; 99 EUR' => 'Ĝisdatigu al PRO — 99 EUR','Fluid Checkout PRO – Bundle' => 'Fluid Checkout PRO - Pakaĵo','All PRO features + All add-ons for a special price.' => 'Ĉiuj PRO-ecoj + Ĉiuj aldonaĵoj por speciala prezo.','The bundle includes all add-ons we currently offer and Fluid Checkout add-ons we launch in the future.' => 'La pakaĵo inkluzivas ĉiujn aldonaĵojn, kiujn ni nuntempe ofertas, kaj Fluid Checkout-aldonaĵojn, kiujn ni lanĉas estonte.','226 EUR value (Save 42%)' => 'Valoro de 226 EUR (Konservu 42%)','Google Address Autocomplete' => 'Guglo-adreso Aŭtomata kompletigo','Address Book' => 'Adreslibro','EU-VAT Assistant' => 'EU-VAT Asistanto','All future add-ons *' => 'Ĉiuj estontaj aldonaĵoj *','* For as long as you have an active subscription.' => '* Dum vi havas aktivan abonon.','Get the Bundle – Only 129 EUR' => 'Akiru la Pakaĵon - Nur 129 EUR','All prices shown in EUR. If there are any divergencies with the prices on our website, the offers shown on the website superseed these and will be applied.' => 'Ĉiuj prezoj montriĝas en EUR. Se estas iuj diverĝoj kun la prezoj en nia retejo, la ofertoj montritaj en la retejo anstataŭas ĉi tiujn kaj estos aplikataj.','Add-ons' => 'Aldonaĵoj','Enhance your checkout experience with these add-ons.' => 'Plibonigu vian kasperton per ĉi tiuj aldonaĵoj.','I already have it – Hide this offer' => 'Mi jam havas ĝin - Kaŝi ĉi tiun oferton','Installed &mdash; Activate' => 'Instalita — Aktivigi','Activated' => 'Aktivigita','Up to 40% less checkout fields to fill in.' => 'Ĝis 40% malpli da kasaj kampoj por plenigi.','Avoid delivery delays and unsatisfied customers. Collect the <strong>correct address information</strong> from the first time they buy with you.' => 'Evitu liverajn prokrastojn kaj nekontentajn klientojn. Kolektu la <strong>ĝustajn adresinformojn</strong> de la unua fojo, kiam ili aĉetas kun vi.','Get this add-on &mdash; 29 EUR' => 'Akiru ĉi tiun aldonaĵon — 29 EUR','Address book' => 'Adreslibro','Multiple saved addresses for shipping and billing.' => 'Multoblaj konservitaj adresoj por sendado kaj fakturado.','Let customers <strong>save multiple shipping and billing addresses</strong> on their account and choose which ones to use at checkout and cart pages.' => 'Lasu klientojn <strong>konservi plurajn sendajn kaj fakturajn adresojn</strong> en sia konto kaj elektu kiujn uzi ĉe paĝoj kaj ĉarpaĝoj.','Get this add-on &mdash; 59 EUR' => 'Akiru ĉi tiun aldonaĵon — 59 EUR','Simplified EU-VAT validation for your store.' => 'Simpligita EU-VAT-validigo por via vendejo.','Collect and <strong>validate EU VAT numbers at the checkout page</strong>, removes tax charges on reverse charge basis and confirms customer location when needed.' => 'Kolektu kaj <strong>validigu EU-VAT-numerojn ĉe la kaspaĝo</strong> , forigas impostajn kostojn laŭ inversa pagendaĵo kaj konfirmas klientan lokon kiam necese.','Get this add-on &mdash; 39 EUR' => 'Akiru ĉi tiun aldonaĵon — 39 EUR','All add-ons are sold separately and require the <strong>Fluid Checkout Lite</strong> plugin to be installed and activated, except when noted.' => 'Ĉiuj aldonaĵoj estas venditaj aparte kaj postulas ke la kromaĵo <strong>Fluid Checkout Lite</strong> estu instalita kaj aktivigita, krom kiam oni rimarkas.','Select an image' => 'Elektu bildon','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to get your license key, or <a href="%s" target="_blank">purchase a new license key</a>.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Ensalutu al via konto</a> por akiri vian permesiloŝlosilon, aŭ <a href="%s" target="_blank">aĉeti novan permesilon</a> .','Valid until %s.' => 'Valida ĝis %s.','Expired on %s.' => 'Eksvalidiĝis je %s.','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to renew your license key and continue to receive updates and support.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Ensalutu al via konto</a> por renovigi vian permesilŝlosilon kaj daŭre ricevi ĝisdatigojn kaj subtenon.','License key cancelled.' => 'Licencŝlosilo nuligita.','<a href="%s" target="_blank">Purchase a new license key</a> and continue to receive updates and support.' => '<a href="%s" target="_blank">Aĉetu novan permesilŝlosilon</a> kaj daŭre ricevu ĝisdatigojn kaj subtenon.','Error: ' => 'Eraro: ','Setup & Documentation' => 'Instalado kaj Dokumentado','Great! Your checkout page is now running on Fluid Checkout.' => 'Bonege! Via pagpaĝo nun funkcias ĉe Fluid Checkout.','Here are a few resources for you to get started:' => 'Jen kelkaj rimedoj por ke vi komencu:','Read the installation guide <a href="%s" target="_blank">Getting stated with Fluid Checkout</a>.' => 'Legu la instal-gvidilon <a href="%s" target="_blank">Deklaro kun Fluid Checkout</a> .','Setup layout and design on the <a href="%s">checkout options</a>.' => 'Agordu aranĝon kaj dezajnon sur la <a href="%s">opcioj de kaso</a> .','Check if there are any <a href="%s">integration options</a> available for other plugins you have installed.' => 'Kontrolu ĉu ekzistas <a href="%s">integraj opcioj</a> disponeblaj por aliaj kromprogramoj, kiujn vi instalis.','Visit <a href="%s" target="_blank">our documentation</a> for more information about Fluid Checkout features.' => 'Vizitu <a href="%s" target="_blank">nian dokumentaron</a> por pliaj informoj pri Fluid Checkout-funkcioj.','If you ever need help, <a href="%s" target="_blank">open a support ticket</a> on our official support channel.' => 'Se vi iam bezonas helpon, <a href="%s" target="_blank">malfermu subtenan bileton</a> sur nia oficiala subtena kanalo.','Cart layout' => 'Ĉaro aranĝo','Cart optimizations' => 'Ĉaro-optimumoj','Enable cart page optimizations' => 'Ebligu ĉarajn paĝajn optimumojn','Display the cart page with a layout similar to the checkout page.' => 'Montru la ĉarpaĝon kun aranĝo simila al la pagpaĝo.','Header and footer' => 'Kapo kaj piedlinio','Header and footer template' => 'Ŝablono de kaplinio kaj piedlinio','Controls whether to use the distraction free page header and footer or keep the currently active theme\'s header and footer.' => 'Kontrolas ĉu uzi la kaplinion kaj piedpaĝon sen distraĵoj aŭ konservi la kaplinion kaj piedlinion de la nuntempe aktiva temo.','Distraction free header and footer' => 'Sen distra kaplinio kaj piedlinio','Theme\'s header and footer' => 'La kaplinio kaj piedlinio de la temo','Cart elements' => 'Elementoj de ĉaro','Make the order summary stay visible on the cart page while scrolling' => 'Faru la mendan resumon resti videbla sur la ĉarpaĝo dum rulumado','Coupon codes' => 'Kuponaj kodoj','Select position where to display the coupon codes section on the cart page. Only applicable when AJAX cart and the integrated coupon codes in the checkout page are enabled.' => 'Elektu pozicion kie montri la sekcion pri kuponkodoj sur la ĉarpaĝo. Nur aplikebla kiam AJAX-ĉaro kaj la integraj kuponkodoj en la pagpaĝo estas ebligitaj.','Hidden' => 'Kaŝita','Before the cart items section' => 'Antaŭ la ĉaro eroj sekcio','Inside the cart items section' => 'Ene de la sekcio de eroj de ĉaro','After the cart items section' => 'Post la sekcio de eroj de ĉaro','Before the shipping section' => 'Antaŭ la senda sekcio','After the shipping section' => 'Post la senda sekcio','Before the order summary' => 'Antaŭ la mendo resumo','Inside the order summary' => 'Ene de la menda resumo','Before the order totals' => 'Antaŭ la ordo totalas','After the order totals' => 'Post la ordo totalas','After the order summary' => 'Post la mendo resumo','Shipping' => 'Sendado','Select position where to display the shipping costs and calculator on the cart page.' => 'Elektu pozicion kie montri la sendokostojn kaj kalkulilon sur la ĉarpaĝo.','Display as a cart section' => 'Montru kiel ĉaro sekcio','Cross-sells' => 'Intervendoj','Select position where to display the cross-sells section on the cart page.' => 'Elektu pozicion, kie montri la sekcion de kruc-vendoj sur la ĉarpaĝo.','Before the cart actions' => 'Antaŭ la ĉaraj agoj','After the cart actions' => 'Post la ĉaraj agoj','Layout of cross-sell items on the cart page.' => 'Aranĝo de kruc-vendaj eroj sur la ĉarpaĝo.','Optimized horizontal cross-sells layout' => 'Optimumigita horizontala kruc-venda aranĝo','Theme\'s cross-sells layout' => 'Temo trans-vendas aranĝon','Number of cross-sell items to display on the cart page' => 'Nombro da kruc-vendaj eroj por montri sur la ĉarpaĝo','Trust symbols &amp; badges' => 'Fidu simbolojn kaj insignojn','Add widget areas to the cart page' => 'Aldonu fenestraĵareojn al la ĉarpaĝo','These widget areas are used to add trust symbols and trust badges on the cart page.' => 'Ĉi tiuj fenestraĵareoj estas uzataj por aldoni fidajn simbolojn kaj fidajn insignojn sur la ĉarpaĝo.','Checkout' => 'Checkout','Layout &amp; design' => 'Aranĝo &amp; dezajno','Checkout layout' => 'Checkout aranĝo','Design template' => 'Dezajna ŝablono','General styles for the checkout steps, order summary and other sections. Might also apply to other pages such as the Cart, Order Received and View Order pages.' => 'Ĝeneralaj stiloj por la pagpaŝoj, mendo-resumo kaj aliaj sekcioj. Eble ankaŭ validi por aliaj paĝoj kiel la Paĝoj Ĉaro, Mendo Ricevita kaj Vidi Mendo.','Dark mode' => 'Malhela reĝimo','Enable dark mode' => 'Ebligu malhelan reĝimon','Header and footer templates' => 'Ŝablonoj de kaplinio kaj piedlinio','We recommend using the distraction free header and footer to avoid distractions at the checkout page. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Read the research about cart abandonment</a>.' => 'Ni rekomendas uzi la senpagan kaplinion kaj piedlinion por eviti distraĵojn ĉe la pagpaĝo. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Legu la esploron pri forlaso de ĉaroj</a> .','Logo image' => 'Logobildo','Choose an image to be displayed on the checkout page header. Only applies when using the distraction free header and footer.' => 'Elektu bildon por esti montrita sur la paĝa kaplinio. Nur validas kiam vi uzas la senpagan kaplinion kaj piedlinion.','Header background color' => 'Header fonkoloro','Choose a background color for the checkout page header. Only applies when using the distraction free header and footer.' => 'Elektu fonkoloron por la paĝa kaplinio. Nur validas kiam vi uzas la senpagan kaplinion kaj piedlinion.','HTML color value. ie: #f3f3f3' => 'HTML-kolorvaloro. te: #f3f3f3','Page background color' => 'Paĝa fonkoloro','Choose a background color for the checkout page. Color is applied to the <em>body</em> element.' => 'Elektu fonkoloron por la pagpaĝo. Koloro estas aplikata al la <em>korpoelemento</em> .','Footer background color' => 'Fonkoloro de la piedlinio','Choose a background color for the checkout page footer. Only applies when using the distraction free header and footer.' => 'Elektu fonkoloron por la paĝa piedpaĝo. Nur validas kiam vi uzas la senpagan kaplinion kaj piedlinion.','Checkout elements' => 'Checkout elementoj','Progress bar' => 'Progresa stango','Display the checkout progress bar when using multi-step checkout layout' => 'Montru la kasan progresbreton kiam vi uzas plurpaŝan kasan aranĝon','Make the checkout progress bar stay visible while scrolling' => 'Faru, ke la kasa progresbreto restu videbla dum rulumado','Express checkout' => 'Rapida kaso','Enable the express checkout section' => 'Ebligu la sekcion de rapidpago','Displays the express checkout section at checkout when supported payment gateways have this feature enabled.' => 'Montras la sekcion de rapidpago ĉe la pago kiam subtenataj pagpordoj havas ĉi tiun funkcion ebligita.','Display express checkout buttons in one line for larger screens' => 'Montru rapidajn kasajn butonojn en unu linio por pli grandaj ekranoj','Ignore additional checkout required fields when paying with a compatible express checkout payment gateway' => 'Ignoru aldonajn postulatajn kampojn kiam vi pagas per kongrua rapidpaga pagpordo','Make the order summary stay visible while scrolling' => 'Faru la mendan resumon resti videbla dum rulumado','Choose a background color for the order summary section.' => 'Elektu fonkoloron por la menda resuma sekcio.','Highlight the order totals row in the order summary table' => 'Emfazu la vicon de sumoj de ordo en la resuma ordo','Most useful when the order summary section does not have a highlighted background color. Might also apply to the Cart, Order Received and View Order pages when using Fluid Checkout PRO.' => 'Plej utila kiam la mendo resuma sekcio ne havas emfazitan fonkoloron. Eble ankaŭ aplikiĝas al la paĝoj Ĉaro, Mendo Ricevita kaj Vidi Mendo kiam vi uzas Fluid Checkout PRO.','Action link on the order summary at checkout.' => 'Aga ligilo sur la mendo-resumo ĉe la kaso.','Edit cart link' => 'Redakti ĉaron ligilon','Cart items count' => 'Ĉaraj eroj kalkulas','Link to shop page' => 'Ligo al butiko paĝo','Cart items' => 'Ĉaretoj','Enable options to edit cart items on the checkout page' => 'Ebligu opciojn por redakti ĉarajn erojn sur la pagpaĝo','Allow customers to change product quantities or removing items directly at the checkout page.' => 'Permesu al klientoj ŝanĝi produktkvantojn aŭ forigi erojn rekte ĉe la pagpaĝo.','Hide cart items errors at the checkout page' => 'Kaŝi erarojn pri ĉaro-aĵoj ĉe la pagpaĝo','Do not display the cart items errors message at the top of the checkout page. When submitting the checkout form to complete the purchase, these error messages will always be displayed.' => 'Ne montru la mesaĝon pri eraroj pri ĉaroj ĉe la supro de la pagpaĝo. Kiam vi sendas la pagformularon por kompletigi la aĉeton, ĉi tiuj erarmesaĝoj ĉiam aperos.','Place order' => 'Faru mendon','Define the position to display "Place order" and terms checkbox section.' => 'Difinu la pozicion por montri &quot;Metu mendon&quot; kaj markobuton-sekcion de terminoj.','Some options might not be compatible with some plugins and themes.' => 'Iuj opcioj eble ne kongruas kun iuj kromaĵoj kaj temoj.','Below the payment section' => 'Sub la pago sekcio','Below the order summary' => 'Sub la menda resumo','Both below the payment section and the order summary' => 'Ambaŭ sub la pago sekcio kaj la mendo resumo','Add widget areas to the checkout page' => 'Aldonu fenestraĵareojn al la pagpaĝo','These widget areas are used to add trust symbols and trust badges on the checkout page.' => 'Ĉi tiuj fenestraĵareoj estas uzataj por aldoni fidajn simbolojn kaj fidajn insignojn sur la pagpaĝo.','Display checkout sidebar widgets only when viewing the last checkout step on mobile devices when using multi-step checkout layout' => 'Montru elŝutajn flankajn fenestraĵojn nur kiam vi vidas la lastan elŝutpaŝon sur porteblaj aparatoj kiam vi uzas plurpaŝan elŝutan aranĝon','Enable integrated coupon code section' => 'Ebligu integran kuponkodon sekcion','Only applicable if use of coupon codes are enabled in the WooCommerce settings.' => 'Nur aplikebla se uzo de kuponkodoj estas ebligita en la agordoj de WooCommerce.','Display the coupon codes section title' => 'Montru la titolon de sekcio de kuponkodoj','Only applicable when coupon code is displayed as a separate section on the checkout or cart pages.' => 'Nur aplikebla kiam kuponokodo estas montrata kiel aparta sekcio sur la kaso aŭ ĉarpaĝoj.','Select position where to display the coupon codes section on the checkout page.' => 'Elektu pozicion kie montri la sekcion pri kuponkodoj sur la pagpaĝo.','Before the checkout steps' => 'Antaŭ la kaso paŝoj','As a substep before payment methods' => 'Kiel subpaŝo antaŭ pagmetodoj','As a substep after payment methods' => 'Kiel subpaŝo post pagmetodoj','Select style of the "apply coupon" button. Only applicable when the coupon code section is displayed "Before the checkout steps" on the checkout page, or "Before the cart items section" on the cart page.' => 'Elektu stilon de la butono &quot;apliki kuponon&quot;. Nur aplikebla kiam la sekcio de kuponkodo estas montrata &quot;Antaŭ la paŝaj paŝoj&quot; sur la pagpaĝo, aŭ &quot;Antaŭ la sekcio de ĉaro eroj&quot; sur la ĉarpaĝo.','Default button style' => 'Defaŭlta butonstilo','"Add" link button' => '&quot;Aldoni&quot; ligilo butono','Checkout fields' => 'Kadokampoj','Optional fields' => 'Laŭvolaj kampoj','Hide optional fields behind a link button' => 'Kaŝi laŭvolajn kampojn malantaŭ ligilo-butono','It is recommended to keep this option checked to reduce the number of open input fields, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">read the research</a>.' => 'Oni rekomendas konservi ĉi tiun opcion markita por redukti la nombron da malfermitaj enigkampoj, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">legu la esploron</a> .','Do not hide "Address line 2" fields behind a link button' => 'Ne kaŝu kampojn &quot;Adreslinio 2&quot; malantaŭ ligbutono','Recommended only when most customers actually need the "Address line 2" field, or when getting the right shipping address is crucial (ie. if delivering food and other perishable products).' => 'Rekomendita nur kiam plej multaj klientoj efektive bezonas la kampon &quot;Adreslinio 2&quot;, aŭ kiam akiri la ĝustan sendadreson estas decida (t.e. se liverado de manĝaĵoj kaj aliaj pereemaj produktoj).','Display the "Add" link buttons in lowercase' => 'Montru la butonojn de ligilo &quot;Aldoni&quot; minuskle','Make the labels of optional field "Add" link button as <code>lowercase</code> (ie. "Add phone number" instead of "Add Phone Number"). This option also affects the link buttons for coupon code fields.' => 'Faru la etikedojn de la nedeviga kampo &quot;Aldoni&quot; ligilo-butonon kiel <code>lowercase</code> (t.e. &quot;Aldoni telefonnumeron&quot; anstataŭ &quot;Aldoni Telefonan Numeron&quot;). Ĉi tiu opcio ankaŭ influas la ligajn butonojn por kuponkodaj kampoj.','Billing address' => 'Faktura adreso','Select position where to display the billing address section on the checkout page.' => 'Elektu pozicion kie montri la sekcion pri faktura adreso sur la pagpaĝo.','As a step before shipping' => 'Kiel paŝo antaŭ sendado','As a sub-step before the shipping address section' => 'Kiel subpaŝo antaŭ la sekcio de sendadreso','As a sub-step after the shipping address section' => 'Kiel subpaŝo post la sekcio de sendadreso','As a step after shipping' => 'Kiel paŝo post sendo','As a sub-step before the payment section' => 'Kiel subpaŝo antaŭ la paga sekcio','Force billing and shipping addresses to the same (single section)' => 'Devigi fakturajn kaj sendajn adresojn al la sama (ununura sekcio)','Checkbox for "Same as shipping/billing address" checked by default' => 'Markobutono por &quot;Sama kiel senda/faktura adreso&quot; defaŭlte markita','The checkbox "Same as shiping address" will start as checked by default when the shipping address section is displayed first. <br>The checkbox "Same as billing address" will start as checked by default when the billing address section is displayed first. <br> It is recommended to leave this option checked as to significantly reduce the number of open input fields at checkout, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">read the research</a>.' => 'La markobutono &quot;Same kiel sendadreso&quot; komenciĝos kiel markita defaŭlte kiam la sekcio de sendadreso montriĝas unue.<br> La markobutono &quot;Sama kiel faktura adreso&quot; komenciĝos kiel markita defaŭlte kiam la sekcio de faktura adreso montriĝas unue.<br> Oni rekomendas lasi ĉi tiun opcion markita por signife redukti la nombron de malfermitaj enigkampoj ĉe la kaso, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">legu la esploron</a> .','Highlight the billing address section in the checkout form' => 'Emfazu la sekcion pri faktura adreso en la pagformularo','Shipping address' => 'Sendo adreso','Highlight the shipping address section in the checkout form' => 'Emfazu la sekcion pri sendaadreso en la pagformularo','Shipping methods' => 'Sendaj metodoj','Choose in which position to display the shipping methods section.' => 'Elektu en kiu pozicio montri la sekcion pri sendaj metodoj.','Before shipping address' => 'Antaŭ senda adreso','After shipping address' => 'Post senda adreso','Prevent automatic selection of the first shipping method' => 'Malhelpi aŭtomatan elekton de la unua senda metodo','When enabled, the first shipping method available will not be automatically selected when no other shipping method was previously selected for each shipping package. <br>This option will be automatically enabled if the option for clearing the selected shipping method is enabled for the Local Pickup feature.' => 'Kiam ĝi estas ebligita, la unua senda metodo disponebla ne estos aŭtomate elektita kiam neniu alia senda metodo antaŭe estis elektita por ĉiu senda pako.<br> Ĉi tiu opcio aŭtomate estos ebligita se la opcio por forigi la elektitan sendan metodon estas ebligita por la Loka Kaptado.','Local pickup' => 'Loka ŝarĝo','Removes shipping address section when a local pickup shipping method is selected.' => 'Forigas sekcion pri sendaadreso kiam estas elektita loka elekta senda metodo.','Replaces the shipping address with the pickup point location when a local pickup shipping method is selected.' => 'Anstataŭigas la sendo-adreson per la loko de ŝarĝo kiam loka elekta senda metodo estas elektita.','Show option to clear shipping methods in the pickup location substep' => 'Montru opcion por malplenigi sendajn metodojn en la subpaŝo de kolekta loko','Show a link button on the pickup location substep to clear the chosen shipping methods. This can be used to allow showing the shipping address section again if a local pickup method was previously selected.' => 'Montru ligan butonon sur la subpaŝo de alpreno por forigi la elektitajn sendajn metodojn. Ĉi tio povas esti uzata por permesi montri la sekcion de sendadreso denove se loka elekta metodo antaŭe estis elektita.','Choose which address to save as the shipping address for local pickup orders.' => 'Elektu kiun adreson konservi kiel la sendo-adreso por lokaj pretaj mendoj.','Save the selected pickup location' => 'Konservu la elektitan kolektejon','Save same as the billing address' => 'Konservu same kiel la faktura adreso','Do not save any shipping address' => 'Ne konservu ajnan sendadreson','Company name field for shipping address' => 'Kampo pri nomo de kompanio por senda adreso','Change visibility for the company name field for the shipping address section on the checkout form.' => 'Ŝanĝu videblecon por la kompania nomo-kampo por la sendadresa sekcio sur la kasa formularo.','If field is set as "optional", which is the default visibility state, no changes will be applied to let other plugins apply any changes they need.' => 'Se kampo estas agordita kiel &quot;laŭvola&quot;, kiu estas la defaŭlta videbleco, neniuj ŝanĝoj estos aplikitaj por lasi aliajn kromaĵojn apliki ajnajn ŝanĝojn kiujn ili bezonas.','Hidden (remove field)' => 'Kaŝita (forigi kampon)','Optional' => 'Laŭvola','Required' => 'Bezonata','Add shipping phone field to the checkout form.' => 'Aldonu sendan telefonan kampon al la pagformularo.','Choose in which step to display the shipping phone field.' => 'Elektu en kiu paŝo montri la sendotelefonan kampon.','Contact step' => 'Kontakta paŝo','Billing phone' => 'Faktura telefono','Add billing phone field to the checkout form.' => 'Aldonu fakturan telefonkampon al la pagformularo.','Choose in which step to display the billing phone field.' => 'Elektu en kiu paŝo montri la fakturan telefonon kampon.','Enable international phone number format and validation for phone fields' => 'Ebligu internacian telefonnumerformaton kaj validigon por telefonkampoj','Format and validate phone numbers according to each country.' => 'Formatu kaj validigu telefonnumerojn laŭ ĉiu lando.','Enable phone number validation based on country rules' => 'Ebligu telefonnumeron-validigon laŭ landaj reguloj','When disabled, the phone field validation will not check if country or area codes are valid for the country.' => 'Kiam estas malŝaltita, la telefona kampvalidado ne kontrolos ĉu lando aŭ regionaj kodoj validas por la lando.','Only show allowed countries for shipping or billing' => 'Montru nur permesitajn landojn por sendado aŭ fakturado','When enabled, only the countries allowed for shipping will be available in the shipping phone field, and only countries allowed for billing will be available for the billing phone field.' => 'Kiam ĝi estas ebligita, nur la landoj permesitaj por sendo estos disponeblaj en la ekspedtelefona kampo, kaj nur landoj permesitaj por fakturado estos disponeblaj por la faktura telefonkampo.','Show country code beside the flag' => 'Montru landokodon apud la flago','Show an example of a valid phone number inside phone fields' => 'Montru ekzemplon de valida telefonnumero ene de telefonkampoj','Do not change placeholders' => 'Ne ŝanĝu anstataŭaĵojn','Show if not defined' => 'Montru se ne estas difinita','Always show' => 'Ĉiam montru','Order notes' => 'Mendu notojn','Define the visibility of the additional order notes field.' => 'Difinu la videblecon de la aldona mendo-notoj-kampo.','Gift options' => 'Donacaj elektoj','Display gift message and other gift options at the checkout page' => 'Montru donacmesaĝon kaj aliajn donacajn opciojn ĉe la pagpaĝo','Allow customers to add a gift message and other gift related options to the order.' => 'Permesu klientojn aldoni donacmesaĝon kaj aliajn donacojn rilatajn eblojn al la mendo.','Display the gift message fields always expanded' => 'Montru la donacmesaĝokampojn ĉiam pligrandigitaj','Display the gift message as part of the order details table instead of a separate section' => 'Montru la donacan mesaĝon kiel parto de la mendo-tabelo anstataŭ aparta sekcio','This option affects the order confirmation page (thank you page) and order details on account pages, emails and packing slips.' => 'Ĉi tiu opcio influas la mendan konfirmpaĝon (dankon-paĝon) kaj menddetalojn sur kontopaĝoj, retpoŝtoj kaj pakslipoj.','Account matching' => 'Konta kongruo','Enable the account matching feature' => 'Ebligu la kongruan funkcion','Associate the guest customer\'s orders with their existing account when an account already exists with the customer\'s contact details.' => 'Asociu la mendojn de la gastokliento kun ilia ekzistanta konto kiam konto jam ekzistas kun la kontaktaj detaloj de la kliento.','Display message when an account exists with the email address provided' => 'Montru mesaĝon kiam konto ekzistas kun la retadreso provizita','Replaces the account creation fields with a notification and option to log in. In some contexts, it might be recommended to leave this option disabled to protect the privacy of customers.' => 'Anstataŭigas la kreajn kampojn de konto per sciigo kaj opcio por ensaluti. En iuj kuntekstoj, oni povus rekomendi lasi ĉi tiun opcion malŝaltita por protekti la privatecon de klientoj.','Dashboard' => 'Panelo','Integrations' => 'Integriĝoj','No integrations available at the moment on this section. The options related to each plugin will only appear here if that plugin is activated.' => 'Neniuj integriĝoj disponeblaj nuntempe en ĉi tiu sekcio. La opcioj rilataj al ĉiu kromaĵo aperos ĉi tie nur se tiu aldonaĵo estas aktivigita.','License keys' => 'Licencaj ŝlosiloj','No license keys options available at the moment on this section. The options related to each plugin or add-on will appear here when that plugin is activated.' => 'Neniuj opcioj pri permesilaj ŝlosiloj disponeblaj nuntempe en ĉi tiu sekcio. La opcioj rilataj al ĉiu kromaĵo aŭ aldonaĵo aperos ĉi tie kiam tiu kromaĵo estas aktivigita.','Visit our website for more information about our plugins and add-ons.' => 'Vizitu nian retejon por pliaj informoj pri niaj kromaĵoj kaj aldonaĵoj.','Order pay page' => 'Ordopaĝo paĝo','Order pay' => 'Ordonu pagon','Enable order pay page optimizations' => 'Ebligu mendo-pagpaĝajn optimumojn','Changes the layout of order pay page for existing orders pending payment from the customer.' => 'Ŝanĝas la aranĝon de mendo-pagpaĝo por ekzistantaj mendoj atendante pagon de la kliento.','Add widget areas to the order pay page' => 'Aldonu fenestraĵareojn al la mendo-pagpaĝo','These widget areas are used to add trust symbols and trust badges on the order pay page.' => 'Ĉi tiuj fenestraĵareoj estas uzataj por aldoni fidajn simbolojn kaj fidajn insignojn sur la mendo-pagpaĝo.','Thank you page & order details layout' => 'Dankon paĝo kaj mendo detaloj aranĝo','Order details optimizations' => 'Ordo detaloj optimumoj','Enable thank you page and order details optimizations' => 'Ebligu dankpaĝon kaj mendo-detalaj optimumigoj','Changes the layout of order details on the thank you page and account pages.' => 'Ŝanĝas la aranĝon de mendaj detaloj sur la dankopaĝo kaj kontopaĝoj.','Enable order details customizations on email notifications' => 'Ebligu mendodetalojn personigojn en retpoŝtaj sciigoj','Display new sections and change order of sections on email notifications. Styles of order details on email notifications remain unchanged.' => 'Montru novajn sekciojn kaj ŝanĝu ordon de sekcioj pri retpoŝtaj sciigoj. Stiloj de mendaj detaloj pri retpoŝtaj sciigoj restas senŝanĝaj.','Thank you page' => 'Dankon paĝo','These options affect the thank you page, also known as order received or order confirmation pages.' => 'Ĉi tiuj opcioj influas la dankon paĝon, ankaŭ konata kiel mendo ricevita aŭ mendo konfirma paĝoj.','Order details layout' => 'Ordo detaloj aranĝo','Display the order details with wide layout on the thank you page' => 'Montru la menddetalojn kun larĝa aranĝo sur la dankopaĝo','Add widget areas to the thank you page' => 'Aldonu fenestraĵareojn al la dankopaĝo','These widget areas are used to add trust symbols and trust badges on the thank you page.' => 'Ĉi tiuj fenestraĵareoj estas uzataj por aldoni fidajn simbolojn kaj fidajn insignojn sur la dankopaĝo.','These options affect the thank you page , view order details on account pages and on email notifications.' => 'Ĉi tiuj opcioj influas la dankon-paĝon, rigardu menddetalojn sur kontopaĝoj kaj en retpoŝtaj sciigoj.','Order actions' => 'Ordigi agojn','Choose in which position to display the order actions.' => 'Elektu en kiu pozicio montri la ordonajn agojn.','Inside the order overview' => 'Ene de la ordo superrigardo','After the order overview' => 'Post la ordo superrigardo','After order sections' => 'Post ordo sekcioj','Order status' => 'Ordo statuso','Display the order status progress bar' => 'Montru la progresan stango de la mendo','Choose in which position to display the section.' => 'Elektu en kiu pozicio montri la sekcion.','After the order overview and payment instructions' => 'Post la mendo superrigardo kaj pago instrukcioj','Before the order items section' => 'Antaŭ la mendo eroj sekcio','Inside the order items section' => 'Ene de la sekcio de mendoj','After the order items section' => 'Post la mendo eroj sekcio','On the sidebar' => 'Sur la flanka kolumno','Order downloads' => 'Mendu elŝutojn','Gift message' => 'Donaca mesaĝo','Tools' => 'Iloj','Troubleshooting' => 'Solvado de problemoj','Debug options' => 'Sencimigaj opcioj','Debug mode' => 'Sencimiga reĝimo','Using debug mode affects the website performance. Only use this option while troubleshooting.' => 'Uzi sencimigan reĝimon influas la agadon de la retejo. Nur uzu ĉi tiun opcion dum solvo de problemoj.','Load unminified assets' => 'Ŝarĝu neminiigitajn aktivojn','Enhanced select fields' => 'Plibonigitaj elektaj kampoj','Replace <code>select2</code> dropdown components with <code>TomSelect</code>' => 'Anstataŭigu <code>select2</code> falmenuajn komponantojn per <code>TomSelect</code>','TomSelect is a simpler dropdown selection component which is less prone to errors than Select2, while offering the same features that are actually used on WooCommerce checkout pages.' => 'TomSelect estas pli simpla menu-elekta komponanto, kiu estas malpli inklina al eraroj ol Select2, dum ĝi ofertas la samajn funkciojn, kiuj efektive estas uzataj en la paĝoj de WooCommerce.','Fix automatic zoom-in on form fields' => 'Ripari aŭtomatan zoom-en sur formularaj kampoj','Set <code>font-size</code> inside form fields to 16px' => 'Agordu <code>font-size</code> ene de formularaj kampoj al 16px','When the font size inside form fields is smaller than 16px, Safari and other browsers might automatically zoom in on mobile devices to make the text easier to read. When this option is enabled, it will set the font size for inside the form fields to 16px on pages optimized by Fluid Checkout to avoid it zooming in.' => 'Kiam la tiparo ene de formularaj kampoj estas pli malgranda ol 16 px, Safaro kaj aliaj retumiloj povas aŭtomate zomi en porteblaj aparatoj por faciligi la legadon de la teksto. Kiam ĉi tiu opcio estas ebligita, ĝi agordos la tiparon por ene de la formularaj kampoj al 16px sur paĝoj optimumigitaj de Fluid Checkout por eviti ke ĝi zomi.','The shipping destination is always set to "Default to customer shipping address" when Fluid Checkout is activated. Customers can still provide different shipping and billing addresses during checkout, an option for setting the default billing address to be the same as the shipping address is available at WooCommerce > Settings > Fluid Checkout.' => 'La sendocelloko ĉiam estas agordita al &quot;Defaŭlta al klienta senda adreso&quot; kiam Fluid Checkout estas aktivigita. Klientoj ankoraŭ povas provizi malsamajn sendajn kaj fakturadresojn dum la kaso, opcio por agordi la defaŭltan fakturadreson por esti la sama kiel la sendadreso disponeblas ĉe WooCommerce &gt; Agordoj &gt; Fluid Checkout.','Fluid Checkout' => 'Fluida Checkout','Settings' => 'Agordoj','Support' => 'Subteno','<a target="_blank" href="%s">Upgrade to PRO</a> to unlock more options.' => '<a target="_blank" href="%s">Ĝisdatigu al PRO</a> por malŝlosi pliajn eblojn.','(PRO)' => '(PRO)','(experimental)' => '(eksperimenta)','This is an experimental feature and may not work as expected with all themes and plugins. Use it with caution.' => 'Ĉi tio estas eksperimenta funkcio kaj eble ne funkcias kiel atendite kun ĉiuj temoj kaj kromprogramoj. Uzu ĝin kun singardemo.','Confirm' => 'Konfirmu','Verify Phone Number' => 'Kontrolu Telefonan Numeron','Please verify your phone number.' => 'Bonvolu kontroli vian telefonnumeron.','The Bluehost Plugin' => 'La Bluehost Kromaĵo','Custom fields' => 'Propraj kampoj','Enable custom shipping email field' => 'Ebligu laŭmendan sendan retpoŝtan kampon','When enabled, the shipping email field from the Bluehost plugin will be added to the checkout form.' => 'Kiam ĝi estas ebligita, la senda retpoŝta kampo de la aldonaĵo Bluehost estos aldonita al la pagformularo.','Captcha Pro by BestWebSoft' => 'Captcha Pro de BestWebSoft','Captcha position' => 'Captcha pozicio','Define the position to display the captcha section. Some positions might not work depending on the captcha type chosen.' => 'Difinu la pozicion por montri la kapĉan sekcion. Iuj pozicioj eble ne funkcias depende de la kapĉa tipo elektita.','Selecting a pickup point is required before proceeding.' => 'Elekti kolektan punkton necesas antaŭ ol daŭrigi.','Pickup point' => 'Kaptpunkto','Pickup point not selected yet.' => 'Kaptpunkto ankoraŭ ne elektita.','Pickup point:' => 'Kaptopunkto:','Mailchimp for WooCommerce' => 'Mailchimp por WooCommerce','Subscribe checkbox' => 'Abonu markobutono','Override the position for the subscribe checkbox on the checkout page and always display it on the contact step' => 'Anstataŭigu la pozicion por la markobutono de abono sur la pagpaĝo kaj ĉiam montru ĝin sur la kontakta paŝo','Before Shipping Address' => 'Antaŭ Senda Adreso','(originally "%s")' => '(origine &quot;%s&quot;)','After Shipping Address' => 'Post sendo-adreso','Selecting a collection point is required when shipping with FermoPoint.' => 'Elekto de kolektopunkto estas postulata kiam sendado kun FermoPoint.','If checked informs typos in email to users. (This feature has been disabled because Fluid Checkout offers the feature. Changes to this option will not take effect)' => 'Se markita, informas tajperarojn en retpoŝto al uzantoj. (Ĉi tiu funkcio estis malŝaltita ĉar Fluid Checkout ofertas la funkcion. Ŝanĝoj al ĉi tiu opcio ne efektiviĝos)','Enable Mail Check: (disabled feature)' => 'Ebligu Poŝtkontrolon: (malŝaltita funkcio)','Carrier:' => 'Kondukisto:','Sendinblue - WooCommerce Email Marketing' => 'Sendinblue - Retpoŝta Merkatado de WooCommerce','Sign up checkbox position' => 'Registriĝi markobutono pozicio','Move the sign up checkbox to the contact step' => 'Movu la markobutonon de registriĝo al la kontakta paŝo','When enabled, the checkbox will be moved to the contact step independently of the display location defined in the Sendinblue plugin settings.' => 'Kiam ĝi estas ebligita, la markobutono estos movita al la kontakta paŝo sendepende de la montra loko difinita en la agordoj de la kromaĵo Sendinblue.','Theme Betheme' => 'Temo Betheme','Checkout progress' => 'Pagprogreso','Output the checkout steps section from Betheme on the checkout, cart and order received pages.' => 'Eligu la sekcion pri paŝaj paŝoj de Betheme sur la paĝoj de paĝoj, ĉaro kaj mendo ricevitaj.','Theme Fennik' => 'Temo Fennik','Breadcrumbs' => 'Panpecetoj','Output the breadcrumbs section from the Fennik theme when using Fluid Checkout header and footer.' => 'Eligu la sekcion de pano de la Fennik-temo kiam vi uzas Fluid Checkout-kapon kaj piedlinion.','Theme Go' => 'Temo Iru','My account layout' => 'Mia konta aranĝo','Enable wide layout on "My account" page.' => 'Ebligu larĝan aranĝon sur la paĝo &quot;Mia konto&quot;.','Theme Impreza' => 'Temo Impreza','Header' => 'Kapo','Spacing for site header at the checkout page (in pixels)' => 'Interspaco por retejo-kapo ĉe la pagpaĝo (en pikseloj)','Only applicable when using the Impreza theme header at the checkout page.' => 'Nur aplikebla kiam vi uzas la temon de Impreza ĉe la pagpaĝo.','Theme Kapee' => 'Temo Kapee','Output the checkout steps section from the Kapee theme on the checkout, cart and order received pages.' => 'Eligu la sekcion pri paŝaj paŝoj el la temo Kapee sur la ricevitajn paĝojn pri pago, ĉaro kaj mendo.','Theme Porto' => 'Temo Porto','Output the checkout steps section from the Porto theme on the checkout, cart and order received pages.' => 'Eligu la sekcion pri paŝaj paŝoj el la temo Porto sur la ricevitajn paĝojn pri pago, ĉaro kaj mendo.','Theme Pressmart' => 'Temo Pressmart','Output the checkout steps section from the Pressmart theme on the checkout, cart and order received pages.' => 'Eligu la sekcion pri paŝaj paŝoj de la temo Pressmart sur la paĝoj de paĝoj, ĉaro kaj mendo ricevitaj.','Theme The Gem' => 'Temo La Gemo','Output the checkout steps section from The Gem theme when using Fluid Checkout header and footer.' => 'Eligu la sekcion pri paŝaj paŝoj de The Gem-temo kiam vi uzas Fluid Checkout-kapon kaj piedlinion.','Theme Woodmart' => 'Temo Woodmart','Output the checkout steps section from the Woodmart theme when using Fluid Checkout header and footer.' => 'Eligu la sekcion pri paŝaj paŝoj el la temo Woodmart kiam vi uzas Fluid Checkout-kapon kaj piedlinion.','Checkout options' => 'Ebloj de kaso','Disable the theme checkout options' => 'Malebligu la temo-kontrolopciojn','The options display product image, quantity field, remove button and link to product page added by the theme are disabled by default.' => 'La opcioj montras produktan bildon, kvantokampon, forigu butonon kaj ligon al produkta paĝo aldonita de la temo estas malebligitaj defaŭlte.','Theme Atomion' => 'Temo Atomion','Order progress' => 'Ordoprogreso','Display order progress from the theme' => 'Montru ordo progreso de la temo','Display the form field labels visible on the page' => 'Montru la formkampajn etikedojn videblajn sur la paĝo','It is recommended to keep this option enabled for a better user experience.' => 'Oni rekomendas konservi ĉi tiun opcion ebligita por pli bona sperto de uzanto.','Theme ZK Nito' => 'Temo ZK Nito','Add extra fields to the checkout form' => 'Aldonu kromajn kampojn al la pagformularo','Adds an extra shipping email and shipping phone field to the shipping address section.' => 'Aldonas kroman sendan retpoŝton kaj sendotelefonan kampon al la sekcio de sendadreso.','Go to the home page' => 'Iru al la ĉefpaĝo','Skip to content' => 'Saltu al enhavo','Create an account' => 'Kreu konton','Log in to your account' => 'Ensalutu al via konto','Already have an account?' => 'Ĉu jam havas konton?','Logged in as %1$s. <a href="%2$s">Log out</a>' => 'Ensalutinta kiel %1$s. <a href="%2$s">Elsaluti</a>','Your order has a total amount due of %s. No further payment is needed.' => 'Via mendo havas totalan kvanton pagenda de %s. Ne necesas plua pago.','Close and continue with checkout' => 'Fermu kaj daŭrigu kun pagado','Continue' => 'Daŭrigu','Parcel terminal' => 'Pakaĵterminalo','<p>This widget has been replaced by Fluid Checkout. </p><p>When using Fluid Checkout, the checkout page needs to use the original WooCommerce shortcode <code>[woocommerce_checkout]</code> to work properly.</p><p>For more information, visit the Fluid Checkout documentation.</p>' => '<p>Ĉi tiu fenestraĵo estis anstataŭigita per Fluid Checkout.</p><p> Kiam vi uzas Fluid Checkout, la pagopaĝo devas uzi la originalan mallongkodon de WooCommerce <code>[woocommerce_checkout]</code> por taŭge funkcii.</p><p> Por pliaj informoj, vizitu la dokumentaron de Fluid Checkout.</p>','Theme The7' => 'Temo La7','Additional header sections' => 'Pliaj kapliniaj sekcioj','Output additional header sections if enabled in the theme settings.' => 'Eligu pliajn titolajn sekciojn se ebligita en la temo-agordoj.','More information' => 'Pliaj informoj','Pick a Locker' => 'Elektu Ŝlosfakon','Page template' => 'Paĝa ŝablono','Use `Order confirmation` page template for block-based themes' => 'Uzu la paĝoŝablonon `Mendkonfirmo` por blokbazitaj temoj','Only applies when using a block-based theme.' => 'Nur validas kiam oni uzas blok-bazitan temon.','WooCommerce Stripe Gateway' => 'WooCommerce Stripe Gateway','Payment form' => 'Pagformularo','Apply styles to the Stripe payment form fields' => 'Apliku stilojn al la kampoj de la pagformularo de Stripe','When enabled, Fluid Checkout will apply styles optimized for compatibility with the plugin. You may want to disable this if you are using custom styles for the Stripe payment form fields.' => 'Kiam ebligita, Fluid Checkout aplikos stilojn optimumigitajn por kongruo kun la kromprogramo. Eble vi volas malebligi ĉi tion se vi uzas kutimajn stilojn por la kampoj de la pagformularo de Stripe.','Theme Nyture' => 'Temo Nyture','Output the checkout steps section from the Nyture theme when using Fluid Checkout.' => 'Eligu la sekcion pri kaspaŝoj el la temo Nyture kiam vi uzas Fluid Checkout.','Maybe be forced as "required" if the billing address section is displayed after the shipping address section, and the billing phone field is set as "required. This is needed to ensure the shipping address can be copied to the billing address when that option is checked.' => 'Eble ĝi estu devigita kiel &quot;deviga&quot; se la faktura adreso estas montrata post la senda adreso, kaj la faktura telefona kampo estas agordita kiel &quot;deviga&quot;. Ĉi tio necesas por certigi, ke la senda adreso povas esti kopiita al la faktura adreso kiam tiu opcio estas markita.','Maybe be forced as "required" if the billing address section is displayed after the billing address section, and the billing phone field is set as "required. This is needed to ensure the billing address can be copied to the billing address when that option is checked.' => 'Eble ĝi estu devigita kiel &quot;deviga&quot; se la faktura adreso estas montrata post la faktura adreso, kaj la faktura telefono estas agordita kiel &quot;deviga&quot;. Ĉi tio necesas por certigi, ke la faktura adreso povas esti kopiita al la faktura adreso kiam tiu opcio estas markita.','Allows customers to make payments for orders that are either created manually by the store admin or not completed during checkout.' => 'Permesas al klientoj pagi por mendoj, kiujn aŭ la administranto de la vendejo kreis permane aŭ ne kompletigis dum la kaso.','The plugin WooCommerce Checkout Manager by Quadlayers is not compatible with Fluid Checkout' => 'La kromprogramo WooCommerce Checkout Manager de Quadlayers ne kongruas kun Fluid Checkout.','Company name field for billing address' => 'Kampo por la nomo de la firmao por faktura adreso','Change visibility for the company name field for the billing address section on the checkout form.' => 'Ŝanĝu videblecon por la kampo &quot;firmaa nomo&quot; por la sekcio pri faktura adreso en la kasformularo.','Theme Bookshelf' => 'Tema Librobreto','Output the checkout steps section from the Bookshelf theme when using Fluid Checkout.' => 'Eligu la sekcion pri kaspaŝoj el la temo Librobreto kiam vi uzas Fluid Checkout.','Account password' => 'Konto-pasvorto','An account will be created with the information provided at checkout when completing the order.' => 'Konto estos kreita kun la informoj donitaj ĉe la kaso kiam vi kompletigas la mendon.','Choose the style of the progress bar.' => 'Elektu la stilon de la progresbreto.','Bars' => 'Trinkejoj','Theme Ireca' => 'Temo Ireca','WooCommerce page layout' => 'Paĝaranĝo de WooCommerce','Force wide layout on WooCommerce pages' => 'Devigi larĝan aranĝon sur WooCommerce-paĝoj','This ensures the content section is not too narrow on WooCommerce pages including checkout, cart, order received, order pay and account pages.' => 'Ĉi tio certigas, ke la enhavsekcio ne estas tro mallarĝa sur WooCommerce-paĝoj, inkluzive de kaso, ĉaro, mendo ricevita, mendopago kaj kontopaĝoj.','Button label for applying coupon codesApply' => 'Apliki','Checkout step titleContact' => 'Kontaktu','Checkout step titleShipping' => 'Sendado','Checkout step titleBilling' => 'Fakturado','Checkout step titlePayment' => 'Pago','Checkout substep change link labelChange' => 'Ŝanĝi','Checkout substep save link labelSave changes' => 'Konservu ŝanĝojn','Substep review textNone.' => 'Neniu.','Substep review field format%1$s' => '%1$s','Substep review field format: with label%2$s: %1$s' => '%2$s: %1$s','Image uploader.No image selected.' => 'Neniu bildo elektita.','Settings sectionCart' => 'Ĉaro','Settings sectionOrder pay' => 'Ordonu pagon','Settings sectionThank you' => 'Dankon','Settings section titleIntegrations' => 'Integriĝoj','Settings section titleLicense keys' => 'Licencaj ŝlosiloj','Captcha positionBefore place order section' => 'Antaŭ meti mendo sekcio','Captcha positionBefore place order button' => 'Antaŭ meti mendo butonon','No shipping method chosen label for the order summary--' => '--','Close button aria-labelClose' => 'Fermu','Log in link label at checkout contact stepLog in' => 'Ensalutu','Log in separator label at for when guest checkout is disabledOr continue below' => 'Aŭ daŭrigu sube','Log in separator label at for when guest checkout is disabledOr continue as a guest' => 'Aŭ daŭrigu kiel gasto','Clear image selection on admin pages.Remove image' => 'Forigi bildon',] ];