return [ 'project-id-version' => 'fluid-checkout','mime-version' => '1.0','content-type' => 'text/plain; charset=utf-8','content-transfer-encoding' => '8bit','language-team' => 'Fluid Checkout','last-translator' => 'Fluid Checkout','pot-creation-date' => '2025-03-05 23:46+0000','report-msgid-bugs-to' => 'https://fluidcheckout.com/support','x-poedit-basepath' => '..','x-poedit-keywordslist' => '__;_e;_ex:1,2c;_n:1,2;_n_noop:1,2;_nx:1,2,4c;_nx_noop:1,2,3c;_x:1,2c;esc_attr__;esc_attr_e;esc_attr_x:1,2c;esc_html__;esc_html_e;esc_html_x:1,2c','x-poedit-searchpath-0' => '.','x-poedit-searchpathexcluded-0' => '*.js','x-poedit-sourcecharset' => 'UTF-8','plural-forms' => 'nplurals=2; plural=(n != 1);','messages' => ['WooCommerce' => 'WooCommerce','Go to Plugin Search' => 'Joan Plugin bilaketara','Activate %s' => 'Aktibatu %s','<strong>%1$s</strong> requires <strong>%2$s</strong> to be installed and activated. <a href="%4$s">%3$s</a>' => '<strong>%1$s</strong> <strong>%2$s</strong> instalatu eta aktibatu behar da. <a href="%4$s">%3$s</a>','Enter your code here' => 'Sartu zure kodea hemen','Add %s' => 'Gehitu %s','Remove' => 'Kendu','Order number and receipt will be sent to this email address.' => 'Eskaera zenbakia eta ordainagiria helbide elektroniko honetara bidaliko dira.','Apartment, unit, building, floor, etc.' => 'Apartamentua, unitatea, eraikina, solairua, etab.','Shipping phone' => 'Bidalketa telefonoa','Only used for shipping-related questions.' => 'Bidalketarekin lotutako galderetarako soilik erabiltzen da.','Multi-step' => 'Urrats anitzekoa','Single step' => 'Urrats bakarra','Click to go to the order summary' => 'Egin klik eskaeraren laburpenera joateko','Cart total:' => 'Saskia guztira:','Proceed to %s' => 'Joan %s-era','Proceed to contact' => 'Jarraitu harremanetan','Proceed to shipping' => 'Bidalketarekin jarraitu','Proceed to billing' => 'Jarraitu fakturaziora','Proceed to payment' => 'Ordainketarekin jarraitu','My contact' => 'Nire kontaktua','Shipping to' => 'Bidalketa','Shipping method' => 'Bidalketa metodoa','Additional notes' => 'Ohar osagarriak','Billing to' => 'Fakturazioa','Payment method' => 'Ordainketa modua','Step %1$s of %2$s' => '%1$s/%2$s urratsa','Change: %s' => 'Aldaketa: %s','An account will be created with the information provided.' => 'Emandako informazioarekin kontu bat sortuko da.','Not selected yet.' => 'Oraindik hautatu gabe.','Same as shipping address' => 'Bidalketa helbidearen berdina','Same as billing address' => 'Fakturazio helbidearen berdina','Order summary' => 'Eskaeraren laburpena','Edit cart' => 'Editatu saskia','Did you mean %s?' => '%s esan nahi al zenuen?','Change email address to: {suggestion-value}' => 'Aldatu helbide elektroniko honetara: {suggestion-value}','This is a required field.' => 'Hau nahitaezko eremua da.','This is not a valid email address.' => 'Hau ez da baliozko helbide elektronikoa.','This field does not match the related field value.' => 'Eremu hau ez dator bat erlazionatutako eremuaren balioarekin.','The CPF number "{cpf_number}" is invalid.' => '&quot;{cpf_number}&quot; CPF zenbakia ez da baliozkoa.','The CNPJ number "{cnpj_number}" is invalid.' => '&quot;{cnpj_number}&quot; CNPJ zenbakia ez da baliozkoa.','(Required)' => '(Derrigorrezkoa)','Checkout Header - Desktop' => 'Checkout goiburua - Mahaigaina','Display widgets on the checkout header for large screens. Only displayed if using the plugin\'s checkout header.' => 'Bistaratu widget-ak ordaintzeko goiburuan pantaila handietarako. Pluginaren ordainketa-goiburua erabiltzen baduzu soilik bistaratzen da.','Checkout Header - Mobile' => 'Checkout goiburua - Mugikorra','Display widgets below the checkout header for mobile devices. Only displayed if using the plugin\'s checkout header.' => 'Erakutsi widget-ak ordainketa-goiburuaren azpian gailu mugikorretarako. Pluginaren ordainketa-goiburua erabiltzen baduzu soilik bistaratzen da.','Checkout Sidebar' => 'Ordainketa alboko barra','Display widgets on the checkout sidebar after the order summary.' => 'Erakutsi widget-ak ordainketa alboko barran eskaeraren laburpenaren ondoren.','Checkout Order Summary' => 'Ordainketa eskaeraren laburpena','Display widgets inside the order summary at the checkout page.' => 'Erakutsi widget-ak eskaeraren laburpenaren barruan ordainketa-orrian.','Checkout Below Place Order' => 'Egin eskaeraren azpian','Display widgets below the place order button at the checkout page.' => 'Erakutsi widget-ak eskaera egiteko botoiaren azpian ordainketa-orrian.','Checkout Footer' => 'Ordainketa orria','Display widgets on the checkout footer. Only displayed if using the plugin\'s checkout footer.' => 'Bistaratu widget-ak ordainketa-oinean. Pluginaren ordainketa-oina erabiltzen baduzu bakarrik bistaratzen da.','Classic' => 'Klasikoa','Boxed' => 'Kutxatuta','Split' => 'Zatitu','Minimalist' => 'Minimalista','Fluid Checkout and Delivery Date plugins by CodeRockz' => 'Fluid Checkout eta Delivery Date pluginak CodeRockz-ek','<p>We detected that you are using the plugins Delivery & Pickup Date Time for WooCommerce (free and PRO) by CodeRockz. Integration with these plugins have been moved to Fluid Checkout PRO.</p>' => '<p>CodeRockz-ek WooCommercerako Delivery &amp; Pickup Date Time pluginak (doakoak eta PRO) erabiltzen ari zarela detektatu dugu. Plugin hauekin integrazioa Fluid Checkout PROra eraman da.</p>','Upgrade to PRO' => 'Berritu PRO-ra','Fluid Checkout database updated successfully' => 'Fluid Checkout datu-basea behar bezala eguneratu da','The database changes where applied successfully.' => 'Datu-basea ondo aplikatzen denean aldatzen da.','Ok' => 'Ados','Dismiss' => 'Baztertu','Fluid Checkout database update' => 'Fluid Checkout datu-basearen eguneratzea','Some changes to the database are required. As with any update, we recommend you to <strong>take a full backup of your website before proceeding</strong>.' => 'Datu-basean aldaketa batzuk egin behar dira. Eguneratze guztietan bezala, <strong>zure webgunearen babeskopia osoa egitea gomendatzen dizugu jarraitu aurretik</strong> .','Update database' => 'Eguneratu datu-basea','Fluid Checkout is not compatible with the Divi Builder checkout layout' => 'Fluid Checkout ez da bateragarria Divi Builder checkout diseinuarekin','<p>Fluid Checkout needs the classic WooCommerce shortcode-based checkout form to work. When using the Divi Builder to customize the look and feel of the checkout page, the classic shortcodes are replaced with custom widgets from the Divi Builder for each part of the checkout page. These custom widgets are not compatible with Fluid Checkout.</p><p>Please edit your checkout page replacing the Divi Builder widgets with the a text widget containing the shortcode <code>[woocommerce_checkout]</code>.</p>' => '<p>Fluid Checkout-ek WooCommerce kode laburrean oinarritutako ordainketa inprimaki klasikoa behar du funtzionatzeko. Divi Builder-a ordainketa-orriaren itxura pertsonalizatzeko erabiltzean, kode labur klasikoak Divi Builder-eko widget pertsonalizatuekin ordezkatzen dira ordainketa-orriaren zati bakoitzeko. Widget pertsonalizatu hauek ez dira bateragarriak Fluid Checkout-ekin.</p><p> Mesedez, editatu ordainketa orria Divi Builder widget-ak ordezkatuz <code>[woocommerce_checkout]</code> kode laburra duen testu widget batekin.</p>','Edit the checkout page' => 'Editatu ordainketa orria','Read the documentation.' => 'Irakurri dokumentazioa.','Germanized PRO multistep checkout feature needs to be disabled when using Fluid Checkout' => 'Germanized PRO urrats anitzeko ordainketa eginbidea desgaitu egin behar da Fluid Checkout erabiltzean','When using Fluid Checkout, the multistep feature from the Germanized PRO plugin becomes unnecessary and can cause <strong>critical errors while processing new orders</strong>. Please disable the Germanized PRO multistep checkout feature in the plugin settings.' => 'Fluid Checkout erabiltzean, Germanized PRO pluginaren urrats anitzeko eginbidea ez da beharrezkoa izango eta <strong>akats larriak sor ditzake eskaera berriak prozesatzen diren bitartean</strong> . Mesedez, desgaitu Germanized PRO urrats anitzeko ordainketa eginbidea pluginaren ezarpenetan.','Go to Germanized settings' => 'Joan germanizatutako ezarpenetara','Thanks for choosing Fluid Checkout!' => 'Eskerrik asko Fluid Checkout aukeratzeagatik!','You have been using the plugin for a while now. How do you like it so far? <br>Please give us a quick rating, we appreciate every bit of feedback and it encourages us to keep improving the plugin :)' => 'Plugin-a erabiltzen ari zara pixka bat. Nola gustatzen zaizu orain arte?<br> Mesedez, eman iezaguzu balorazio azkar bat, iritzi guztiak eskertzen ditugu eta plugina hobetzen jarraitzera animatzen gaitu :)','Rate the plugin' => 'Baloratu plugina','I need help!' => 'Laguntza behar dut!','The plugin WooCommerce Checkout Manager by Quadlayers is not compatibile with Fluid Checkout' => 'Quadlayers-en WooCommerce Checkout Manager plugina ez da Fluid Checkout-ekin bateragarria','Some features of WooCommerce Checkout Manager by Quadlayers might not work as expected when using it with Fluid Checkout. Read <a href="%s">our documentation</a> for more information and alternative solutions.' => 'Baliteke Quadlayers-en WooCommerce Checkout Manager-en eginbide batzuk espero bezala ez funtzionatzea Fluid Checkout-ekin erabiltzean. Irakurri <a href="%s">gure dokumentazioa</a> informazio gehiago eta irtenbide alternatiboak lortzeko.','Read documentation' => 'Irakurri dokumentazioa','Don\'t show this again' => 'Ez erakutsi hau berriro','Special offers' => 'Eskaintza bereziak','Extend all the goodness of Fluid Checkout to your cart page and thank you pages.' => 'Hedatu Fluid Checkout-en ontasun guztia zure saskiaren orrialdera eta eskerrik asko orrietara.','Fluid Checkout PRO' => 'Fluid Checkout PRO','Design templates, optimized cart and order received pages, account matching, and much more.' => 'Diseinatzeko txantiloiak, saskia optimizatu eta jasotako eskaerak orriak, kontu bat etortzea eta askoz gehiago.','The best tools to further <strong>improve your WooCommerce checkout conversion rate</strong> and make the purchase experience consistent on the entire journey.' => 'Tresna onenak <strong>zure WooCommerce ordainketen bihurketa-tasa gehiago hobetzeko</strong> eta erosketa-esperientzia koherentea izan dadin bidaia osoan.','More design templates' => 'Diseinu txantiloi gehiago','Optimized cart page' => 'Orga orri optimizatua','Optimized order received / thank you page' => 'Jasotako eskaera optimizatua / eskerrik asko orria','Edit cart contents at checkout page' => 'Editatu saskiaren edukia ordainketa orrian','Account matching / user matching' => 'Kontua bat etortzea / erabiltzailea bat etortzea','Express Checkout buttons from supported payment methods' => 'Onartutako ordainketa-metodoetako Express Checkout botoiak','Trust symbols on the cart and thank you pages' => 'Konfiantza-sinboloak saskian eta eskerrik asko orrialdeetan','More positions for the coupon code on the checkout page' => 'Kupoi kodearen posizio gehiago ordainketa orrian','Integrated coupon code field on the cart page' => 'Kupoi-kode integratua saskiaren orrian','International phone numbers' => 'Nazioarteko telefono zenbakiak','Gift messages and packing slips' => 'Opari mezuak eta ontziratze-agiriak','Local pickup and in-store collection' => 'Bertako bilketa eta denda barruko bilketa','Upgrade to PRO &mdash; 99 EUR' => 'Berritu PRO-ra — 99 EUR','Fluid Checkout PRO – Bundle' => 'Fluid Checkout PRO - Sorta','All PRO features + All add-ons for a special price.' => 'PRO ezaugarri guztiak + Gehigarri guztiak prezio berezi baten truke.','The bundle includes all add-ons we currently offer and Fluid Checkout add-ons we launch in the future.' => 'Sorteak gaur egun eskaintzen ditugun gehigarri guztiak eta etorkizunean abiarazten ditugun Fluid Checkout gehigarriak biltzen ditu.','226 EUR value (Save 42%)' => '226 euroko balioa (Gorde % 42)','Google Address Autocomplete' => 'Google Helbidea osatze automatikoa','Address Book' => 'Helbide Liburua','EU-VAT Assistant' => 'EB-BEZ laguntzailea','All future add-ons *' => 'Etorkizuneko gehigarri guztiak *','* For as long as you have an active subscription.' => '* Harpidetza aktiboa duzun bitartean.','Get the Bundle – Only 129 EUR' => 'Lortu sorta - 129 euro bakarrik','All prices shown in EUR. If there are any divergencies with the prices on our website, the offers shown on the website superseed these and will be applied.' => 'Prezio guztiak eurotan agertzen dira. Gure webguneko prezioekin desberdintasunik balego, webgunean agertzen diren eskaintzak hauek gainditzen ditu eta aplikatuko dira.','Add-ons' => 'Gehigarriak','Enhance your checkout experience with these add-ons.' => 'Hobetu ordainketa-esperientzia gehigarri hauekin.','I already have it – Hide this offer' => 'Dagoeneko daukat - Ezkutatu eskaintza hau','Installed &mdash; Activate' => 'Instalatua — Aktibatu','Activated' => 'Aktibatuta','Up to 40% less checkout fields to fill in.' => 'Gehienez % 40 gutxiago ordaintzeko eremuak betetzeko.','Avoid delivery delays and unsatisfied customers. Collect the <strong>correct address information</strong> from the first time they buy with you.' => 'Saihestu entrega-atzerapenak eta pozik ez dauden bezeroak. Bildu <strong>helbide-informazio zuzena</strong> zurekin erosten duten lehen alditik.','Get this add-on &mdash; 29 EUR' => 'Eskuratu gehigarri hau - 29 EUR','Address book' => 'Helbide liburua','Multiple saved addresses for shipping and billing.' => 'Bidalketa eta fakturaziorako gordetako hainbat helbide.','Let customers <strong>save multiple shipping and billing addresses</strong> on their account and choose which ones to use at checkout and cart pages.' => 'Utzi bezeroei <strong>bidalketa- eta fakturazio-helbide anitz gorde</strong> beren kontuan eta aukeratu zein erabili ordaintzeko eta saskiaren orrietan.','Get this add-on &mdash; 59 EUR' => 'Eskuratu gehigarri hau - 59 EUR','Simplified EU-VAT validation for your store.' => 'EB-BEZaren baliozkotze sinplifikatua zure dendarako.','Collect and <strong>validate EU VAT numbers at the checkout page</strong>, removes tax charges on reverse charge basis and confirms customer location when needed.' => 'Bildu eta <strong>balioztatu EBko BEZ-zenbakiak ordainketa-orrian</strong> , zerga-gastuak kendu alderantzizko kargaren arabera eta bezeroaren kokapena berresten du behar denean.','Get this add-on &mdash; 39 EUR' => 'Eskuratu gehigarri hau - 39 EUR','All add-ons are sold separately and require the <strong>Fluid Checkout Lite</strong> plugin to be installed and activated, except when noted.' => 'Gehigarri guztiak bereiz saltzen dira eta <strong>Fluid Checkout Lite</strong> plugina instalatu eta aktibatu behar da, adierazitakoan izan ezik.','Select an image' => 'Hautatu irudi bat','No image selected.' => 'Ez da irudirik hautatu.','Remove image' => 'Kendu irudia','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to get your license key, or <a href="%s" target="_blank">purchase a new license key</a>.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Hasi saioa zure kontuan</a> zure lizentzia-gakoa lortzeko, edo <a href="%s" target="_blank">lizentzia-gako berri bat erosteko</a> .','Valid until %s.' => '%s arte baliozkoa.','Expired on %s.' => '%s egunean iraungi zen.','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to renew your license key and continue to receive updates and support.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Hasi saioa zure kontuan</a> zure lizentzia-gakoa berritzeko eta eguneraketak eta laguntza jasotzen jarraitzeko.','License key cancelled.' => 'Lizentzia-giltza bertan behera utzi da.','<a href="%s" target="_blank">Purchase a new license key</a> and continue to receive updates and support.' => '<a href="%s" target="_blank">Erosi lizentzia-gako berri bat</a> eta jarraitu eguneratzeak eta laguntza jasotzen.','Error: ' => 'Errorea: ','Setup & Documentation' => 'Konfigurazioa eta dokumentazioa','Great! Your checkout page is now running on Fluid Checkout.' => 'Bikaina! Zure ordainketa orria Fluid Checkout-en martxan dago.','Here are a few resources for you to get started:' => 'Hona hemen baliabide batzuk hasteko:','Read the installation guide <a href="%s" target="_blank">Getting stated with Fluid Checkout</a>.' => 'Irakurri Instalazio-gida <a href="%s" target="_blank">Getting stated Fluid Checkout-ekin</a> .','Setup layout and design on the <a href="%s">checkout options</a>.' => 'Konfiguratu diseinua eta diseinua <a href="%s">ordainketa-aukeretan</a> .','Check if there are any <a href="%s">integration options</a> available for other plugins you have installed.' => 'Egiaztatu instalatu dituzun beste pluginetarako <a href="%s">integrazio-aukerarik</a> dagoen.','Visit <a href="%s" target="_blank">our documentation</a> for more information about Fluid Checkout features.' => 'Bisitatu <a href="%s" target="_blank">gure dokumentazioa</a> Fluid Checkout eginbideei buruzko informazio gehiago lortzeko.','If you ever need help, <a href="%s" target="_blank">open a support ticket</a> on our official support channel.' => 'Inoiz laguntza behar baduzu, <a href="%s" target="_blank">ireki laguntza-txartel bat</a> gure laguntza-kanal ofizialean.','Cart layout' => 'Saskiaren diseinua','Cart optimizations' => 'Saskiaren optimizazioak','Enable cart page optimizations' => 'Gaitu saskiaren orriaren optimizazioak','Display the cart page with a layout similar to the checkout page.' => 'Erakutsi saskiaren orria ordainketa-orriaren antzeko diseinu batekin.','Header and footer' => 'Goiburua eta oina','Header and footer template' => 'Goiburua eta oinaren txantiloia','Controls whether to use the distraction free page header and footer or keep the currently active theme\'s header and footer.' => 'Distrakziorik gabeko orriaren goiburua eta oina erabili edo unean aktibo dagoen gaiaren goiburua eta oina mantendu behar den kontrolatzen du.','Distraction free header and footer' => 'Distrakziorik gabeko goiburua eta oina','Theme\'s header and footer' => 'Gaiaren goiburua eta oina','Cart elements' => 'Orga-elementuak','Make the order summary stay visible on the cart page while scrolling' => 'Egin eskaeraren laburpena ikusgai saskiaren orrian korritzen duzun bitartean','Coupon codes' => 'Kupoi kodeak','Select position where to display the coupon codes section on the cart page. Only applicable when AJAX cart and the integrated coupon codes in the checkout page are enabled.' => 'Hautatu saskiaren orrian kupoi-kodeen atala bistaratzeko posizioa. AJAX saskia eta ordainketa-orrian kupoi-kode integratuak gaituta daudenean soilik aplikagarria da.','Hidden' => 'Ezkutuan','Before the cart items section' => 'Saskia elementuen atalaren aurretik','Inside the cart items section' => 'Saskiaren elementuen atalaren barruan','After the cart items section' => 'Saskia elementuen atalaren ondoren','Before the shipping section' => 'Bidalketa atalaren aurretik','After the shipping section' => 'Bidalketa atalaren ondoren','Before the order summary' => 'Eskaeraren laburpena baino lehen','Inside the order summary' => 'Eskaeraren laburpenaren barruan','Before the order totals' => 'Aginduak guztizkoak baino lehen','After the order totals' => 'Aginduaren guztizkoen ondoren','After the order summary' => 'Aginduaren laburpena egin ondoren','Shipping' => 'Bidalketa','Select position where to display the shipping costs and calculator on the cart page.' => 'Hautatu saskiaren orrian bidalketa-gastuak eta kalkulagailua bistaratzeko posizioa.','Display as a cart section' => 'Erakutsi saskia atal gisa','Cross-sells' => 'Salmenta gurutzatuak','Select position where to display the cross-sells section on the cart page.' => 'Hautatu saskiaren orrialdeko salmenta gurutzatuak atala bistaratzeko posizioa.','Before the cart actions' => 'Orga ekintzen aurretik','After the cart actions' => 'Orga ekintzen ostean','Layout of cross-sell items on the cart page.' => 'Salmenta gurutzatutako elementuen diseinua saskiaren orrian.','Optimized horizontal cross-sells layout' => 'Salmenta gurutzatuen diseinu horizontal optimizatua','Theme\'s cross-sells layout' => 'Gaiaren salmenta gurutzatuen diseinua','Number of cross-sell items to display on the cart page' => 'Saskiaren orrian bistaratu beharreko salmenta gurutzatutako elementu kopurua','Trust symbols &amp; badges' => 'Konfiantza-sinboloak eta txapak','Add widget areas to the cart page' => 'Gehitu widget-eremuak saskiaren orrira','These widget areas are used to add trust symbols and trust badges on the cart page.' => 'Widget-eremu hauek saskiaren orrian konfiantza-ikurrak eta konfiantza-txapak gehitzeko erabiltzen dira.','Checkout' => 'Ordainketa','Layout &amp; design' => 'Diseinua eta diseinua','Checkout layout' => 'Ordainketa diseinua','Design template' => 'Diseinu txantiloia','General styles for the checkout steps, order summary and other sections. Might also apply to other pages such as the Cart, Order Received and View Order pages.' => 'Ordainketa urratsetarako, eskaeraren laburpena eta beste ataletarako estilo orokorrak. Baliteke beste orri batzuei ere aplikatzea, hala nola Saskia, Jasotako eskaera eta Ikusi eskaera orrietan.','Dark mode' => 'Modu iluna','Enable dark mode' => 'Gaitu modu iluna','Header and footer templates' => 'Goiburuko eta oinetako txantiloiak','We recommend using the distraction free header and footer to avoid distractions at the checkout page. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Read the research about cart abandonment</a>.' => 'Distrakziorik gabeko goiburua eta orri-oina erabiltzea gomendatzen dugu ordainketa-orrian distrakziorik ez izateko. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Irakurri gurdiak uzteari buruzko ikerketa</a> .','Logo image' => 'Logotipoaren irudia','Choose an image to be displayed on the checkout page header. Only applies when using the distraction free header and footer.' => 'Aukeratu ordainketa-orriaren goiburuan bistaratzeko irudi bat. Distrakziorik gabeko goiburua eta oina erabiltzean soilik aplikatzen da.','Header background color' => 'Goiburuko atzeko planoaren kolorea','Choose a background color for the checkout page header. Only applies when using the distraction free header and footer.' => 'Aukeratu atzeko planoko kolorea ordainketa-orriaren goibururako. Distrakziorik gabeko goiburua eta oina erabiltzean soilik aplikatzen da.','HTML color value. ie: #f3f3f3' => 'HTML kolore-balioa. hau da: #f3f3f3','Page background color' => 'Orriaren atzeko planoaren kolorea','Choose a background color for the checkout page. Color is applied to the <em>body</em> element.' => 'Aukeratu atzeko planoko kolorea ordainketa-orrirako. Kolorea <em>gorputz-</em> elementuari aplikatzen zaio.','Footer background color' => 'Oinaren atzeko planoaren kolorea','Choose a background color for the checkout page footer. Only applies when using the distraction free header and footer.' => 'Aukeratu atzeko planoko kolorea ordainketa-orriaren oinerako. Distrakziorik gabeko goiburua eta oina erabiltzean soilik aplikatzen da.','Checkout elements' => 'Ordainketa elementuak','Progress bar' => 'Aurrerapen-barra','Display the checkout progress bar when using multi-step checkout layout' => 'Bistaratu ordainketaren aurrerapen-barra urrats anitzeko ordainketa-diseinua erabiltzen duzunean','Make the checkout progress bar stay visible while scrolling' => 'Egin ordainketaren aurrerapen-barra ikusgai egon dadin korritzen duzun bitartean','Express checkout' => 'Ordainketa express','Enable the express checkout section' => 'Gaitu ordainketa zehatza atala','Displays the express checkout section at checkout when supported payment gateways have this feature enabled.' => 'Ordainketa-pasabide onargarriek eginbide hau gaituta dutenean, ordainketa zehatzeko atala bistaratzen du ordainketetan.','Display express checkout buttons in one line for larger screens' => 'Bistaratu ordaintzeko botoiak lerro batean pantaila handiagoetarako','Ignore additional checkout required fields when paying with a compatible express checkout payment gateway' => 'Ez ikusi ordaintzeko beharrezkoak diren eremu gehigarriak ordainketa espresuko ordainketa-pasabide bateragarri batekin ordaintzerakoan','Make the order summary stay visible while scrolling' => 'Egin eskaeraren laburpena ikusgai egon korritzen duzun bitartean','Choose a background color for the order summary section.' => 'Aukeratu atzeko planoko kolorea eskaeraren laburpenaren atalerako.','Highlight the order totals row in the order summary table' => 'Nabarmendu eskaeraren guztizkoen errenkada eskaeraren laburpen-taulan','Most useful when the order summary section does not have a highlighted background color. Might also apply to the Cart, Order Received and View Order pages when using Fluid Checkout PRO.' => 'Erabilgarriena eskaeraren laburpenaren atalak atzeko planoko kolore nabarmenik ez duenean. Saskia, Jasotako eskaera eta Ikusi eskaera orrietan ere aplika daiteke Fluid Checkout PRO erabiltzen duzunean.','Action link on the order summary at checkout.' => 'Ekintza-esteka eskaeraren laburpenean ordaintzean.','Edit cart link' => 'Editatu saskiaren esteka','Cart items count' => 'Saskiko elementuak zenbatzen dira','Link to shop page' => 'Esteka denda orrira','Cart items' => 'Saskia elementuak','Enable options to edit cart items on the checkout page' => 'Gaitu saskiko elementuak editatzeko aukerak ordainketa-orrian','Allow customers to change product quantities or removing items directly at the checkout page.' => 'Eman bezeroei produktuen kantitateak aldatzeko edo zuzenean elementuak kentzeko ordainketa orrian.','Hide cart items errors at the checkout page' => 'Ezkutatu saskiko elementuen erroreak ordainketa-orrian','Do not display the cart items errors message at the top of the checkout page. When submitting the checkout form to complete the purchase, these error messages will always be displayed.' => 'Ez erakutsi saskiko elementuen erroreen mezua ordainketa-orriaren goialdean. Erosketa osatzeko ordainketa-inprimakia bidaltzean, errore-mezu hauek beti agertuko dira.','Place order' => 'Egin eskaera','Define the position to display "Place order" and terms checkbox section.' => 'Definitu &quot;Egin eskaera&quot; eta baldintzak kontrol-laukiaren atala bistaratzeko posizioa.','Some options might not be compatible with some plugins and themes.' => 'Baliteke aukera batzuk plugin eta gai batzuekin bateragarriak ez izatea.','Below the payment section' => 'Ordainketa atalaren azpian','Below the order summary' => 'Eskaeraren laburpenaren azpian','Both below the payment section and the order summary' => 'Ordainketa atalaren azpian eta eskaeraren laburpena','Add widget areas to the checkout page' => 'Gehitu widget eremuak ordainketa-orrian','These widget areas are used to add trust symbols and trust badges on the checkout page.' => 'Widget-eremu hauek ordainketa-orrian konfiantza-ikurrak eta konfiantzazko bereizgarriak gehitzeko erabiltzen dira.','Display checkout sidebar widgets only when viewing the last checkout step on mobile devices when using multi-step checkout layout' => 'Bistaratu ordainketa alboko barraren widget-ak gailu mugikorretan azken ordainketa-urratsa ikusten duzunean urrats anitzeko ordainketa-diseinua erabiltzen denean','Enable integrated coupon code section' => 'Gaitu kupoi-kode integratua atala','Only applicable if use of coupon codes are enabled in the WooCommerce settings.' => 'WooCommerce ezarpenetan kupoi kodeak erabiltzea gaituta badago soilik aplikagarria.','Display the coupon codes section title' => 'Erakutsi kupoi-kodeen atalaren izenburua','Only applicable when coupon code is displayed as a separate section on the checkout or cart pages.' => 'Kupoi-kodea ordainketa-orrietan edo gurdi-orrietan aparteko atal gisa bistaratzen denean soilik aplikagarria da.','Select position where to display the coupon codes section on the checkout page.' => 'Hautatu ordainketa-orrian kupoi-kodeen atala bistaratzeko posizioa.','Before the checkout steps' => 'Ordainketa urratsen aurretik','As a substep before payment methods' => 'Ordainketa metodoen aurreko azpiurrats gisa','As a substep after payment methods' => 'Ordainketa-metodoen ondorengo azpi-urrats gisa','Select style of the "apply coupon" button. Only applicable when the coupon code section is displayed "Before the checkout steps" on the checkout page, or "Before the cart items section" on the cart page.' => 'Hautatu &quot;aplikatu kupoia&quot; botoiaren estiloa. Bakarrik aplikagarria da kupoi-kodearen atala ordainketa-orrian &quot;Orroa egiteko urratsen aurretik&quot; edo saskiaren orrian &quot;Saskiaren elementuen atala baino lehen&quot; bistaratzen denean.','Default button style' => 'Botoiaren estilo lehenetsia','"Add" link button' => '&quot;Gehitu&quot; esteka botoia','Checkout fields' => 'Ordainketa eremuak','Optional fields' => 'Aukerako eremuak','Hide optional fields behind a link button' => 'Ezkutatu aukerako eremuak esteka-botoi baten atzean','It is recommended to keep this option checked to reduce the number of open input fields, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">read the research</a>.' => 'Aukera hau markatuta mantentzea gomendatzen da sarrera-eremu irekien kopurua murrizteko, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">irakurri ikerketa</a> .','Do not hide "Address line 2" fields behind a link button' => 'Ez ezkutatu &quot;Helbidearen 2. lerroa&quot; eremuak esteka-botoi baten atzean','Recommended only when most customers actually need the "Address line 2" field, or when getting the right shipping address is crucial (ie. if delivering food and other perishable products).' => 'Gomendatzen da bezero gehienek &quot;Helbidea 2. lerroa&quot; eremua benetan behar dutenean edo bidalketa-helbide egokia lortzea funtsezkoa denean (adibidez, janaria eta beste produktu galkorrak entregatzen badituzu).','Display the "Add" link buttons in lowercase' => 'Erakutsi &quot;Gehitu&quot; esteka-botoiak minuskulaz','Make the labels of optional field "Add" link button as <code>lowercase</code> (ie. "Add phone number" instead of "Add Phone Number"). This option also affects the link buttons for coupon code fields.' => 'Egin aukerako eremuko &quot;Gehitu&quot; esteka-botoiaren etiketak <code>lowercase</code> gisa (adibidez, &quot;Gehitu telefono-zenbakia&quot; &quot;Gehitu telefono-zenbakia&quot;ren ordez). Aukera honek kupoi-kodeen eremuen esteka-botoiei ere eragiten die.','Billing address' => 'Fakturazio helbidea','Select position where to display the billing address section on the checkout page.' => 'Hautatu ordainketa-orrian fakturazio helbidearen atala bistaratzeko posizioa.','As a step before shipping' => 'Bidalketa aurreko urrats gisa','As a sub-step before the shipping address section' => 'Bidalketa helbidearen atalaren aurreko azpi-urrats gisa','As a sub-step after the shipping address section' => 'Bidalketa helbidearen atalaren ondorengo azpi-urrats gisa','As a step after shipping' => 'Bidalketa ondorengo urrats gisa','As a sub-step before the payment section' => 'Ordainketa atalaren aurreko azpi-urrats gisa','Force billing and shipping addresses to the same (single section)' => 'Behartu fakturazio eta bidalketa helbideak berdinera (atal bakarra)','Checkbox for "Same as shipping/billing address" checked by default' => '&quot;Bidalketa/fakturazio-helbidearen berdina&quot; kontrol-laukia lehenespenez markatuta dago','The checkbox "Same as shiping address" will start as checked by default when the shipping address section is displayed first. <br>The checkbox "Same as billing address" will start as checked by default when the billing address section is displayed first. <br> It is recommended to leave this option checked as to significantly reduce the number of open input fields at checkout, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">read the research</a>.' => '&quot;Bidalketa-helbidearen berdina&quot; kontrol-laukia lehenespenez markatuta dagoen moduan hasiko da bidalketa-helbidearen atala lehenik bistaratzen denean.<br> &quot;Fakturazio-helbidearen berdina&quot; kontrol-laukia lehenespenez markatuta dagoen moduan hasiko da fakturazio-helbidearen atala lehenik bistaratzen denean.<br> Aukera hau markatuta uztea gomendatzen da ordainketan sarrerako eremu irekien kopurua nabarmen murrizteko, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">irakurri ikerketa</a> .','Highlight the billing address section in the checkout form' => 'Nabarmendu fakturazio helbidearen atala ordainketa egiteko formularioan','Shipping address' => 'Bidalketa helbidea','Highlight the shipping address section in the checkout form' => 'Nabarmendu bidalketa helbidearen atala ordainketa egiteko formularioan','Shipping methods' => 'Bidalketa metodoak','Choose in which position to display the shipping methods section.' => 'Aukeratu bidalketa-metodoen atala zein posiziotan bistaratu.','Before shipping address' => 'Bidalketa helbidea baino lehen','After shipping address' => 'Bidalketa helbidearen ondoren','Prevent automatic selection of the first shipping method' => 'Eragotzi lehen bidalketa-metodoaren hautaketa automatikoa','When enabled, the first shipping method available will not be automatically selected when no other shipping method was previously selected for each shipping package. <br>This option will be automatically enabled if the option for clearing the selected shipping method is enabled for the Local Pickup feature.' => 'Gaituta dagoenean, eskuragarri dagoen lehen bidalketa-metodoa ez da automatikoki hautatuko bidalketa-pakete bakoitzeko beste bidalketa-metodorik hautatu ez denean.<br> Aukera hau automatikoki gaituko da hautatutako bidalketa-metodoa garbitzeko aukera gaituta badago Tokiko bilketa eginbiderako.','Local pickup' => 'Tokiko bilketa','Removes shipping address section when a local pickup shipping method is selected.' => 'Bidalketa helbidearen atala kentzen du tokiko bilketa bidalketa metodo bat hautatzen denean.','Replaces the shipping address with the pickup point location when a local pickup shipping method is selected.' => 'Bidalketa helbidea bilketa puntuaren kokapenarekin ordezkatzen du tokiko bilketa bidalketa metodo bat hautatzen denean.','Show option to clear shipping methods in the pickup location substep' => 'Erakutsi bidalketa-metodoak garbitzeko aukera jasotzeko kokapenaren azpi-urratsean','Show a link button on the pickup location substep to clear the chosen shipping methods. This can be used to allow showing the shipping address section again if a local pickup method was previously selected.' => 'Erakutsi esteka-botoia bilketa-kokapeneko azpi-urratsean aukeratutako bidalketa-metodoak garbitzeko. Hau erabil daiteke bidalketa helbidearen atala berriro erakusteko aldez aurretik tokiko bilketa-metodo bat hautatu bazen.','Choose which address to save as the shipping address for local pickup orders.' => 'Aukeratu zein helbide gorde nahi duzun tokiko bilketa-eskaeretarako bidalketa-helbide gisa.','Save the selected pickup location' => 'Gorde hautatutako bilketa-lekua','Save same as the billing address' => 'Gorde fakturazio helbidea bezala','Do not save any shipping address' => 'Ez gorde bidalketa-helbiderik','Company name field for shipping address' => 'Enpresaren izenaren eremua bidalketa-helbiderako','Change visibility for the company name field for the shipping address section on the checkout form.' => 'Aldatu ordainketa-inprimakiko bidalketa helbidearen ataleko enpresaren izenaren eremuaren ikusgarritasuna.','If field is set as "optional", which is the default visibility state, no changes will be applied to let other plugins apply any changes they need.' => 'Eremua &quot;aukerakoa&quot; gisa ezartzen bada, hau da, ikusgarritasun-egoera lehenetsia, ez da aldaketarik aplikatuko beste plugin-ek behar dituzten aldaketak aplika ditzaten.','Hidden (remove field)' => 'Ezkutatuta (kendu eremua)','Optional' => 'Aukerakoa','Required' => 'Beharrezkoa','Add shipping phone field to the checkout form.' => 'Gehitu bidalketa-telefonoaren eremua ordainketa-inprimakiari.','Choose in which step to display the shipping phone field.' => 'Aukeratu zein urrats bistaratzeko bidalketa-telefonoaren eremua.','Contact step' => 'Harremanetarako urratsa','Billing phone' => 'Fakturazio telefonoa','Add billing phone field to the checkout form.' => 'Gehitu fakturazio telefonoaren eremua ordainketa egiteko formularioan.','Choose in which step to display the billing phone field.' => 'Aukeratu zein urrats bistaratzeko fakturazio telefonoaren eremua.','Enable international phone number format and validation for phone fields' => 'Gaitu nazioarteko telefono-zenbakien formatua eta baliozkotzea telefono-eremuetarako','Format and validate phone numbers according to each country.' => 'Formateatu eta baliozkotu telefono-zenbakiak herrialde bakoitzaren arabera.','Enable phone number validation based on country rules' => 'Gaitu telefono-zenbakiaren baliozkotzea herrialdeko arauetan oinarrituta','When disabled, the phone field validation will not check if country or area codes are valid for the country.' => 'Desgaituta dagoenean, telefonoaren eremuaren baliozkotzeak ez du egiaztatuko herrialde edo eremu-kodeak herrialderako balio duten ala ez.','Only show allowed countries for shipping or billing' => 'Erakutsi baimendutako herrialdeak soilik bidaltzeko edo fakturatzeko','When enabled, only the countries allowed for shipping will be available in the shipping phone field, and only countries allowed for billing will be available for the billing phone field.' => 'Gaituta dagoenean, bidalketak egiteko baimendutako herrialdeak soilik egongo dira eskuragarri bidalketa-telefonoaren eremuan, eta fakturaziorako baimendutako herrialdeak bakarrik egongo dira erabilgarri fakturazio-telefonoaren eremuan.','Show country code beside the flag' => 'Erakutsi herrialde-kodea banderaren ondoan','Show an example of a valid phone number inside phone fields' => 'Erakutsi baliozko telefono-zenbaki baten adibide bat telefono-eremuen barruan','Do not change placeholders' => 'Ez aldatu leku-markak','Show if not defined' => 'Erakutsi definitu ez bada','Always show' => 'Erakutsi beti','Order notes' => 'Ordena oharrak','Define the visibility of the additional order notes field.' => 'Definitu eskaera-ohar osagarrien eremuaren ikusgarritasuna.','Gift options' => 'Opari aukerak','Display gift message and other gift options at the checkout page' => 'Erakutsi opari-mezua eta beste opari-aukera batzuk ordainketa-orrian','Allow customers to add a gift message and other gift related options to the order.' => 'Eman bezeroei opari-mezu bat eta opariekin lotutako beste aukera batzuk gehitzeko eskaerari.','Display the gift message fields always expanded' => 'Bistaratu opari-mezuen eremuak beti zabalduta','Display the gift message as part of the order details table instead of a separate section' => 'Bistaratu opari-mezua eskaeraren xehetasunen taularen zati gisa, atal bereizi baten ordez','This option affects the order confirmation page (thank you page) and order details on account pages, emails and packing slips.' => 'Aukera honek eskaeraren berrespen-orrian (eskerrak orria) eta kontu-orrietan, mezu elektronikoetan eta ontziratze-agirian eragiten du.','Account matching' => 'Kontua bat etortzea','Enable the account matching feature' => 'Gaitu kontua lotzeko eginbidea','Associate the guest customer\'s orders with their existing account when an account already exists with the customer\'s contact details.' => 'Lotu bezero gonbidatuaren eskaerak lehendik duen kontuarekin kontu bat dagoeneko bezeroaren kontaktu-datuekin dagoenean.','Display message when an account exists with the email address provided' => 'Bistaratu mezua emandako helbide elektronikoarekin kontu bat dagoenean','Replaces the account creation fields with a notification and option to log in. In some contexts, it might be recommended to leave this option disabled to protect the privacy of customers.' => 'Kontua sortzeko eremuak jakinarazpen batekin eta saioa hasteko aukerarekin ordezkatzen ditu. Zenbait testuingurutan, baliteke aukera hau desgaituta uztea bezeroen pribatutasuna babesteko.','Dashboard' => 'Aginte-panela','Integrations' => 'Integrazioak','No integrations available at the moment on this section. The options related to each plugin will only appear here if that plugin is activated.' => 'Momentuz ez dago integraziorik atal honetan. Plugin bakoitzari lotutako aukerak hemen agertuko dira plugin hori aktibatuta badago.','License keys' => 'Lizentzia-gakoak','No license keys options available at the moment on this section. The options related to each plugin or add-on will appear here when that plugin is activated.' => 'Momentuz ez dago lizentzia-gakoen aukerarik atal honetan. Plugin edo gehigarri bakoitzari lotutako aukerak hemen agertuko dira plugin hori aktibatuta dagoenean.','Visit our website for more information about our plugins and add-ons.' => 'Bisitatu gure webgunea gure pluginei eta gehigarriei buruzko informazio gehiago lortzeko.','Order pay page' => 'Ordainketa eskaera orria','Order pay' => 'Ordainketa agindu','Enable order pay page optimizations' => 'Gaitu eskaera ordaintzeko orrien optimizazioak','Changes the layout of order pay page for existing orders pending payment from the customer.' => 'Bezeroaren ordainketa zain dauden eskaeren eskaera-orriaren diseinua aldatzen du.','Add widget areas to the order pay page' => 'Gehitu widget eremuak eskaera ordaintzeko orrian','These widget areas are used to add trust symbols and trust badges on the order pay page.' => 'Widget-eremu hauek konfiantza-ikurrak eta konfiantzazko bereizgarriak gehitzeko erabiltzen dira eskaera ordaintzeko orrian.','Thank you page & order details layout' => 'Eskerrik asko orriaren eta eskaeraren xehetasunen diseinua','Order details optimizations' => 'Eskaeraren xehetasunen optimizazioak','Enable thank you page and order details optimizations' => 'Gaitu eskerrik asko orria eta eskaeraren xehetasunen optimizazioak','Changes the layout of order details on the thank you page and account pages.' => 'Eskaeraren xehetasunen diseinua aldatzen du esker onen orrian eta kontuaren orrietan.','Enable order details customizations on email notifications' => 'Gaitu eskaeraren xehetasunen pertsonalizazioak posta elektronikoko jakinarazpenetan','Display new sections and change order of sections on email notifications. Styles of order details on email notifications remain unchanged.' => 'Erakutsi atal berriak eta aldatu posta elektronikoko jakinarazpenetan atalen ordena. Posta elektronikoko jakinarazpenetako eskaeraren xehetasunen estiloak ez dira aldatu.','Thank you page' => 'Eskerrik asko orrialdea','These options affect the thank you page, also known as order received or order confirmation pages.' => 'Aukera hauek eskerrak emateko orrialdeari eragiten diote, jasotako eskaera edo eskaera berrespen orri gisa ere ezagutzen dena.','Order details layout' => 'Eskaeraren xehetasunen diseinua','Display the order details with wide layout on the thank you page' => 'Erakutsi eskaeraren xehetasunak diseinu zabalarekin eskerrak emateko orrian','Add widget areas to the thank you page' => 'Gehitu widget-eremuak eskerrak emateko orrialdean','These widget areas are used to add trust symbols and trust badges on the thank you page.' => 'Widget-eremu hauek esker on orrialdean konfiantza-ikurrak eta konfiantza-txapak gehitzeko erabiltzen dira.','These options affect the thank you page , view order details on account pages and on email notifications.' => 'Aukera hauek eskerrak emateko orriari eragiten diote, ikusi eskaeraren xehetasunak kontuko orrialdeetan eta posta elektronikoko jakinarazpenetan.','Order actions' => 'Agindu ekintzak','Choose in which position to display the order actions.' => 'Aukeratu ordenaren ekintzak zein posiziotan bistaratu.','Inside the order overview' => 'Eskaeraren ikuspegi orokorraren barruan','After the order overview' => 'Eskaera orokorraren ondoren','After order sections' => 'Agindu atalen ondoren','Order status' => 'Eskaeraren egoera','Display the order status progress bar' => 'Bistaratu eskaeraren egoeraren aurrerapen-barra','Choose in which position to display the section.' => 'Aukeratu atala zein posiziotan bistaratu.','After the order overview and payment instructions' => 'Eskaeraren ikuspegi orokorra eta ordainketa argibideak ondoren','Before the order items section' => 'Eskaera elementuen atalaren aurretik','Inside the order items section' => 'Eskaera-elementuen atalaren barruan','After the order items section' => 'Eskaera elementuen atalaren ondoren','On the sidebar' => 'Alboko barran','Order downloads' => 'Eska ezazu deskargak','Gift message' => 'Opari mezua','Tools' => 'Tresnak','Troubleshooting' => 'Arazoak konpontzea','Debug options' => 'Arazketa aukerak','Debug mode' => 'Arazte modua','Using debug mode affects the website performance. Only use this option while troubleshooting.' => 'Arazketa modua erabiltzeak webgunearen errendimenduari eragiten dio. Erabili aukera hau arazoak konpontzen dituzun bitartean.','Load unminified assets' => 'Kargatu gutxitu gabeko aktiboak','Enhanced select fields' => 'Aukeratutako eremu hobetuak','Replace <code>select2</code> dropdown components with <code>TomSelect</code>' => 'Ordeztu <code>select2</code> goitibeherako osagaiak <code>TomSelect</code> ekin','TomSelect is a simpler dropdown selection component which is less prone to errors than Select2, while offering the same features that are actually used on WooCommerce checkout pages.' => 'TomSelect goitibeherako hautapen osagai sinpleagoa da, Select2-k baino akatsak izateko joera gutxiago duena, WooCommerce ordaintzeko orrietan benetan erabiltzen diren funtzio berdinak eskaintzen dituen bitartean.','Fix automatic zoom-in on form fields' => 'Konpondu inprimaki-eremuetan zoom automatikoa','Set <code>font-size</code> inside form fields to 16px' => 'Ezarri <code>font-size</code> inprimaki-eremuen barruan 16 px-en','When the font size inside form fields is smaller than 16px, Safari and other browsers might automatically zoom in on mobile devices to make the text easier to read. When this option is enabled, it will set the font size for inside the form fields to 16px on pages optimized by Fluid Checkout to avoid it zooming in.' => 'Inprimaki-eremuen barruko letra-tamaina 16 px baino txikiagoa denean, Safari eta beste arakatzaileek automatikoki handitu dezakete gailu mugikorretan testua errazago irakurtzeko. Aukera hau gaituta dagoenean, inprimaki-eremuen barruko letra-tamaina 16 px-koa ezarriko du Fluid Checkout-ek optimizatutako orrialdeetan, handitu ez dadin.','The shipping destination is always set to "Default to customer shipping address" when Fluid Checkout is activated. Customers can still provide different shipping and billing addresses during checkout, an option for setting the default billing address to be the same as the shipping address is available at WooCommerce > Settings > Fluid Checkout.' => 'Bidalketa-helmuga &quot;Bezeroaren bidalketa-helbide lehenetsia&quot; gisa ezartzen da beti, Fluid Checkout aktibatuta dagoenean. Bezeroek bidalketa- eta fakturazio-helbide desberdinak eman ditzakete ordainketa egitean, fakturazio-helbide lehenetsia bidalketa-helbidearen berdina izan dadin ezartzeko aukera bat eskuragarri dago WooCommerce &gt; Ezarpenak &gt; Fluid Checkout atalean.','Fluid Checkout' => 'Fluid Checkout','Settings' => 'Ezarpenak','Support' => 'Laguntza','<a target="_blank" href="%s">Upgrade to PRO</a> to unlock more options.' => '<a target="_blank" href="%s">Bertsio-berritu PRO-ra</a> aukera gehiago desblokeatzeko.','(PRO)' => '(PRO)','(experimental)' => '(esperimentala)','This is an experimental feature and may not work as expected with all themes and plugins. Use it with caution.' => 'Ezaugarri esperimental bat da eta baliteke espero bezala ez funtzionatzea gai eta plugin guztiekin. Erabili kontuz.','Confirm' => 'Berretsi','Verify Phone Number' => 'Egiaztatu telefono zenbakia','Please verify your phone number.' => 'Egiaztatu telefono-zenbakia.','The Bluehost Plugin' => 'Bluehost plugina','Custom fields' => 'Eremu pertsonalizatuak','Enable custom shipping email field' => 'Gaitu bidalketa elektronikoaren eremu pertsonalizatua','When enabled, the shipping email field from the Bluehost plugin will be added to the checkout form.' => 'Gaituta dagoenean, Bluehost pluginaren bidalketa-mezuaren eremua ordaintzeko formularioan gehituko da.','Captcha Pro by BestWebSoft' => 'BestWebSoft-en Captcha Pro','Captcha position' => 'Captcha posizioa','Define the position to display the captcha section. Some positions might not work depending on the captcha type chosen.' => 'Definitu captcha atala bistaratzeko posizioa. Baliteke posizio batzuk ez funtzionatzea aukeratutako captcha motaren arabera.','Selecting a pickup point is required before proceeding.' => 'Jarraitu aurretik jasotzeko puntu bat hautatzea beharrezkoa da.','Pickup point' => 'Jasotzeko puntua','Pickup point not selected yet.' => 'Jasotze-puntua ez da hautatu oraindik.','Pickup point:' => 'Jasotzeko puntua:','Mailchimp for WooCommerce' => 'Mailchimp WooCommercerako','Subscribe checkbox' => 'Harpidetu kontrol-laukia','Override the position for the subscribe checkbox on the checkout page and always display it on the contact step' => 'Gainidatzi ordainketa-orriko harpidetza-laukiaren posizioa eta bistaratu beti kontaktuaren urratsean','Before Shipping Address' => 'Bidalketa helbidea baino lehen','(originally "%s")' => '(jatorriz &quot;%s&quot;)','After Shipping Address' => 'Bidalketa helbidearen ondoren','Selecting a collection point is required when shipping with FermoPoint.' => 'Bilketa-puntu bat hautatzea beharrezkoa da FermoPoint-ekin bidaltzeko.','If checked informs typos in email to users. (This feature has been disabled because Fluid Checkout offers the feature. Changes to this option will not take effect)' => 'Markatuta badago, erabiltzaileei mezu elektronikoetan akatsen berri ematen die. (Eginbide hau desgaitu egin da, Fluid Checkout-ek eginbidea eskaintzen duelako. Aukera honen aldaketek ez dute eraginik izango)','Enable Mail Check: (disabled feature)' => 'Gaitu posta-egiaztapena: (eginbide desgaituta)','Carrier:' => 'Garraiolaria:','Sendinblue - WooCommerce Email Marketing' => 'Sendinblue - WooCommerce Email Marketing','Sign up checkbox position' => 'Erregistratu kontrol-laukiaren posizioa','Move the sign up checkbox to the contact step' => 'Eraman izena emateko kontrol-laukia kontaktuaren urratsera','When enabled, the checkbox will be moved to the contact step independently of the display location defined in the Sendinblue plugin settings.' => 'Gaituta dagoenean, kontrol-laukia kontaktu-urratsera eramango da Sendinblue pluginaren ezarpenetan zehaztutako bistaratze-kokapenetik independenteki.','Theme Betheme' => 'Betheme gaia','Checkout progress' => 'Ordainketa aurrerapena','Output the checkout steps section from Betheme on the checkout, cart and order received pages.' => 'Irte ezazu Betheme-ko ordainketa-urratsen atala ordainketa, saskia eta jasotako eskaera orrietan.','Theme Fennik' => 'Gaia Fennik','Breadcrumbs' => 'Ogi birrindua','Output the breadcrumbs section from the Fennik theme when using Fluid Checkout header and footer.' => 'Irte ezazu Fennik gaiaren ogi-mamien atala Fluid Checkout goiburua eta oina erabiltzean.','Theme Go' => 'Gaia Go','My account layout' => 'Nire kontuaren diseinua','Enable wide layout on "My account" page.' => 'Gaitu diseinu zabala &quot;Nire kontua&quot; orrian.','Theme Impreza' => 'Gaia Impreza','Header' => 'Goiburua','Spacing for site header at the checkout page (in pixels)' => 'Orrialdearen goiburuko tartea ordainketa-orrian (pixeletan)','Only applicable when using the Impreza theme header at the checkout page.' => 'Ordainketa orrian Impreza gaiaren goiburua erabiltzean soilik aplikagarria.','Theme Kapee' => 'Gaia Kapee','Output the checkout steps section from the Kapee theme on the checkout, cart and order received pages.' => 'Irte ezazu Kapee gaiaren ordainketa-urratsen atala ordainketa, saskia eta jasotako eskaera orrietan.','Theme Porto' => 'Gaia Porto','Output the checkout steps section from the Porto theme on the checkout, cart and order received pages.' => 'Irte ezazu ordaintzeko urratsen atala Porto gaiaren ordainketa, gurdi eta eskaera jasotako orrietan.','Theme Pressmart' => 'Gaia Pressmart','Output the checkout steps section from the Pressmart theme on the checkout, cart and order received pages.' => 'Irte ezazu ordaintzeko urratsen atala Pressmart gaiaren ordainketa, saskia eta jasotako eskaera orrietan.','Theme The Gem' => 'Gaia Harribitxia','Output the checkout steps section from The Gem theme when using Fluid Checkout header and footer.' => 'Irte ezazu The Gem gaiaren ordainketa-urratsen atala Fluid Checkout goiburua eta oina erabiltzean.','Theme Woodmart' => 'Woodmart gaia','Output the checkout steps section from the Woodmart theme when using Fluid Checkout header and footer.' => 'Irte ezazu ordaintzeko urratsen atala Woodmart gaitik Fluid Checkout goiburua eta oina erabiltzen duzunean.','Checkout options' => 'Ordainketa aukerak','Disable the theme checkout options' => 'Desgaitu gaiaren ordainketa aukerak','The options display product image, quantity field, remove button and link to product page added by the theme are disabled by default.' => 'Aukerak produktuaren irudia, kantitatearen eremua, kendu botoia eta gaiak gehitutako produktuaren orrirako esteka bistaratzen ditu lehenespenez.','Theme Atomion' => 'Gaia Atomion','Order progress' => 'Eskaeraren aurrerapena','Display order progress from the theme' => 'Erakutsi gaiaren eskaeraren aurrerapena','Display the form field labels visible on the page' => 'Erakutsi orrialdean ikusgai dauden inprimaki-eremuen etiketak','It is recommended to keep this option enabled for a better user experience.' => 'Aukera hau gaituta mantentzea gomendatzen da erabiltzailearen esperientzia hobea izateko.','Theme ZK Nito' => 'Gaia ZK Nito','Add extra fields to the checkout form' => 'Gehitu eremu gehigarriak ordainketa-inprimakiari','Adds an extra shipping email and shipping phone field to the shipping address section.' => 'Bidalketa helbide elektronikoa eta bidalketa telefono eremua gehitzen ditu bidalketa helbidearen atalean.','Go to the home page' => 'Joan hasierako orrialdera','Skip to content' => 'Saltatu edukira','Create an account' => 'Sortu kontu bat','Log in to your account' => 'Hasi saioa zure kontuan','Already have an account?' => 'Dagoeneko kontu bat duzu?','Logged in as %1$s. <a href="%2$s">Log out</a>' => '%1$s gisa hasi da saioa. <a href="%2$s">Amaitu saioa</a>','Your order has a total amount due of %s. No further payment is needed.' => 'Zure eskaerak %s-ko zenbateko osoa du. Ez da ordainketa gehiago behar.','Close and continue with checkout' => 'Itxi eta jarraitu ordainketarekin','Continue' => 'Jarraitu','Parcel terminal' => 'Paketeen terminala','<p>This widget has been replaced by Fluid Checkout. </p><p>When using Fluid Checkout, the checkout page needs to use the original WooCommerce shortcode <code>[woocommerce_checkout]</code> to work properly.</p><p>For more information, visit the Fluid Checkout documentation.</p>' => '<p>Widget hau Fluid Checkout-ekin ordezkatu da.</p><p> Fluid Checkout erabiltzean, ordainketa-orriak jatorrizko WooCommerce shortcode <code>[woocommerce_checkout]</code> erabili behar du behar bezala funtzionatzeko.</p><p> Informazio gehiago lortzeko, bisitatu Fluid Checkout dokumentazioa.</p>','Theme The7' => 'Gaia The7','Additional header sections' => 'Goiburuko atal gehigarriak','Output additional header sections if enabled in the theme settings.' => 'Iretzi goiburuko atal gehigarriak gaiaren ezarpenetan gaituta badago.','More information' => 'Informazio gehiago','Pick a Locker' => 'Aukeratu armairu bat','Page template' => 'Orrialdearen txantiloia','Use `Order confirmation` page template for block-based themes' => 'Erabili `Eskaera berresteko` orriaren txantiloia blokeetan oinarritutako gaietarako','Only applies when using a block-based theme.' => 'Blokeetan oinarritutako gai bat erabiltzean bakarrik aplikatzen da.','Button label for applying coupon codesApply' => 'Aplikatu','Checkout step titleContact' => 'Harremanetan jarri','Checkout step titleShipping' => 'Bidalketa','Checkout step titleBilling' => 'Fakturazioa','Checkout step titlePayment' => 'Ordainketa','Checkout substep change link labelChange' => 'Aldatu','Checkout substep save link labelSave changes' => 'Gorde aldaketak','Substep review textNone.' => 'Bat ere ez.','Substep review field format%1$s' => '%1$s','Substep review field format: with label%2$s: %1$s' => '%2$s: %1$s','Image uploader.No image selected.' => 'Ez da irudirik hautatu.','Settings sectionCart' => 'Saskia','Settings sectionOrder pay' => 'Ordainketa agindu','Settings sectionThank you' => 'Eskerrik asko','Settings section titleIntegrations' => 'Integrazioak','Settings section titleLicense keys' => 'Lizentzia-gakoak','Captcha positionBefore place order section' => 'Eskaera egin aurretik','Captcha positionBefore place order button' => 'Eskaera egin aurretik botoia','No shipping method chosen label for the order summary--' => '--','Close button aria-labelClose' => 'Itxi','Log in link label at checkout contact stepLog in' => 'Hasi saioa','Log in separator label at for when guest checkout is disabledOr continue below' => 'Edo jarraitu behean','Log in separator label at for when guest checkout is disabledOr 