return [ 'project-id-version' => 'fluid-checkout','mime-version' => '1.0','content-type' => 'text/plain; charset=utf-8','content-transfer-encoding' => '8bit','language-team' => 'Fluid Checkout','last-translator' => 'Fluid Checkout','pot-creation-date' => '2025-03-05 23:46+0000','report-msgid-bugs-to' => 'https://fluidcheckout.com/support','x-poedit-basepath' => '..','x-poedit-keywordslist' => '__;_e;_ex:1,2c;_n:1,2;_n_noop:1,2;_nx:1,2,4c;_nx_noop:1,2,3c;_x:1,2c;esc_attr__;esc_attr_e;esc_attr_x:1,2c;esc_html__;esc_html_e;esc_html_x:1,2c','x-poedit-searchpath-0' => '.','x-poedit-searchpathexcluded-0' => '*.js','x-poedit-sourcecharset' => 'UTF-8','plural-forms' => 'nplurals=2; plural=(n != 1);','messages' => ['WooCommerce' => 'WooCommerce','Go to Plugin Search' => 'Buka Pencarian Plugin','Activate %s' => 'Aktifkan %s','<strong>%1$s</strong> requires <strong>%2$s</strong> to be installed and activated. <a href="%4$s">%3$s</a>' => '<strong>%1$s</strong> membutuhkan <strong>%2$s</strong> untuk diinstal dan diaktifkan. <a href="%4$s">%3$s</a>','Enter your code here' => 'Masukkan kode Anda di sini','Add %s' => 'Tambahkan %s','Remove' => 'Menghapus','Order number and receipt will be sent to this email address.' => 'Nomor pesanan dan tanda terima akan dikirimkan ke alamat email ini.','Apartment, unit, building, floor, etc.' => 'Apartemen, unit, gedung, lantai, dll.','Shipping phone' => 'Telepon pengiriman','Only used for shipping-related questions.' => 'Hanya digunakan untuk pertanyaan terkait pengiriman.','Multi-step' => 'Multi-langkah','Single step' => 'Satu langkah','Click to go to the order summary' => 'Klik untuk membuka ringkasan pesanan','Cart total:' => 'Total keranjang:','Proceed to %s' => 'Lanjutkan ke %s','Proceed to contact' => 'Lanjutkan untuk menghubungi','Proceed to shipping' => 'Lanjutkan ke pengiriman','Proceed to billing' => 'Lanjutkan ke penagihan','Proceed to payment' => 'Lanjutkan ke pembayaran','My contact' => 'Kontak saya','Shipping to' => 'Pengiriman ke','Shipping method' => 'Metode pengiriman','Additional notes' => 'Catatan tambahan','Billing to' => 'Penagihan ke','Payment method' => 'Metode pembayaran','Step %1$s of %2$s' => 'Langkah %1 dari %2','Change: %s' => 'Perubahan: %s','An account will be created with the information provided.' => 'Akun akan dibuat dengan informasi yang diberikan.','Not selected yet.' => 'Belum dipilih.','Same as shipping address' => 'Sama seperti alamat pengiriman','Same as billing address' => 'Sama dengan alamat penagihan','Order summary' => 'Ringkasan pesanan','Edit cart' => 'Edit keranjang','Did you mean %s?' => 'Maksudmu %s?','Change email address to: {suggestion-value}' => 'Ubah alamat email menjadi: {nilai-saran}','This is a required field.' => 'Ini adalah bidang yang wajib diisi.','This is not a valid email address.' => 'Ini bukan alamat email yang valid.','This field does not match the related field value.' => 'Bidang ini tidak cocok dengan nilai bidang terkait.','The CPF number "{cpf_number}" is invalid.' => 'Nomor CPF "{cpf_number}" tidak valid.','The CNPJ number "{cnpj_number}" is invalid.' => 'Nomor CNPJ "{cnpj_number}" tidak valid.','(Required)' => '(Wajib)','Checkout Header - Desktop' => 'Header Pembayaran - Desktop','Display widgets on the checkout header for large screens. Only displayed if using the plugin\'s checkout header.' => 'Menampilkan widget pada header checkout untuk layar besar. Hanya ditampilkan jika menggunakan header checkout plugin.','Checkout Header - Mobile' => 'Header Pembayaran - Seluler','Display widgets below the checkout header for mobile devices. Only displayed if using the plugin\'s checkout header.' => 'Menampilkan widget di bawah tajuk checkout untuk perangkat seluler. Hanya ditampilkan jika menggunakan header checkout plugin.','Checkout Sidebar' => 'Bilah Sisi Pembayaran','Display widgets on the checkout sidebar after the order summary.' => 'Tampilkan widget pada bilah sisi checkout setelah ringkasan pesanan.','Checkout Order Summary' => 'Ringkasan Pesanan Pembayaran','Display widgets inside the order summary at the checkout page.' => 'Menampilkan widget di dalam ringkasan pesanan di halaman checkout.','Checkout Below Place Order' => 'Pembayaran di bawah ini Tempatkan Pesanan','Display widgets below the place order button at the checkout page.' => 'Tampilkan widget di bawah tombol tempatkan pesanan di halaman checkout.','Checkout Footer' => 'Footer Pembayaran','Display widgets on the checkout footer. Only displayed if using the plugin\'s checkout footer.' => 'Menampilkan widget pada footer checkout. Hanya ditampilkan jika menggunakan footer checkout plugin.','Classic' => 'Klasik','Boxed' => 'Kotak','Split' => 'Membagi','Minimalist' => 'Minimalis','Fluid Checkout and Delivery Date plugins by CodeRockz' => 'Plugin Fluid Checkout dan Tanggal Pengiriman oleh CodeRockz','<p>We detected that you are using the plugins Delivery & Pickup Date Time for WooCommerce (free and PRO) by CodeRockz. Integration with these plugins have been moved to Fluid Checkout PRO.</p>' => '<p>Kami mendeteksi bahwa Anda menggunakan plugin Delivery & Pickup Date Time untuk WooCommerce (gratis dan PRO) oleh CodeRockz. Integrasi dengan plugin ini telah dipindahkan ke Fluid Checkout PRO.</p>','Upgrade to PRO' => 'Tingkatkan ke PRO','Fluid Checkout database updated successfully' => 'Database Fluid Checkout berhasil diperbarui','The database changes where applied successfully.' => 'Basis data berubah jika berhasil diterapkan.','Ok' => 'Baiklah.','Dismiss' => 'Bubarkan.','Fluid Checkout database update' => 'Pembaruan basis data Fluid Checkout','Some changes to the database are required. As with any update, we recommend you to <strong>take a full backup of your website before proceeding</strong>.' => 'Beberapa perubahan pada basis data diperlukan. Seperti halnya pembaruan apa pun, kami sarankan Anda untuk <strong>membuat cadangan penuh situs web Anda sebelum melanjutkan</strong>.','Update database' => 'Perbarui basis data','Fluid Checkout is not compatible with the Divi Builder checkout layout' => 'Fluid Checkout tidak kompatibel dengan tata letak checkout Divi Builder','<p>Fluid Checkout needs the classic WooCommerce shortcode-based checkout form to work. When using the Divi Builder to customize the look and feel of the checkout page, the classic shortcodes are replaced with custom widgets from the Divi Builder for each part of the checkout page. These custom widgets are not compatible with Fluid Checkout.</p><p>Please edit your checkout page replacing the Divi Builder widgets with the a text widget containing the shortcode <code>[woocommerce_checkout]</code>.</p>' => '<p>Fluid Checkout membutuhkan formulir checkout berbasis kode pendek WooCommerce klasik agar dapat berfungsi. Ketika menggunakan Divi Builder untuk menyesuaikan tampilan dan nuansa halaman checkout, kode pendek klasik diganti dengan widget kustom dari Divi Builder untuk setiap bagian halaman checkout. Widget khusus ini tidak kompatibel dengan Fluid Checkout.</p> <p>Silakan edit halaman checkout Anda dengan mengganti widget Divi Builder dengan widget teks yang berisi kode pendek <kode>[woocommerce_checkout]</kode>.</p>','Edit the checkout page' => 'Mengedit halaman pembayaran','Read the documentation.' => 'Baca dokumentasi.','Germanized PRO multistep checkout feature needs to be disabled when using Fluid Checkout' => 'Fitur pembayaran multilangkah Germanized PRO perlu dinonaktifkan saat menggunakan Fluid Checkout','When using Fluid Checkout, the multistep feature from the Germanized PRO plugin becomes unnecessary and can cause <strong>critical errors while processing new orders</strong>. Please disable the Germanized PRO multistep checkout feature in the plugin settings.' => 'Saat menggunakan Fluid Checkout, fitur multistep dari plugin Germanized PRO menjadi tidak diperlukan dan dapat menyebabkan <strong>kesalahan kritis saat memproses pesanan baru</strong>. Harap nonaktifkan fitur checkout multistep Germanized PRO di pengaturan plugin.','Go to Germanized settings' => 'Buka pengaturan bahasa Jerman','Thanks for choosing Fluid Checkout!' => 'Terima kasih telah memilih Fluid Checkout!','You have been using the plugin for a while now. How do you like it so far? <br>Please give us a quick rating, we appreciate every bit of feedback and it encourages us to keep improving the plugin :)' => 'Anda telah menggunakan plugin ini untuk sementara waktu. Bagaimana Anda menyukainya sejauh ini? <br>Silakan beri kami penilaian singkat, kami menghargai setiap umpan balik dan mendorong kami untuk terus meningkatkan plugin ini :)','Rate the plugin' => 'Beri nilai plugin','I need help!' => 'Aku butuh bantuan!','The plugin WooCommerce Checkout Manager by Quadlayers is not compatibile with Fluid Checkout' => 'Plugin WooCommerce Checkout Manager oleh Quadlayers tidak kompatibel dengan Fluid Checkout','Some features of WooCommerce Checkout Manager by Quadlayers might not work as expected when using it with Fluid Checkout. Read <a href="%s">our documentation</a> for more information and alternative solutions.' => 'Beberapa fitur WooCommerce Checkout Manager oleh Quadlayers mungkin tidak berfungsi seperti yang diharapkan ketika menggunakannya dengan Fluid Checkout. Baca <a href="%s">dokumentasi kami</a> untuk informasi lebih lanjut dan solusi alternatif.','Read documentation' => 'Baca dokumentasi','Don\'t show this again' => 'Jangan tunjukkan ini lagi','Special offers' => 'Penawaran khusus','Extend all the goodness of Fluid Checkout to your cart page and thank you pages.' => 'Perluas semua kebaikan Fluid Checkout ke halaman keranjang dan halaman terima kasih Anda.','Fluid Checkout PRO' => 'Fluid Checkout PRO','Design templates, optimized cart and order received pages, account matching, and much more.' => 'Templat desain, keranjang yang dioptimalkan dan halaman penerimaan pesanan, pencocokan akun, dan masih banyak lagi.','The best tools to further <strong>improve your WooCommerce checkout conversion rate</strong> and make the purchase experience consistent on the entire journey.' => 'Alat terbaik untuk <strong>meningkatkan tingkat konversi checkout WooCommerce Anda</strong> dan membuat pengalaman pembelian yang konsisten di seluruh perjalanan.','More design templates' => 'Lebih banyak templat desain','Optimized cart page' => 'Halaman keranjang yang dioptimalkan','Optimized order received / thank you page' => 'Pesanan yang diterima / halaman terima kasih yang dioptimalkan','Edit cart contents at checkout page' => 'Mengedit isi keranjang di halaman checkout','Account matching / user matching' => 'Pencocokan akun / pencocokan pengguna','Express Checkout buttons from supported payment methods' => 'Tombol Pembayaran Cepat dari metode pembayaran yang didukung','Trust symbols on the cart and thank you pages' => 'Simbol kepercayaan pada keranjang dan halaman ucapan terima kasih','More positions for the coupon code on the checkout page' => 'Lebih banyak posisi untuk kode kupon di halaman checkout','Integrated coupon code field on the cart page' => 'Kolom kode kupon terintegrasi di halaman troli','International phone numbers' => 'Nomor telepon internasional','Gift messages and packing slips' => 'Pesan hadiah dan slip kemasan','Local pickup and in-store collection' => 'Penjemputan lokal dan pengambilan di dalam toko','Upgrade to PRO &mdash; 99 EUR' => 'Tingkatkan ke PRO &mdash; 99 EUR','Fluid Checkout PRO – Bundle' => 'Fluid Checkout PRO - Bundel','All PRO features + All add-ons for a special price.' => 'Semua fitur PRO + Semua add-on dengan harga khusus.','The bundle includes all add-ons we currently offer and Fluid Checkout add-ons we launch in the future.' => 'Bundel ini mencakup semua add-on yang saat ini kami tawarkan dan add-on Fluid Checkout yang akan kami luncurkan di masa mendatang.','226 EUR value (Save 42%)' => 'Nilai 226 EUR (Hemat 42%)','Google Address Autocomplete' => 'Pelengkapan Otomatis Alamat Google','Address Book' => 'Buku Alamat','EU-VAT Assistant' => 'Asisten PPN Uni Eropa','All future add-ons *' => 'Semua pengaya di masa mendatang *','* For as long as you have an active subscription.' => '* Selama Anda memiliki langganan aktif.','Get the Bundle – Only 129 EUR' => 'Dapatkan Bundel - Hanya 129 EUR','All prices shown in EUR. If there are any divergencies with the prices on our website, the offers shown on the website superseed these and will be applied.' => 'Semua harga ditampilkan dalam EUR. Jika ada perbedaan dengan harga di situs web kami, penawaran yang ditampilkan di situs web akan menggantikannya dan akan diterapkan.','Add-ons' => 'Pengaya','Enhance your checkout experience with these add-ons.' => 'Tingkatkan pengalaman checkout Anda dengan add-on ini.','I already have it – Hide this offer' => 'Saya sudah memilikinya - Sembunyikan penawaran ini','Installed &mdash; Activate' => 'Terinstal &mdash; Aktifkan','Activated' => 'Diaktifkan','Up to 40% less checkout fields to fill in.' => 'Hingga 40% lebih sedikit kolom pembayaran yang harus diisi.','Avoid delivery delays and unsatisfied customers. Collect the <strong>correct address information</strong> from the first time they buy with you.' => 'Hindari penundaan pengiriman dan pelanggan yang tidak puas. Kumpulkan informasi alamat yang benar sejak pertama kali mereka membeli dengan Anda.','Get this add-on &mdash; 29 EUR' => 'Dapatkan add-on ini &mdash; 29 EUR','Address book' => 'Buku alamat','Multiple saved addresses for shipping and billing.' => 'Beberapa alamat tersimpan untuk pengiriman dan penagihan.','Let customers <strong>save multiple shipping and billing addresses</strong> on their account and choose which ones to use at checkout and cart pages.' => 'Biarkan pelanggan <strong>menyimpan beberapa alamat pengiriman dan penagihan</strong> di akun mereka dan memilih yang mana yang akan digunakan di halaman checkout dan troli.','Get this add-on &mdash; 59 EUR' => 'Dapatkan add-on ini &mdash; 59 EUR','Simplified EU-VAT validation for your store.' => 'Validasi PPN UE yang disederhanakan untuk toko Anda.','Collect and <strong>validate EU VAT numbers at the checkout page</strong>, removes tax charges on reverse charge basis and confirms customer location when needed.' => 'Mengumpulkan dan <strong>memvalidasi nomor PPN Uni Eropa di halaman checkout</strong>, menghapus biaya pajak dengan basis biaya balik dan mengonfirmasi lokasi pelanggan bila diperlukan.','Get this add-on &mdash; 39 EUR' => 'Dapatkan add-on ini &mdash; 39 EUR','All add-ons are sold separately and require the <strong>Fluid Checkout Lite</strong> plugin to be installed and activated, except when noted.' => 'Semua add-on dijual secara terpisah dan memerlukan plugin <strong>Fluid Checkout Lite</strong> untuk diinstal dan diaktifkan, kecuali jika disebutkan.','Select an image' => 'Memilih gambar','No image selected.' => 'Tidak ada gambar yang dipilih.','Remove image' => 'Menghapus gambar','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to get your license key, or <a href="%s" target="_blank">purchase a new license key</a>.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Masuk ke akun Anda</a> untuk mendapatkan kunci lisensi, atau <a href="%s" target="_blank">membeli kunci lisensi baru</a>.','Valid until %s.' => 'Berlaku hingga %s.','Expired on %s.' => 'Kedaluwarsa pada %s.','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to renew your license key and continue to receive updates and support.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Masuk ke akun Anda</a> untuk memperbarui kunci lisensi Anda dan terus menerima pembaruan dan dukungan.','License key cancelled.' => 'Kunci lisensi dibatalkan.','<a href="%s" target="_blank">Purchase a new license key</a> and continue to receive updates and support.' => '<a href="%s" target="_blank">Beli kunci lisensi baru</a> dan terus menerima pembaruan dan dukungan.','Error: ' => 'Kesalahan:','Setup & Documentation' => 'Pengaturan & Dokumentasi','Great! Your checkout page is now running on Fluid Checkout.' => 'Bagus! Halaman checkout Anda sekarang sudah berjalan di Fluid Checkout.','Here are a few resources for you to get started:' => 'Berikut adalah beberapa sumber daya untuk Anda memulai:','Read the installation guide <a href="%s" target="_blank">Getting stated with Fluid Checkout</a>.' => 'Baca panduan instalasi <a href="%s" target="_blank">Mendapatkan pernyataan dengan Fluid Checkout</a>.','Setup layout and design on the <a href="%s">checkout options</a>.' => 'Atur tata letak dan desain pada <a href="%s">pilihan pembayaran</a>.','Check if there are any <a href="%s">integration options</a> available for other plugins you have installed.' => 'Periksa apakah ada <a href="%s">pilihan integrasi</a> yang tersedia untuk plugin lain yang telah Anda instal.','Visit <a href="%s" target="_blank">our documentation</a> for more information about Fluid Checkout features.' => 'Kunjungi <a href="%s" target="_blank">dokumentasi kami</a> untuk informasi lebih lanjut tentang fitur Fluid Checkout.','If you ever need help, <a href="%s" target="_blank">open a support ticket</a> on our official support channel.' => 'Jika Anda memerlukan bantuan, <a href="%s" target="_blank">buka tiket dukungan</a> di saluran dukungan resmi kami.','Cart layout' => 'Tata letak gerobak','Cart optimizations' => 'Pengoptimalan troli','Enable cart page optimizations' => 'Mengaktifkan pengoptimalan halaman keranjang','Display the cart page with a layout similar to the checkout page.' => 'Tampilkan halaman keranjang dengan tata letak yang mirip dengan halaman checkout.','Header and footer' => 'Header dan footer','Header and footer template' => 'Templat header dan footer','Controls whether to use the distraction free page header and footer or keep the currently active theme\'s header and footer.' => 'Mengontrol apakah akan menggunakan header dan footer halaman bebas gangguan atau mempertahankan header dan footer tema yang sedang aktif.','Distraction free header and footer' => 'Header dan footer bebas gangguan','Theme\'s header and footer' => 'Header dan footer tema','Cart elements' => 'Elemen keranjang','Make the order summary stay visible on the cart page while scrolling' => 'Membuat ringkasan pesanan tetap terlihat di halaman troli saat menggulir','Coupon codes' => 'Kode kupon','Select position where to display the coupon codes section on the cart page. Only applicable when AJAX cart and the integrated coupon codes in the checkout page are enabled.' => 'Pilih posisi untuk menampilkan bagian kode kupon pada halaman keranjang. Hanya berlaku jika keranjang AJAX dan kode kupon terintegrasi di halaman checkout diaktifkan.','Hidden' => 'Tersembunyi','Before the cart items section' => 'Sebelum bagian item keranjang','Inside the cart items section' => 'Di dalam bagian item keranjang','After the cart items section' => 'Setelah bagian item keranjang','Before the shipping section' => 'Sebelum bagian pengiriman','After the shipping section' => 'Setelah bagian pengiriman','Before the order summary' => 'Sebelum ringkasan pesanan','Inside the order summary' => 'Di dalam ringkasan pesanan','Before the order totals' => 'Sebelum total pesanan','After the order totals' => 'Setelah total pesanan','After the order summary' => 'Setelah ringkasan pesanan','Shipping' => 'Pengiriman','Select position where to display the shipping costs and calculator on the cart page.' => 'Pilih posisi untuk menampilkan biaya pengiriman dan kalkulator pada halaman keranjang.','Display as a cart section' => 'Tampilkan sebagai bagian keranjang','Cross-sells' => 'Penjualan silang','Select position where to display the cross-sells section on the cart page.' => 'Pilih posisi untuk menampilkan bagian cross-sells pada halaman troli.','Before the cart actions' => 'Sebelum tindakan gerobak','After the cart actions' => 'Setelah tindakan gerobak','Layout of cross-sell items on the cart page.' => 'Tata letak item penjualan silang di halaman troli.','Optimized horizontal cross-sells layout' => 'Tata letak penjualan silang horizontal yang dioptimalkan','Theme\'s cross-sells layout' => 'Tata letak penjualan silang tema','Number of cross-sell items to display on the cart page' => 'Jumlah item penjualan silang yang akan ditampilkan di halaman troli','Trust symbols &amp; badges' => 'Simbol & lencana kepercayaan','Add widget areas to the cart page' => 'Menambahkan area widget ke halaman keranjang','These widget areas are used to add trust symbols and trust badges on the cart page.' => 'Area widget ini digunakan untuk menambahkan simbol kepercayaan dan lencana kepercayaan pada halaman keranjang.','Checkout' => 'Pembayaran','Layout &amp; design' => 'Tata letak & desain','Checkout layout' => 'Tata letak pembayaran','Design template' => 'Templat desain','General styles for the checkout steps, order summary and other sections. Might also apply to other pages such as the Cart, Order Received and View Order pages.' => 'Gaya umum untuk langkah-langkah checkout, ringkasan pesanan, dan bagian lainnya. Dapat juga diterapkan pada halaman lain seperti halaman Keranjang, Pesanan Diterima, dan Lihat Pesanan.','Dark mode' => 'Mode gelap','Enable dark mode' => 'Mengaktifkan mode gelap','Header and footer templates' => 'Templat header dan footer','We recommend using the distraction free header and footer to avoid distractions at the checkout page. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Read the research about cart abandonment</a>.' => 'Kami menyarankan untuk menggunakan header dan footer bebas gangguan untuk menghindari gangguan pada halaman checkout. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Baca penelitian tentang pengabaian keranjang belanja</a>.','Logo image' => 'Gambar logo','Choose an image to be displayed on the checkout page header. Only applies when using the distraction free header and footer.' => 'Pilih gambar yang akan ditampilkan pada header halaman checkout. Hanya berlaku saat menggunakan header dan footer bebas gangguan.','Header background color' => 'Warna latar belakang header','Choose a background color for the checkout page header. Only applies when using the distraction free header and footer.' => 'Pilih warna latar belakang untuk header halaman checkout. Hanya berlaku saat menggunakan header dan footer bebas gangguan.','HTML color value. ie: #f3f3f3' => 'Nilai warna HTML. yaitu: #f3f3f3','Page background color' => 'Warna latar belakang halaman','Choose a background color for the checkout page. Color is applied to the <em>body</em> element.' => 'Pilih warna latar belakang untuk halaman checkout. Warna diterapkan pada elemen <em>badan</em>.','Footer background color' => 'Warna latar belakang footer','Choose a background color for the checkout page footer. Only applies when using the distraction free header and footer.' => 'Pilih warna latar belakang untuk footer halaman checkout. Hanya berlaku saat menggunakan header dan footer bebas gangguan.','Checkout elements' => 'Elemen pembayaran','Progress bar' => 'Bilah kemajuan','Display the checkout progress bar when using multi-step checkout layout' => 'Menampilkan bilah kemajuan checkout saat menggunakan tata letak checkout multi-langkah','Make the checkout progress bar stay visible while scrolling' => 'Membuat bilah progres checkout tetap terlihat saat menggulir','Express checkout' => 'Pembayaran ekspres','Enable the express checkout section' => 'Aktifkan bagian pembayaran cepat','Displays the express checkout section at checkout when supported payment gateways have this feature enabled.' => 'Menampilkan bagian pembayaran cepat pada saat checkout ketika gateway pembayaran yang didukung mengaktifkan fitur ini.','Display express checkout buttons in one line for larger screens' => 'Menampilkan tombol pembayaran cepat dalam satu baris untuk layar yang lebih besar','Ignore additional checkout required fields when paying with a compatible express checkout payment gateway' => 'Abaikan kolom wajib checkout tambahan saat membayar dengan gateway pembayaran checkout ekspres yang kompatibel','Make the order summary stay visible while scrolling' => 'Membuat ringkasan pesanan tetap terlihat saat menggulir','Choose a background color for the order summary section.' => 'Pilih warna latar belakang untuk bagian ringkasan pesanan.','Highlight the order totals row in the order summary table' => 'Sorot baris total pesanan di tabel ringkasan pesanan','Most useful when the order summary section does not have a highlighted background color. Might also apply to the Cart, Order Received and View Order pages when using Fluid Checkout PRO.' => 'Paling berguna ketika bagian ringkasan pesanan tidak memiliki warna latar belakang yang disorot. Mungkin juga berlaku untuk halaman Keranjang, Pesanan Diterima dan Lihat Pesanan saat menggunakan Fluid Checkout PRO.','Action link on the order summary at checkout.' => 'Tautan tindakan pada ringkasan pesanan saat pembayaran.','Edit cart link' => 'Edit tautan keranjang','Cart items count' => 'Jumlah item keranjang','Link to shop page' => 'Tautan ke halaman toko','Cart items' => 'Item keranjang','Enable options to edit cart items on the checkout page' => 'Mengaktifkan opsi untuk mengedit item keranjang di halaman checkout','Allow customers to change product quantities or removing items directly at the checkout page.' => 'Izinkan pelanggan untuk mengubah jumlah produk atau menghapus item secara langsung di halaman checkout.','Hide cart items errors at the checkout page' => 'Sembunyikan kesalahan item keranjang di halaman checkout','Do not display the cart items errors message at the top of the checkout page. When submitting the checkout form to complete the purchase, these error messages will always be displayed.' => 'Jangan tampilkan pesan kesalahan item keranjang di bagian atas halaman checkout. Saat mengirimkan formulir checkout untuk menyelesaikan pembelian, pesan kesalahan ini akan selalu ditampilkan.','Place order' => 'Tempatkan pesanan','Define the position to display "Place order" and terms checkbox section.' => 'Tentukan posisi untuk menampilkan bagian kotak centang "Tempatkan pesanan" dan persyaratan.','Some options might not be compatible with some plugins and themes.' => 'Beberapa opsi mungkin tidak kompatibel dengan beberapa plugin dan tema.','Below the payment section' => 'Di bawah bagian pembayaran','Below the order summary' => 'Di bawah ringkasan pesanan','Both below the payment section and the order summary' => 'Baik di bawah bagian pembayaran dan ringkasan pesanan','Add widget areas to the checkout page' => 'Menambahkan area widget ke halaman checkout','These widget areas are used to add trust symbols and trust badges on the checkout page.' => 'Area widget ini digunakan untuk menambahkan simbol kepercayaan dan lencana kepercayaan pada halaman checkout.','Display checkout sidebar widgets only when viewing the last checkout step on mobile devices when using multi-step checkout layout' => 'Menampilkan widget bilah samping checkout hanya saat melihat langkah checkout terakhir di perangkat seluler saat menggunakan tata letak checkout multi-langkah','Enable integrated coupon code section' => 'Aktifkan bagian kode kupon terintegrasi','Only applicable if use of coupon codes are enabled in the WooCommerce settings.' => 'Hanya berlaku jika penggunaan kode kupon diaktifkan di pengaturan WooCommerce.','Display the coupon codes section title' => 'Menampilkan judul bagian kode kupon','Only applicable when coupon code is displayed as a separate section on the checkout or cart pages.' => 'Hanya berlaku jika kode kupon ditampilkan sebagai bagian terpisah pada halaman checkout atau keranjang.','Select position where to display the coupon codes section on the checkout page.' => 'Pilih posisi untuk menampilkan bagian kode kupon pada halaman checkout.','Before the checkout steps' => 'Sebelum langkah-langkah pembayaran','As a substep before payment methods' => 'Sebagai langkah awal sebelum metode pembayaran','As a substep after payment methods' => 'Sebagai langkah selanjutnya setelah metode pembayaran','Select style of the "apply coupon" button. Only applicable when the coupon code section is displayed "Before the checkout steps" on the checkout page, or "Before the cart items section" on the cart page.' => 'Pilih gaya tombol "terapkan kupon". Hanya berlaku jika bagian kode kupon ditampilkan "Sebelum langkah checkout" di halaman checkout, atau "Sebelum bagian item keranjang" di halaman keranjang.','Default button style' => 'Gaya tombol default','"Add" link button' => 'Tombol tautan "Tambah"','Checkout fields' => 'Bidang pembayaran','Optional fields' => 'Bidang opsional','Hide optional fields behind a link button' => 'Menyembunyikan bidang opsional di belakang tombol tautan','It is recommended to keep this option checked to reduce the number of open input fields, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">read the research</a>.' => 'Disarankan untuk tetap mencentang opsi ini untuk mengurangi jumlah kolom input yang terbuka, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">baca penelitian</a>.','Do not hide "Address line 2" fields behind a link button' => 'Jangan sembunyikan bidang "Baris alamat 2" di belakang tombol tautan','Recommended only when most customers actually need the "Address line 2" field, or when getting the right shipping address is crucial (ie. if delivering food and other perishable products).' => 'Direkomendasikan hanya ketika sebagian besar pelanggan benar-benar membutuhkan kolom "Alamat baris 2", atau ketika mendapatkan alamat pengiriman yang tepat sangat penting (misalnya, jika mengirimkan makanan dan produk yang mudah rusak lainnya).','Display the "Add" link buttons in lowercase' => 'Menampilkan tombol tautan "Tambah" dalam huruf kecil','Make the labels of optional field "Add" link button as <code>lowercase</code> (ie. "Add phone number" instead of "Add Phone Number"). This option also affects the link buttons for coupon code fields.' => 'Jadikan label tombol tautan bidang opsional "Tambah" sebagai <kode>huruf kecil</kode> (mis. "Tambahkan nomor telepon", bukan "Tambahkan Nomor Telepon"). Opsi ini juga mempengaruhi tombol tautan untuk bidang kode kupon.','Billing address' => 'Alamat penagihan','Select position where to display the billing address section on the checkout page.' => 'Pilih posisi untuk menampilkan bagian alamat penagihan pada halaman checkout.','As a step before shipping' => 'Sebagai langkah sebelum pengiriman','As a sub-step before the shipping address section' => 'Sebagai sub-langkah sebelum bagian alamat pengiriman','As a sub-step after the shipping address section' => 'Sebagai sub-langkah setelah bagian alamat pengiriman','As a step after shipping' => 'Sebagai langkah setelah pengiriman','As a sub-step before the payment section' => 'Sebagai sub-langkah sebelum bagian pembayaran','Force billing and shipping addresses to the same (single section)' => 'Memaksa penagihan dan alamat pengiriman ke alamat yang sama (satu bagian)','Checkbox for "Same as shipping/billing address" checked by default' => 'Kotak centang untuk "Sama dengan alamat pengiriman/penagihan" dicentang secara default','The checkbox "Same as shiping address" will start as checked by default when the shipping address section is displayed first. <br>The checkbox "Same as billing address" will start as checked by default when the billing address section is displayed first. <br> It is recommended to leave this option checked as to significantly reduce the number of open input fields at checkout, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">read the research</a>.' => 'Kotak centang "Sama dengan alamat pengiriman" akan mulai dicentang secara default ketika bagian alamat pengiriman ditampilkan pertama kali. <br>Kotak centang "Sama dengan alamat penagihan" akan mulai dicentang secara default ketika bagian alamat penagihan ditampilkan pertama kali. <br> Disarankan untuk membiarkan opsi ini tetap dicentang untuk mengurangi jumlah kolom input yang terbuka secara signifikan pada saat checkout, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">baca penelitian</a>.','Highlight the billing address section in the checkout form' => 'Sorot bagian alamat penagihan di formulir pembayaran','Shipping address' => 'Alamat pengiriman','Highlight the shipping address section in the checkout form' => 'Sorot bagian alamat pengiriman di formulir checkout','Shipping methods' => 'Metode pengiriman','Choose in which position to display the shipping methods section.' => 'Pilih di posisi mana untuk menampilkan bagian metode pengiriman.','Before shipping address' => 'Sebelum alamat pengiriman','After shipping address' => 'Setelah alamat pengiriman','Prevent automatic selection of the first shipping method' => 'Mencegah pemilihan otomatis metode pengiriman pertama','When enabled, the first shipping method available will not be automatically selected when no other shipping method was previously selected for each shipping package. <br>This option will be automatically enabled if the option for clearing the selected shipping method is enabled for the Local Pickup feature.' => 'Jika diaktifkan, metode pengiriman pertama yang tersedia tidak akan dipilih secara otomatis jika tidak ada metode pengiriman lain yang dipilih sebelumnya untuk setiap paket pengiriman. <br>Opsi ini akan diaktifkan secara otomatis jika opsi untuk menghapus metode pengiriman yang dipilih diaktifkan untuk fitur Pengambilan Lokal.','Local pickup' => 'Penjemputan lokal','Removes shipping address section when a local pickup shipping method is selected.' => 'Menghapus bagian alamat pengiriman ketika metode pengiriman pengambilan lokal dipilih.','Replaces the shipping address with the pickup point location when a local pickup shipping method is selected.' => 'Mengganti alamat pengiriman dengan lokasi titik pengambilan ketika metode pengiriman pengambilan lokal dipilih.','Show option to clear shipping methods in the pickup location substep' => 'Tampilkan opsi untuk menghapus metode pengiriman di sub-langkah lokasi pengambilan','Show a link button on the pickup location substep to clear the chosen shipping methods. This can be used to allow showing the shipping address section again if a local pickup method was previously selected.' => 'Tampilkan tombol tautan pada sub-langkah lokasi pengambilan untuk menghapus metode pengiriman yang dipilih. Ini dapat digunakan untuk menampilkan kembali bagian alamat pengiriman jika metode pengambilan lokal telah dipilih sebelumnya.','Choose which address to save as the shipping address for local pickup orders.' => 'Pilih alamat mana yang akan disimpan sebagai alamat pengiriman untuk pesanan pengambilan lokal.','Save the selected pickup location' => 'Menyimpan lokasi penjemputan yang dipilih','Save same as the billing address' => 'Simpan sama dengan alamat penagihan','Do not save any shipping address' => 'Jangan simpan alamat pengiriman apa pun','Company name field for shipping address' => 'Kolom nama perusahaan untuk alamat pengiriman','Change visibility for the company name field for the shipping address section on the checkout form.' => 'Ubah visibilitas untuk bidang nama perusahaan untuk bagian alamat pengiriman pada formulir checkout.','If field is set as "optional", which is the default visibility state, no changes will be applied to let other plugins apply any changes they need.' => 'Jika field ditetapkan sebagai "opsional", yang merupakan status visibilitas default, tidak ada perubahan yang akan diterapkan untuk membiarkan plugin lain menerapkan perubahan apa pun yang mereka butuhkan.','Hidden (remove field)' => 'Tersembunyi (hapus bidang)','Optional' => 'Opsional','Required' => 'Diperlukan','Add shipping phone field to the checkout form.' => 'Tambahkan kolom telepon pengiriman ke formulir pembayaran.','Choose in which step to display the shipping phone field.' => 'Pilih di langkah mana untuk menampilkan bidang telepon pengiriman.','Contact step' => 'Langkah kontak','Billing phone' => 'Telepon penagihan','Add billing phone field to the checkout form.' => 'Tambahkan bidang telepon penagihan ke formulir pembayaran.','Choose in which step to display the billing phone field.' => 'Pilih pada langkah mana untuk menampilkan bidang telepon penagihan.','Enable international phone number format and validation for phone fields' => 'Mengaktifkan format dan validasi nomor telepon internasional untuk bidang telepon','Format and validate phone numbers according to each country.' => 'Memformat dan memvalidasi nomor telepon sesuai dengan masing-masing negara.','Enable phone number validation based on country rules' => 'Mengaktifkan validasi nomor telepon berdasarkan aturan negara','When disabled, the phone field validation will not check if country or area codes are valid for the country.' => 'Jika dinonaktifkan, validasi bidang telepon tidak akan memeriksa apakah kode negara atau area valid untuk negara tersebut.','Only show allowed countries for shipping or billing' => 'Hanya tampilkan negara yang diizinkan untuk pengiriman atau penagihan','When enabled, only the countries allowed for shipping will be available in the shipping phone field, and only countries allowed for billing will be available for the billing phone field.' => 'Jika diaktifkan, hanya negara yang diizinkan untuk pengiriman yang akan tersedia di bidang telepon pengiriman, dan hanya negara yang diizinkan untuk penagihan yang akan tersedia di bidang telepon penagihan.','Show country code beside the flag' => 'Tampilkan kode negara di samping bendera','Show an example of a valid phone number inside phone fields' => 'Tampilkan contoh nomor telepon yang valid di dalam bidang telepon','Do not change placeholders' => 'Jangan ubah placeholder','Show if not defined' => 'Tampilkan jika tidak ditentukan','Always show' => 'Selalu tunjukkan','Order notes' => 'Catatan pesanan','Define the visibility of the additional order notes field.' => 'Menentukan visibilitas bidang catatan pesanan tambahan.','Gift options' => 'Pilihan hadiah','Display gift message and other gift options at the checkout page' => 'Menampilkan pesan hadiah dan pilihan hadiah lainnya di halaman checkout','Allow customers to add a gift message and other gift related options to the order.' => 'Izinkan pelanggan untuk menambahkan pesan hadiah dan opsi terkait hadiah lainnya pada pesanan.','Display the gift message fields always expanded' => 'Menampilkan bidang pesan hadiah yang selalu diperluas','Display the gift message as part of the order details table instead of a separate section' => 'Menampilkan pesan hadiah sebagai bagian dari tabel detail pesanan, bukan bagian terpisah','This option affects the order confirmation page (thank you page) and order details on account pages, emails and packing slips.' => 'Opsi ini memengaruhi halaman konfirmasi pesanan (halaman terima kasih) dan detail pesanan pada halaman akun, email, dan slip pengepakan.','Account matching' => 'Pencocokan akun','Enable the account matching feature' => 'Mengaktifkan fitur pencocokan akun','Associate the guest customer\'s orders with their existing account when an account already exists with the customer\'s contact details.' => 'Mengaitkan pesanan pelanggan tamu dengan akun mereka yang sudah ada jika akun sudah ada dengan detail kontak pelanggan.','Display message when an account exists with the email address provided' => 'Menampilkan pesan ketika ada akun dengan alamat email yang diberikan','Replaces the account creation fields with a notification and option to log in. In some contexts, it might be recommended to leave this option disabled to protect the privacy of customers.' => 'Mengganti kolom pembuatan akun dengan notifikasi dan opsi untuk masuk. Dalam beberapa konteks, mungkin disarankan untuk menonaktifkan opsi ini untuk melindungi privasi pelanggan.','Dashboard' => 'Dasbor','Integrations' => 'Integrasi','No integrations available at the moment on this section. The options related to each plugin will only appear here if that plugin is activated.' => 'Tidak ada integrasi yang tersedia saat ini pada bagian ini. Opsi yang terkait dengan setiap plugin hanya akan muncul di sini jika plugin tersebut diaktifkan.','License keys' => 'Kunci lisensi','No license keys options available at the moment on this section. The options related to each plugin or add-on will appear here when that plugin is activated.' => 'Tidak ada opsi kunci lisensi yang tersedia saat ini pada bagian ini. Opsi yang terkait dengan setiap plugin atau pengaya akan muncul di sini ketika plugin tersebut diaktifkan.','Visit our website for more information about our plugins and add-ons.' => 'Kunjungi situs web kami untuk informasi lebih lanjut tentang plugin dan add-on kami.','Order pay page' => 'Halaman pembayaran pesanan','Order pay' => 'Pembayaran pesanan','Enable order pay page optimizations' => 'Mengaktifkan pengoptimalan halaman pembayaran pesanan','Changes the layout of order pay page for existing orders pending payment from the customer.' => 'Mengubah tata letak halaman pembayaran pesanan untuk pesanan yang sedang menunggu pembayaran dari pelanggan.','Add widget areas to the order pay page' => 'Menambahkan area widget ke halaman pembayaran pesanan','These widget areas are used to add trust symbols and trust badges on the order pay page.' => 'Area widget ini digunakan untuk menambahkan simbol kepercayaan dan lencana kepercayaan pada halaman pembayaran pesanan.','Thank you page & order details layout' => 'Halaman terima kasih & tata letak detail pesanan','Order details optimizations' => 'Pengoptimalan detail pesanan','Enable thank you page and order details optimizations' => 'Aktifkan pengoptimalan halaman terima kasih dan detail pesanan','Changes the layout of order details on the thank you page and account pages.' => 'Mengubah tata letak detail pesanan pada halaman ucapan terima kasih dan halaman akun.','Enable order details customizations on email notifications' => 'Mengaktifkan kustomisasi detail pesanan pada notifikasi email','Display new sections and change order of sections on email notifications. Styles of order details on email notifications remain unchanged.' => 'Menampilkan bagian baru dan mengubah urutan bagian pada notifikasi email. Gaya detail pesanan pada notifikasi email tetap tidak berubah.','Thank you page' => 'Halaman terima kasih','These options affect the thank you page, also known as order received or order confirmation pages.' => 'Opsi-opsi ini memengaruhi halaman terima kasih, yang juga dikenal sebagai halaman pesanan diterima atau halaman konfirmasi pesanan.','Order details layout' => 'Tata letak detail pesanan','Display the order details with wide layout on the thank you page' => 'Menampilkan detail pesanan dengan tata letak yang lebar pada halaman ucapan terima kasih','Add widget areas to the thank you page' => 'Menambahkan area widget ke halaman ucapan terima kasih','These widget areas are used to add trust symbols and trust badges on the thank you page.' => 'Area widget ini digunakan untuk menambahkan simbol kepercayaan dan lencana kepercayaan pada halaman ucapan terima kasih.','These options affect the thank you page , view order details on account pages and on email notifications.' => 'Opsi-opsi ini memengaruhi halaman ucapan terima kasih, melihat detail pesanan pada halaman akun dan pada pemberitahuan email.','Order actions' => 'Tindakan pemesanan','Choose in which position to display the order actions.' => 'Pilih di posisi mana untuk menampilkan tindakan pesanan.','Inside the order overview' => 'Di dalam ikhtisar pesanan','After the order overview' => 'Setelah ikhtisar pesanan','After order sections' => 'Setelah bagian pesanan','Order status' => 'Status pesanan','Display the order status progress bar' => 'Menampilkan bilah kemajuan status pesanan','Choose in which position to display the section.' => 'Pilih di posisi mana untuk menampilkan bagian tersebut.','After the order overview and payment instructions' => 'Setelah ikhtisar pesanan dan instruksi pembayaran','Before the order items section' => 'Sebelum bagian item pesanan','Inside the order items section' => 'Di dalam bagian item pesanan','After the order items section' => 'Setelah bagian item pesanan','On the sidebar' => 'Di bilah sisi','Order downloads' => 'Pesan unduhan','Gift message' => 'Pesan hadiah','Tools' => 'Peralatan','Troubleshooting' => 'Pemecahan masalah','Debug options' => 'Opsi debug','Debug mode' => 'Mode debug','Using debug mode affects the website performance. Only use this option while troubleshooting.' => 'Menggunakan mode debug akan memengaruhi kinerja situs web. Hanya gunakan opsi ini saat memecahkan masalah.','Load unminified assets' => 'Memuat aset yang tidak dimodifikasi','Enhanced select fields' => 'Bidang tertentu yang disempurnakan','Replace <code>select2</code> dropdown components with <code>TomSelect</code>' => 'Ganti komponen dropdown <kode>pilih2</kode> dengan <kode>TomSelect</kode>','TomSelect is a simpler dropdown selection component which is less prone to errors than Select2, while offering the same features that are actually used on WooCommerce checkout pages.' => 'TomSelect adalah komponen pilihan dropdown yang lebih sederhana dan tidak terlalu rentan terhadap kesalahan dibandingkan Select2, sekaligus menawarkan fitur yang sama dengan yang sebenarnya digunakan pada halaman checkout WooCommerce.','Fix automatic zoom-in on form fields' => 'Memperbaiki pembesaran otomatis pada bidang formulir','Set <code>font-size</code> inside form fields to 16px' => 'Setel <kode>ukuran-font</kode> di dalam bidang formulir ke 16px','When the font size inside form fields is smaller than 16px, Safari and other browsers might automatically zoom in on mobile devices to make the text easier to read. When this option is enabled, it will set the font size for inside the form fields to 16px on pages optimized by Fluid Checkout to avoid it zooming in.' => 'Ketika ukuran font di dalam kolom formulir lebih kecil dari 16px, Safari dan browser lain mungkin secara otomatis memperbesar pada perangkat seluler untuk membuat teks lebih mudah dibaca. Ketika opsi ini diaktifkan, opsi ini akan mengatur ukuran font di dalam kolom formulir menjadi 16px pada halaman yang dioptimalkan oleh Fluid Checkout agar tidak diperbesar.','The shipping destination is always set to "Default to customer shipping address" when Fluid Checkout is activated. Customers can still provide different shipping and billing addresses during checkout, an option for setting the default billing address to be the same as the shipping address is available at WooCommerce > Settings > Fluid Checkout.' => 'Tujuan pengiriman selalu diatur ke "Default ke alamat pengiriman pelanggan" ketika Fluid Checkout diaktifkan. Pelanggan masih dapat memberikan alamat pengiriman dan penagihan yang berbeda saat checkout, opsi untuk mengatur alamat penagihan default agar sama dengan alamat pengiriman tersedia di WooCommerce > Pengaturan > Fluid Checkout.','Fluid Checkout' => 'Pembayaran Cairan','Settings' => 'Pengaturan','Support' => 'Dukungan','<a target="_blank" href="%s">Upgrade to PRO</a> to unlock more options.' => '<a target="_blank" href="%s">Ubah ke PRO</a> untuk membuka lebih banyak opsi.','(PRO)' => '(PRO)','(experimental)' => '(eksperimental)','This is an experimental feature and may not work as expected with all themes and plugins. Use it with caution.' => 'Ini adalah fitur eksperimental dan mungkin tidak bekerja seperti yang diharapkan pada semua tema dan plugin. Gunakan dengan hati-hati.','Confirm' => 'Konfirmasi','Verify Phone Number' => 'Verifikasi Nomor Telepon','Please verify your phone number.' => 'Harap verifikasi nomor telepon Anda.','The Bluehost Plugin' => 'Plugin Bluehost','Custom fields' => 'Bidang khusus','Enable custom shipping email field' => 'Mengaktifkan bidang email pengiriman khusus','When enabled, the shipping email field from the Bluehost plugin will be added to the checkout form.' => 'Ketika diaktifkan, kolom email pengiriman dari plugin Bluehost akan ditambahkan ke formulir checkout.','Captcha Pro by BestWebSoft' => 'Captcha Pro oleh BestWebSoft','Captcha position' => 'Posisi captcha','Define the position to display the captcha section. Some positions might not work depending on the captcha type chosen.' => 'Tentukan posisi untuk menampilkan bagian captcha. Beberapa posisi mungkin tidak dapat digunakan, tergantung jenis captcha yang dipilih.','Selecting a pickup point is required before proceeding.' => 'Memilih titik penjemputan diperlukan sebelum melanjutkan.','Pickup point' => 'Titik penjemputan','Pickup point not selected yet.' => 'Titik penjemputan belum dipilih.','Pickup point:' => 'Titik penjemputan:','Mailchimp for WooCommerce' => 'Mailchimp untuk WooCommerce','Subscribe checkbox' => 'Kotak centang Berlangganan','Override the position for the subscribe checkbox on the checkout page and always display it on the contact step' => 'Timpa posisi kotak centang berlangganan di halaman checkout dan selalu tampilkan di langkah kontak','Before Shipping Address' => 'Sebelum Alamat Pengiriman','(originally "%s")' => '(aslinya "%s")','After Shipping Address' => 'Setelah Alamat Pengiriman','Selecting a collection point is required when shipping with FermoPoint.' => 'Memilih titik pengambilan diperlukan saat melakukan pengiriman dengan FermoPoint.','If checked informs typos in email to users. (This feature has been disabled because Fluid Checkout offers the feature. Changes to this option will not take effect)' => 'Jika dicentang akan menginformasikan kesalahan pengetikan pada email kepada pengguna. (Fitur ini telah dinonaktifkan karena Fluid Checkout menawarkan fitur tersebut. Perubahan pada opsi ini tidak akan berpengaruh)','Enable Mail Check: (disabled feature)' => 'Aktifkan Pemeriksaan Surat: (fitur yang dinonaktifkan)','Carrier:' => 'Pembawa:','Sendinblue - WooCommerce Email Marketing' => 'Sendinblue - Pemasaran Email WooCommerce','Sign up checkbox position' => 'Posisi kotak centang pendaftaran','Move the sign up checkbox to the contact step' => 'Pindahkan kotak centang daftar ke langkah kontak','When enabled, the checkbox will be moved to the contact step independently of the display location defined in the Sendinblue plugin settings.' => 'Ketika diaktifkan, kotak centang akan dipindahkan ke langkah kontak secara independen dari lokasi tampilan yang ditentukan dalam pengaturan plugin Sendinblue.','Theme Betheme' => 'Tema Betheme','Checkout progress' => 'Kemajuan pembayaran','Output the checkout steps section from Betheme on the checkout, cart and order received pages.' => 'Keluarkan bagian langkah-langkah checkout dari Betheme di halaman checkout, keranjang dan pesanan yang diterima.','Theme Fennik' => 'Tema Fennik','Breadcrumbs' => 'Remah roti','Output the breadcrumbs section from the Fennik theme when using Fluid Checkout header and footer.' => 'Keluarkan bagian remah roti dari tema Fennik saat menggunakan header dan footer Fluid Checkout.','Theme Go' => 'Tema Pergi','My account layout' => 'Tata letak akun saya','Enable wide layout on "My account" page.' => 'Aktifkan tata letak lebar pada halaman "Akun saya".','Theme Impreza' => 'Tema Impreza','Header' => 'Header','Spacing for site header at the checkout page (in pixels)' => 'Spasi untuk tajuk situs di halaman pembayaran (dalam piksel)','Only applicable when using the Impreza theme header at the checkout page.' => 'Hanya berlaku saat menggunakan header tema Impreza di halaman checkout.','Theme Kapee' => 'Tema Kapee','Output the checkout steps section from the Kapee theme on the checkout, cart and order received pages.' => 'Keluarkan bagian langkah-langkah checkout dari tema Kapee pada halaman checkout, keranjang, dan pesanan yang diterima.','Theme Porto' => 'Tema Porto','Output the checkout steps section from the Porto theme on the checkout, cart and order received pages.' => 'Keluarkan bagian langkah-langkah checkout dari tema Porto pada halaman checkout, keranjang, dan pesanan yang diterima.','Theme Pressmart' => 'Tema Pressmart','Output the checkout steps section from the Pressmart theme on the checkout, cart and order received pages.' => 'Keluarkan bagian langkah-langkah checkout dari tema Pressmart pada halaman checkout, keranjang, dan pesanan yang diterima.','Theme The Gem' => 'Tema Permata','Output the checkout steps section from The Gem theme when using Fluid Checkout header and footer.' => 'Keluarkan bagian langkah-langkah checkout dari tema The Gem saat menggunakan header dan footer Fluid Checkout.','Theme Woodmart' => 'Tema Woodmart','Output the checkout steps section from the Woodmart theme when using Fluid Checkout header and footer.' => 'Keluarkan bagian langkah-langkah checkout dari tema Woodmart saat menggunakan header dan footer Fluid Checkout.','Checkout options' => 'Opsi pembayaran','Disable the theme checkout options' => 'Menonaktifkan opsi pembayaran tema','The options display product image, quantity field, remove button and link to product page added by the theme are disabled by default.' => 'Opsi menampilkan gambar produk, bidang kuantitas, tombol hapus dan tautan ke halaman produk yang ditambahkan oleh tema dinonaktifkan secara default.','Theme Atomion' => 'Tema Atomion','Order progress' => 'Kemajuan pesanan','Display order progress from the theme' => 'Menampilkan kemajuan pesanan dari tema','Display the form field labels visible on the page' => 'Menampilkan label bidang formulir yang terlihat pada halaman','It is recommended to keep this option enabled for a better user experience.' => 'Sebaiknya tetap mengaktifkan opsi ini untuk pengalaman pengguna yang lebih baik.','Theme ZK Nito' => 'Tema ZK Nito','Add extra fields to the checkout form' => 'Menambahkan bidang tambahan ke formulir pembayaran','Adds an extra shipping email and shipping phone field to the shipping address section.' => 'Menambahkan email pengiriman tambahan dan bidang telepon pengiriman ke bagian alamat pengiriman.','Go to the home page' => 'Buka halaman beranda','Skip to content' => 'Loncat ke konten','Create an account' => 'Membuat akun','Log in to your account' => 'Masuk ke akun Anda','Already have an account?' => 'Sudah memiliki akun?','Logged in as %1$s. <a href="%2$s">Log out</a>' => 'Masuk sebagai %1$s. <a href="%2$s">Keluar</a>','Your order has a total amount due of %s. No further payment is needed.' => 'Pesanan Anda memiliki jumlah total yang harus dibayar sebesar %s. Tidak diperlukan pembayaran lebih lanjut.','Close and continue with checkout' => 'Tutup dan lanjutkan dengan pembayaran','Continue' => 'Lanjutkan','Parcel terminal' => 'Terminal paket','<p>This widget has been replaced by Fluid Checkout. </p><p>When using Fluid Checkout, the checkout page needs to use the original WooCommerce shortcode <code>[woocommerce_checkout]</code> to work properly.</p><p>For more information, visit the Fluid Checkout documentation.</p>' => '<p>Widget ini telah digantikan oleh Fluid Checkout. </p> <p> </p> <p>Saat menggunakan Fluid Checkout, halaman checkout harus menggunakan kode pendek WooCommerce asli <kode>[woocommerce_checkout]</kode> agar dapat berfungsi dengan baik.</p> <p>Untuk informasi lebih lanjut, kunjungi dokumentasi Fluid Checkout.</p>','Theme The7' => 'Tema The7','Additional header sections' => 'Bagian header tambahan','Output additional header sections if enabled in the theme settings.' => 'Menghasilkan bagian header tambahan jika diaktifkan dalam pengaturan tema.','More information' => 'Informasi lebih lanjut','Button label for applying coupon codesApply' => 'Menerapkan','Checkout step titleContact' => 'Kontak','Checkout step titleShipping' => 'Pengiriman','Checkout step titleBilling' => 'Penagihan','Checkout step titlePayment' => 'Pembayaran','Checkout substep change link labelChange' => 'Perubahan','Checkout substep save link labelSave changes' => 'Menyimpan perubahan','Substep review textNone.' => 'Tidak ada.','Substep review field format%1$s' => '%1$s','Substep review field format: with label%2$s: %1$s' => '%2$s: %1$s','Image uploader.No image selected.' => 'Tidak ada gambar yang dipilih.','Settings sectionCart' => 'Keranjang','Settings sectionOrder pay' => 'Pembayaran pesanan','Settings sectionThank you' => 'Terima kasih.','Settings section titleIntegrations' => 'Integrasi','Settings section titleLicense keys' => 'Kunci lisensi','Captcha positionBefore place order section' => 'Sebelum bagian pemesanan tempat','Captcha positionBefore place order button' => 'Sebelum tombol tempatkan pesanan','No shipping method chosen label for the order summary--' => '--','Close button aria-labelClose' => 'Tutup','Log in link label at checkout contact stepLog in' => 'Masuk','Log in separator label at for when guest checkout is disabledOr continue below' => 'Atau lanjutkan di bawah ini','Log in separator label at for when guest checkout is disabledOr continue as a guest' => 'Atau lanjutkan sebagai tamu',] ];