return [ 'project-id-version' => 'fluid-checkout','mime-version' => '1.0','content-type' => 'text/plain; charset=utf-8','content-transfer-encoding' => '8bit','language-team' => 'Fluid Checkout','last-translator' => 'Fluid Checkout','pot-creation-date' => '2025-03-05 23:46+0000','report-msgid-bugs-to' => 'https://fluidcheckout.com/support','x-poedit-basepath' => '..','x-poedit-keywordslist' => '__;_e;_ex:1,2c;_n:1,2;_n_noop:1,2;_nx:1,2,4c;_nx_noop:1,2,3c;_x:1,2c;esc_attr__;esc_attr_e;esc_attr_x:1,2c;esc_html__;esc_html_e;esc_html_x:1,2c','x-poedit-searchpath-0' => '.','x-poedit-searchpathexcluded-0' => '*.js','x-poedit-sourcecharset' => 'UTF-8','plural-forms' => 'nplurals=2; plural=(n % 10 != 1 || n % 100 == 11);','messages' => ['WooCommerce' => 'WooCommerce','Go to Plugin Search' => 'Farðu í Plugin Search','Activate %s' => 'Virkjaðu %s','<strong>%1$s</strong> requires <strong>%2$s</strong> to be installed and activated. <a href="%4$s">%3$s</a>' => '<strong>%1$s</strong> krefst þess að <strong>%2$s</strong> sé uppsett og virkjað. <a href="%4$s">%3$s</a>','Enter your code here' => 'Sláðu inn kóðann þinn hér','Add %s' => 'Bæta við %s','Remove' => 'Fjarlægja','Order number and receipt will be sent to this email address.' => 'Pöntunarnúmer og kvittun verða send á þetta netfang.','Apartment, unit, building, floor, etc.' => 'Íbúð, eining, bygging, hæð osfrv.','Shipping phone' => 'Sendingarsími','Only used for shipping-related questions.' => 'Aðeins notað fyrir sendingartengdar spurningar.','Multi-step' => 'Fjölþrepa','Single step' => 'Eitt skref','Click to go to the order summary' => 'Smelltu til að fara í pöntunaryfirlit','Cart total:' => 'Samtals körfu:','Proceed to %s' => 'Haltu áfram í %s','Proceed to contact' => 'Haltu áfram að hafa samband','Proceed to shipping' => 'Haltu áfram að sendingu','Proceed to billing' => 'Haltu áfram að innheimtu','Proceed to payment' => 'Haltu áfram að greiðslu','My contact' => 'Tengiliður minn','Shipping to' => 'Sending til','Shipping method' => 'Sendingaraðferð','Additional notes' => 'Viðbótar athugasemdir','Billing to' => 'Innheimta til','Payment method' => 'Greiðslumáti','Step %1$s of %2$s' => 'Skref %1$s af %2$s','Change: %s' => 'Breyting: %s','An account will be created with the information provided.' => 'Reikningur verður stofnaður með þeim upplýsingum sem gefnar eru upp.','Not selected yet.' => 'Ekki valið ennþá.','Same as shipping address' => 'Sama og sendingarheimili','Same as billing address' => 'Sama og heimilisfang innheimtu','Order summary' => 'Pantanayfirlit','Edit cart' => 'Breyta körfu','Did you mean %s?' => 'Áttirðu við %s?','Change email address to: {suggestion-value}' => 'Breyta netfangi í: {suggestion-value}','This is a required field.' => 'Þetta er áskilinn reitur.','This is not a valid email address.' => 'Þetta er ekki gilt netfang.','This field does not match the related field value.' => 'Þessi reitur passar ekki við tengda reitinn.','The CPF number "{cpf_number}" is invalid.' => 'CPF númerið „{cpf_number}“ er ógilt.','The CNPJ number "{cnpj_number}" is invalid.' => 'CNPJ númerið „{cnpj_number}“ er ógilt.','(Required)' => '(Áskilið)','Checkout Header - Desktop' => 'Checkout Header - Skrifborð','Display widgets on the checkout header for large screens. Only displayed if using the plugin\'s checkout header.' => 'Birta græjur á afgreiðsluhaus fyrir stóra skjái. Sýnist aðeins ef þú notar útskráningarhaus viðbótarinnar.','Checkout Header - Mobile' => 'Checkout Header - Farsími','Display widgets below the checkout header for mobile devices. Only displayed if using the plugin\'s checkout header.' => 'Birta græjur fyrir neðan afgreiðsluhaus fyrir farsíma. Sýnist aðeins ef þú notar útskráningarhaus viðbótarinnar.','Checkout Sidebar' => 'Afgreiðslu hliðarstika','Display widgets on the checkout sidebar after the order summary.' => 'Birta græjur á hliðarstikunni fyrir afgreiðslu eftir pöntunaryfirlitið.','Checkout Order Summary' => 'Yfirlit yfir pöntunarpöntun','Display widgets inside the order summary at the checkout page.' => 'Birta græjur inni í pöntunaryfirlitinu á afgreiðslusíðunni.','Checkout Below Place Order' => 'Útskráning fyrir neðan Pantaðu pöntun','Display widgets below the place order button at the checkout page.' => 'Birta græjur fyrir neðan pöntunarhnappinn á greiðslusíðunni.','Checkout Footer' => 'Afgreiðslufótur','Display widgets on the checkout footer. Only displayed if using the plugin\'s checkout footer.' => 'Birta græjur á afgreiðslufóti. Sýnist aðeins ef þú notar afgreiðslufót viðbótarinnar.','Classic' => 'Klassískt','Boxed' => 'Boxað','Split' => 'Skipta','Minimalist' => 'Minimalískt','Fluid Checkout and Delivery Date plugins by CodeRockz' => 'Fluid Checkout og Delivery Date viðbætur frá CodeRockz','<p>We detected that you are using the plugins Delivery & Pickup Date Time for WooCommerce (free and PRO) by CodeRockz. Integration with these plugins have been moved to Fluid Checkout PRO.</p>' => '<p>Við komumst að því að þú ert að nota viðbæturnar Afhendingar- og afhendingartími fyrir WooCommerce (ókeypis og PRO) frá CodeRockz. Samþætting við þessar viðbætur hefur verið færð yfir í Fluid Checkout PRO.</p>','Upgrade to PRO' => 'Uppfærðu í PRO','Fluid Checkout database updated successfully' => 'Fluid Checkout gagnagrunnur uppfærður','The database changes where applied successfully.' => 'Gagnagrunnurinn breytist þar sem það var beitt með góðum árangri.','Ok' => 'Allt í lagi','Dismiss' => 'Segja frá','Fluid Checkout database update' => 'Fluid Checkout gagnagrunnsuppfærsla','Some changes to the database are required. As with any update, we recommend you to <strong>take a full backup of your website before proceeding</strong>.' => 'Nokkrar breytingar á gagnagrunninum eru nauðsynlegar. Eins og með allar uppfærslur mælum við með að þú <strong>takir fullt öryggisafrit af vefsíðunni þinni áður en þú heldur áfram</strong> .','Update database' => 'Uppfærðu gagnagrunn','Fluid Checkout is not compatible with the Divi Builder checkout layout' => 'Fluid Checkout er ekki samhæft við Divi Builder afgreiðsluskipulag','<p>Fluid Checkout needs the classic WooCommerce shortcode-based checkout form to work. When using the Divi Builder to customize the look and feel of the checkout page, the classic shortcodes are replaced with custom widgets from the Divi Builder for each part of the checkout page. These custom widgets are not compatible with Fluid Checkout.</p><p>Please edit your checkout page replacing the Divi Builder widgets with the a text widget containing the shortcode <code>[woocommerce_checkout]</code>.</p>' => '<p>Fluid Checkout þarf klassíska WooCommerce stuttkóða-undirstaða útskráningareyðublað til að virka. Þegar Divi Builder er notað til að sérsníða útlit og tilfinningu afgreiðslusíðunnar, er klassískum stuttkóðum skipt út fyrir sérsniðnar græjur frá Divi Builder fyrir hvern hluta afgreiðslusíðunnar. Þessar sérsniðnu græjur eru ekki samhæfar við Fluid Checkout.</p><p> Vinsamlega breyttu útskráningarsíðunni þinni og skiptu Divi Builder græjunum út fyrir textagræjuna sem inniheldur stuttkóðann <code>[woocommerce_checkout]</code> .</p>','Edit the checkout page' => 'Breyttu afgreiðslusíðunni','Read the documentation.' => 'Lestu skjölin.','Germanized PRO multistep checkout feature needs to be disabled when using Fluid Checkout' => 'Þýskt PRO fjölþrepa útskráningareiginleiki þarf að vera óvirkur þegar Fluid Checkout er notað','When using Fluid Checkout, the multistep feature from the Germanized PRO plugin becomes unnecessary and can cause <strong>critical errors while processing new orders</strong>. Please disable the Germanized PRO multistep checkout feature in the plugin settings.' => 'Þegar Fluid Checkout er notað verður fjölþrepa eiginleikinn frá Germanized PRO viðbótinni óþarfur og getur valdið <strong>mikilvægum villum við vinnslu nýrra pantana</strong> . Vinsamlegast slökktu á Germanized PRO fjölþrepa afgreiðslueiginleika í stillingum viðbótarinnar.','Go to Germanized settings' => 'Farðu í þýskar stillingar','Thanks for choosing Fluid Checkout!' => 'Takk fyrir að velja Fluid Checkout!','You have been using the plugin for a while now. How do you like it so far? <br>Please give us a quick rating, we appreciate every bit of feedback and it encourages us to keep improving the plugin :)' => 'Þú hefur notað viðbótina í nokkurn tíma. Hvernig líkar þér það hingað til?<br> Vinsamlegast gefðu okkur skjóta einkunn, við kunnum að meta allar athugasemdir og það hvetur okkur til að halda áfram að bæta viðbótina :)','Rate the plugin' => 'Gefðu viðbótinni einkunn','I need help!' => 'Ég þarf hjálp!','The plugin WooCommerce Checkout Manager by Quadlayers is not compatibile with Fluid Checkout' => 'Viðbótin WooCommerce Checkout Manager frá Quadlayers er ekki samhæfð við Fluid Checkout','Some features of WooCommerce Checkout Manager by Quadlayers might not work as expected when using it with Fluid Checkout. Read <a href="%s">our documentation</a> for more information and alternative solutions.' => 'Sumir eiginleikar WooCommerce Checkout Manager frá Quadlayers virka kannski ekki eins og búist var við þegar þeir eru notaðir með Fluid Checkout. Lestu <a href="%s">skjölin okkar</a> fyrir frekari upplýsingar og aðrar lausnir.','Read documentation' => 'Lestu skjöl','Don\'t show this again' => 'Ekki sýna þetta aftur','Special offers' => 'Sértilboð','Extend all the goodness of Fluid Checkout to your cart page and thank you pages.' => 'Útvíkkaðu allt það góða við Fluid Checkout á körfusíðuna þína og þakkarsíður.','Fluid Checkout PRO' => 'Fluid Checkout PRO','Design templates, optimized cart and order received pages, account matching, and much more.' => 'Hönnun sniðmát, fínstilltu körfu og mótteknar síður, reikningssamsvörun og margt fleira.','The best tools to further <strong>improve your WooCommerce checkout conversion rate</strong> and make the purchase experience consistent on the entire journey.' => 'Bestu verkfærin til að <strong>bæta WooCommerce greiðsluhlutfallið þitt</strong> enn frekar og gera kaupupplifunina samræmda á öllu ferðalaginu.','More design templates' => 'Fleiri hönnunarsniðmát','Optimized cart page' => 'Bjartsýni körfusíðu','Optimized order received / thank you page' => 'Fínstillt röð móttekin / þakka þér síða','Edit cart contents at checkout page' => 'Breyttu innihaldi körfunnar á kassasíðu','Account matching / user matching' => 'Reikningssamsvörun / notendasamsvörun','Express Checkout buttons from supported payment methods' => 'Hraðgreiðsluhnappar frá studdum greiðslumáta','Trust symbols on the cart and thank you pages' => 'Treystu táknum á körfunni og þakkarsíðum','More positions for the coupon code on the checkout page' => 'Fleiri stöður fyrir afsláttarmiða kóða á greiðslusíðunni','Integrated coupon code field on the cart page' => 'Innbyggður afsláttarmiðakóðareitur á körfusíðunni','International phone numbers' => 'Alþjóðleg símanúmer','Gift messages and packing slips' => 'Gjafaskilaboð og fylgiseðlar','Local pickup and in-store collection' => 'Afhending á staðnum og söfnun í verslun','Upgrade to PRO &mdash; 99 EUR' => 'Uppfærsla í PRO — 99 EUR','Fluid Checkout PRO – Bundle' => 'Fluid Checkout PRO – Búnt','All PRO features + All add-ons for a special price.' => 'Allir PRO eiginleikar + Allar viðbætur fyrir sérstakt verð.','The bundle includes all add-ons we currently offer and Fluid Checkout add-ons we launch in the future.' => 'Pakkinn inniheldur allar viðbætur sem við bjóðum núna og Fluid Checkout viðbætur sem við kynnum í framtíðinni.','226 EUR value (Save 42%)' => '226 EUR gildi (Sparið 42%)','Google Address Autocomplete' => 'Sjálfvirk útfylling Google heimilisfangs','Address Book' => 'Heimilisfangabók','EU-VAT Assistant' => 'EU-VSK aðstoðarmaður','All future add-ons *' => 'Allar framtíðarviðbætur *','* For as long as you have an active subscription.' => '* Svo lengi sem þú ert með virka áskrift.','Get the Bundle – Only 129 EUR' => 'Fáðu pakkann - Aðeins 129 EUR','All prices shown in EUR. If there are any divergencies with the prices on our website, the offers shown on the website superseed these and will be applied.' => 'Öll verð sýnd í evrum. Ef einhver frávik eru á verði á vefsíðu okkar koma tilboðin sem sýnd eru á vefsíðunni í stað þeirra og verða notuð.','Add-ons' => 'Viðbætur','Enhance your checkout experience with these add-ons.' => 'Bættu afgreiðsluupplifun þína með þessum viðbótum.','I already have it – Hide this offer' => 'Ég á það nú þegar - Fela þetta tilboð','Installed &mdash; Activate' => 'Uppsett — Virkja','Activated' => 'Virkjað','Up to 40% less checkout fields to fill in.' => 'Allt að 40% færri afgreiðslureitum til að fylla út.','Avoid delivery delays and unsatisfied customers. Collect the <strong>correct address information</strong> from the first time they buy with you.' => 'Forðastu afhendingartafir og óánægða viðskiptavini. Safnaðu <strong>réttum heimilisfangsupplýsingum</strong> frá fyrsta skipti sem þeir kaupa með þér.','Get this add-on &mdash; 29 EUR' => 'Fáðu þessa viðbót — 29 EUR','Address book' => 'Heimilisfangabók','Multiple saved addresses for shipping and billing.' => 'Mörg vistuð heimilisföng fyrir sendingu og innheimtu.','Let customers <strong>save multiple shipping and billing addresses</strong> on their account and choose which ones to use at checkout and cart pages.' => 'Leyfðu viðskiptavinum <strong>að vista mörg sendingar- og innheimtuheimilisföng</strong> á reikningnum sínum og veldu hver þau á að nota við útskráningu og körfusíður.','Get this add-on &mdash; 59 EUR' => 'Fáðu þessa viðbót — 59 EUR','Simplified EU-VAT validation for your store.' => 'Einfölduð ESB-VSK staðfesting fyrir verslunina þína.','Collect and <strong>validate EU VAT numbers at the checkout page</strong>, removes tax charges on reverse charge basis and confirms customer location when needed.' => 'Safnaðu og <strong>staðfestu virðisaukaskattsnúmer ESB á greiðslusíðunni</strong> , fjarlægir skattgjöld á öfugri gjaldfærslu og staðfestir staðsetningu viðskiptavinar þegar þörf krefur.','Get this add-on &mdash; 39 EUR' => 'Fáðu þessa viðbót — 39 EUR','All add-ons are sold separately and require the <strong>Fluid Checkout Lite</strong> plugin to be installed and activated, except when noted.' => 'Allar viðbætur eru seldar sérstaklega og krefjast þess að <strong>Fluid Checkout Lite</strong> viðbótin sé sett upp og virkjuð, nema þegar tekið er fram.','Select an image' => 'Veldu mynd','No image selected.' => 'Engin mynd valin.','Remove image' => 'Fjarlægðu mynd','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to get your license key, or <a href="%s" target="_blank">purchase a new license key</a>.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Skráðu þig inn á reikninginn þinn</a> til að fá leyfislykilinn þinn, eða <a href="%s" target="_blank">keyptu nýjan leyfislykil</a> .','Valid until %s.' => 'Gildir til %s.','Expired on %s.' => 'Rennur út %s.','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to renew your license key and continue to receive updates and support.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Skráðu þig inn á reikninginn þinn</a> til að endurnýja leyfislykilinn þinn og halda áfram að fá uppfærslur og stuðning.','License key cancelled.' => 'Leyfislykill afturkallaður.','<a href="%s" target="_blank">Purchase a new license key</a> and continue to receive updates and support.' => '<a href="%s" target="_blank">Kauptu nýjan leyfislykil</a> og haltu áfram að fá uppfærslur og stuðning.','Error: ' => 'Villa: ','Setup & Documentation' => 'Uppsetning og skjöl','Great! Your checkout page is now running on Fluid Checkout.' => 'Frábært! Afgreiðslusíðan þín er nú í gangi á Fluid Checkout.','Here are a few resources for you to get started:' => 'Hér eru nokkur úrræði fyrir þig til að byrja:','Read the installation guide <a href="%s" target="_blank">Getting stated with Fluid Checkout</a>.' => 'Lestu uppsetningarhandbókina <a href="%s" target="_blank">Komdu fram með Fluid Checkout</a> .','Setup layout and design on the <a href="%s">checkout options</a>.' => 'Uppsetning skipulags og hönnunar á <a href="%s">afgreiðslumöguleikum</a> .','Check if there are any <a href="%s">integration options</a> available for other plugins you have installed.' => 'Athugaðu hvort það séu einhverjir <a href="%s">samþættingarmöguleikar</a> í boði fyrir önnur viðbætur sem þú hefur sett upp.','Visit <a href="%s" target="_blank">our documentation</a> for more information about Fluid Checkout features.' => 'Skoðaðu <a href="%s" target="_blank">skjölin okkar</a> til að fá frekari upplýsingar um Fluid Checkout eiginleika.','If you ever need help, <a href="%s" target="_blank">open a support ticket</a> on our official support channel.' => 'Ef þú þarft einhvern tíma hjálp, <a href="%s" target="_blank">opnaðu stuðningsmiða</a> á opinberu stuðningsrásinni okkar.','Cart layout' => 'Skipulag körfu','Cart optimizations' => 'Hagræðingar körfu','Enable cart page optimizations' => 'Virkja fínstillingu körfusíðu','Display the cart page with a layout similar to the checkout page.' => 'Birtu körfusíðuna með útliti svipað og afgreiðslusíðunni.','Header and footer' => 'Haus og fótur','Header and footer template' => 'Sniðmát fyrir haus og fót','Controls whether to use the distraction free page header and footer or keep the currently active theme\'s header and footer.' => 'Stýrir því hvort nota eigi truflunarlausan síðuhaus og -fót eða halda haus og fæti þess þema sem er virkt.','Distraction free header and footer' => 'Truflunlaus haus og fótur','Theme\'s header and footer' => 'Haus og fótur þema','Cart elements' => 'Körfuþættir','Make the order summary stay visible on the cart page while scrolling' => 'Láttu pöntunaryfirlitið vera sýnilegt á körfusíðunni meðan þú flettir','Coupon codes' => 'Afsláttarmiða kóðar','Select position where to display the coupon codes section on the cart page. Only applicable when AJAX cart and the integrated coupon codes in the checkout page are enabled.' => 'Veldu staðsetningu þar sem á að birta afsláttarkóðahlutann á körfusíðunni. Gildir aðeins þegar AJAX körfan og samþættir afsláttarmiðakóðar á afgreiðslusíðunni eru virkjuð.','Hidden' => 'Falinn','Before the cart items section' => 'Á undan hlutum í körfu','Inside the cart items section' => 'Inni í vörukörfuhlutanum','After the cart items section' => 'Eftir kaflann um körfuvörur','Before the shipping section' => 'Fyrir sendingarhlutann','After the shipping section' => 'Eftir sendingarhlutann','Before the order summary' => 'Áður en pöntunaryfirlit','Inside the order summary' => 'Inni í pöntunaryfirliti','Before the order totals' => 'Áður en pöntunin er samtals','After the order totals' => 'Eftir heildartölur pöntunarinnar','After the order summary' => 'Eftir pöntunarsamantekt','Shipping' => 'Sending','Select position where to display the shipping costs and calculator on the cart page.' => 'Veldu staðsetningu þar sem á að birta sendingarkostnað og reiknivél á körfusíðunni.','Display as a cart section' => 'Birta sem körfuhluta','Cross-sells' => 'Krosssölur','Select position where to display the cross-sells section on the cart page.' => 'Veldu staðsetningu þar sem á að birta krosssöluhlutann á körfusíðunni.','Before the cart actions' => 'Áður en aðgerðir körfu','After the cart actions' => 'Eftir körfuaðgerðirnar','Layout of cross-sell items on the cart page.' => 'Skipulag krosssöluvara á körfusíðunni.','Optimized horizontal cross-sells layout' => 'Fínstillt lárétt krosssöluskipulag','Theme\'s cross-sells layout' => 'Krosssöluskipulag þema','Number of cross-sell items to display on the cart page' => 'Fjöldi krosssöluvara til að birta á körfusíðunni','Trust symbols &amp; badges' => 'Traust tákn og merki','Add widget areas to the cart page' => 'Bættu græjusvæðum við körfusíðuna','These widget areas are used to add trust symbols and trust badges on the cart page.' => 'Þessi græjusvæði eru notuð til að bæta við trauststáknum og traustmerkjum á körfusíðunni.','Checkout' => 'Útskráning','Layout &amp; design' => 'Skipulag &amp; hönnun','Checkout layout' => 'Skipulag kassa','Design template' => 'Hönnunarsniðmát','General styles for the checkout steps, order summary and other sections. Might also apply to other pages such as the Cart, Order Received and View Order pages.' => 'Almennur stíll fyrir útskráningarskref, pantanayfirlit og aðra hluta. Gæti líka átt við aðrar síður eins og körfu, pöntun móttekin og skoða pöntun síður.','Dark mode' => 'Dökk stilling','Enable dark mode' => 'Virkjaðu dökka stillingu','Header and footer templates' => 'Sniðmát fyrir haus og fót','We recommend using the distraction free header and footer to avoid distractions at the checkout page. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Read the research about cart abandonment</a>.' => 'Við mælum með því að nota truflunarlausan haus og fót til að forðast truflun á afgreiðslusíðunni. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Lestu rannsóknina um að kerra sé yfirgefin</a> .','Logo image' => 'Logo mynd','Choose an image to be displayed on the checkout page header. Only applies when using the distraction free header and footer.' => 'Veldu mynd til að birta á haus afgreiðslusíðunnar. Gildir aðeins þegar truflunlaus haus og fótur er notaður.','Header background color' => 'Bakgrunnslitur haus','Choose a background color for the checkout page header. Only applies when using the distraction free header and footer.' => 'Veldu bakgrunnslit fyrir haus afgreiðslusíðunnar. Á aðeins við þegar truflunlaus haus og fótur er notaður.','HTML color value. ie: #f3f3f3' => 'HTML litagildi. þ.e.: #f3f3f3','Page background color' => 'Bakgrunnslitur síðu','Choose a background color for the checkout page. Color is applied to the <em>body</em> element.' => 'Veldu bakgrunnslit fyrir afgreiðslusíðuna. Litur er borinn á <em>líkamshlutann</em> .','Footer background color' => 'Bakgrunnslitur fóta','Choose a background color for the checkout page footer. Only applies when using the distraction free header and footer.' => 'Veldu bakgrunnslit fyrir síðufót afgreiðslusíðunnar. Á aðeins við þegar truflunlaus haus og fótur er notaður.','Checkout elements' => 'Útskráningarþættir','Progress bar' => 'Framvindustika','Display the checkout progress bar when using multi-step checkout layout' => 'Birta framvindustikuna þegar þú notar fjölþrepa útfærsluútlit','Make the checkout progress bar stay visible while scrolling' => 'Láttu framvindustikuna afgreiðslu vera sýnileg meðan þú flettir','Express checkout' => 'Hraðútskráning','Enable the express checkout section' => 'Virkjaðu hraðgreiðsluhlutann','Displays the express checkout section at checkout when supported payment gateways have this feature enabled.' => 'Sýnir hraðgreiðsluhlutann við greiðslu þegar studdar greiðslugáttir hafa þennan eiginleika virkan.','Display express checkout buttons in one line for larger screens' => 'Sýndu hraðafgreiðsluhnappa í einni línu fyrir stærri skjái','Ignore additional checkout required fields when paying with a compatible express checkout payment gateway' => 'Hunsa frekari útskráningarreitir þegar greitt er með samhæfri hraðgreiðslugátt','Make the order summary stay visible while scrolling' => 'Láttu pöntunaryfirlitið vera sýnilegt meðan þú flettir','Choose a background color for the order summary section.' => 'Veldu bakgrunnslit fyrir pöntunarsamantektarhlutann.','Highlight the order totals row in the order summary table' => 'Auðkenndu línuna með pöntunarsamtölum í pöntunaryfirlitstöflunni','Most useful when the order summary section does not have a highlighted background color. Might also apply to the Cart, Order Received and View Order pages when using Fluid Checkout PRO.' => 'Gagnlegast þegar pöntunarsamantektarhlutinn er ekki með auðkenndan bakgrunnslit. Gæti einnig átt við um körfu, pöntun móttekin og skoða pöntun síður þegar Fluid Checkout PRO er notað.','Action link on the order summary at checkout.' => 'Aðgerðartengil á pöntunaryfirliti við útskráningu.','Edit cart link' => 'Breyta körfutengli','Cart items count' => 'Körfuhlutir telja','Link to shop page' => 'Tengill á verslunarsíðu','Cart items' => 'Körfuvörur','Enable options to edit cart items on the checkout page' => 'Virkjaðu valkosti til að breyta körfuhlutum á greiðslusíðunni','Allow customers to change product quantities or removing items directly at the checkout page.' => 'Leyfa viðskiptavinum að breyta vörumagni eða fjarlægja vörur beint á afgreiðslusíðunni.','Hide cart items errors at the checkout page' => 'Fela villur í körfuvörum á greiðslusíðunni','Do not display the cart items errors message at the top of the checkout page. When submitting the checkout form to complete the purchase, these error messages will always be displayed.' => 'Ekki birta villuskilaboðin fyrir vörukörfu efst á greiðslusíðunni. Þegar þú sendir inn greiðslueyðublaðið til að ganga frá kaupunum munu þessi villuboð alltaf birtast.','Place order' => 'Leggðu inn pöntun','Define the position to display "Place order" and terms checkbox section.' => 'Skilgreindu staðsetninguna til að birta „Setja pöntun“ og skilmála gátreitinn.','Some options might not be compatible with some plugins and themes.' => 'Sumir valkostir gætu ekki verið samhæfir sumum viðbótum og þemum.','Below the payment section' => 'Fyrir neðan greiðsluhlutann','Below the order summary' => 'Fyrir neðan pöntunarsamantekt','Both below the payment section and the order summary' => 'Bæði fyrir neðan greiðsluhlutann og pöntunaryfirlitið','Add widget areas to the checkout page' => 'Bættu græjusvæðum við afgreiðslusíðuna','These widget areas are used to add trust symbols and trust badges on the checkout page.' => 'Þessi græjusvæði eru notuð til að bæta við trauststáknum og traustmerkjum á afgreiðslusíðunni.','Display checkout sidebar widgets only when viewing the last checkout step on mobile devices when using multi-step checkout layout' => 'Sýna greiðsluhliðargræjur aðeins þegar þú skoðar síðasta greiðsluþrepið í fartækjum þegar þú notar fjölþrepa greiðsluútlit','Enable integrated coupon code section' => 'Virkja samþættan afsláttarmiða kóða hluta','Only applicable if use of coupon codes are enabled in the WooCommerce settings.' => 'Gildir aðeins ef notkun afsláttarmiðakóða er virkjuð í WooCommerce stillingunum.','Display the coupon codes section title' => 'Birta titil afsláttarmiða kóða kafla','Only applicable when coupon code is displayed as a separate section on the checkout or cart pages.' => 'Gildir aðeins þegar afsláttarmiðakóði er sýndur sem sérstakur hluti á greiðslu- eða körfusíðum.','Select position where to display the coupon codes section on the checkout page.' => 'Veldu staðsetningu þar sem á að birta afsláttarkóðahlutann á greiðslusíðunni.','Before the checkout steps' => 'Áður en útskráningarskref','As a substep before payment methods' => 'Sem undirskref fyrir greiðslumáta','As a substep after payment methods' => 'Sem undirskref eftir greiðslumáta','Select style of the "apply coupon" button. Only applicable when the coupon code section is displayed "Before the checkout steps" on the checkout page, or "Before the cart items section" on the cart page.' => 'Veldu stíl á &quot;sækja afsláttarmiða&quot; hnappinn. Gildir aðeins þegar afsláttarmiðakóðihlutinn er sýndur „Áður en greiðsluþrep“ er á greiðslusíðunni, eða „Áður en hlutur í körfu“ á körfusíðunni.','Default button style' => 'Sjálfgefinn hnappastíll','"Add" link button' => '„Bæta við“ tengilhnappi','Checkout fields' => 'Afgreiðslureitir','Optional fields' => 'Valfrjálsir reitir','Hide optional fields behind a link button' => 'Fela valfrjálsa reiti fyrir aftan tengihnapp','It is recommended to keep this option checked to reduce the number of open input fields, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">read the research</a>.' => 'Mælt er með því að haka við þennan valmöguleika til að fækka opnum innsláttarreitum, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">lestu rannsóknina</a> .','Do not hide "Address line 2" fields behind a link button' => 'Ekki fela &quot;Heimilisfangslína 2&quot; reiti fyrir aftan tengihnapp','Recommended only when most customers actually need the "Address line 2" field, or when getting the right shipping address is crucial (ie. if delivering food and other perishable products).' => 'Aðeins er mælt með því þegar flestir viðskiptavinir þurfa raunverulega „Heimilisfangslína 2“ reitinn, eða þegar mikilvægt er að fá rétt sendingarheimili (þ.e. ef afhending er matvæli og aðrar viðkvæmar vörur).','Display the "Add" link buttons in lowercase' => 'Birtu „Bæta við“ hlekkjahnappana með litlum staf','Make the labels of optional field "Add" link button as <code>lowercase</code> (ie. "Add phone number" instead of "Add Phone Number"). This option also affects the link buttons for coupon code fields.' => 'Gerðu merkingarnar á valfrjálsu reitnum „Bæta við“ tengihnappi sem <code>lowercase</code> (þ.e. „Bæta við símanúmeri“ í stað „Bæta við símanúmeri“). Þessi valkostur hefur einnig áhrif á hlekkjahnappa fyrir afsláttarmiða kóða reiti.','Billing address' => 'Heimilisfang innheimtu','Select position where to display the billing address section on the checkout page.' => 'Veldu staðsetningu þar sem á að birta hlutann innheimtu heimilisfang á greiðslusíðunni.','As a step before shipping' => 'Sem skref fyrir sendingu','As a sub-step before the shipping address section' => 'Sem undirskref á undan sendingarfangahlutanum','As a sub-step after the shipping address section' => 'Sem undirskref á eftir sendingarheimilisfangshlutanum','As a step after shipping' => 'Sem skref eftir sendingu','As a sub-step before the payment section' => 'Sem undirskref á undan greiðsluhlutanum','Force billing and shipping addresses to the same (single section)' => 'Þvingaðu innheimtu- og sendingarheimilisföng í sama (einn hluti)','Checkbox for "Same as shipping/billing address" checked by default' => 'Gátreiturinn fyrir „Sama og sendingar-/innheimtuheimilisfang“ merktur sjálfgefið','The checkbox "Same as shiping address" will start as checked by default when the shipping address section is displayed first. <br>The checkbox "Same as billing address" will start as checked by default when the billing address section is displayed first. <br> It is recommended to leave this option checked as to significantly reduce the number of open input fields at checkout, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">read the research</a>.' => 'Gátreiturinn „Sama og sendingarheimilisfang“ byrjar sem sjálfgefið hakað þegar hlutinn sendingarheimili birtist fyrst.<br> Gátreiturinn &quot;Sama og innheimtuheimilisfang&quot; mun byrja sem valið sjálfgefið þegar hlutinn innheimtu heimilisfang birtist fyrst.<br> Mælt er með því að hafa þennan valmöguleika merkt til að draga verulega úr fjölda opinna innsláttarreitna við útskráningu, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">lestu rannsóknina</a> .','Highlight the billing address section in the checkout form' => 'Auðkenndu hlutann innheimtuheimilisfang í greiðsluforminu','Shipping address' => 'Heimilisfang sendingar','Highlight the shipping address section in the checkout form' => 'Auðkenndu sendingarheimilisfangið í greiðsluforminu','Shipping methods' => 'Sendingaraðferðir','Choose in which position to display the shipping methods section.' => 'Veldu í hvaða stöðu á að birta kaflann um sendingaraðferðir.','Before shipping address' => 'Fyrir sendingar heimilisfang','After shipping address' => 'Eftir sendingar heimilisfang','Prevent automatic selection of the first shipping method' => 'Koma í veg fyrir sjálfvirkt val á fyrstu sendingaraðferðinni','When enabled, the first shipping method available will not be automatically selected when no other shipping method was previously selected for each shipping package. <br>This option will be automatically enabled if the option for clearing the selected shipping method is enabled for the Local Pickup feature.' => 'Þegar það er virkt verður fyrsti sendingaraðferðin sem til er ekki valin sjálfkrafa þegar engin önnur sendingaraðferð var áður valin fyrir hvern sendingarpakka.<br> Þessi valkostur verður sjálfkrafa virkur ef valkosturinn til að hreinsa valinn sendingaraðferð er virkur fyrir eiginleikann Staðbundin afhending.','Local pickup' => 'Heimilisflutningur','Removes shipping address section when a local pickup shipping method is selected.' => 'Fjarlægir sendingarheimilisfang þegar staðbundin sendingaraðferð er valin.','Replaces the shipping address with the pickup point location when a local pickup shipping method is selected.' => 'Kemur í stað sendingarstaðarins fyrir afhendingarstaðinn þegar staðbundin sendingaraðferð er valin.','Show option to clear shipping methods in the pickup location substep' => 'Sýna valkostinn til að hreinsa sendingaraðferðir í undirþrepinu afhendingarstað','Show a link button on the pickup location substep to clear the chosen shipping methods. This can be used to allow showing the shipping address section again if a local pickup method was previously selected.' => 'Sýndu tengihnapp á undirþrepinu fyrir afhendingarstað til að hreinsa valda sendingaraðferðir. Þetta er hægt að nota til að leyfa að sýna sendingarheimilisfangið aftur ef staðbundin afhendingaraðferð var áður valin.','Choose which address to save as the shipping address for local pickup orders.' => 'Veldu hvaða heimilisfang á að vista sem sendingarfang fyrir staðbundnar afhendingarpantanir.','Save the selected pickup location' => 'Vistaðu valinn afhendingarstað','Save same as the billing address' => 'Vista það sama og heimilisfang innheimtu','Do not save any shipping address' => 'Ekki vista neitt sendingarheimili','Company name field for shipping address' => 'Fyrirtækisheiti fyrir sendingarheimilisfang','Change visibility for the company name field for the shipping address section on the checkout form.' => 'Breyta sýnileika fyrir reitinn fyrir nafn fyrirtækis fyrir sendingarheimilisfangshlutann á afgreiðslueyðublaðinu.','If field is set as "optional", which is the default visibility state, no changes will be applied to let other plugins apply any changes they need.' => 'Ef reiturinn er stilltur sem &quot;valfrjáls&quot;, sem er sjálfgefið sýnileikaástand, verður engum breytingum beitt til að leyfa öðrum viðbætur að beita þeim breytingum sem þeir þurfa.','Hidden (remove field)' => 'Falinn (fjarlægja reit)','Optional' => 'Valfrjálst','Required' => 'Áskilið','Add shipping phone field to the checkout form.' => 'Bættu sendingarsímareit við afgreiðslueyðublaðið.','Choose in which step to display the shipping phone field.' => 'Veldu í hvaða skrefi á að birta sendingarsímareitinn.','Contact step' => 'Hafðu skref','Billing phone' => 'Innheimtusími','Add billing phone field to the checkout form.' => 'Bættu innheimtusími reitnum við útskráningareyðublaðið.','Choose in which step to display the billing phone field.' => 'Veldu í hvaða skrefi á að birta reikningssímareitinn.','Enable international phone number format and validation for phone fields' => 'Virkjaðu alþjóðlegt símanúmerasnið og staðfestingu fyrir símareiti','Format and validate phone numbers according to each country.' => 'Forsníða og staðfesta símanúmer í samræmi við hvert land.','Enable phone number validation based on country rules' => 'Virkjaðu staðfestingu símanúmera byggt á landsreglum','When disabled, the phone field validation will not check if country or area codes are valid for the country.' => 'Þegar það er óvirkt mun staðfesting símareitsins ekki athuga hvort lands- eða svæðisnúmer séu gild fyrir landið.','Only show allowed countries for shipping or billing' => 'Sýndu aðeins leyfð lönd fyrir sendingu eða innheimtu','When enabled, only the countries allowed for shipping will be available in the shipping phone field, and only countries allowed for billing will be available for the billing phone field.' => 'Þegar það er virkt verða aðeins löndin sem leyfð er fyrir sendingu tiltæk í reitnum fyrir sendingarsíma og aðeins lönd sem leyfð er fyrir innheimtu verða tiltæk fyrir innheimtusími reitinn.','Show country code beside the flag' => 'Sýndu landskóða við hlið fánans','Show an example of a valid phone number inside phone fields' => 'Sýndu dæmi um gilt símanúmer í símareitum','Do not change placeholders' => 'Ekki skipta um staðgengla','Show if not defined' => 'Sýna ef ekki skilgreint','Always show' => 'Sýna alltaf','Order notes' => 'Pantaðu athugasemdir','Define the visibility of the additional order notes field.' => 'Skilgreindu sýnileika reitsins fyrir viðbótarpöntunarskýringar.','Gift options' => 'Gjafavalkostir','Display gift message and other gift options at the checkout page' => 'Sýndu gjafaskilaboð og aðra gjafavalkosti á afgreiðslusíðunni','Allow customers to add a gift message and other gift related options to the order.' => 'Leyfðu viðskiptavinum að bæta gjafaskilaboðum og öðrum gjafatengdum valkostum við pöntunina.','Display the gift message fields always expanded' => 'Birta gjafaskilaboðareitina alltaf stækkaðir','Display the gift message as part of the order details table instead of a separate section' => 'Birta gjafaskilaboðin sem hluta af pöntunarupplýsingatöflunni í stað sérstaks hluta','This option affects the order confirmation page (thank you page) and order details on account pages, emails and packing slips.' => 'Þessi valkostur hefur áhrif á pöntunarstaðfestingarsíðuna (þakka þér) og pöntunarupplýsingar á reikningssíðum, tölvupósti og fylgiseðlum.','Account matching' => 'Reikningssamsvörun','Enable the account matching feature' => 'Virkjaðu samsvörunareiginleika reikningsins','Associate the guest customer\'s orders with their existing account when an account already exists with the customer\'s contact details.' => 'Tengja pantanir gesta viðskiptavinar við núverandi reikning þeirra þegar reikningur er þegar til með tengiliðaupplýsingum viðskiptavinarins.','Display message when an account exists with the email address provided' => 'Birta skilaboð þegar reikningur er til með uppgefnu netfangi','Replaces the account creation fields with a notification and option to log in. In some contexts, it might be recommended to leave this option disabled to protect the privacy of customers.' => 'Kemur í stað reikningsstofnunarreitanna fyrir tilkynningu og möguleika á að skrá þig inn. Í sumum tilfellum gæti verið mælt með því að hafa þennan valkost óvirkan til að vernda friðhelgi viðskiptavina.','Dashboard' => 'Mælaborð','Integrations' => 'Samþættingar','No integrations available at the moment on this section. The options related to each plugin will only appear here if that plugin is activated.' => 'Engar samþættingar í boði eins og er á þessum hluta. Valmöguleikarnir sem tengjast hverri viðbót munu aðeins birtast hér ef þessi viðbót er virkjuð.','License keys' => 'Leyfislyklar','No license keys options available at the moment on this section. The options related to each plugin or add-on will appear here when that plugin is activated.' => 'Engir möguleikar fyrir leyfislykla í boði eins og er á þessum hluta. Valmöguleikarnir sem tengjast hverri viðbót eða viðbót munu birtast hér þegar þessi viðbót er virkjuð.','Visit our website for more information about our plugins and add-ons.' => 'Farðu á vefsíðu okkar til að fá frekari upplýsingar um viðbætur okkar og viðbætur.','Order pay page' => 'Pöntunargreiðslusíða','Order pay' => 'Panta borga','Enable order pay page optimizations' => 'Virkja pöntunargreiðslusíðufínstillingu','Changes the layout of order pay page for existing orders pending payment from the customer.' => 'Breytir útliti pöntunargreiðslusíðu fyrir fyrirliggjandi pantanir sem bíða greiðslu frá viðskiptavini.','Add widget areas to the order pay page' => 'Bættu græjusvæðum við pöntunargreiðslusíðuna','These widget areas are used to add trust symbols and trust badges on the order pay page.' => 'Þessi græjusvæði eru notuð til að bæta við trauststáknum og traustmerkjum á pöntunargreiðslusíðunni.','Thank you page & order details layout' => 'Þakka þér fyrir uppsetningu síðu og pöntunarupplýsinga','Order details optimizations' => 'Bestun pöntunarupplýsinga','Enable thank you page and order details optimizations' => 'Virkjaðu þakkarsíðu og fínstillingu pöntunarupplýsinga','Changes the layout of order details on the thank you page and account pages.' => 'Breytir útliti pöntunarupplýsinga á þakkarsíðunni og reikningssíðum.','Enable order details customizations on email notifications' => 'Virkjaðu aðlögun pöntunarupplýsinga í tölvupósttilkynningum','Display new sections and change order of sections on email notifications. Styles of order details on email notifications remain unchanged.' => 'Birta nýja hluta og breyta röð hluta í tölvupósttilkynningum. Stíll pöntunarupplýsinga á tilkynningum í tölvupósti er óbreytt.','Thank you page' => 'Þakkarsíðu','These options affect the thank you page, also known as order received or order confirmation pages.' => 'Þessir valkostir hafa áhrif á þakkarsíðuna, einnig þekkt sem móttekin pöntun eða pöntunarstaðfestingarsíður.','Order details layout' => 'Skipulag pöntunarupplýsinga','Display the order details with wide layout on the thank you page' => 'Birta pöntunarupplýsingarnar með breiðu skipulagi á þakkarsíðunni','Add widget areas to the thank you page' => 'Bættu græjusvæðum við þakkarsíðuna','These widget areas are used to add trust symbols and trust badges on the thank you page.' => 'Þessi græjusvæði eru notuð til að bæta við trauststáknum og traustsmerkjum á þakkarsíðunni.','These options affect the thank you page , view order details on account pages and on email notifications.' => 'Þessir valkostir hafa áhrif á þakkarsíðuna , skoða pöntunarupplýsingar á reikningssíðum og á tilkynningum í tölvupósti.','Order actions' => 'Panta aðgerðir','Choose in which position to display the order actions.' => 'Veldu í hvaða stöðu á að birta röðunaraðgerðirnar.','Inside the order overview' => 'Inni í pöntunaryfirliti','After the order overview' => 'Eftir pöntunaryfirlit','After order sections' => 'Eftir pöntunarhluta','Order status' => 'Staða pöntunar','Display the order status progress bar' => 'Birta framvindustikuna fyrir pöntunarstöðu','Choose in which position to display the section.' => 'Veldu í hvaða stöðu á að birta hlutann.','After the order overview and payment instructions' => 'Eftir pöntunaryfirlit og greiðsluleiðbeiningar','Before the order items section' => 'Áður en hluti pöntunarvara','Inside the order items section' => 'Inni í pöntunarhlutanum','After the order items section' => 'Eftir pöntunarhlutahlutann','On the sidebar' => 'Á hliðarstikunni','Order downloads' => 'Panta niðurhal','Gift message' => 'Gjöf skilaboð','Tools' => 'Verkfæri','Troubleshooting' => 'Úrræðaleit','Debug options' => 'Villuleitarvalkostir','Debug mode' => 'Villuleitarstilling','Using debug mode affects the website performance. Only use this option while troubleshooting.' => 'Að nota villuleitarstillingu hefur áhrif á árangur vefsíðunnar. Notaðu þennan valmöguleika aðeins við úrræðaleit.','Load unminified assets' => 'Hlaða ósamræmdum eignum','Enhanced select fields' => 'Auknir valreitir','Replace <code>select2</code> dropdown components with <code>TomSelect</code>' => 'Skiptu út <code>select2</code> fellilistanum fyrir <code>TomSelect</code>','TomSelect is a simpler dropdown selection component which is less prone to errors than Select2, while offering the same features that are actually used on WooCommerce checkout pages.' => 'TomSelect er einfaldari fellivalshluti sem er minna viðkvæmur fyrir villum en Select2, en býður upp á sömu eiginleika og eru í raun notaðir á WooCommerce afgreiðslusíðum.','Fix automatic zoom-in on form fields' => 'Lagaðu sjálfvirkan aðdrátt á eyðublaðareiti','Set <code>font-size</code> inside form fields to 16px' => 'Stilltu <code>font-size</code> innan eyðublaða á 16px','When the font size inside form fields is smaller than 16px, Safari and other browsers might automatically zoom in on mobile devices to make the text easier to read. When this option is enabled, it will set the font size for inside the form fields to 16px on pages optimized by Fluid Checkout to avoid it zooming in.' => 'Þegar leturstærðin innan eyðublaða er minni en 16px gætu Safari og aðrir vafrar aðdráttarafl sjálfkrafa í farsímum til að auðvelda lestur textans. Þegar þessi valkostur er virkur mun hann stilla leturstærðina fyrir innan eyðublaðsreitanna á 16px á síðum sem eru fínstilltar af Fluid Checkout til að koma í veg fyrir að það þysi inn.','The shipping destination is always set to "Default to customer shipping address" when Fluid Checkout is activated. Customers can still provide different shipping and billing addresses during checkout, an option for setting the default billing address to be the same as the shipping address is available at WooCommerce > Settings > Fluid Checkout.' => 'Sendingaráfangastaðurinn er alltaf stilltur á „Sjálfgefið sendingar heimilisfang viðskiptavina“ þegar Fluid Checkout er virkjað. Viðskiptavinir geta samt gefið upp mismunandi sendingar- og innheimtuheimilisföng meðan á útskráningu stendur, möguleiki á að stilla sjálfgefið innheimtuheimilisfang til að vera það sama og sendingarheimilisfangið er fáanlegt á WooCommerce &gt; Stillingar &gt; Fluid Checkout.','Fluid Checkout' => 'Fluid Checkout','Settings' => 'Stillingar','Support' => 'Stuðningur','<a target="_blank" href="%s">Upgrade to PRO</a> to unlock more options.' => '<a target="_blank" href="%s">Uppfærðu í PRO</a> til að opna fleiri valkosti.','(PRO)' => '(PRO)','(experimental)' => '(tilraunaverkefni)','This is an experimental feature and may not work as expected with all themes and plugins. Use it with caution.' => 'Þetta er tilraunaeiginleiki og virkar kannski ekki eins og búist var við með öllum þemum og viðbótum. Notaðu það með varúð.','Confirm' => 'Staðfesta','Verify Phone Number' => 'Staðfestu símanúmer','Please verify your phone number.' => 'Vinsamlegast staðfestu símanúmerið þitt.','The Bluehost Plugin' => 'Bluehost viðbótin','Custom fields' => 'Sérsniðnir reitir','Enable custom shipping email field' => 'Virkja sérsniðna sendingarpóstreit','When enabled, the shipping email field from the Bluehost plugin will be added to the checkout form.' => 'Þegar kveikt er á því verður sendingarpóstreiturinn frá Bluehost viðbótinni bætt við greiðsluformið.','Captcha Pro by BestWebSoft' => 'Captcha Pro frá BestWebSoft','Captcha position' => 'Captcha staða','Define the position to display the captcha section. Some positions might not work depending on the captcha type chosen.' => 'Skilgreindu staðsetningu til að sýna captcha hlutann. Sumar stöður gætu ekki virka eftir því hvaða captcha tegund er valin.','Selecting a pickup point is required before proceeding.' => 'Nauðsynlegt er að velja afhendingarstað áður en haldið er áfram.','Pickup point' => 'Afhendingarstaður','Pickup point not selected yet.' => 'Afhendingarstaður ekki valinn ennþá.','Pickup point:' => 'Afhendingarstaður:','Mailchimp for WooCommerce' => 'Mailchimp fyrir WooCommerce','Subscribe checkbox' => 'Gátreitur fyrir áskrift','Override the position for the subscribe checkbox on the checkout page and always display it on the contact step' => 'Hneka stöðu fyrir áskriftargátreitinn á útskráningarsíðunni og birta hann alltaf á tengiliðaskrefinu','Before Shipping Address' => 'Fyrir sendingar heimilisfang','(originally "%s")' => '(upphaflega &quot;%s&quot;)','After Shipping Address' => 'Eftir sendingar heimilisfang','Selecting a collection point is required when shipping with FermoPoint.' => 'Nauðsynlegt er að velja söfnunarstað þegar sent er með FermoPoint.','If checked informs typos in email to users. (This feature has been disabled because Fluid Checkout offers the feature. Changes to this option will not take effect)' => 'Ef hakað er við það tilkynnir notendum innsláttarvillur í tölvupósti. (Þessi eiginleiki hefur verið gerður óvirkur vegna þess að Fluid Checkout býður upp á eiginleikann. Breytingar á þessum valkosti taka ekki gildi)','Enable Mail Check: (disabled feature)' => 'Virkja póstathugun: (óvirkur eiginleiki)','Carrier:' => 'Flutningsaðili:','Sendinblue - WooCommerce Email Marketing' => 'Sendinblue - WooCommerce tölvupóstmarkaðssetning','Sign up checkbox position' => 'Skráning gátreit stöðu','Move the sign up checkbox to the contact step' => 'Færðu skráningargátreitinn í tengiliðaskrefið','When enabled, the checkbox will be moved to the contact step independently of the display location defined in the Sendinblue plugin settings.' => 'Þegar það er virkt verður gátreiturinn færður í tengiliðaþrepið óháð skjástaðnum sem er skilgreind í stillingum Sendinblue viðbótarinnar.','Theme Betheme' => 'Þema Betheme','Checkout progress' => 'Framfarir í úttekt','Output the checkout steps section from Betheme on the checkout, cart and order received pages.' => 'Settu út greiðsluþrephlutann frá Betheme á greiðslu-, körfu- og pöntunarsíðunum.','Theme Fennik' => 'Þema Fennik','Breadcrumbs' => 'Brauðrasp','Output the breadcrumbs section from the Fennik theme when using Fluid Checkout header and footer.' => 'Settu út brauðmolahlutann úr Fennik þemanu þegar þú notar Fluid Checkout haus og fót.','Theme Go' => 'Þema Go','My account layout' => 'Útlit reikningsins míns','Enable wide layout on "My account" page.' => 'Virkjaðu breitt útlit á síðunni „Reikningurinn minn“.','Theme Impreza' => 'Þema Impreza','Header' => 'Fyrirsögn','Spacing for site header at the checkout page (in pixels)' => 'Bil fyrir síðuhaus á afgreiðslusíðunni (í punktum)','Only applicable when using the Impreza theme header at the checkout page.' => 'Gildir aðeins þegar Impreza þemahausinn er notaður á afgreiðslusíðunni.','Theme Kapee' => 'Þema Kapee','Output the checkout steps section from the Kapee theme on the checkout, cart and order received pages.' => 'Settu út greiðsluþrephlutann úr Kapee þemanu á greiðslu-, körfu- og pöntunarsíðunum.','Theme Porto' => 'Þema Porto','Output the checkout steps section from the Porto theme on the checkout, cart and order received pages.' => 'Settu út greiðsluþrephlutann úr Porto þemanu á greiðslu-, körfu- og pöntunarsíðunum.','Theme Pressmart' => 'Þema Pressmart','Output the checkout steps section from the Pressmart theme on the checkout, cart and order received pages.' => 'Settu út greiðsluþrephlutann úr Pressmart þemanu á greiðslu-, körfu- og pöntunarsíðunum.','Theme The Gem' => 'Þema The Gem','Output the checkout steps section from The Gem theme when using Fluid Checkout header and footer.' => 'Settu út greiðsluþrephlutann úr Gem þemanu þegar þú notar Fluid Checkout haus og fót.','Theme Woodmart' => 'Þema Woodmart','Output the checkout steps section from the Woodmart theme when using Fluid Checkout header and footer.' => 'Gefðu út stöðvunarskrefhlutann úr Woodmart þemanu þegar þú notar Fluid Checkout haus og fót.','Checkout options' => 'Afgreiðslumöguleikar','Disable the theme checkout options' => 'Slökktu á greiðsluvalkostum þema','The options display product image, quantity field, remove button and link to product page added by the theme are disabled by default.' => 'Valkostirnir sýna vörumynd, magnreit, fjarlægja hnapp og tengil á vörusíðu sem þemað hefur bætt við eru sjálfgefið óvirkir.','Theme Atomion' => 'Þema Atomion','Order progress' => 'Framvindu pöntunar','Display order progress from the theme' => 'Sýna framvindu pöntunar frá þemað','Display the form field labels visible on the page' => 'Birtu merkimiða eyðublaðsreitsins sem sjást á síðunni','It is recommended to keep this option enabled for a better user experience.' => 'Mælt er með því að hafa þennan valkost virkan til að fá betri notendaupplifun.','Theme ZK Nito' => 'Þema ZK Nito','Add extra fields to the checkout form' => 'Bættu við aukareitum við útskráningareyðublaðið','Adds an extra shipping email and shipping phone field to the shipping address section.' => 'Bætir auka sendingartölvupósti og sendingarsímasviði við sendingarheimilisfangshlutann.','Go to the home page' => 'Farðu á heimasíðuna','Skip to content' => 'Fara í efni','Create an account' => 'Búðu til reikning','Log in to your account' => 'Skráðu þig inn á reikninginn þinn','Already have an account?' => 'Ertu nú þegar með reikning?','Logged in as %1$s. <a href="%2$s">Log out</a>' => 'Skráður inn sem %1$s. <a href="%2$s">Skráðu þig út</a>','Your order has a total amount due of %s. No further payment is needed.' => 'Pöntun þín hefur heildarfjárhæð sem er í gjalddaga upp á %s. Ekki er þörf á frekari greiðslu.','Close and continue with checkout' => 'Lokaðu og haltu áfram með útskráningu','Continue' => 'Halda áfram','Parcel terminal' => 'Bögglastöð','<p>This widget has been replaced by Fluid Checkout. </p><p>When using Fluid Checkout, the checkout page needs to use the original WooCommerce shortcode <code>[woocommerce_checkout]</code> to work properly.</p><p>For more information, visit the Fluid Checkout documentation.</p>' => '<p>Þessari græju hefur verið skipt út fyrir Fluid Checkout.</p><p> Þegar Fluid Checkout er notað þarf afgreiðslusíðan að nota upprunalega WooCommerce stuttkóðann <code>[woocommerce_checkout]</code> til að virka rétt.</p><p> Fyrir frekari upplýsingar, skoðaðu Fluid Checkout skjölin.</p>','Theme The7' => 'Þema The7','Additional header sections' => 'Viðbótarhauskaflar','Output additional header sections if enabled in the theme settings.' => 'Gefðu út fleiri haushluta ef virkt í þemastillingunum.','More information' => 'Frekari upplýsingar','Pick a Locker' => 'Veldu skáp','Page template' => 'Síðusniðmát','Use `Order confirmation` page template for block-based themes' => 'Notið sniðmát fyrir „staðfestingu pöntunar“ fyrir blokkatengd þemu','Only applies when using a block-based theme.' => 'Á aðeins við þegar notað er blokkbundið þema.','Button label for applying coupon codesApply' => 'Sækja um','Checkout step titleContact' => 'Hafðu samband','Checkout step titleShipping' => 'Sending','Checkout step titleBilling' => 'Innheimta','Checkout step titlePayment' => 'Greiðsla','Checkout substep change link labelChange' => 'Breyta','Checkout substep save link labelSave changes' => 'Vista breytingar','Substep review textNone.' => 'Engin.','Substep review field format%1$s' => '%1$s','Substep review field format: with label%2$s: %1$s' => '%2$s: %1$s','Image uploader.No image selected.' => 'Engin mynd valin.','Settings sectionCart' => 'Karfa','Settings sectionOrder pay' => 'Panta borga','Settings sectionThank you' => 'Þakka þér fyrir','Settings section titleIntegrations' => 'Samþættingar','Settings section titleLicense keys' => 'Leyfislyklar','Captcha positionBefore place order section' => 'Fyrir pöntunarhluta','Captcha positionBefore place order button' => 'Fyrir pöntunarhnappinn','No shipping method chosen label for the order summary--' => '--','Close button aria-labelClose' => 'Loka','Log in link label at checkout contact stepLog in' => 'Skráðu þig inn','Log in separator label at for when guest checkout is disabledOr continue below' => 'Eða haltu áfram hér að neðan','Log in separator label at for when guest checkout is disabledOr continue as a guest' => 'Eða haltu áfram sem gestur',] ];