return [ 'project-id-version' => 'fluid-checkout','mime-version' => '1.0','content-type' => 'text/plain; charset=utf-8','content-transfer-encoding' => '8bit','language-team' => 'Fluid Checkout','last-translator' => 'Fluid Checkout','pot-creation-date' => '2025-03-05 23:46+0000','report-msgid-bugs-to' => 'https://fluidcheckout.com/support','x-poedit-basepath' => '..','x-poedit-keywordslist' => '__;_e;_ex:1,2c;_n:1,2;_n_noop:1,2;_nx:1,2,4c;_nx_noop:1,2,3c;_x:1,2c;esc_attr__;esc_attr_e;esc_attr_x:1,2c;esc_html__;esc_html_e;esc_html_x:1,2c','x-poedit-searchpath-0' => '.','x-poedit-searchpathexcluded-0' => '*.js','x-poedit-sourcecharset' => 'UTF-8','plural-forms' => 'nplurals=2; plural=(n != 1);','messages' => ['WooCommerce' => 'WooCommerce','Go to Plugin Search' => 'Pindhah menyang Panelusuran Plugin','Activate %s' => 'Aktifake %s','<strong>%1$s</strong> requires <strong>%2$s</strong> to be installed and activated. <a href="%4$s">%3$s</a>' => '<strong>%1$s</strong> mbutuhake <strong>%2$s</strong> diinstal lan diaktifake. <a href="%4$s">%3$s</a>','Enter your code here' => 'Ketik kode sampeyan ing kene','Add %s' => 'Tambah %s','Remove' => 'Mbusak','Order number and receipt will be sent to this email address.' => 'Nomer pesenan lan kuitansi bakal dikirim menyang alamat email iki.','Apartment, unit, building, floor, etc.' => 'Apartemen, unit, bangunan, lantai, lsp.','Shipping phone' => 'Telpon pengiriman','Only used for shipping-related questions.' => 'Mung digunakake kanggo pitakonan sing gegandhengan karo pengiriman.','Multi-step' => 'Multi-langkah','Single step' => 'Langkah tunggal','Click to go to the order summary' => 'Klik kanggo pindhah menyang ringkesan pesenan','Cart total:' => 'Total keranjang:','Proceed to %s' => 'Terusake menyang %s','Proceed to contact' => 'Terusake kontak','Proceed to shipping' => 'Nerusake kanggo pangiriman','Proceed to billing' => 'Nerusake menyang tagihan','Proceed to payment' => 'Nerusake kanggo pembayaran','My contact' => 'Kontakku','Shipping to' => 'Pengiriman menyang','Shipping method' => 'Cara pengiriman','Additional notes' => 'Cathetan tambahan','Billing to' => 'Tagihan kanggo','Payment method' => 'Cara pembayaran','Step %1$s of %2$s' => 'Langkah %1$s saka %2$s','Change: %s' => 'Ganti: %s','An account will be created with the information provided.' => 'Akun bakal digawe kanthi informasi sing diwenehake.','Not selected yet.' => 'Durung dipilih.','Same as shipping address' => 'Padha karo alamat pengiriman','Same as billing address' => 'Padha alamat tagihan','Order summary' => 'Ringkesan pesenan','Edit cart' => 'Sunting grobag','Did you mean %s?' => 'Maksudmu %s?','Change email address to: {suggestion-value}' => 'Ganti alamat email menyang: {suggestion-value}','This is a required field.' => 'Iki minangka lapangan sing dibutuhake.','This is not a valid email address.' => 'Iki dudu alamat email sing bener.','This field does not match the related field value.' => 'Kolom iki ora cocog karo nilai kolom sing gegandhengan.','The CPF number "{cpf_number}" is invalid.' => 'Nomer CPF &quot;{cpf_number}&quot; ora valid.','The CNPJ number "{cnpj_number}" is invalid.' => 'Nomer CNPJ &quot;{cnpj_number}&quot; ora valid.','(Required)' => '(Dibutuhake)','Checkout Header - Desktop' => 'Header Checkout - Desktop','Display widgets on the checkout header for large screens. Only displayed if using the plugin\'s checkout header.' => 'Tampilake widget ing header checkout kanggo layar gedhe. Mung ditampilake yen nggunakake header checkout plugin.','Checkout Header - Mobile' => 'Header Checkout - Mobile','Display widgets below the checkout header for mobile devices. Only displayed if using the plugin\'s checkout header.' => 'Tampilake widget ing ngisor header checkout kanggo piranti seluler. Mung ditampilake yen nggunakake header checkout plugin.','Checkout Sidebar' => 'Checkout Sidebar','Display widgets on the checkout sidebar after the order summary.' => 'Tampilake widget ing sidebar checkout sawise ringkesan pesenan.','Checkout Order Summary' => 'Ringkesan Pesenan Checkout','Display widgets inside the order summary at the checkout page.' => 'Tampilake widget ing ringkesan pesenan ing kaca checkout.','Checkout Below Place Order' => 'Checkout Ngisor Panggonan Order','Display widgets below the place order button at the checkout page.' => 'Tampilake widget ing ngisor tombol pesenan ing kaca checkout.','Checkout Footer' => 'Checkout Footer','Display widgets on the checkout footer. Only displayed if using the plugin\'s checkout footer.' => 'Tampilake widget ing footer checkout. Mung ditampilake yen nggunakake footer checkout plugin.','Classic' => 'Klasik','Boxed' => 'kothak','Split' => 'Pisah','Minimalist' => 'Minimalis','Fluid Checkout and Delivery Date plugins by CodeRockz' => 'Plugin Checkout Cairan lan Tanggal Pangiriman dening CodeRockz','<p>We detected that you are using the plugins Delivery & Pickup Date Time for WooCommerce (free and PRO) by CodeRockz. Integration with these plugins have been moved to Fluid Checkout PRO.</p>' => '<p>Kita ndeteksi manawa sampeyan nggunakake plugin Delivery &amp; Pickup Date Time kanggo WooCommerce (gratis lan PRO) dening CodeRockz. Integrasi karo plugin iki wis dipindhah menyang Fluid Checkout PRO.</p>','Upgrade to PRO' => 'Nganyarke menyang PRO','Fluid Checkout database updated successfully' => 'Database Fluid Checkout dianyari kanthi sukses','The database changes where applied successfully.' => 'Basis data owah-owahan ing ngendi ditrapake kanthi sukses.','Ok' => 'oke','Dismiss' => 'Mbucal','Fluid Checkout database update' => 'Nganyari database Fluid Checkout','Some changes to the database are required. As with any update, we recommend you to <strong>take a full backup of your website before proceeding</strong>.' => 'Sawetara owah-owahan ing database dibutuhake. Kaya nganyari apa wae, disaranake sampeyan <strong>njupuk serep lengkap situs web sadurunge nerusake</strong> .','Update database' => 'Nganyari database','Fluid Checkout is not compatible with the Divi Builder checkout layout' => 'Fluid Checkout ora kompatibel karo tata letak checkout Divi Builder','<p>Fluid Checkout needs the classic WooCommerce shortcode-based checkout form to work. When using the Divi Builder to customize the look and feel of the checkout page, the classic shortcodes are replaced with custom widgets from the Divi Builder for each part of the checkout page. These custom widgets are not compatible with Fluid Checkout.</p><p>Please edit your checkout page replacing the Divi Builder widgets with the a text widget containing the shortcode <code>[woocommerce_checkout]</code>.</p>' => '<p>Fluid Checkout mbutuhake formulir checkout adhedhasar kode cekak WooCommerce klasik supaya bisa digunakake. Nalika nggunakake Divi Builder kanggo ngatur tampilan lan rasa kaca checkout, shortcode klasik diganti karo widget khusus saka Divi Builder kanggo saben bagean saka kaca checkout. Widget khusus iki ora kompatibel karo Fluid Checkout.</p><p> Mangga sunting kaca checkout sampeyan ngganti widget Divi Builder karo widget teks sing ngemot shortcode <code>[woocommerce_checkout]</code> .</p>','Edit the checkout page' => 'Sunting kaca checkout','Read the documentation.' => 'Maca dokumentasi.','Germanized PRO multistep checkout feature needs to be disabled when using Fluid Checkout' => 'Fitur checkout multistep PRO Jerman kudu dipateni nalika nggunakake Fluid Checkout','When using Fluid Checkout, the multistep feature from the Germanized PRO plugin becomes unnecessary and can cause <strong>critical errors while processing new orders</strong>. Please disable the Germanized PRO multistep checkout feature in the plugin settings.' => 'Nalika nggunakake Fluid Checkout, fitur multistep saka plugin Germanized PRO dadi ora perlu lan bisa nyebabake <strong>kesalahan kritis nalika ngolah pesenan anyar</strong> . Mangga mateni fitur checkout multistep PRO Germanized ing setelan plugin.','Go to Germanized settings' => 'Pindhah menyang setelan Jerman','Thanks for choosing Fluid Checkout!' => 'Thanks kanggo milih Fluid Checkout!','You have been using the plugin for a while now. How do you like it so far? <br>Please give us a quick rating, we appreciate every bit of feedback and it encourages us to keep improving the plugin :)' => 'Sampeyan wis nggunakake plugin kanggo sawetara wektu saiki. Kepiye carane sampeyan seneng nganti saiki?<br> Mangga menehi rating cepet, kita appreciate saben dicokot saka saran lan nyengkuyung kita kanggo terus nambah plugin :)','Rate the plugin' => 'Rate plugin','I need help!' => 'Aku butuh bantuan!','The plugin WooCommerce Checkout Manager by Quadlayers is not compatibile with Fluid Checkout' => 'Plugin WooCommerce Checkout Manager dening Quadlayers ora kompatibel karo Fluid Checkout','Some features of WooCommerce Checkout Manager by Quadlayers might not work as expected when using it with Fluid Checkout. Read <a href="%s">our documentation</a> for more information and alternative solutions.' => 'Sawetara fitur WooCommerce Checkout Manager dening Quadlayers bisa uga ora bisa digunakake kaya sing dikarepake nalika nggunakake Fluid Checkout. Waca <a href="%s">dokumentasi kita</a> kanggo informasi luwih lengkap lan solusi alternatif.','Read documentation' => 'Maca dokumentasi','Don\'t show this again' => 'Aja nuduhake iki maneh','Special offers' => 'Penawaran khusus','Extend all the goodness of Fluid Checkout to your cart page and thank you pages.' => 'Ngluwihi kabeh kabecikan Fluid Checkout menyang kaca cart lan kaca matur nuwun.','Fluid Checkout PRO' => 'Cairan Checkout PRO','Design templates, optimized cart and order received pages, account matching, and much more.' => 'Cithakan desain, cart sing dioptimalake lan kaca sing ditampa pesenan, pencocokan akun, lan liya-liyane.','The best tools to further <strong>improve your WooCommerce checkout conversion rate</strong> and make the purchase experience consistent on the entire journey.' => 'Alat paling apik kanggo <strong>nambah tingkat konversi checkout WooCommerce</strong> lan nggawe pengalaman tuku konsisten ing kabeh perjalanan.','More design templates' => 'Cithakan desain liyane','Optimized cart page' => 'Kaca cart sing dioptimalake','Optimized order received / thank you page' => 'Pesenan sing dioptimalake ditampa / kaca matur nuwun','Edit cart contents at checkout page' => 'Owahi isi cart ing kaca checkout','Account matching / user matching' => 'Pencocokan akun / cocog pangguna','Express Checkout buttons from supported payment methods' => 'Tombol Express Checkout saka cara pembayaran sing didhukung','Trust symbols on the cart and thank you pages' => 'Simbol kepercayaan ing cart lan kaca matur nuwun','More positions for the coupon code on the checkout page' => 'Posisi liyane kanggo kode coupon ing kaca checkout','Integrated coupon code field on the cart page' => 'Kolom kode kupon terpadu ing kaca cart','International phone numbers' => 'Nomer telpon internasional','Gift messages and packing slips' => 'Pesen hadiah lan slip packing','Local pickup and in-store collection' => 'Pickup lokal lan koleksi ing toko','Upgrade to PRO &mdash; 99 EUR' => 'Nganyarke menyang PRO - 99 EUR','Fluid Checkout PRO – Bundle' => 'Cairan Checkout PRO - Bundel','All PRO features + All add-ons for a special price.' => 'Kabeh fitur PRO + Kabeh tambahan kanggo rega khusus.','The bundle includes all add-ons we currently offer and Fluid Checkout add-ons we launch in the future.' => 'Bundel kasebut kalebu kabeh tambahan sing saiki ditawakake lan tambahan Fluid Checkout sing bakal diluncurake ing mangsa ngarep.','226 EUR value (Save 42%)' => 'Nilai 226 EUR (Ngirit 42%)','Google Address Autocomplete' => 'Ngrampungake Otomatis Alamat Google','Address Book' => 'Buku Alamat','EU-VAT Assistant' => 'Asisten EU-VAT','All future add-ons *' => 'Kabeh tambahan ing mangsa ngarep *','* For as long as you have an active subscription.' => '* Anggere sampeyan duwe langganan aktif.','Get the Bundle – Only 129 EUR' => 'Entuk Bundle - Mung 129 EUR','All prices shown in EUR. If there are any divergencies with the prices on our website, the offers shown on the website superseed these and will be applied.' => 'Kabeh prices ditampilake ing EUR. Yen ana prabédan karo rega ing situs web kita, tawaran sing ditampilake ing situs web bakal diganti lan bakal ditrapake.','Add-ons' => 'Tambahan','Enhance your checkout experience with these add-ons.' => 'Ningkatake pengalaman checkout kanthi tambahan iki.','I already have it – Hide this offer' => 'Aku wis duwe - Singidaken tawaran iki','Installed &mdash; Activate' => 'Diinstal - Aktifake','Activated' => 'Diaktifake','Up to 40% less checkout fields to fill in.' => 'Nganti 40% kothak checkout kurang kanggo diisi.','Avoid delivery delays and unsatisfied customers. Collect the <strong>correct address information</strong> from the first time they buy with you.' => 'Ngindhari telat pangiriman lan pelanggan sing ora marem. Nglumpukake <strong>informasi alamat sing bener</strong> saka pisanan padha tuku karo sampeyan.','Get this add-on &mdash; 29 EUR' => 'Entuk tambahan iki - 29 EUR','Address book' => 'Buku alamat','Multiple saved addresses for shipping and billing.' => 'Akeh alamat sing disimpen kanggo pengiriman lan tagihan.','Let customers <strong>save multiple shipping and billing addresses</strong> on their account and choose which ones to use at checkout and cart pages.' => 'Ayo pelanggan <strong>nyimpen pirang-pirang alamat pengiriman lan tagihan</strong> ing akun lan pilih sing digunakake ing kaca checkout lan cart.','Get this add-on &mdash; 59 EUR' => 'Entuk tambahan iki - 59 EUR','Simplified EU-VAT validation for your store.' => 'Validasi EU-VAT sing disederhanakake kanggo toko sampeyan.','Collect and <strong>validate EU VAT numbers at the checkout page</strong>, removes tax charges on reverse charge basis and confirms customer location when needed.' => 'Nglumpukake lan <strong>validasi nomer VAT EU ing kaca checkout</strong> , mbusak biaya pajak kanthi mbalikke lan konfirmasi lokasi pelanggan yen dibutuhake.','Get this add-on &mdash; 39 EUR' => 'Entuk tambahan iki - 39 EUR','All add-ons are sold separately and require the <strong>Fluid Checkout Lite</strong> plugin to be installed and activated, except when noted.' => 'Kabeh tambahan didol kanthi kapisah lan mbutuhake plugin <strong>Fluid Checkout Lite</strong> kanggo diinstal lan diaktifake, kajaba yen dicathet.','Select an image' => 'Pilih gambar','No image selected.' => 'Ora ana gambar sing dipilih.','Remove image' => 'Mbusak gambar','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to get your license key, or <a href="%s" target="_blank">purchase a new license key</a>.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Mlebu menyang akun</a> kanggo entuk kunci lisensi, utawa <a href="%s" target="_blank">tuku kunci lisensi anyar</a> .','Valid until %s.' => 'Valid nganti %s.','Expired on %s.' => 'Kadaluwarsa ing %s.','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to renew your license key and continue to receive updates and support.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Mlebu menyang akun</a> kanggo nganyari kunci lisensi lan terus nampa nganyari lan dhukungan.','License key cancelled.' => 'Kunci lisensi dibatalake.','<a href="%s" target="_blank">Purchase a new license key</a> and continue to receive updates and support.' => '<a href="%s" target="_blank">Tuku kunci lisensi anyar</a> lan terus nampa nganyari lan dhukungan.','Error: ' => 'kesalahan: ','Setup & Documentation' => 'Setup &amp; Dokumentasi','Great! Your checkout page is now running on Fluid Checkout.' => 'apik tenan! Kaca checkout sampeyan saiki mlaku ing Fluid Checkout.','Here are a few resources for you to get started:' => 'Ing ngisor iki sawetara sumber kanggo sampeyan miwiti:','Read the installation guide <a href="%s" target="_blank">Getting stated with Fluid Checkout</a>.' => 'Waca pandhuan instalasi <a href="%s" target="_blank">Njupuk nyatakake karo Fluid Checkout</a> .','Setup layout and design on the <a href="%s">checkout options</a>.' => 'Setel tata letak lan desain ing <a href="%s">opsi checkout</a> .','Check if there are any <a href="%s">integration options</a> available for other plugins you have installed.' => 'Priksa manawa ana <a href="%s">pilihan integrasi</a> sing kasedhiya kanggo plugin liyane sing wis diinstal.','Visit <a href="%s" target="_blank">our documentation</a> for more information about Fluid Checkout features.' => 'Dolan maring <a href="%s" target="_blank">dokumentasi</a> kanggo informasi luwih lengkap babagan fitur Fluid Checkout.','If you ever need help, <a href="%s" target="_blank">open a support ticket</a> on our official support channel.' => 'Yen sampeyan butuh bantuan, <a href="%s" target="_blank">bukak tiket dhukungan</a> ing saluran dhukungan resmi.','Cart layout' => 'tata kréta','Cart optimizations' => 'Optimizations cart','Enable cart page optimizations' => 'Aktifake optimasi kaca cart','Display the cart page with a layout similar to the checkout page.' => 'Tampilake kaca cart kanthi tata letak sing padha karo kaca checkout.','Header and footer' => 'Header lan footer','Header and footer template' => 'Cithakan header lan footer','Controls whether to use the distraction free page header and footer or keep the currently active theme\'s header and footer.' => 'Ngontrol manawa nggunakake header lan footer kaca tanpa gangguan utawa njaga header lan footer tema sing saiki aktif.','Distraction free header and footer' => 'Header lan footer bebas gangguan','Theme\'s header and footer' => 'Header lan footer tema','Cart elements' => 'unsur kréta','Make the order summary stay visible on the cart page while scrolling' => 'Nggawe ringkesan pesenan tetep katon ing kaca cart nalika nggulung','Coupon codes' => 'kode kupon','Select position where to display the coupon codes section on the cart page. Only applicable when AJAX cart and the integrated coupon codes in the checkout page are enabled.' => 'Pilih posisi kanggo nampilake bagean kode kupon ing kaca cart. Mung ditrapake nalika AJAX cart lan kode kupon terpadu ing kaca checkout diaktifake.','Hidden' => 'didhelikake','Before the cart items section' => 'Sadurunge bagean item cart','Inside the cart items section' => 'Nang bagean item cart','After the cart items section' => 'Sawise bagean barang cart','Before the shipping section' => 'Sadurunge bagean pangiriman','After the shipping section' => 'Sawise bagean pangiriman','Before the order summary' => 'Sadurunge ringkesan pesenan','Inside the order summary' => 'Ing ringkesan pesenan','Before the order totals' => 'Sadurunge pesenan total','After the order totals' => 'Sawise total pesenan','After the order summary' => 'Sawise ringkesan pesenan','Shipping' => 'Pengiriman','Select position where to display the shipping costs and calculator on the cart page.' => 'Pilih posisi kanggo nampilake biaya pengiriman lan kalkulator ing kaca cart.','Display as a cart section' => 'Tampilake minangka bagean cart','Cross-sells' => 'Cross-sadean','Select position where to display the cross-sells section on the cart page.' => 'Pilih posisi ing ngendi kanggo nampilake bagean cross-sells ing kaca cart.','Before the cart actions' => 'Sadurunge tumindak cart','After the cart actions' => 'Sawise tumindak cart','Layout of cross-sell items on the cart page.' => 'Tata letak barang adol silang ing kaca kréta.','Optimized horizontal cross-sells layout' => 'Optimized tata letak cross-sade horisontal','Theme\'s cross-sells layout' => 'Tata letak lintas-sade tema','Number of cross-sell items to display on the cart page' => 'Jumlah item cross-sell kanggo ditampilake ing kaca cart','Trust symbols &amp; badges' => 'Simbol kepercayaan &amp; lencana','Add widget areas to the cart page' => 'Tambah area widget menyang kaca cart','These widget areas are used to add trust symbols and trust badges on the cart page.' => 'Wilayah widget iki digunakake kanggo nambah simbol kapercayan lan lencana kapercayan ing kaca cart.','Checkout' => 'Checkout','Layout &amp; design' => 'Tata letak &amp; desain','Checkout layout' => 'tata letak checkout','Design template' => 'Cithakan desain','General styles for the checkout steps, order summary and other sections. Might also apply to other pages such as the Cart, Order Received and View Order pages.' => 'Gaya umum kanggo langkah checkout, ringkesan pesenan lan bagean liyane. Bisa uga ditrapake kanggo kaca liyane kayata Cart, Order Received lan View Order page.','Dark mode' => 'Mode peteng','Enable dark mode' => 'Aktifake mode peteng','Header and footer templates' => 'Cithakan header lan footer','We recommend using the distraction free header and footer to avoid distractions at the checkout page. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Read the research about cart abandonment</a>.' => 'Disaranake nggunakake header lan footer bebas gangguan kanggo ngindhari gangguan ing kaca checkout. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Maca riset babagan ninggalake kreta</a> .','Logo image' => 'Gambar logo','Choose an image to be displayed on the checkout page header. Only applies when using the distraction free header and footer.' => 'Pilih gambar sing bakal ditampilake ing header kaca checkout. Mung ditrapake nalika nggunakake header lan footer bebas gangguan.','Header background color' => 'Werna latar mburi header','Choose a background color for the checkout page header. Only applies when using the distraction free header and footer.' => 'Pilih werna latar mburi kanggo header kaca checkout. Mung ditrapake nalika nggunakake header lan footer bebas gangguan.','HTML color value. ie: #f3f3f3' => 'Nilai warna HTML. yaiku: #f3f3f3','Page background color' => 'Werna latar mburi kaca','Choose a background color for the checkout page. Color is applied to the <em>body</em> element.' => 'Pilih werna latar mburi kanggo kaca checkout. Werna ditrapake kanggo unsur <em>awak</em> .','Footer background color' => 'Werna latar mburi footer','Choose a background color for the checkout page footer. Only applies when using the distraction free header and footer.' => 'Pilih werna latar mburi kanggo footer kaca checkout. Mung ditrapake nalika nggunakake header lan footer bebas gangguan.','Checkout elements' => 'Elemen checkout','Progress bar' => 'Bar kemajuan','Display the checkout progress bar when using multi-step checkout layout' => 'Tampilake garis kemajuan checkout nalika nggunakake tata letak checkout multi-langkah','Make the checkout progress bar stay visible while scrolling' => 'Nggawe garis kemajuan checkout tetep katon nalika nggulung','Express checkout' => 'Checkout ekspres','Enable the express checkout section' => 'Aktifake bagean checkout ekspres','Displays the express checkout section at checkout when supported payment gateways have this feature enabled.' => 'Nampilake bagean checkout ekspres nalika checkout nalika gateway pembayaran sing didhukung nduweni fitur iki.','Display express checkout buttons in one line for larger screens' => 'Tampilake tombol checkout ekspres ing siji baris kanggo layar sing luwih gedhe','Ignore additional checkout required fields when paying with a compatible express checkout payment gateway' => 'Nglirwakake lapangan checkout tambahan sing dibutuhake nalika mbayar nganggo gateway pembayaran checkout ekspres sing kompatibel','Make the order summary stay visible while scrolling' => 'Nggawe ringkesan pesenan tetep katon nalika nggulung','Choose a background color for the order summary section.' => 'Pilih werna latar mburi kanggo bagean ringkesan pesenan.','Highlight the order totals row in the order summary table' => 'Sorot baris total pesenan ing tabel ringkesan pesenan','Most useful when the order summary section does not have a highlighted background color. Might also apply to the Cart, Order Received and View Order pages when using Fluid Checkout PRO.' => 'Paling migunani nalika bagean ringkesan pesenan ora duwe warna latar mburi sing disorot. Bisa uga ditrapake ing Kréta, Pesenan Ditampa lan Ndeleng kaca Pesenan nalika nggunakake Fluid Checkout PRO.','Action link on the order summary at checkout.' => 'Link tumindak ing ringkesan pesenan ing checkout.','Edit cart link' => 'Sunting pranala cart','Cart items count' => 'Item cart count','Link to shop page' => 'Link menyang kaca toko','Cart items' => 'Barang kréta','Enable options to edit cart items on the checkout page' => 'Aktifake opsi kanggo ngowahi item cart ing kaca checkout','Allow customers to change product quantities or removing items directly at the checkout page.' => 'Ngidini pelanggan ngganti jumlah produk utawa mbusak item langsung ing kaca checkout.','Hide cart items errors at the checkout page' => 'Singidaken kesalahan item cart ing kaca checkout','Do not display the cart items errors message at the top of the checkout page. When submitting the checkout form to complete the purchase, these error messages will always be displayed.' => 'Aja nampilake pesen kesalahan item cart ing sisih ndhuwur kaca checkout. Nalika ngirim formulir checkout kanggo ngrampungake tuku, pesen kesalahan iki bakal tansah ditampilake.','Place order' => 'Panggonan pesenan','Define the position to display "Place order" and terms checkbox section.' => 'Netepake posisi kanggo nampilake &quot;Pesenan&quot; lan bagean kothak centhang.','Some options might not be compatible with some plugins and themes.' => 'Sawetara opsi bisa uga ora kompatibel karo sawetara plugin lan tema.','Below the payment section' => 'Ing ngisor bagean pembayaran','Below the order summary' => 'Ing ngisor iki ringkesan pesenan','Both below the payment section and the order summary' => 'Loro-lorone ing ngisor bagean pembayaran lan ringkesan pesenan','Add widget areas to the checkout page' => 'Tambah area widget menyang kaca checkout','These widget areas are used to add trust symbols and trust badges on the checkout page.' => 'Wilayah widget iki digunakake kanggo nambah simbol kepercayaan lan lencana kepercayaan ing kaca checkout.','Display checkout sidebar widgets only when viewing the last checkout step on mobile devices when using multi-step checkout layout' => 'Tampilake widget sidebar checkout mung nalika ndeleng langkah checkout pungkasan ing piranti seluler nalika nggunakake tata letak checkout multi-langkah','Enable integrated coupon code section' => 'Aktifake bagean kode kupon terpadu','Only applicable if use of coupon codes are enabled in the WooCommerce settings.' => 'Mung ditrapake yen panggunaan kode kupon diaktifake ing setelan WooCommerce.','Display the coupon codes section title' => 'Tampilake judhul bagean kode kupon','Only applicable when coupon code is displayed as a separate section on the checkout or cart pages.' => 'Mung ditrapake nalika kode kupon ditampilake minangka bagean kapisah ing kaca checkout utawa cart.','Select position where to display the coupon codes section on the checkout page.' => 'Pilih posisi kanggo nampilake bagean kode kupon ing kaca checkout.','Before the checkout steps' => 'Sadurunge langkah checkout','As a substep before payment methods' => 'Minangka substep sadurunge cara pembayaran','As a substep after payment methods' => 'Minangka substep sawise cara pembayaran','Select style of the "apply coupon" button. Only applicable when the coupon code section is displayed "Before the checkout steps" on the checkout page, or "Before the cart items section" on the cart page.' => 'Pilih gaya tombol &quot;aplikasi kupon&quot;. Mung ditrapake nalika bagean kode coupon ditampilake &quot;Sadurunge langkah checkout&quot; ing kaca checkout, utawa &quot;Sadurunge bagean item cart&quot; ing kaca cart.','Default button style' => 'Gaya tombol standar','"Add" link button' => '&quot;Tambah&quot; tombol link','Checkout fields' => 'kothak Checkout','Optional fields' => 'Kothak opsional','Hide optional fields behind a link button' => 'Singidaken kolom opsional ing mburi tombol link','It is recommended to keep this option checked to reduce the number of open input fields, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">read the research</a>.' => 'Disaranake supaya opsi iki dicenthang kanggo ngurangi jumlah kolom input mbukak, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">maca riset</a> .','Do not hide "Address line 2" fields behind a link button' => 'Aja ndhelikake kolom &quot;Alamat baris 2&quot; ing mburi tombol link','Recommended only when most customers actually need the "Address line 2" field, or when getting the right shipping address is crucial (ie. if delivering food and other perishable products).' => 'Dianjurake mung nalika paling pelanggan bener mbutuhake lapangan &quot;Alamat baris 2&quot;, utawa nalika njupuk alamat kapal tengen iku wigati (ie. yen ngirim pangan lan produk rusak liyane).','Display the "Add" link buttons in lowercase' => 'Tampilake tombol link &quot;Tambah&quot; ing huruf cilik','Make the labels of optional field "Add" link button as <code>lowercase</code> (ie. "Add phone number" instead of "Add Phone Number"). This option also affects the link buttons for coupon code fields.' => 'Nggawe label ing kolom opsional tombol link &quot;Tambah&quot; minangka <code>lowercase</code> (ie. &quot;Tambah nomer telpon&quot; tinimbang &quot;Tambah Nomer Telpon&quot;). Opsi iki uga mengaruhi tombol link kanggo kolom kode kupon.','Billing address' => 'Alamat tagihan','Select position where to display the billing address section on the checkout page.' => 'Pilih posisi kanggo nampilake bagean alamat tagihan ing kaca checkout.','As a step before shipping' => 'Minangka langkah sadurunge ngirim','As a sub-step before the shipping address section' => 'Minangka sub-langkah sadurunge bagean alamat pengiriman','As a sub-step after the shipping address section' => 'Minangka sub-langkah sawise bagean alamat pengiriman','As a step after shipping' => 'Minangka langkah sawise pangiriman','As a sub-step before the payment section' => 'Minangka sub-langkah sadurunge bagean pembayaran','Force billing and shipping addresses to the same (single section)' => 'Meksa tagihan lan alamat pengiriman menyang padha (bagean siji)','Checkbox for "Same as shipping/billing address" checked by default' => 'Centhang kothak &quot;Sama karo alamat pengiriman/tagihan&quot; kanthi gawan','The checkbox "Same as shiping address" will start as checked by default when the shipping address section is displayed first. <br>The checkbox "Same as billing address" will start as checked by default when the billing address section is displayed first. <br> It is recommended to leave this option checked as to significantly reduce the number of open input fields at checkout, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">read the research</a>.' => 'Kothak centhang &quot;Same as shiping address&quot; bakal diwiwiti kaya sing dicenthang kanthi gawan nalika bagean alamat pengiriman ditampilake dhisik.<br> Kothak centhang &quot;Sama karo alamat tagihan&quot; bakal diwiwiti kaya sing dicenthang kanthi gawan nalika bagean alamat tagihan ditampilake dhisik.<br> Disaranake kanggo ninggalake opsi iki dicenthang minangka Ngartekno nyuda jumlah kolom input mbukak ing checkout, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">maca riset</a> .','Highlight the billing address section in the checkout form' => 'Nyorot bagean alamat tagihan ing formulir checkout','Shipping address' => 'alamat pengiriman','Highlight the shipping address section in the checkout form' => 'Nyorot bagean alamat pengiriman ing formulir checkout','Shipping methods' => 'cara pengiriman','Choose in which position to display the shipping methods section.' => 'Pilih ing posisi kanggo nampilake bagean cara pengiriman.','Before shipping address' => 'Sadurunge alamat pengiriman','After shipping address' => 'Sawise alamat pengiriman','Prevent automatic selection of the first shipping method' => 'Nyegah pilihan otomatis saka cara pengiriman pisanan','When enabled, the first shipping method available will not be automatically selected when no other shipping method was previously selected for each shipping package. <br>This option will be automatically enabled if the option for clearing the selected shipping method is enabled for the Local Pickup feature.' => 'Yen diaktifake, cara pengiriman pisanan sing kasedhiya ora bakal dipilih kanthi otomatis nalika ora ana cara pengiriman liyane sing sadurunge dipilih kanggo saben paket pengiriman.<br> Opsi iki bakal diaktifake kanthi otomatis yen opsi kanggo mbusak cara pengiriman sing dipilih diaktifake kanggo fitur Pickup Lokal.','Local pickup' => 'Pickup lokal','Removes shipping address section when a local pickup shipping method is selected.' => 'Mbusak bagean alamat pengiriman nalika cara pengiriman pickup lokal dipilih.','Replaces the shipping address with the pickup point location when a local pickup shipping method is selected.' => 'Ngganti alamat pengiriman karo lokasi titik pickup nalika cara pengiriman pickup lokal dipilih.','Show option to clear shipping methods in the pickup location substep' => 'Tampilake pilihan kanggo mbusak cara pengiriman ing substep lokasi pickup','Show a link button on the pickup location substep to clear the chosen shipping methods. This can be used to allow showing the shipping address section again if a local pickup method was previously selected.' => 'Tampilake tombol link ing substep lokasi pickup kanggo mbusak cara pengiriman sing dipilih. Iki bisa digunakake kanggo ngidini nuduhake bagean alamat pengiriman maneh yen cara pickup lokal sadurunge dipilih.','Choose which address to save as the shipping address for local pickup orders.' => 'Pilih alamat sing bakal disimpen minangka alamat pengiriman kanggo pesenan pickup lokal.','Save the selected pickup location' => 'Simpen lokasi pickup sing dipilih','Save same as the billing address' => 'Simpen padha karo alamat tagihan','Do not save any shipping address' => 'Aja nyimpen alamat pengiriman','Company name field for shipping address' => 'Kolom jeneng perusahaan kanggo alamat pengiriman','Change visibility for the company name field for the shipping address section on the checkout form.' => 'Ganti visibilitas kanggo kolom jeneng perusahaan kanggo bagean alamat pengiriman ing formulir checkout.','If field is set as "optional", which is the default visibility state, no changes will be applied to let other plugins apply any changes they need.' => 'Yen lapangan disetel minangka &quot;opsional&quot;, yaiku status visibilitas standar, ora ana owah-owahan sing bakal ditrapake supaya plugin liyane bisa ngetrapake owah-owahan sing dibutuhake.','Hidden (remove field)' => 'Didhelikake (mbusak kolom)','Optional' => 'Opsional','Required' => 'dibutuhake','Add shipping phone field to the checkout form.' => 'Tambah kolom telpon pengiriman menyang formulir checkout.','Choose in which step to display the shipping phone field.' => 'Pilih ing endi langkah kanggo nampilake kolom telpon pengiriman.','Contact step' => 'Langkah kontak','Billing phone' => 'Telpon tagihan','Add billing phone field to the checkout form.' => 'Tambah kolom telpon tagihan menyang formulir checkout.','Choose in which step to display the billing phone field.' => 'Pilih ing langkah kanggo nampilake kolom telpon tagihan.','Enable international phone number format and validation for phone fields' => 'Aktifake format nomer telpon internasional lan validasi kanggo kolom telpon','Format and validate phone numbers according to each country.' => 'Format lan validasi nomer telpon miturut saben negara.','Enable phone number validation based on country rules' => 'Aktifake validasi nomer telpon adhedhasar aturan negara','When disabled, the phone field validation will not check if country or area codes are valid for the country.' => 'Yen dipateni, validasi kolom telpon ora bakal mriksa yen kode negara utawa wilayah valid kanggo negara kasebut.','Only show allowed countries for shipping or billing' => 'Mung nuduhake negara sing diijini kanggo pengiriman utawa tagihan','When enabled, only the countries allowed for shipping will be available in the shipping phone field, and only countries allowed for billing will be available for the billing phone field.' => 'Yen diaktifake, mung negara sing diidini kanggo ngirim sing kasedhiya ing kolom telpon pengiriman, lan mung negara sing diidini kanggo tagihan sing kasedhiya kanggo kolom telpon tagihan.','Show country code beside the flag' => 'Tampilake kode negara ing jejere gendéra','Show an example of a valid phone number inside phone fields' => 'Tampilake conto nomer telpon sing bener ing kolom telpon','Do not change placeholders' => 'Aja ngganti placeholder','Show if not defined' => 'Tampilake yen ora ditetepake','Always show' => 'Tansah nuduhake','Order notes' => 'cathetan pesenan','Define the visibility of the additional order notes field.' => 'Netepake visibilitas lapangan cathetan pesenan tambahan.','Gift options' => 'Pilihan hadiah','Display gift message and other gift options at the checkout page' => 'Tampilake pesen hadiah lan pilihan hadiah liyane ing kaca checkout','Allow customers to add a gift message and other gift related options to the order.' => 'Ngidini pelanggan nambah pesen hadiah lan opsi liyane sing gegandhengan karo hadiah menyang pesenan.','Display the gift message fields always expanded' => 'Tampilake kothak pesen hadiah tansah ditambahi','Display the gift message as part of the order details table instead of a separate section' => 'Tampilake pesen hadiah minangka bagean saka tabel rincian pesenan tinimbang bagean sing kapisah','This option affects the order confirmation page (thank you page) and order details on account pages, emails and packing slips.' => 'Opsi iki mengaruhi kaca konfirmasi pesenan (kaca matur nuwun) lan rincian pesenan ing kaca akun, email lan slip pengepakan.','Account matching' => 'Akun cocog','Enable the account matching feature' => 'Aktifake fitur cocog akun','Associate the guest customer\'s orders with their existing account when an account already exists with the customer\'s contact details.' => 'Sambungake pesenan pelanggan tamu karo akun sing wis ana nalika akun wis ana karo rincian kontak pelanggan.','Display message when an account exists with the email address provided' => 'Tampilake pesen nalika ana akun kanthi alamat email sing diwenehake','Replaces the account creation fields with a notification and option to log in. In some contexts, it might be recommended to leave this option disabled to protect the privacy of customers.' => 'Ngganti kolom nggawe akun kanthi kabar lan pilihan kanggo mlebu. Ing sawetara konteks, disaranake supaya opsi iki dipateni kanggo nglindhungi privasi pelanggan.','Dashboard' => 'Dashboard','Integrations' => 'Integrasi','No integrations available at the moment on this section. The options related to each plugin will only appear here if that plugin is activated.' => 'Ora ana integrasi sing kasedhiya ing bagean iki. Opsi sing ana gandhengane karo saben plugin mung bakal katon ing kene yen plugin kasebut diaktifake.','License keys' => 'Tombol lisensi','No license keys options available at the moment on this section. The options related to each plugin or add-on will appear here when that plugin is activated.' => 'Ora ana pilihan kunci lisensi sing kasedhiya ing bagean iki. Opsi sing ana gandhengane karo saben plugin utawa tambahan bakal katon ing kene nalika plugin kasebut diaktifake.','Visit our website for more information about our plugins and add-ons.' => 'Dolan maring situs web kita kanggo informasi luwih lengkap babagan plugin lan tambahan.','Order pay page' => 'Kaca mbayar pesenan','Order pay' => 'Pembayaran pesenan','Enable order pay page optimizations' => 'Aktifake optimasi kaca mbayar pesenan','Changes the layout of order pay page for existing orders pending payment from the customer.' => 'Ngganti tata letak kaca pembayaran pesenan kanggo pesenan sing wis ana sing nunggu pembayaran saka pelanggan.','Add widget areas to the order pay page' => 'Tambah wilayah widget menyang kaca mbayar pesenan','These widget areas are used to add trust symbols and trust badges on the order pay page.' => 'Wilayah widget iki digunakake kanggo nambah simbol kapercayan lan lencana kapercayan ing kaca pembayaran pesenan.','Thank you page & order details layout' => 'Matur nuwun tata letak halaman &amp; rincian pesanan','Order details optimizations' => 'Optimasi rincian pesenan','Enable thank you page and order details optimizations' => 'Aktifake kaca matur nuwun lan optimasi rincian pesenan','Changes the layout of order details on the thank you page and account pages.' => 'Ngganti tata letak rincian pesenan ing kaca matur nuwun lan kaca akun.','Enable order details customizations on email notifications' => 'Aktifake kustomisasi rincian pesenan ing kabar email','Display new sections and change order of sections on email notifications. Styles of order details on email notifications remain unchanged.' => 'Tampilake bagean anyar lan ganti urutan bagean ing kabar email. Gaya rincian pesenan ing kabar email tetep ora owah.','Thank you page' => 'Matur nuwun kaca','These options affect the thank you page, also known as order received or order confirmation pages.' => 'Opsi iki mengaruhi kaca matur nuwun, uga dikenal minangka kaca pesenan sing ditampa utawa kaca konfirmasi pesenan.','Order details layout' => 'Tata letak rincian pesenan','Display the order details with wide layout on the thank you page' => 'Tampilake rincian pesenan kanthi tata letak sing amba ing kaca matur nuwun','Add widget areas to the thank you page' => 'Tambah area widget menyang kaca matur nuwun','These widget areas are used to add trust symbols and trust badges on the thank you page.' => 'Wilayah widget iki digunakake kanggo nambah simbol kepercayaan lan lencana kepercayaan ing kaca matur nuwun.','These options affect the thank you page , view order details on account pages and on email notifications.' => 'Opsi iki mengaruhi kaca matur nuwun, ndeleng rincian pesenan ing kaca akun lan kabar email.','Order actions' => 'Tumindak dhawuh','Choose in which position to display the order actions.' => 'Pilih ing posisi kanggo nampilake tumindak urutan.','Inside the order overview' => 'Ing ringkesan pesenan','After the order overview' => 'Sawise ringkesan pesenan','After order sections' => 'Sawise bagean pesenan','Order status' => 'Status pesenan','Display the order status progress bar' => 'Tampilake garis kemajuan status pesenan','Choose in which position to display the section.' => 'Pilih ing posisi kanggo nampilake bagean kasebut.','After the order overview and payment instructions' => 'Sawise ringkesan pesenan lan instruksi pembayaran','Before the order items section' => 'Sadurunge bagean pesenan item','Inside the order items section' => 'Ing bagean pesenan item','After the order items section' => 'Sawise bagean pesenan item','On the sidebar' => 'Ing sidebar','Order downloads' => 'Ngundhuh pesenan','Gift message' => 'Pesen hadiah','Tools' => 'piranti','Troubleshooting' => 'Ngatasi masalah','Debug options' => 'Pilihan debug','Debug mode' => 'Mode debug','Using debug mode affects the website performance. Only use this option while troubleshooting.' => 'Nggunakake mode debug mengaruhi kinerja situs web. Gunakake pilihan iki mung nalika ngatasi masalah.','Load unminified assets' => 'Muat aset sing ora dikurangi','Enhanced select fields' => 'Bidang pilih sing ditingkatake','Replace <code>select2</code> dropdown components with <code>TomSelect</code>' => 'Ganti komponen dropdown <code>select2</code> karo <code>TomSelect</code>','TomSelect is a simpler dropdown selection component which is less prone to errors than Select2, while offering the same features that are actually used on WooCommerce checkout pages.' => 'TomSelect minangka komponen pilihan dropdown sing luwih gampang sing kurang rawan kesalahan tinimbang Select2, nalika nawakake fitur sing padha sing digunakake ing kaca checkout WooCommerce.','Fix automatic zoom-in on form fields' => 'Ndandani zoom-in otomatis ing kolom formulir','Set <code>font-size</code> inside form fields to 16px' => 'Setel <code>font-size</code> ing kolom formulir dadi 16px','When the font size inside form fields is smaller than 16px, Safari and other browsers might automatically zoom in on mobile devices to make the text easier to read. When this option is enabled, it will set the font size for inside the form fields to 16px on pages optimized by Fluid Checkout to avoid it zooming in.' => 'Nalika ukuran font ing kolom formulir luwih cilik tinimbang 16px, Safari lan browser liyane bisa kanthi otomatis nggedhekake piranti seluler kanggo nggawe teks luwih gampang diwaca. Yen opsi iki diaktifake, bakal nyetel ukuran font ing kolom formulir menyang 16px ing kaca sing dioptimalake dening Fluid Checkout supaya ora nggedhekake.','The shipping destination is always set to "Default to customer shipping address" when Fluid Checkout is activated. Customers can still provide different shipping and billing addresses during checkout, an option for setting the default billing address to be the same as the shipping address is available at WooCommerce > Settings > Fluid Checkout.' => 'Tujuan pengiriman tansah disetel menyang &quot;Default kanggo alamat pengiriman pelanggan&quot; nalika Fluid Checkout diaktifake. Pelanggan isih bisa nyedhiyakake alamat pengiriman lan tagihan sing beda-beda sajrone checkout, pilihan kanggo nyetel alamat tagihan standar supaya padha karo alamat pengiriman kasedhiya ing WooCommerce&gt; Setelan&gt; Checkout Cairan.','Fluid Checkout' => 'Cairan Checkout','Settings' => 'Setelan','Support' => 'Dhukungan','<a target="_blank" href="%s">Upgrade to PRO</a> to unlock more options.' => '<a target="_blank" href="%s">Nganyarke menyang PRO</a> kanggo mbukak kunci pilihan liyane.','(PRO)' => '(PRO)','(experimental)' => '(eksperimental)','This is an experimental feature and may not work as expected with all themes and plugins. Use it with caution.' => 'Iki minangka fitur eksperimen lan bisa uga ora bisa digunakake kaya sing dikarepake karo kabeh tema lan plugin. Gunakake kanthi ati-ati.','Confirm' => 'Konfirmasi','Verify Phone Number' => 'Verifikasi Nomer Telpon','Please verify your phone number.' => 'Mangga verifikasi nomer telpon.','The Bluehost Plugin' => 'Plugin Bluehost','Custom fields' => 'Kothak khusus','Enable custom shipping email field' => 'Aktifake kolom email pengiriman khusus','When enabled, the shipping email field from the Bluehost plugin will be added to the checkout form.' => 'Yen diaktifake, kolom email pengiriman saka plugin Bluehost bakal ditambahake menyang formulir checkout.','Captcha Pro by BestWebSoft' => 'Captcha Pro dening BestWebSoft','Captcha position' => 'Posisi Captcha','Define the position to display the captcha section. Some positions might not work depending on the captcha type chosen.' => 'Netepake posisi kanggo nampilake bagean captcha. Sawetara posisi bisa uga ora bisa digunakake gumantung saka jinis captcha sing dipilih.','Selecting a pickup point is required before proceeding.' => 'Milih titik pickup dibutuhake sadurunge nerusake.','Pickup point' => 'Titik pickup','Pickup point not selected yet.' => 'Titik pickup durung dipilih.','Pickup point:' => 'Titik Pickup:','Mailchimp for WooCommerce' => 'Mailchimp kanggo WooCommerce','Subscribe checkbox' => 'Langganan kothak centhang','Override the position for the subscribe checkbox on the checkout page and always display it on the contact step' => 'Ganti posisi kanggo kothak mriksa langganan ing kaca checkout lan tansah nampilake ing langkah kontak','Before Shipping Address' => 'Sadurunge Alamat Pengiriman','(originally "%s")' => '(asline &quot;%s&quot;)','After Shipping Address' => 'Sawise Alamat Pengiriman','Selecting a collection point is required when shipping with FermoPoint.' => 'Milih titik koleksi dibutuhake nalika ngirim nganggo FermoPoint.','If checked informs typos in email to users. (This feature has been disabled because Fluid Checkout offers the feature. Changes to this option will not take effect)' => 'Yen dicenthang ngandhani salah ketik ing email menyang pangguna. (Fitur iki wis dipateni amarga Fluid Checkout nawakake fitur kasebut. Owah-owahan ing pilihan iki ora bakal ditrapake)','Enable Mail Check: (disabled feature)' => 'Aktifake Priksa Mail: (fitur dipateni)','Carrier:' => 'Pembawa:','Sendinblue - WooCommerce Email Marketing' => 'Sendinblue - Pemasaran Email WooCommerce','Sign up checkbox position' => 'Ndaftar posisi kothak centhang','Move the sign up checkbox to the contact step' => 'Pindhah kothak tandha munggah menyang langkah kontak','When enabled, the checkbox will be moved to the contact step independently of the display location defined in the Sendinblue plugin settings.' => 'Yen diaktifake, kothak centhang bakal dipindhah menyang langkah kontak kanthi bebas saka lokasi tampilan sing ditetepake ing setelan plugin Sendinblue.','Theme Betheme' => 'Tema Betheme','Checkout progress' => 'kemajuan Checkout','Output the checkout steps section from Betheme on the checkout, cart and order received pages.' => 'Output bagean langkah-langkah checkout saka Betheme ing kaca checkout, cart lan pesenan sing ditampa.','Theme Fennik' => 'Tema Fennik','Breadcrumbs' => 'Breadcrumbs','Output the breadcrumbs section from the Fennik theme when using Fluid Checkout header and footer.' => 'Output bagean breadcrumbs saka tema Fennik nalika nggunakake Fluid Checkout header lan footer.','Theme Go' => 'Tema Go','My account layout' => 'Tata letak akunku','Enable wide layout on "My account" page.' => 'Aktifake tata letak amba ing kaca &quot;Akunku&quot;.','Theme Impreza' => 'Tema Impreza','Header' => 'Header','Spacing for site header at the checkout page (in pixels)' => 'Spasi kanggo header situs ing kaca checkout (ing piksel)','Only applicable when using the Impreza theme header at the checkout page.' => 'Mung ditrapake nalika nggunakake header tema Impreza ing kaca checkout.','Theme Kapee' => 'Tema Kapee','Output the checkout steps section from the Kapee theme on the checkout, cart and order received pages.' => 'Output bagean langkah checkout saka tema Kapee ing checkout, cart lan pesenan ditampa kaca.','Theme Porto' => 'Tema Porto','Output the checkout steps section from the Porto theme on the checkout, cart and order received pages.' => 'Output bagean langkah checkout saka tema Porto ing checkout, cart lan pesenan ditampa kaca.','Theme Pressmart' => 'Tema Pressmart','Output the checkout steps section from the Pressmart theme on the checkout, cart and order received pages.' => 'Output bagean langkah-langkah checkout saka tema Pressmart ing kaca checkout, cart lan pesenan sing ditampa.','Theme The Gem' => 'Tema The Gem','Output the checkout steps section from The Gem theme when using Fluid Checkout header and footer.' => 'Output bagean langkah-langkah checkout saka tema Permata nalika nggunakake header lan footer Fluid Checkout.','Theme Woodmart' => 'Tema Woodmart','Output the checkout steps section from the Woodmart theme when using Fluid Checkout header and footer.' => 'Output bagean langkah-langkah checkout saka tema Woodmart nalika nggunakake header lan footer Fluid Checkout.','Checkout options' => 'Pilihan checkout','Disable the theme checkout options' => 'Pateni opsi checkout tema','The options display product image, quantity field, remove button and link to product page added by the theme are disabled by default.' => 'Opsi nampilake gambar produk, kolom kuantitas, tombol mbusak lan link menyang kaca produk sing ditambahake kanthi tema dipateni kanthi gawan.','Theme Atomion' => 'Tema Atomion','Order progress' => 'Kemajuan pesenan','Display order progress from the theme' => 'Tampilake kemajuan urutan saka tema','Display the form field labels visible on the page' => 'Tampilake label kolom formulir sing katon ing kaca kasebut','It is recommended to keep this option enabled for a better user experience.' => 'Disaranake supaya opsi iki aktif kanggo pengalaman pangguna sing luwih apik.','Theme ZK Nito' => 'Tema ZK Nito','Add extra fields to the checkout form' => 'Tambah kothak ekstra menyang formulir checkout','Adds an extra shipping email and shipping phone field to the shipping address section.' => 'Nambahake lapangan email pengiriman lan telpon pengiriman menyang bagean alamat pengiriman.','Go to the home page' => 'Pindhah menyang kaca ngarep','Skip to content' => 'Lumpat menyang isi','Create an account' => 'Nggawe akun','Log in to your account' => 'Mlebu menyang akun sampeyan','Already have an account?' => 'Wis duwe akun?','Logged in as %1$s. <a href="%2$s">Log out</a>' => 'Mlebet minangka %1$s. <a href="%2$s">metu</a>','Your order has a total amount due of %s. No further payment is needed.' => 'Pesenan sampeyan duwe jumlah total amarga %s. Ora ana pembayaran maneh sing dibutuhake.','Close and continue with checkout' => 'Nutup lan terus karo checkout','Continue' => 'Terus','Parcel terminal' => 'Terminal paket','<p>This widget has been replaced by Fluid Checkout. </p><p>When using Fluid Checkout, the checkout page needs to use the original WooCommerce shortcode <code>[woocommerce_checkout]</code> to work properly.</p><p>For more information, visit the Fluid Checkout documentation.</p>' => '<p>Widget iki wis diganti dening Fluid Checkout.</p><p> Nalika nggunakake Fluid Checkout, kaca checkout kudu nggunakake kode cekak WooCommerce asli <code>[woocommerce_checkout]</code> supaya bisa mlaku kanthi bener.</p><p> Kanggo informasi luwih lengkap, bukak dokumentasi Fluid Checkout.</p>','Theme The7' => 'Tema 7','Additional header sections' => 'Bagean header tambahan','Output additional header sections if enabled in the theme settings.' => 'Output bagean header tambahan yen diaktifake ing setelan tema.','More information' => 'informasi liyane','Button label for applying coupon codesApply' => 'nglamar','Checkout step titleContact' => 'Kontak','Checkout step titleShipping' => 'Pengiriman','Checkout step titleBilling' => 'Tagihan','Checkout step titlePayment' => 'pambayaran','Checkout substep change link labelChange' => 'Ganti','Checkout substep save link labelSave changes' => 'Simpen owah-owahan','Substep review textNone.' => 'ora ana.','Substep review field format%1$s' => '%1$s','Substep review field format: with label%2$s: %1$s' => '%2$s: %1$s','Image uploader.No image selected.' => 'Ora ana gambar sing dipilih.','Settings sectionCart' => 'grobag','Settings sectionOrder pay' => 'Pembayaran pesenan','Settings sectionThank you' => 'matur nuwun','Settings section titleIntegrations' => 'Integrasi','Settings section titleLicense keys' => 'Tombol lisensi','Captcha positionBefore place order section' => 'Sadurunge bagean pesenan panggonan','Captcha positionBefore place order button' => 'Tombol sadurunge pesenan','No shipping method chosen label for the order summary--' => '--','Close button aria-labelClose' => 'Nutup','Log in link label at checkout contact stepLog in' => 'mlebu log','Log in separator label at for when guest checkout is disabledOr continue below' => 'Utawa terus ing ngisor iki','Log in separator label at for when guest checkout is disabledOr continue as a guest' => 'Utawa terus dadi tamu',] ];