return [ 'project-id-version' => 'fluid-checkout','mime-version' => '1.0','content-type' => 'text/plain; charset=utf-8','content-transfer-encoding' => '8bit','language-team' => 'Fluid Checkout','last-translator' => 'Fluid Checkout','pot-creation-date' => '2025-02-26 10:57+0000','report-msgid-bugs-to' => 'https://fluidcheckout.com/support','x-poedit-basepath' => '..','x-poedit-keywordslist' => '__;_e;_ex:1,2c;_n:1,2;_n_noop:1,2;_nx:1,2,4c;_nx_noop:1,2,3c;_x:1,2c;esc_attr__;esc_attr_e;esc_attr_x:1,2c;esc_html__;esc_html_e;esc_html_x:1,2c','x-poedit-searchpath-0' => '.','x-poedit-searchpathexcluded-0' => '*.js','x-poedit-sourcecharset' => 'UTF-8','plural-forms' => 'nplurals=2; plural=(n != 1);','messages' => ['WooCommerce' => 'WooCommerce','Go to Plugin Search' => 'Nenda kwenye Utafutaji wa programu-jalizi','Activate %s' => 'Washa %s','<strong>%1$s</strong> requires <strong>%2$s</strong> to be installed and activated. <a href="%4$s">%3$s</a>' => '<strong>%1$s</strong> inahitaji <strong>%2$s</strong> kusakinishwa na kuamishwa. <a href="%4$s">%3$s</a>','Enter your code here' => 'Weka nambari yako hapa','Add %s' => 'Ongeza %s','Remove' => 'Ondoa','Order number and receipt will be sent to this email address.' => 'Nambari ya agizo na risiti zitatumwa kwa anwani hii ya barua pepe.','Apartment, unit, building, floor, etc.' => 'Ghorofa, kitengo, jengo, sakafu, nk.','Shipping phone' => 'Simu ya kusafirisha','Only used for shipping-related questions.' => 'Inatumika kwa maswali yanayohusiana na usafirishaji pekee.','Multi-step' => 'Hatua nyingi','Single step' => 'Hatua moja','Click to go to the order summary' => 'Bofya ili kwenda kwa muhtasari wa agizo','Cart total:' => 'Jumla ya rukwama:','Proceed to %s' => 'Nenda kwa %s','Proceed to contact' => 'Endelea kuwasiliana','Proceed to shipping' => 'Endelea na usafirishaji','Proceed to billing' => 'Endelea kwenye malipo','Proceed to payment' => 'Endelea hadi malipo','My contact' => 'Mawasiliano yangu','Shipping to' => 'Kusafirisha kwenda','Shipping method' => 'Mbinu ya usafirishaji','Additional notes' => 'Vidokezo vya ziada','Billing to' => 'Kutoza bili','Payment method' => 'Njia ya malipo','Step %1$s of %2$s' => 'Hatua ya %1$s ya %2$s','Change: %s' => 'Badilisha: %s','An account will be created with the information provided.' => 'Akaunti itaundwa na habari iliyotolewa.','Not selected yet.' => 'Bado haijachaguliwa.','Same as shipping address' => 'Sawa na anwani ya usafirishaji','Same as billing address' => 'Sawa na anwani ya kutuma bili','Order summary' => 'Muhtasari wa agizo','Edit cart' => 'Badilisha rukwama','Did you mean %s?' => 'Je, ulimaanisha %s?','Change email address to: {suggestion-value}' => 'Badilisha anwani ya barua pepe iwe: {suggestion-value}','This is a required field.' => 'Hii ni sehemu inayohitajika.','This is not a valid email address.' => 'Hii si barua pepe halali.','This field does not match the related field value.' => 'Sehemu hii hailingani na thamani ya sehemu husika.','The CPF number "{cpf_number}" is invalid.' => 'Nambari ya CPF &quot;{cpf_number}&quot; si sahihi.','The CNPJ number "{cnpj_number}" is invalid.' => 'Nambari ya CNPJ &quot;{cnpj_number}&quot; si sahihi.','(Required)' => '(Inahitajika)','Checkout Header - Desktop' => 'Kichwa cha Malipo - Eneo-kazi','Display widgets on the checkout header for large screens. Only displayed if using the plugin\'s checkout header.' => 'Onyesha wijeti kwenye kichwa cha malipo kwa skrini kubwa. Inaonyeshwa tu ikiwa unatumia kichwa cha malipo cha programu-jalizi.','Checkout Header - Mobile' => 'Kichwa cha Malipo - Simu ya Mkononi','Display widgets below the checkout header for mobile devices. Only displayed if using the plugin\'s checkout header.' => 'Onyesha wijeti chini ya kichwa cha malipo cha vifaa vya rununu. Inaonyeshwa tu ikiwa unatumia kichwa cha malipo cha programu-jalizi.','Checkout Sidebar' => 'Upau wa Malipo','Display widgets on the checkout sidebar after the order summary.' => 'Onyesha wijeti kwenye upau wa malipo baada ya muhtasari wa agizo.','Checkout Order Summary' => 'Muhtasari wa Agizo la Malipo','Display widgets inside the order summary at the checkout page.' => 'Onyesha wijeti ndani ya muhtasari wa agizo kwenye ukurasa wa malipo.','Checkout Below Place Order' => 'Angalia Chini ya Weka Agizo','Display widgets below the place order button at the checkout page.' => 'Onyesha wijeti chini ya kitufe cha kuagiza mahali kwenye ukurasa wa malipo.','Checkout Footer' => 'Checkout Footer','Display widgets on the checkout footer. Only displayed if using the plugin\'s checkout footer.' => 'Onyesha wijeti kwenye kijachini cha malipo. Inaonyeshwa tu ikiwa unatumia kijachini cha malipo ya programu-jalizi.','Classic' => 'Classic','Boxed' => 'Imewekwa kwenye sanduku','Split' => 'Gawanya','Minimalist' => 'Minimalist','Fluid Checkout and Delivery Date plugins by CodeRockz' => 'Malipo ya Majimaji na Programu jalizi za Tarehe ya Kutuma na CodeRockz','<p>We detected that you are using the plugins Delivery & Pickup Date Time for WooCommerce (free and PRO) by CodeRockz. Integration with these plugins have been moved to Fluid Checkout PRO.</p>' => '<p>Tuligundua kuwa unatumia programu jalizi Muda wa Kutuma &amp; Tarehe ya Kuchukua kwa WooCommerce (bila malipo na PRO) na CodeRockz. Kuunganishwa na programu-jalizi hizi kumehamishwa hadi Fluid Checkout PRO.</p>','Upgrade to PRO' => 'Pata toleo jipya la PRO','Fluid Checkout database updated successfully' => 'Hifadhidata ya Malipo ya Maji imesasishwa kwa mafanikio','The database changes where applied successfully.' => 'Hifadhidata inabadilika inapotumika kwa mafanikio.','Ok' => 'Sawa','Dismiss' => 'Ondoa','Fluid Checkout database update' => 'Usasishaji wa hifadhidata ya Malipo ya Maji','Some changes to the database are required. As with any update, we recommend you to <strong>take a full backup of your website before proceeding</strong>.' => 'Baadhi ya mabadiliko kwenye hifadhidata yanahitajika. Kama ilivyo kwa sasisho lolote, tunapendekeza <strong>uchukue nakala kamili ya tovuti yako kabla ya kuendelea</strong> .','Update database' => 'Sasisha hifadhidata','Fluid Checkout is not compatible with the Divi Builder checkout layout' => 'Malipo ya Maji hayaoani na mpangilio wa malipo wa Divi Builder','<p>Fluid Checkout needs the classic WooCommerce shortcode-based checkout form to work. When using the Divi Builder to customize the look and feel of the checkout page, the classic shortcodes are replaced with custom widgets from the Divi Builder for each part of the checkout page. These custom widgets are not compatible with Fluid Checkout.</p><p>Please edit your checkout page replacing the Divi Builder widgets with the a text widget containing the shortcode <code>[woocommerce_checkout]</code>.</p>' => '<p>Malipo ya Majimaji yanahitaji fomu ya malipo ya msingi ya njia fupi ya WooCommerce ili kufanya kazi. Unapotumia Divi Builder kubinafsisha mwonekano na mwonekano wa ukurasa wa kulipa, misimbo fupi ya kawaida hubadilishwa na wijeti maalum kutoka kwa Divi Builder kwa kila sehemu ya ukurasa wa malipo. Wijeti hizi maalum hazioani na Malipo ya Maji.</p><p> Tafadhali hariri ukurasa wako wa kulipa ukibadilisha wijeti za Divi Builder kwa wijeti ya maandishi iliyo na msimbo mkato <code>[woocommerce_checkout]</code> .</p>','Edit the checkout page' => 'Hariri ukurasa wa malipo','Read the documentation.' => 'Soma nyaraka.','Germanized PRO multistep checkout feature needs to be disabled when using Fluid Checkout' => 'Kipengele cha kulipa kwa hatua nyingi cha Kijerumani cha PRO kinahitaji kuzimwa unapotumia Malipo ya Maji','When using Fluid Checkout, the multistep feature from the Germanized PRO plugin becomes unnecessary and can cause <strong>critical errors while processing new orders</strong>. Please disable the Germanized PRO multistep checkout feature in the plugin settings.' => 'Unapotumia Fluid Checkout, kipengele cha hatua nyingi kutoka kwa programu-jalizi ya Germanized PRO huwa si lazima na kinaweza kusababisha <strong>hitilafu kubwa wakati wa kuchakata maagizo mapya</strong> . Tafadhali zima kipengele cha Malipo cha hatua nyingi cha Germanized PRO katika mipangilio ya programu-jalizi.','Go to Germanized settings' => 'Nenda kwa mipangilio ya Kijerumani','Thanks for choosing Fluid Checkout!' => 'Asante kwa kuchagua Malipo ya Maji!','You have been using the plugin for a while now. How do you like it so far? <br>Please give us a quick rating, we appreciate every bit of feedback and it encourages us to keep improving the plugin :)' => 'Umekuwa ukitumia programu-jalizi kwa muda sasa. Unapendaje hadi sasa?<br> Tafadhali tupe ukadiriaji wa haraka, tunathamini kila maoni na inatuhimiza kuendelea kuboresha programu-jalizi :)','Rate the plugin' => 'Kadiria programu-jalizi','I need help!' => 'Nahitaji msaada!','The plugin WooCommerce Checkout Manager by Quadlayers is not compatibile with Fluid Checkout' => 'Kidhibiti cha Malipo cha WooCommerce na Quadlayers hakiendani na Malipo ya Maji','Some features of WooCommerce Checkout Manager by Quadlayers might not work as expected when using it with Fluid Checkout. Read <a href="%s">our documentation</a> for more information and alternative solutions.' => 'Baadhi ya vipengele vya Meneja wa Malipo wa WooCommerce na Quadlayers huenda visifanye kazi kama inavyotarajiwa wakati wa kuitumia kwa Malipo ya Maji. Soma <a href="%s">hati zetu</a> kwa habari zaidi na suluhisho mbadala.','Read documentation' => 'Soma nyaraka','Don\'t show this again' => 'Usionyeshe hii tena','Special offers' => 'Matoleo maalum','Extend all the goodness of Fluid Checkout to your cart page and thank you pages.' => 'Panua uzuri wote wa Malipo ya Maji kwenye ukurasa wako wa rukwama na kurasa za asante.','Fluid Checkout PRO' => 'Fluid Checkout PRO','Design templates, optimized cart and order received pages, account matching, and much more.' => 'Violezo vya muundo, rukwama iliyoboreshwa na kuagiza kurasa zilizopokelewa, kulinganisha akaunti, na mengi zaidi.','The best tools to further <strong>improve your WooCommerce checkout conversion rate</strong> and make the purchase experience consistent on the entire journey.' => 'Zana bora zaidi za <strong>kuboresha zaidi kiwango chako cha walioshawishika kulipia WooCommerce</strong> na kufanya hali ya ununuzi ifanane katika safari nzima.','More design templates' => 'Violezo zaidi vya kubuni','Optimized cart page' => 'Ukurasa wa rukwama ulioboreshwa','Optimized order received / thank you page' => 'Agizo lililoboreshwa limepokelewa / ukurasa wa asante','Edit cart contents at checkout page' => 'Hariri yaliyomo kwenye rukwama kwenye ukurasa wa malipo','Account matching / user matching' => 'Ulinganishaji wa akaunti / ulinganishaji wa mtumiaji','Express Checkout buttons from supported payment methods' => 'Vifungo vya Express Checkout kutoka kwa njia za malipo zinazotumika','Trust symbols on the cart and thank you pages' => 'Alama za uaminifu kwenye rukwama na kurasa za asante','More positions for the coupon code on the checkout page' => 'Nafasi zaidi za msimbo wa kuponi kwenye ukurasa wa malipo','Integrated coupon code field on the cart page' => 'Sehemu iliyojumuishwa ya msimbo wa kuponi kwenye ukurasa wa rukwama','International phone numbers' => 'Nambari za simu za kimataifa','Gift messages and packing slips' => 'Ujumbe wa zawadi na karatasi za kufunga','Local pickup and in-store collection' => 'Mkusanyiko wa eneo na mkusanyo wa dukani','Upgrade to PRO &mdash; 99 EUR' => 'Pata toleo jipya la PRO — 99 EUR','Fluid Checkout PRO – Bundle' => 'Fluid Checkout PRO - Bundle','All PRO features + All add-ons for a special price.' => 'Vipengele vyote vya PRO + Viongezi vyote kwa bei maalum.','The bundle includes all add-ons we currently offer and Fluid Checkout add-ons we launch in the future.' => 'Kifurushi hiki kinajumuisha programu jalizi zote tunazotoa kwa sasa na programu jalizi za Malipo ya Maji tutakayozindua siku zijazo.','226 EUR value (Save 42%)' => 'Thamani ya EUR 226 (Okoa 42%)','Google Address Autocomplete' => 'Anwani ya Google Imekamilishwa Kiotomatiki','Address Book' => 'Kitabu cha Anwani','EU-VAT Assistant' => 'Msaidizi wa EU-VAT','All future add-ons *' => 'Nyongeza zote za siku zijazo *','* For as long as you have an active subscription.' => '* Kwa muda mrefu kama una usajili unaoendelea.','Get the Bundle – Only 129 EUR' => 'Pata Bundle - EUR 129 pekee','All prices shown in EUR. If there are any divergencies with the prices on our website, the offers shown on the website superseed these and will be applied.' => 'Bei zote zinaonyeshwa katika EUR. Ikiwa kuna tofauti zozote na bei kwenye tovuti yetu, matoleo yaliyoonyeshwa kwenye tovuti yanachukua nafasi hizi na yatatumika.','Add-ons' => 'Viongezi','Enhance your checkout experience with these add-ons.' => 'Boresha utumiaji wako wa malipo na programu jalizi hizi.','I already have it – Hide this offer' => 'Tayari ninayo - Ficha ofa hii','Installed &mdash; Activate' => 'Imesakinishwa - Amilisha','Activated' => 'Imewashwa','Up to 40% less checkout fields to fill in.' => 'Hadi 40% chini ya sehemu za kulipa ili kujaza.','Avoid delivery delays and unsatisfied customers. Collect the <strong>correct address information</strong> from the first time they buy with you.' => 'Epuka ucheleweshaji wa uwasilishaji na wateja wasioridhika. Kusanya <strong>maelezo sahihi ya anwani</strong> kutoka mara ya kwanza wanaponunua nawe.','Get this add-on &mdash; 29 EUR' => 'Pata programu jalizi hii — 29 EUR','Address book' => 'Kitabu cha anwani','Multiple saved addresses for shipping and billing.' => 'Anwani nyingi zilizohifadhiwa za usafirishaji na malipo.','Let customers <strong>save multiple shipping and billing addresses</strong> on their account and choose which ones to use at checkout and cart pages.' => 'Waruhusu wateja <strong>wahifadhi anwani nyingi za usafirishaji na bili</strong> kwenye akaunti zao na uchague zipi watatumia wakati wa kulipa na kurasa za rukwama.','Get this add-on &mdash; 59 EUR' => 'Pata programu jalizi hii — 59 EUR','Simplified EU-VAT validation for your store.' => 'Uthibitishaji wa EU-VAT uliorahisishwa kwa duka lako.','Collect and <strong>validate EU VAT numbers at the checkout page</strong>, removes tax charges on reverse charge basis and confirms customer location when needed.' => 'Kusanya na <strong>uidhinishe nambari za VAT za Umoja wa Ulaya kwenye ukurasa wa kulipa</strong> , huondoa ada za kodi kwa misingi ya malipo ya kinyume na huthibitisha eneo la mteja inapohitajika.','Get this add-on &mdash; 39 EUR' => 'Pata programu jalizi hii — 39 EUR','All add-ons are sold separately and require the <strong>Fluid Checkout Lite</strong> plugin to be installed and activated, except when noted.' => 'Viongezi vyote vinauzwa kivyake na vinahitaji programu-jalizi ya <strong>Fluid Checkout Lite</strong> kusakinishwa na kuamilishwa, isipokuwa inapobainishwa.','Select an image' => 'Chagua picha','No image selected.' => 'Hakuna picha iliyochaguliwa.','Remove image' => 'Ondoa picha','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to get your license key, or <a href="%s" target="_blank">purchase a new license key</a>.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Ingia katika akaunti yako</a> ili upate ufunguo wako wa leseni, au <a href="%s" target="_blank">ununue ufunguo mpya wa leseni</a> .','Valid until %s.' => 'Itatumika hadi %s.','Expired on %s.' => 'Muda wake uliisha mnamo %s.','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to renew your license key and continue to receive updates and support.' => '<a href="https://fluidcheckout.com/account/" target="_blank">Ingia katika akaunti yako</a> ili usasishe ufunguo wako wa leseni na uendelee kupokea masasisho na usaidizi.','License key cancelled.' => 'Kitufe cha leseni kimeghairiwa.','<a href="%s" target="_blank">Purchase a new license key</a> and continue to receive updates and support.' => '<a href="%s" target="_blank">Nunua ufunguo mpya wa leseni</a> na uendelee kupokea masasisho na usaidizi.','Error: ' => 'Hitilafu: ','Setup & Documentation' => 'Mipangilio na Uhifadhi','Great! Your checkout page is now running on Fluid Checkout.' => 'Kubwa! Ukurasa wako wa Malipo sasa unatumia Malipo ya Maji.','Here are a few resources for you to get started:' => 'Hapa kuna nyenzo chache za wewe kuanza:','Read the installation guide <a href="%s" target="_blank">Getting stated with Fluid Checkout</a>.' => 'Soma mwongozo wa usakinishaji <a href="%s" target="_blank">Kupata maelezo kwa Malipo ya Maji</a> .','Setup layout and design on the <a href="%s">checkout options</a>.' => 'Sanidi mpangilio na muundo kwenye <a href="%s">chaguo za malipo</a> .','Check if there are any <a href="%s">integration options</a> available for other plugins you have installed.' => 'Angalia ikiwa kuna <a href="%s">chaguo zozote za ujumuishaji</a> zinazopatikana kwa programu-jalizi zingine ambazo umesakinisha.','Visit <a href="%s" target="_blank">our documentation</a> for more information about Fluid Checkout features.' => 'Tembelea <a href="%s" target="_blank">hati zetu</a> kwa maelezo zaidi kuhusu vipengele vya Malipo ya Maji.','If you ever need help, <a href="%s" target="_blank">open a support ticket</a> on our official support channel.' => 'Iwapo utahitaji usaidizi, <a href="%s" target="_blank">fungua tikiti ya usaidizi</a> kwenye kituo chetu rasmi cha usaidizi.','Cart layout' => 'Mpangilio wa gari','Cart optimizations' => 'Uboreshaji wa mikokoteni','Enable cart page optimizations' => 'Washa uboreshaji wa ukurasa wa rukwama','Display the cart page with a layout similar to the checkout page.' => 'Onyesha ukurasa wa rukwama na mpangilio sawa na ukurasa wa malipo.','Header and footer' => 'Kichwa na kijachini','Header and footer template' => 'Kiolezo cha kichwa na kijachini','Controls whether to use the distraction free page header and footer or keep the currently active theme\'s header and footer.' => 'Hudhibiti iwapo itatumia kichwa na kijachini cha ukurasa usio na usumbufu au kuweka kichwa na kijachini cha mandhari amilifu kwa sasa.','Distraction free header and footer' => 'Kichwa na kijachini kisicho na usumbufu','Theme\'s header and footer' => 'Kijajuu na kijachini cha mandhari','Cart elements' => 'Vipengele vya gari','Make the order summary stay visible on the cart page while scrolling' => 'Fanya muhtasari wa agizo uendelee kuonekana kwenye ukurasa wa rukwama huku unasogeza','Coupon codes' => 'Misimbo ya kuponi','Select position where to display the coupon codes section on the cart page. Only applicable when AJAX cart and the integrated coupon codes in the checkout page are enabled.' => 'Chagua mahali pa kuonyesha sehemu ya misimbo ya kuponi kwenye ukurasa wa rukwama. Inatumika tu wakati rukwama ya AJAX na misimbo ya kuponi iliyounganishwa katika ukurasa wa kulipa zimewashwa.','Hidden' => 'Imefichwa','Before the cart items section' => 'Kabla ya sehemu ya vitu vya gari','Inside the cart items section' => 'Ndani ya sehemu ya vitu vya gari','After the cart items section' => 'Baada ya sehemu ya vitu vya gari','Before the shipping section' => 'Kabla ya sehemu ya usafirishaji','After the shipping section' => 'Baada ya sehemu ya usafirishaji','Before the order summary' => 'Kabla ya muhtasari wa agizo','Inside the order summary' => 'Ndani ya muhtasari wa agizo','Before the order totals' => 'Kabla ya jumla ya agizo','After the order totals' => 'Baada ya jumla ya agizo','After the order summary' => 'Baada ya muhtasari wa agizo','Shipping' => 'Usafirishaji','Select position where to display the shipping costs and calculator on the cart page.' => 'Chagua mahali pa kuonyesha gharama za usafirishaji na kikokotoo kwenye ukurasa wa rukwama.','Display as a cart section' => 'Onyesha kama sehemu ya rukwama','Cross-sells' => 'Mtambuka','Select position where to display the cross-sells section on the cart page.' => 'Chagua mahali pa kuonyesha sehemu ya kuuza bidhaa tofauti kwenye ukurasa wa rukwama.','Before the cart actions' => 'Kabla ya vitendo vya gari','After the cart actions' => 'Baada ya vitendo vya gari','Layout of cross-sell items on the cart page.' => 'Mpangilio wa bidhaa zinazouzwa kwa njia tofauti kwenye ukurasa wa rukwama.','Optimized horizontal cross-sells layout' => 'Muundo ulioboreshwa wa mauzo mtambuka','Theme\'s cross-sells layout' => 'Mpangilio wa mada mtambuka','Number of cross-sell items to display on the cart page' => 'Idadi ya bidhaa zinazouzwa kwa njia tofauti za kuonyesha kwenye ukurasa wa rukwama','Trust symbols &amp; badges' => 'Alama za uaminifu na beji','Add widget areas to the cart page' => 'Ongeza maeneo ya wijeti kwenye ukurasa wa rukwama','These widget areas are used to add trust symbols and trust badges on the cart page.' => 'Maeneo haya ya wijeti hutumiwa kuongeza alama za uaminifu na beji za uaminifu kwenye ukurasa wa rukwama.','Checkout' => 'Lipa','Layout &amp; design' => 'Muundo na muundo','Checkout layout' => 'Mpangilio wa Malipo','Design template' => 'Kiolezo cha kubuni','General styles for the checkout steps, order summary and other sections. Might also apply to other pages such as the Cart, Order Received and View Order pages.' => 'Mitindo ya jumla ya hatua za malipo, muhtasari wa agizo na sehemu zingine. Inaweza pia kutumika kwa kurasa zingine kama vile Rukwama, Agizo Lililopokelewa na Kuangalia kurasa za Agizo.','Dark mode' => 'Hali ya giza','Enable dark mode' => 'Washa hali ya giza','Header and footer templates' => 'Violezo vya kichwa na kijachini','We recommend using the distraction free header and footer to avoid distractions at the checkout page. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Read the research about cart abandonment</a>.' => 'Tunapendekeza utumie kichwa na kijachini kisicho na usumbufu ili kuepuka usumbufu kwenye ukurasa wa kulipa. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Soma utafiti kuhusu kuacha mikokoteni</a> .','Logo image' => 'Picha ya nembo','Choose an image to be displayed on the checkout page header. Only applies when using the distraction free header and footer.' => 'Chagua picha ya kuonyeshwa kwenye kichwa cha ukurasa wa malipo. Inatumika tu wakati wa kutumia kichwa na kijachini kisicho na usumbufu.','Header background color' => 'Rangi ya mandharinyuma ya kichwa','Choose a background color for the checkout page header. Only applies when using the distraction free header and footer.' => 'Chagua rangi ya usuli kwa kichwa cha ukurasa wa malipo. Inatumika tu wakati wa kutumia kichwa na kijachini kisicho na usumbufu.','HTML color value. ie: #f3f3f3' => 'Thamani ya rangi ya HTML. yaani: #f3f3f3','Page background color' => 'Rangi ya mandharinyuma ya ukurasa','Choose a background color for the checkout page. Color is applied to the <em>body</em> element.' => 'Chagua rangi ya mandharinyuma ya ukurasa wa malipo. Rangi hutumiwa kwa kipengele <em>cha mwili</em> .','Footer background color' => 'Rangi ya mandharinyuma ya kijachini','Choose a background color for the checkout page footer. Only applies when using the distraction free header and footer.' => 'Chagua rangi ya usuli kwa kijachini cha ukurasa wa malipo. Inatumika tu wakati wa kutumia kichwa na kijachini kisicho na usumbufu.','Checkout elements' => 'Vipengele vya malipo','Progress bar' => 'Upau wa maendeleo','Display the checkout progress bar when using multi-step checkout layout' => 'Onyesha upau wa malipo unapotumia mpangilio wa hatua nyingi wa kulipa','Make the checkout progress bar stay visible while scrolling' => 'Fanya upau wa maendeleo ya malipo uendelee kuonekana wakati wa kusogeza','Express checkout' => 'Malipo ya haraka','Enable the express checkout section' => 'Washa sehemu ya malipo ya haraka','Displays the express checkout section at checkout when supported payment gateways have this feature enabled.' => 'Huonyesha sehemu ya malipo ya haraka wakati wa kulipa wakati lango la malipo linalotumika limewashwa kipengele hiki.','Display express checkout buttons in one line for larger screens' => 'Onyesha vitufe vya kulipa katika mstari mmoja kwa skrini kubwa zaidi','Ignore additional checkout required fields when paying with a compatible express checkout payment gateway' => 'Puuza sehemu za ziada za kulipa unapolipa kwa kutumia lango la malipo linalooana la kulipa','Make the order summary stay visible while scrolling' => 'Fanya muhtasari wa agizo uendelee kuonekana wakati wa kusogeza','Choose a background color for the order summary section.' => 'Chagua rangi ya usuli kwa sehemu ya muhtasari wa agizo.','Highlight the order totals row in the order summary table' => 'Angazia jumla ya safu mlalo katika jedwali la muhtasari wa mpangilio','Most useful when the order summary section does not have a highlighted background color. Might also apply to the Cart, Order Received and View Order pages when using Fluid Checkout PRO.' => 'Inafaa zaidi wakati sehemu ya muhtasari wa agizo haina rangi ya mandharinyuma iliyoangaziwa. Inaweza pia kutumika kwa Rukwama, Agizo Lililopokelewa na Kuangalia kurasa za Agizo unapotumia Fluid Checkout PRO.','Action link on the order summary at checkout.' => 'Kiungo cha kitendo kwenye muhtasari wa agizo wakati wa kulipa.','Edit cart link' => 'Badilisha kiungo cha rukwama','Cart items count' => 'Hesabu ya vitu vya gari','Link to shop page' => 'Unganisha kwa ukurasa wa duka','Cart items' => 'Vitu vya gari','Enable options to edit cart items on the checkout page' => 'Washa chaguo za kuhariri vipengee vya rukwama kwenye ukurasa wa malipo','Allow customers to change product quantities or removing items directly at the checkout page.' => 'Ruhusu wateja kubadilisha idadi ya bidhaa au kuondoa bidhaa moja kwa moja kwenye ukurasa wa malipo.','Hide cart items errors at the checkout page' => 'Ficha hitilafu za vipengee vya rukwama kwenye ukurasa wa malipo','Do not display the cart items errors message at the top of the checkout page. When submitting the checkout form to complete the purchase, these error messages will always be displayed.' => 'Usionyeshe ujumbe wa makosa ya vipengee vya rukwama juu ya ukurasa wa malipo. Wakati wa kuwasilisha fomu ya kulipa ili kukamilisha ununuzi, ujumbe huu wa hitilafu utaonyeshwa kila wakati.','Place order' => 'Weka agizo','Define the position to display "Place order" and terms checkbox section.' => 'Bainisha nafasi ya kuonyesha &quot;Weka mpangilio&quot; na sehemu ya kisanduku cha kuteua maneno.','Some options might not be compatible with some plugins and themes.' => 'Baadhi ya chaguo huenda zisioanishwe na baadhi ya programu-jalizi na mandhari.','Below the payment section' => 'Chini ya sehemu ya malipo','Below the order summary' => 'Chini ya muhtasari wa agizo','Both below the payment section and the order summary' => 'Zote mbili chini ya sehemu ya malipo na muhtasari wa agizo','Add widget areas to the checkout page' => 'Ongeza maeneo ya wijeti kwenye ukurasa wa malipo','These widget areas are used to add trust symbols and trust badges on the checkout page.' => 'Maeneo haya ya wijeti hutumiwa kuongeza alama za uaminifu na beji za uaminifu kwenye ukurasa wa malipo.','Display checkout sidebar widgets only when viewing the last checkout step on mobile devices when using multi-step checkout layout' => 'Onyesha wijeti za upau wa malipo unapotazama tu hatua ya mwisho ya kulipa kwenye vifaa vya mkononi unapotumia mpangilio wa hatua nyingi wa kulipa','Enable integrated coupon code section' => 'Washa sehemu ya msimbo wa kuponi iliyojumuishwa','Only applicable if use of coupon codes are enabled in the WooCommerce settings.' => 'Inatumika tu ikiwa matumizi ya misimbo ya kuponi yamewashwa katika mipangilio ya WooCommerce.','Display the coupon codes section title' => 'Onyesha kichwa cha sehemu ya misimbo ya kuponi','Only applicable when coupon code is displayed as a separate section on the checkout or cart pages.' => 'Inatumika tu wakati msimbo wa kuponi unaonyeshwa kama sehemu tofauti kwenye kurasa za malipo au za rukwama.','Select position where to display the coupon codes section on the checkout page.' => 'Chagua mahali pa kuonyesha sehemu ya misimbo ya kuponi kwenye ukurasa wa kulipa.','Before the checkout steps' => 'Kabla ya hatua za malipo','As a substep before payment methods' => 'Kama hatua ndogo kabla ya njia za malipo','As a substep after payment methods' => 'Kama hatua ndogo baada ya njia za malipo','Select style of the "apply coupon" button. Only applicable when the coupon code section is displayed "Before the checkout steps" on the checkout page, or "Before the cart items section" on the cart page.' => 'Chagua mtindo wa kitufe cha &quot;tumia kuponi&quot;. Inatumika tu wakati sehemu ya msimbo wa kuponi inaonyeshwa &quot;Kabla ya hatua za kulipa&quot; kwenye ukurasa wa kulipa, au &quot;Kabla ya sehemu ya vitu vya rukwama&quot; kwenye ukurasa wa rukwama.','Default button style' => 'Mtindo wa kitufe chaguo-msingi','"Add" link button' => 'Kitufe cha kiungo &quot;Ongeza&quot;.','Checkout fields' => 'Sehemu za malipo','Optional fields' => 'Sehemu za hiari','Hide optional fields behind a link button' => 'Ficha sehemu za hiari nyuma ya kitufe cha kiungo','It is recommended to keep this option checked to reduce the number of open input fields, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">read the research</a>.' => 'Inapendekezwa kuweka chaguo hili kuangaliwa ili kupunguza idadi ya maeneo ya pembejeo wazi, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">soma utafiti</a> .','Do not hide "Address line 2" fields behind a link button' => 'Usifiche sehemu za &quot;Anwani ya 2&quot; nyuma ya kitufe cha kiungo','Recommended only when most customers actually need the "Address line 2" field, or when getting the right shipping address is crucial (ie. if delivering food and other perishable products).' => 'Inapendekezwa tu wakati wateja wengi wanahitaji sehemu ya &quot;Anwani ya 2&quot;, au wakati wa kupata anwani sahihi ya usafirishaji ni muhimu (yaani, ikiwa unaleta chakula na bidhaa zingine zinazoharibika).','Display the "Add" link buttons in lowercase' => 'Onyesha vitufe vya kiungo &quot;Ongeza&quot; kwa herufi ndogo','Make the labels of optional field "Add" link button as <code>lowercase</code> (ie. "Add phone number" instead of "Add Phone Number"). This option also affects the link buttons for coupon code fields.' => 'Tengeneza lebo za sehemu ya hiari ya kitufe cha kiungo cha &quot;Ongeza&quot; kama <code>lowercase</code> (yaani. &quot;Ongeza nambari ya simu&quot; badala ya &quot;Ongeza Nambari ya Simu&quot;). Chaguo hili pia huathiri vitufe vya viungo vya sehemu za msimbo wa kuponi.','Billing address' => 'Anwani ya bili','Select position where to display the billing address section on the checkout page.' => 'Chagua mahali pa kuonyesha sehemu ya anwani ya kutuma bili kwenye ukurasa wa malipo.','As a step before shipping' => 'Kama hatua kabla ya kusafirisha','As a sub-step before the shipping address section' => 'Kama hatua ndogo kabla ya sehemu ya anwani ya usafirishaji','As a sub-step after the shipping address section' => 'Kama hatua ndogo baada ya sehemu ya anwani ya usafirishaji','As a step after shipping' => 'Kama hatua baada ya kusafirisha','As a sub-step before the payment section' => 'Kama hatua ndogo kabla ya sehemu ya malipo','Force billing and shipping addresses to the same (single section)' => 'Lazimisha anwani za bili na usafirishaji kwa sehemu moja (sehemu moja)','Checkbox for "Same as shipping/billing address" checked by default' => 'Kisanduku cha kuteua cha &quot;Sawa na anwani ya usafirishaji/ya kutuma bili&quot; kimechaguliwa kwa chaguomsingi','The checkbox "Same as shiping address" will start as checked by default when the shipping address section is displayed first. <br>The checkbox "Same as billing address" will start as checked by default when the billing address section is displayed first. <br> It is recommended to leave this option checked as to significantly reduce the number of open input fields at checkout, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">read the research</a>.' => 'Kisanduku cha kuteua &quot;Sawa na anwani ya mahali bidhaa zitakapopelekwa&quot; kitaanza kama ilivyoteuliwa kwa chaguomsingi wakati sehemu ya anwani ya usafirishaji itaonyeshwa kwanza.<br> Kisanduku cha kuteua &quot;Sawa na anwani ya kutuma bili&quot; kitaanza kama ilivyochaguliwa kwa chaguo-msingi wakati sehemu ya anwani ya bili itaonyeshwa kwanza.<br> Inapendekezwa kuacha chaguo hili likikaguliwa ili kupunguza kwa kiasi kikubwa idadi ya sehemu zilizo wazi za uingizaji wakati wa kulipa, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">soma utafiti</a> .','Highlight the billing address section in the checkout form' => 'Angazia sehemu ya anwani ya kutuma bili katika fomu ya kulipa','Shipping address' => 'Anwani ya usafirishaji','Highlight the shipping address section in the checkout form' => 'Angazia sehemu ya anwani ya usafirishaji katika fomu ya malipo','Shipping methods' => 'Mbinu za usafirishaji','Choose in which position to display the shipping methods section.' => 'Chagua katika nafasi gani ya kuonyesha sehemu ya njia za usafirishaji.','Before shipping address' => 'Kabla ya anwani ya usafirishaji','After shipping address' => 'Baada ya anwani ya usafirishaji','Prevent automatic selection of the first shipping method' => 'Zuia uteuzi otomatiki wa njia ya kwanza ya usafirishaji','When enabled, the first shipping method available will not be automatically selected when no other shipping method was previously selected for each shipping package. <br>This option will be automatically enabled if the option for clearing the selected shipping method is enabled for the Local Pickup feature.' => 'Ikiwezeshwa, mbinu ya kwanza ya usafirishaji inayopatikana haitachaguliwa kiotomatiki wakati hakuna njia nyingine ya usafirishaji iliyochaguliwa hapo awali kwa kila kifurushi cha usafirishaji.<br> Chaguo hili litawezeshwa kiotomatiki ikiwa chaguo la kufuta njia iliyochaguliwa ya usafirishaji imewashwa kwa kipengele cha Uchukuaji wa Ndani.','Local pickup' => 'Uchukuzi wa ndani','Removes shipping address section when a local pickup shipping method is selected.' => 'Huondoa sehemu ya anwani ya usafirishaji wakati mbinu ya usafirishaji ya mahali ulipo imechaguliwa.','Replaces the shipping address with the pickup point location when a local pickup shipping method is selected.' => 'Hubadilisha anwani ya usafirishaji na mahali pa kuchukua wakati njia ya usafirishaji ya karibu imechaguliwa.','Show option to clear shipping methods in the pickup location substep' => 'Onyesha chaguo la kufuta njia za usafirishaji katika hatua ndogo ya eneo la kuchukuliwa','Show a link button on the pickup location substep to clear the chosen shipping methods. This can be used to allow showing the shipping address section again if a local pickup method was previously selected.' => 'Onyesha kitufe cha kiungo kwenye hatua ndogo ya eneo la kuchukuliwa ili kufuta mbinu ulizochagua za usafirishaji. Hii inaweza kutumika kuruhusu kuonyesha sehemu ya anwani ya usafirishaji tena ikiwa mbinu ya kuchukua ilichaguliwa hapo awali.','Choose which address to save as the shipping address for local pickup orders.' => 'Chagua anwani ya kuhifadhi kama anwani ya usafirishaji kwa maagizo ya karibu ya kuchukua.','Save the selected pickup location' => 'Hifadhi eneo lililochaguliwa la kuchukua','Save same as the billing address' => 'Hifadhi sawa na anwani ya kutuma bili','Do not save any shipping address' => 'Usihifadhi anwani yoyote ya usafirishaji','Company name field for shipping address' => 'Sehemu ya jina la kampuni kwa anwani ya usafirishaji','Change visibility for the company name field for the shipping address section on the checkout form.' => 'Badilisha mwonekano wa sehemu ya jina la kampuni kwa sehemu ya anwani ya usafirishaji kwenye fomu ya kulipa.','If field is set as "optional", which is the default visibility state, no changes will be applied to let other plugins apply any changes they need.' => 'Ikiwa sehemu itawekwa kama &quot;hiari&quot;, ambayo ni hali chaguomsingi ya mwonekano, hakuna mabadiliko yatatumika ili kuruhusu programu-jalizi zingine kutekeleza mabadiliko yoyote wanayohitaji.','Hidden (remove field)' => 'Imefichwa (ondoa sehemu)','Optional' => 'Hiari','Required' => 'Inahitajika','Add shipping phone field to the checkout form.' => 'Ongeza sehemu ya simu ya usafirishaji kwenye fomu ya kulipa.','Choose in which step to display the shipping phone field.' => 'Chagua katika hatua gani ya kuonyesha sehemu ya simu ya usafirishaji.','Contact step' => 'Hatua ya mawasiliano','Billing phone' => 'Simu ya malipo','Add billing phone field to the checkout form.' => 'Ongeza sehemu ya simu ya bili kwenye fomu ya kulipa.','Choose in which step to display the billing phone field.' => 'Chagua katika hatua gani ya kuonyesha sehemu ya simu ya bili.','Enable international phone number format and validation for phone fields' => 'Washa umbizo la nambari ya simu ya kimataifa na uthibitishaji kwa sehemu za simu','Format and validate phone numbers according to each country.' => 'Fomati na uthibitishe nambari za simu kulingana na kila nchi.','Enable phone number validation based on country rules' => 'Washa uthibitishaji wa nambari ya simu kulingana na sheria za nchi','When disabled, the phone field validation will not check if country or area codes are valid for the country.' => 'Ikizimwa, uthibitishaji wa sehemu ya simu hautaangalia kama misimbo ya nchi au eneo ni halali kwa nchi.','Only show allowed countries for shipping or billing' => 'Onyesha nchi zinazoruhusiwa kwa usafirishaji au malipo pekee','When enabled, only the countries allowed for shipping will be available in the shipping phone field, and only countries allowed for billing will be available for the billing phone field.' => 'Ikiwezeshwa, ni nchi zinazoruhusiwa tu kwa usafirishaji zitapatikana katika sehemu ya simu za usafirishaji, na ni nchi zinazoruhusiwa tu kulipia ndizo zitapatikana kwa uga wa simu za kutuma bili.','Show country code beside the flag' => 'Onyesha msimbo wa nchi kando ya bendera','Show an example of a valid phone number inside phone fields' => 'Onyesha mfano wa nambari halali ya simu ndani ya sehemu za simu','Do not change placeholders' => 'Usibadilishe vishika nafasi','Show if not defined' => 'Onyesha ikiwa haijafafanuliwa','Always show' => 'Onyesha kila wakati','Order notes' => 'Agiza maelezo','Define the visibility of the additional order notes field.' => 'Bainisha mwonekano wa sehemu ya vidokezo vya agizo la ziada.','Gift options' => 'Chaguzi za zawadi','Display gift message and other gift options at the checkout page' => 'Onyesha ujumbe wa zawadi na chaguo zingine za zawadi kwenye ukurasa wa malipo','Allow customers to add a gift message and other gift related options to the order.' => 'Ruhusu wateja waongeze ujumbe wa zawadi na chaguo zingine zinazohusiana na zawadi kwenye agizo.','Display the gift message fields always expanded' => 'Onyesha sehemu za ujumbe wa zawadi zilizopanuliwa kila wakati','Display the gift message as part of the order details table instead of a separate section' => 'Onyesha ujumbe wa zawadi kama sehemu ya jedwali la maelezo ya agizo badala ya sehemu tofauti','This option affects the order confirmation page (thank you page) and order details on account pages, emails and packing slips.' => 'Chaguo hili linaathiri ukurasa wa uthibitishaji wa agizo (ukurasa wa asante) na uagize maelezo kwenye kurasa za akaunti, barua pepe na hati za kufunga.','Account matching' => 'Kulinganisha akaunti','Enable the account matching feature' => 'Washa kipengele cha kulinganisha akaunti','Associate the guest customer\'s orders with their existing account when an account already exists with the customer\'s contact details.' => 'Husisha maagizo ya mteja aliyealikwa na akaunti yake iliyopo wakati tayari kuna akaunti na maelezo ya mawasiliano ya mteja.','Display message when an account exists with the email address provided' => 'Onyesha ujumbe wakati akaunti ipo na anwani ya barua pepe iliyotolewa','Replaces the account creation fields with a notification and option to log in. In some contexts, it might be recommended to leave this option disabled to protect the privacy of customers.' => 'Hubadilisha sehemu za kuunda akaunti kwa arifa na chaguo la kuingia. Katika baadhi ya miktadha, inaweza kupendekezwa kuacha chaguo hili likiwa limezimwa ili kulinda faragha ya wateja.','Dashboard' => 'Dashibodi','Integrations' => 'Ushirikiano','No integrations available at the moment on this section. The options related to each plugin will only appear here if that plugin is activated.' => 'Hakuna miunganisho inayopatikana kwa sasa kwenye sehemu hii. Chaguo zinazohusiana na kila programu-jalizi zitaonekana hapa tu ikiwa programu-jalizi hiyo imewashwa.','License keys' => 'Vifunguo vya leseni','No license keys options available at the moment on this section. The options related to each plugin or add-on will appear here when that plugin is activated.' => 'Hakuna chaguo za vitufe vya leseni zinazopatikana kwa sasa kwenye sehemu hii. Chaguo zinazohusiana na kila programu-jalizi au programu-jalizi zitaonekana hapa programu-jalizi hiyo itakapowashwa.','Visit our website for more information about our plugins and add-ons.' => 'Tembelea tovuti yetu kwa habari zaidi kuhusu programu-jalizi zetu na nyongeza.','Order pay page' => 'Agiza ukurasa wa malipo','Order pay' => 'Malipo ya agizo','Enable order pay page optimizations' => 'Washa uboreshaji wa ukurasa wa malipo ya agizo','Changes the layout of order pay page for existing orders pending payment from the customer.' => 'Hubadilisha mpangilio wa ukurasa wa malipo ya agizo kwa maagizo yaliyopo yanayosubiri malipo kutoka kwa mteja.','Add widget areas to the order pay page' => 'Ongeza maeneo ya wijeti kwenye ukurasa wa malipo wa agizo','These widget areas are used to add trust symbols and trust badges on the order pay page.' => 'Maeneo haya ya wijeti hutumiwa kuongeza alama za uaminifu na beji za uaminifu kwenye ukurasa wa malipo wa agizo.','Thank you page & order details layout' => 'Asante ukurasa na mpangilio wa maelezo','Order details optimizations' => 'Uboreshaji wa maelezo ya agizo','Enable thank you page and order details optimizations' => 'Washa ukurasa wa asante na uagize uboreshaji wa maelezo','Changes the layout of order details on the thank you page and account pages.' => 'Hubadilisha mpangilio wa maelezo ya agizo kwenye ukurasa wa asante na kurasa za akaunti.','Enable order details customizations on email notifications' => 'Washa ubinafsishaji wa maelezo ya agizo kwenye arifa za barua pepe','Display new sections and change order of sections on email notifications. Styles of order details on email notifications remain unchanged.' => 'Onyesha sehemu mpya na ubadilishe mpangilio wa sehemu kwenye arifa za barua pepe. Mitindo ya maelezo ya agizo kwenye arifa za barua pepe bado haijabadilika.','Thank you page' => 'Asante ukurasa','These options affect the thank you page, also known as order received or order confirmation pages.' => 'Chaguo hizi huathiri ukurasa wa asante, unaojulikana pia kama kurasa za uthibitishaji wa agizo au agizo.','Order details layout' => 'Mpangilio wa maelezo ya agizo','Display the order details with wide layout on the thank you page' => 'Onyesha maelezo ya agizo kwa mpangilio mpana kwenye ukurasa wa asante','Add widget areas to the thank you page' => 'Ongeza maeneo ya wijeti kwenye ukurasa wa asante','These widget areas are used to add trust symbols and trust badges on the thank you page.' => 'Maeneo haya ya wijeti hutumiwa kuongeza alama za uaminifu na beji za uaminifu kwenye ukurasa wa asante.','These options affect the thank you page , view order details on account pages and on email notifications.' => 'Chaguo hizi huathiri ukurasa wa asante , angalia maelezo ya agizo kwenye kurasa za akaunti na arifa za barua pepe.','Order actions' => 'Agiza vitendo','Choose in which position to display the order actions.' => 'Chagua katika nafasi gani ya kuonyesha vitendo vya mpangilio.','Inside the order overview' => 'Ndani ya muhtasari wa agizo','After the order overview' => 'Baada ya muhtasari wa agizo','After order sections' => 'Baada ya sehemu za kuagiza','Order status' => 'Hali ya agizo','Display the order status progress bar' => 'Onyesha upau wa maendeleo ya hali ya agizo','Choose in which position to display the section.' => 'Chagua katika nafasi gani ya kuonyesha sehemu.','After the order overview and payment instructions' => 'Baada ya muhtasari wa agizo na maagizo ya malipo','Before the order items section' => 'Kabla ya sehemu ya vitu vya kuagiza','Inside the order items section' => 'Ndani ya sehemu ya vitu vya kuagiza','After the order items section' => 'Baada ya sehemu ya vitu vya kuagiza','On the sidebar' => 'Kwenye upau wa pembeni','Order downloads' => 'Agiza vipakuliwa','Gift message' => 'Ujumbe wa zawadi','Tools' => 'Zana','Troubleshooting' => 'Kutatua matatizo','Debug options' => 'Chaguzi za utatuzi','Debug mode' => 'Hali ya utatuzi','Using debug mode affects the website performance. Only use this option while troubleshooting.' => 'Kutumia hali ya utatuzi huathiri utendaji wa tovuti. Tumia chaguo hili pekee unapotatua matatizo.','Load unminified assets' => 'Pakia mali isiyopunguzwa','Enhanced select fields' => 'Uga zilizoimarishwa','Replace <code>select2</code> dropdown components with <code>TomSelect</code>' => 'Badilisha vipengee kunjuzi <code>select2</code> na <code>TomSelect</code>','TomSelect is a simpler dropdown selection component which is less prone to errors than Select2, while offering the same features that are actually used on WooCommerce checkout pages.' => 'TomSelect ni sehemu rahisi ya uteuzi wa menyu kunjuzi ambayo haielekei sana kwa makosa kuliko Select2, huku ikitoa vipengele vile vile ambavyo hutumika haswa kwenye kurasa za kulipia za WooCommerce.','Fix automatic zoom-in on form fields' => 'Rekebisha kuvuta kiotomatiki kwenye sehemu za fomu','Set <code>font-size</code> inside form fields to 16px' => 'Weka <code>font-size</code> ndani ya sehemu za fomu kuwa 16px','When the font size inside form fields is smaller than 16px, Safari and other browsers might automatically zoom in on mobile devices to make the text easier to read. When this option is enabled, it will set the font size for inside the form fields to 16px on pages optimized by Fluid Checkout to avoid it zooming in.' => 'Wakati ukubwa wa fonti ndani ya sehemu za fomu ni ndogo kuliko 16px, Safari na vivinjari vingine vinaweza kuvuta kiotomatiki kwenye vifaa vya mkononi ili kurahisisha maandishi kusoma. Chaguo hili likiwashwa, litaweka saizi ya fonti ya ndani ya sehemu za fomu hadi 16px kwenye kurasa zilizoboreshwa na Fluid Checkout ili kuiepusha kukuza ndani.','The shipping destination is always set to "Default to customer shipping address" when Fluid Checkout is activated. Customers can still provide different shipping and billing addresses during checkout, an option for setting the default billing address to be the same as the shipping address is available at WooCommerce > Settings > Fluid Checkout.' => 'Mahali pa usafirishaji kila mara huwekwa kuwa &quot;Anwani-msingi ya usafirishaji ya mteja&quot; wakati Fluid Checkout imewashwa. Wateja bado wanaweza kutoa anwani tofauti za usafirishaji na bili wakati wa kulipa, chaguo la kuweka anwani chaguo-msingi ya kutuma bili kuwa sawa na anwani ya usafirishaji inapatikana katika WooCommerce &gt; Mipangilio &gt; Malipo ya Maji.','Fluid Checkout' => 'Malipo ya Majimaji','Settings' => 'Mipangilio','Support' => 'Msaada','<a target="_blank" href="%s">Upgrade to PRO</a> to unlock more options.' => '<a target="_blank" href="%s">Pata toleo jipya la PRO</a> ili kufungua chaguo zaidi.','(PRO)' => '(PRO)','(experimental)' => '(majaribio)','This is an experimental feature and may not work as expected with all themes and plugins. Use it with caution.' => 'Hiki ni kipengele cha majaribio na huenda kisifanye kazi inavyotarajiwa na mandhari na programu-jalizi zote. Itumie kwa tahadhari.','Confirm' => 'Thibitisha','Verify Phone Number' => 'Thibitisha Nambari ya Simu','Please verify your phone number.' => 'Tafadhali thibitisha nambari yako ya simu.','The Bluehost Plugin' => 'Programu-jalizi ya Bluehost','Custom fields' => 'Sehemu maalum','Enable custom shipping email field' => 'Washa sehemu ya barua pepe maalum ya usafirishaji','When enabled, the shipping email field from the Bluehost plugin will be added to the checkout form.' => 'Ikiwezeshwa, sehemu ya barua pepe ya usafirishaji kutoka kwa programu-jalizi ya Bluehost itaongezwa kwenye fomu ya kulipa.','Captcha Pro by BestWebSoft' => 'Captcha Pro na BestWebSoft','Captcha position' => 'Nafasi ya Captcha','Define the position to display the captcha section. Some positions might not work depending on the captcha type chosen.' => 'Fafanua nafasi ya kuonyesha sehemu ya captcha. Baadhi ya nafasi zinaweza zisifanye kazi kulingana na aina ya captcha iliyochaguliwa.','Selecting a pickup point is required before proceeding.' => 'Inahitajika kuchagua mahali pa kuchukua kabla ya kuendelea.','Pickup point' => 'Sehemu ya kuchukua','Pickup point not selected yet.' => 'Eneo la kuchukua bado halijachaguliwa.','Pickup point:' => 'Mahali pa kuchukua:','Mailchimp for WooCommerce' => 'Mailchimp kwa WooCommerce','Subscribe checkbox' => 'Jisajili kisanduku tiki','Override the position for the subscribe checkbox on the checkout page and always display it on the contact step' => 'Batilisha nafasi ya kisanduku cha kuteua cha kujiandikisha kwenye ukurasa wa malipo na uionyeshe kila wakati kwenye hatua ya mawasiliano','Before Shipping Address' => 'Kabla ya Anwani ya Kusafirisha','(originally "%s")' => '(asili &quot;%s&quot;)','After Shipping Address' => 'Baada ya Anwani ya Usafirishaji','Selecting a collection point is required when shipping with FermoPoint.' => 'Inahitajika kuchagua mahali pa kukusanyia unaposafirisha ukitumia FermoPoint.','If checked informs typos in email to users. (This feature has been disabled because Fluid Checkout offers the feature. Changes to this option will not take effect)' => 'Ikiangaliwa hufahamisha typos katika barua pepe kwa watumiaji. (Kipengele hiki kimezimwa kwa sababu Malipo ya Majimaji hutoa kipengele. Mabadiliko ya chaguo hili hayatafanya kazi)','Enable Mail Check: (disabled feature)' => 'Washa Ukaguzi wa Barua: (kipengele kimezimwa)','Carrier:' => 'Mtoa huduma:','Sendinblue - WooCommerce Email Marketing' => 'Sendinblue - Uuzaji wa Barua pepe wa WooCommerce','Sign up checkbox position' => 'Jisajili nafasi ya kisanduku cha kuteua','Move the sign up checkbox to the contact step' => 'Sogeza kisanduku cha kuteua cha kujisajili hadi kwenye hatua ya mwasiliani','When enabled, the checkbox will be moved to the contact step independently of the display location defined in the Sendinblue plugin settings.' => 'Ikiwashwa, kisanduku cha kuteua kitahamishwa hadi hatua ya mwasiliani bila ya eneo la kuonyesha lililofafanuliwa katika mipangilio ya programu-jalizi ya Sendinblue.','Theme Betheme' => 'Mandhari ya mandhari','Checkout progress' => 'Maendeleo ya malipo','Output the checkout steps section from Betheme on the checkout, cart and order received pages.' => 'Toa sehemu ya hatua za kulipa kutoka kwa Betheme kwenye malipo, rukwama na uagize kurasa zilizopokelewa.','Theme Fennik' => 'Mandhari ya Fennik','Breadcrumbs' => 'Makombo ya mkate','Output the breadcrumbs section from the Fennik theme when using Fluid Checkout header and footer.' => 'Toa sehemu ya makombo kutoka kwa mandhari ya Fennik unapotumia kichwa na kijachini cha Fluid Checkout.','Theme Go' => 'Mandhari Nenda','My account layout' => 'Mpangilio wa akaunti yangu','Enable wide layout on "My account" page.' => 'Washa mpangilio mpana kwenye ukurasa wa &quot;Akaunti yangu&quot;.','Theme Impreza' => 'Mandhari Impreza','Header' => 'Kijajuu','Spacing for site header at the checkout page (in pixels)' => 'Nafasi ya kichwa cha tovuti kwenye ukurasa wa malipo (katika pikseli)','Only applicable when using the Impreza theme header at the checkout page.' => 'Inatumika tu unapotumia kichwa cha mandhari ya Impreza kwenye ukurasa wa malipo.','Theme Kapee' => 'Mandhari ya Kapee','Output the checkout steps section from the Kapee theme on the checkout, cart and order received pages.' => 'Toa sehemu ya hatua za kulipa kutoka kwa mandhari ya Kapee kwenye malipo, rukwama na agiza kurasa zilizopokelewa.','Theme Porto' => 'Mandhari ya Porto','Output the checkout steps section from the Porto theme on the checkout, cart and order received pages.' => 'Toa sehemu ya hatua za kulipa kutoka kwa mandhari ya Porto kwenye malipo, rukwama na uagize kurasa zilizopokewa.','Theme Pressmart' => 'Mada ya Pressmart','Output the checkout steps section from the Pressmart theme on the checkout, cart and order received pages.' => 'Toa sehemu ya hatua za kulipa kutoka kwa mandhari ya Pressmart kwenye sehemu ya kulipa, rukwama na uagize kurasa zilizopokewa.','Theme The Gem' => 'Mandhari ya Gem','Output the checkout steps section from The Gem theme when using Fluid Checkout header and footer.' => 'Toa sehemu ya hatua za kulipa kutoka kwenye mandhari ya Gem unapotumia kichwa na kijachini cha Malipo ya Maji.','Theme Woodmart' => 'Mandhari ya Woodmart','Output the checkout steps section from the Woodmart theme when using Fluid Checkout header and footer.' => 'Toa sehemu ya hatua za kulipa kutoka kwa mandhari ya Woodmart unapotumia kichwa na kijachini cha Fluid Checkout.','Checkout options' => 'Chaguzi za malipo','Disable the theme checkout options' => 'Zima chaguo za malipo ya mandhari','The options display product image, quantity field, remove button and link to product page added by the theme are disabled by default.' => 'Chaguzi zinazoonyesha picha ya bidhaa, uga wa kiasi, kitufe cha kuondoa na kiungo cha ukurasa wa bidhaa ulioongezwa na mandhari huzimwa kwa chaguomsingi.','Theme Atomion' => 'Mandhari ya Atomi','Order progress' => 'Agizo la maendeleo','Display order progress from the theme' => 'Onyesha maendeleo ya mpangilio kutoka kwa mada','Display the form field labels visible on the page' => 'Onyesha lebo za sehemu za fomu zinazoonekana kwenye ukurasa','It is recommended to keep this option enabled for a better user experience.' => 'Inapendekezwa kuwasha chaguo hili kwa matumizi bora ya mtumiaji.','Theme ZK Nito' => 'Mandhari ZK Nito','Add extra fields to the checkout form' => 'Ongeza sehemu za ziada kwenye fomu ya malipo','Adds an extra shipping email and shipping phone field to the shipping address section.' => 'Huongeza barua pepe ya ziada ya usafirishaji na sehemu ya simu ya usafirishaji kwenye sehemu ya anwani ya usafirishaji.','Go to the home page' => 'Nenda kwenye ukurasa wa nyumbani','Skip to content' => 'Ruka hadi yaliyomo','Create an account' => 'Fungua akaunti','Log in to your account' => 'Ingia kwenye akaunti yako','Already have an account?' => 'Je, tayari una akaunti?','Logged in as %1$s. <a href="%2$s">Log out</a>' => 'Umeingia kama %1$s. <a href="%2$s">Toka nje</a>','Your order has a total amount due of %s. No further payment is needed.' => 'Agizo lako lina jumla ya kiasi kinachodaiwa %s. Hakuna malipo zaidi yanayohitajika.','Close and continue with checkout' => 'Funga na uendelee na malipo','Continue' => 'Endelea','Parcel terminal' => 'Terminal ya sehemu','<p>This widget has been replaced by Fluid Checkout. </p><p>When using Fluid Checkout, the checkout page needs to use the original WooCommerce shortcode <code>[woocommerce_checkout]</code> to work properly.</p><p>For more information, visit the Fluid Checkout documentation.</p>' => '<p>Wijeti hii imebadilishwa na Malipo ya Maji.</p><p> Unapotumia Malipo ya Maji, ukurasa wa kulipa unahitaji kutumia njia fupi ya awali ya WooCommerce <code>[woocommerce_checkout]</code> ili kufanya kazi ipasavyo.</p><p> Kwa habari zaidi, tembelea hati za Malipo ya Maji.</p>','Theme The7' => 'Mandhari The7','Additional header sections' => 'Sehemu za ziada za kichwa','Output additional header sections if enabled in the theme settings.' => 'Toa sehemu za kichwa cha ziada ikiwashwa katika mipangilio ya mandhari.','More information' => 'Taarifa zaidi','Pick a Locker' => 'Chagua Locker','Page template' => 'Kiolezo cha ukurasa','Use `Order confirmation` page template for block-based themes' => 'Tumia kiolezo cha ukurasa wa `Uthibitishaji wa Agizo` kwa mandhari yenye msingi wa kuzuia','Only applies when using a block-based theme.' => 'Inatumika tu wakati wa kutumia mandhari yenye msingi wa kizuizi.','Button label for applying coupon codesApply' => 'Omba','Checkout step titleContact' => 'Wasiliana','Checkout step titleShipping' => 'Usafirishaji','Checkout step titleBilling' => 'Bili','Checkout step titlePayment' => 'Malipo','Checkout substep change link labelChange' => 'Badilika','Checkout substep save link labelSave changes' => 'Hifadhi mabadiliko','Substep review textNone.' => 'Hakuna.','Substep review field format%1$s' => '%1$s','Substep review field format: with label%2$s: %1$s' => '%2$s: %1$s','Image uploader.No image selected.' => 'Hakuna picha iliyochaguliwa.','Settings sectionCart' => 'Mkokoteni','Settings sectionOrder pay' => 'Malipo ya agizo','Settings sectionThank you' => 'Asante','Settings section titleIntegrations' => 'Ushirikiano','Settings section titleLicense keys' => 'Vifunguo vya leseni','Captcha positionBefore place order section' => 'Kabla ya sehemu ya kuagiza','Captcha positionBefore place order button' => 'Kabla ya kuweka kitufe','No shipping method chosen label for the order summary--' => '--','Close button aria-labelClose' => 'Funga','Log in link label at checkout contact stepLog in' => 'Ingia','Log in separator label at for when guest checkout is disabledOr continue below' => 'Au endelea hapa chini','Log in separator label at for when guest checkout is disabledOr continue as a guest' => 'Au endelea kama mgeni',] ];