return [ 'project-id-version' => 'fluid-checkout','mime-version' => '1.0','content-type' => 'text/plain; charset=utf-8','content-transfer-encoding' => '8bit','language-team' => 'Fluid Checkout','last-translator' => 'Fluid Checkout','pot-creation-date' => '2025-03-06 01:48+0000','report-msgid-bugs-to' => 'https://fluidcheckout.com/support','x-poedit-basepath' => '..','x-poedit-keywordslist' => '__;_e;_ex:1,2c;_n:1,2;_n_noop:1,2;_nx:1,2,4c;_nx_noop:1,2,3c;_x:1,2c;esc_attr__;esc_attr_e;esc_attr_x:1,2c;esc_html__;esc_html_e;esc_html_x:1,2c','x-poedit-searchpath-0' => '.','x-poedit-searchpathexcluded-0' => '*.js','x-poedit-sourcecharset' => 'UTF-8','plural-forms' => 'nplurals=2; plural=(n != 1);','messages' => ['WooCommerce' => 'வூகாமர்ஸ்','Go to Plugin Search' => 'செருகுநிரல் தேடலுக்குச் செல்லவும்.','Activate %s' => '%s ஐ செயல்படுத்து','<strong>%1$s</strong> requires <strong>%2$s</strong> to be installed and activated. <a href="%4$s">%3$s</a>' => '<strong>%1$s</strong> க்கு <strong>%2$s</strong> நிறுவப்பட்டு செயல்படுத்தப்பட வேண்டும். <a href="%4$s">%3$s</a>','Enter your code here' => 'உங்கள் குறியீட்டை இங்கே உள்ளிடவும்.','Add %s' => '%s ஐச் சேர்','Remove' => 'அகற்று','Order number and receipt will be sent to this email address.' => 'ஆர்டர் எண் மற்றும் ரசீது இந்த மின்னஞ்சல் முகவரிக்கு அனுப்பப்படும்.','Apartment, unit, building, floor, etc.' => 'அபார்ட்மெண்ட், யூனிட், கட்டிடம், தளம், முதலியன.','Shipping phone' => 'ஷிப்பிங் தொலைபேசி எண்','Only used for shipping-related questions.' => 'கப்பல் போக்குவரத்து தொடர்பான கேள்விகளுக்கு மட்டுமே பயன்படுத்தப்படுகிறது.','Multi-step' => 'பல-படி','Single step' => 'ஒற்றை படி','Click to go to the order summary' => 'ஆர்டர் சுருக்கத்திற்குச் செல்ல கிளிக் செய்யவும்.','Cart total:' => 'மொத்த கூடை:','Proceed to %s' => '%s க்குச் செல்','Proceed to contact' => 'தொடர்புக்குச் செல்லவும்','Proceed to shipping' => 'அனுப்புவதற்குத் தொடரவும்','Proceed to billing' => 'பில்லிங்கிற்குச் செல்லவும்','Proceed to payment' => 'பணம் செலுத்தத் தொடங்குங்கள்','My contact' => 'எனது தொடர்பு','Shipping to' => 'அனுப்புதல்','Shipping method' => 'அனுப்பும் முறை','Additional notes' => 'கூடுதல் குறிப்புகள்','Billing to' => 'பில்லிங்','Payment method' => 'கட்டணம் செலுத்தும் முறை','Step %1$s of %2$s' => '%2$s இல் %1$s படி','Change: %s' => 'மாற்றம்: %s','Not selected yet.' => 'இன்னும் தேர்ந்தெடுக்கப்படவில்லை.','Same as shipping address' => 'ஷிப்பிங் முகவரியைப் போலவே','Same as billing address' => 'பில்லிங் முகவரியைப் போலவே','Order summary' => 'ஆர்டர் சுருக்கம்','Edit cart' => 'கூடையைத் திருத்து','Did you mean %s?' => 'நீங்க %s-ஐயா சொன்னீங்க?','Change email address to: {suggestion-value}' => 'மின்னஞ்சல் முகவரியை {suggestion-value} ஆக மாற்றவும்.','This is a required field.' => 'இது அவசியமான புலம்.','This is not a valid email address.' => 'இது செல்லுபடியாகும் மின்னஞ்சல் முகவரி அல்ல.','This field does not match the related field value.' => 'இந்தப் புலம் தொடர்புடைய புல மதிப்புடன் பொருந்தவில்லை.','The CPF number "{cpf_number}" is invalid.' => '&quot;{cpf_number}&quot; என்ற CPF எண் தவறானது.','The CNPJ number "{cnpj_number}" is invalid.' => 'CNPJ எண் &quot;{cnpj_number}&quot; தவறானது.','(Required)' => '(தேவை)','Checkout Header - Desktop' => 'செக்அவுட் தலைப்பு - டெஸ்க்டாப்','Display widgets on the checkout header for large screens. Only displayed if using the plugin\'s checkout header.' => 'பெரிய திரைகளுக்கான செக்அவுட் தலைப்பில் விட்ஜெட்களைக் காண்பி. செருகுநிரலின் செக்அவுட் தலைப்பைப் பயன்படுத்தினால் மட்டுமே காண்பிக்கப்படும்.','Checkout Header - Mobile' => 'செக்அவுட் தலைப்பு - மொபைல்','Display widgets below the checkout header for mobile devices. Only displayed if using the plugin\'s checkout header.' => 'மொபைல் சாதனங்களுக்கான செக்அவுட் தலைப்புக்குக் கீழே விட்ஜெட்களைக் காண்பி. செருகுநிரலின் செக்அவுட் தலைப்பு பயன்படுத்தினால் மட்டுமே காண்பிக்கப்படும்.','Checkout Sidebar' => 'பக்கப்பட்டியைப் பாருங்கள்','Display widgets on the checkout sidebar after the order summary.' => 'ஆர்டர் சுருக்கத்திற்குப் பிறகு செக்அவுட் பக்கப்பட்டியில் விட்ஜெட்களைக் காண்பி.','Checkout Order Summary' => 'செக்அவுட் ஆர்டர் சுருக்கம்','Display widgets inside the order summary at the checkout page.' => 'செக்அவுட் பக்கத்தில் ஆர்டர் சுருக்கத்திற்குள் விட்ஜெட்களைக் காண்பி.','Checkout Below Place Order' => 'இட வரிசைக்கு கீழே செக்அவுட் செய்யவும்','Display widgets below the place order button at the checkout page.' => 'செக்அவுட் பக்கத்தில் இட வரிசை பொத்தானுக்குக் கீழே விட்ஜெட்களைக் காண்பி.','Checkout Footer' => 'செக்அவுட் அடிக்குறிப்பு','Display widgets on the checkout footer. Only displayed if using the plugin\'s checkout footer.' => 'செக்அவுட் அடிக்குறிப்பில் விட்ஜெட்களைக் காண்பி. செருகுநிரலின் செக்அவுட் அடிக்குறிப்பைப் பயன்படுத்தினால் மட்டுமே காண்பிக்கப்படும்.','Classic' => 'கிளாசிக்','Boxed' => 'பெட்டி','Split' => 'பிரி','Minimalist' => 'மினிமலிஸ்ட்','Fluid Checkout and Delivery Date plugins by CodeRockz' => 'CodeRockz வழங்கும் திரவ செக்அவுட் மற்றும் டெலிவரி தேதி செருகுநிரல்கள்','<p>We detected that you are using the plugins Delivery & Pickup Date Time for WooCommerce (free and PRO) by CodeRockz. Integration with these plugins have been moved to Fluid Checkout PRO.</p>' => '<p>நீங்கள் CodeRockz இன் WooCommerce (இலவசம் மற்றும் PRO) க்கான டெலிவரி &amp; பிக்அப் தேதி நேரம் செருகுநிரல்களைப் பயன்படுத்துவதை நாங்கள் கண்டறிந்துள்ளோம். இந்த செருகுநிரல்களுடனான ஒருங்கிணைப்பு Fluid Checkout PRO க்கு நகர்த்தப்பட்டுள்ளது.</p>','Upgrade to PRO' => 'PRO க்கு மேம்படுத்து','Fluid Checkout database updated successfully' => 'திரவ செக்அவுட் தரவுத்தளம் வெற்றிகரமாக புதுப்பிக்கப்பட்டது.','The database changes where applied successfully.' => 'வெற்றிகரமாகப் பயன்படுத்தப்படும் இடத்தில் தரவுத்தளம் மாறுகிறது.','Ok' => 'சரி','Dismiss' => 'நிராகரி','Fluid Checkout database update' => 'திரவ செக்அவுட் தரவுத்தள புதுப்பிப்பு','Some changes to the database are required. As with any update, we recommend you to <strong>take a full backup of your website before proceeding</strong>.' => 'தரவுத்தளத்தில் சில மாற்றங்கள் தேவை. எந்தவொரு புதுப்பிப்பையும் போலவே, <strong>தொடர்வதற்கு முன் உங்கள் வலைத்தளத்தின் முழு காப்புப்பிரதியை எடுக்க</strong> பரிந்துரைக்கிறோம்.','Update database' => 'தரவுத்தளத்தைப் புதுப்பிக்கவும்','Fluid Checkout is not compatible with the Divi Builder checkout layout' => 'திரவ செக்அவுட், Divi Builder செக்அவுட் தளவமைப்புடன் இணக்கமாக இல்லை.','<p>Fluid Checkout needs the classic WooCommerce shortcode-based checkout form to work. When using the Divi Builder to customize the look and feel of the checkout page, the classic shortcodes are replaced with custom widgets from the Divi Builder for each part of the checkout page. These custom widgets are not compatible with Fluid Checkout.</p><p>Please edit your checkout page replacing the Divi Builder widgets with the a text widget containing the shortcode <code>[woocommerce_checkout]</code>.</p>' => '<p>Fluid Checkout வேலை செய்ய கிளாசிக் WooCommerce ஷார்ட்கோட் அடிப்படையிலான செக்அவுட் படிவம் தேவை. செக்அவுட் பக்கத்தின் தோற்றத்தையும் உணர்வையும் தனிப்பயனாக்க Divi Builder ஐப் பயன்படுத்தும் போது, செக்அவுட் பக்கத்தின் ஒவ்வொரு பகுதிக்கும் கிளாசிக் ஷார்ட்கோடுகள் Divi Builder இலிருந்து தனிப்பயன் விட்ஜெட்களால் மாற்றப்படும். இந்த தனிப்பயன் விட்ஜெட்டுகள் Fluid Checkout உடன் இணக்கமாக இல்லை.</p><p> Divi Builder விட்ஜெட்களுக்குப் பதிலாக <code>[woocommerce_checkout]</code> என்ற சுருக்குக்குறியீட்டைக் கொண்ட உரை விட்ஜெட்டைப் பயன்படுத்தி உங்கள் செக்அவுட் பக்கத்தைத் திருத்தவும்.</p>','Edit the checkout page' => 'செக்அவுட் பக்கத்தைத் திருத்தவும்','Read the documentation.' => 'ஆவணங்களைப் படியுங்கள்.','Germanized PRO multistep checkout feature needs to be disabled when using Fluid Checkout' => 'திரவ செக்அவுட்டைப் பயன்படுத்தும் போது ஜெர்மன்மயமாக்கப்பட்ட PRO பலபடி செக்அவுட் அம்சத்தை முடக்க வேண்டும்.','When using Fluid Checkout, the multistep feature from the Germanized PRO plugin becomes unnecessary and can cause <strong>critical errors while processing new orders</strong>. Please disable the Germanized PRO multistep checkout feature in the plugin settings.' => 'Fluid Checkout-ஐப் பயன்படுத்தும் போது, Germanized PRO plugin-இன் multistep அம்சம் தேவையற்றதாகி, <strong>புதிய ஆர்டர்களைச் செயல்படுத்தும்போது முக்கியமான பிழைகளை</strong> ஏற்படுத்தக்கூடும். plugin அமைப்புகளில் Germanized PRO multistep checkout அம்சத்தை முடக்கவும்.','Go to Germanized settings' => 'ஜெர்மன்மயமாக்கப்பட்ட அமைப்புகளுக்குச் செல்லவும்.','Thanks for choosing Fluid Checkout!' => 'திரவ செக்அவுட்டைத் தேர்ந்தெடுத்ததற்கு நன்றி!','You have been using the plugin for a while now. How do you like it so far? <br>Please give us a quick rating, we appreciate every bit of feedback and it encourages us to keep improving the plugin :)' => 'நீங்க கொஞ்ச நாளா இந்த ப்ளக் இன் யூஸ் பண்ணிட்டு இருக்கீங்க. இதுவரைக்கும் உங்களுக்கு இது எப்படிப் பிடிச்சிருக்கு?<br> தயவுசெய்து எங்களுக்கு ஒரு விரைவான மதிப்பீட்டை வழங்குங்கள், ஒவ்வொரு பின்னூட்டத்திற்கும் நாங்கள் நன்றி கூறுகிறோம், மேலும் இது செருகுநிரலை தொடர்ந்து மேம்படுத்த எங்களை ஊக்குவிக்கிறது :)','Rate the plugin' => 'செருகுநிரலை மதிப்பிடவும்.','I need help!' => 'எனக்கு உதவி தேவை!','Some features of WooCommerce Checkout Manager by Quadlayers might not work as expected when using it with Fluid Checkout. Read <a href="%s">our documentation</a> for more information and alternative solutions.' => 'Quadlayers வழங்கும் WooCommerce Checkout Manager இன் சில அம்சங்கள், Fluid Checkout உடன் பயன்படுத்தும் போது எதிர்பார்த்தபடி வேலை செய்யாமல் போகலாம். மேலும் தகவல் மற்றும் மாற்று தீர்வுகளுக்கு <a href="%s">எங்கள் ஆவணங்களைப்</a> படியுங்கள்.','Read documentation' => 'ஆவணங்களைப் படியுங்கள்','Don\'t show this again' => 'இதை மீண்டும் காட்டாதே','Special offers' => 'சிறப்பு சலுகைகள்','Extend all the goodness of Fluid Checkout to your cart page and thank you pages.' => 'உங்கள் கார்ட் பக்கம் மற்றும் நன்றி பக்கங்களுக்கு திரவ செக்அவுட்டின் அனைத்து நன்மைகளையும் விரிவுபடுத்துங்கள்.','Fluid Checkout PRO' => 'திரவ செக்அவுட் புரோ','Design templates, optimized cart and order received pages, account matching, and much more.' => 'வடிவமைப்பு டெம்ப்ளேட்கள், மேம்படுத்தப்பட்ட வண்டி மற்றும் ஆர்டர் பெறப்பட்ட பக்கங்கள், கணக்கு பொருத்தம் மற்றும் பல.','The best tools to further <strong>improve your WooCommerce checkout conversion rate</strong> and make the purchase experience consistent on the entire journey.' => '<strong>உங்கள் WooCommerce செக்அவுட் மாற்று விகிதத்தை மேலும் மேம்படுத்தவும்</strong> , முழு பயணத்திலும் கொள்முதல் அனுபவத்தை சீரானதாக மாற்றவும் சிறந்த கருவிகள்.','More design templates' => 'மேலும் வடிவமைப்பு வார்ப்புருக்கள்','Optimized cart page' => 'மேம்படுத்தப்பட்ட வண்டி பக்கம்','Optimized order received / thank you page' => 'மேம்படுத்தப்பட்ட ஆர்டர் பெறப்பட்டது / நன்றி பக்கம்','Edit cart contents at checkout page' => 'செக்அவுட் பக்கத்தில் கூடை உள்ளடக்கங்களைத் திருத்தவும்.','Account matching / user matching' => 'கணக்கு பொருத்தம் / பயனர் பொருத்தம்','Express Checkout buttons from supported payment methods' => 'ஆதரிக்கப்படும் கட்டண முறைகளிலிருந்து எக்ஸ்பிரஸ் செக்அவுட் பொத்தான்கள்','Trust symbols on the cart and thank you pages' => 'வண்டியிலும் நன்றி பக்கங்களிலும் உள்ள சின்னங்களை நம்புங்கள்.','More positions for the coupon code on the checkout page' => 'செக் அவுட் பக்கத்தில் கூப்பன் குறியீட்டிற்கான கூடுதல் நிலைகள்','Integrated coupon code field on the cart page' => 'கார்ட் பக்கத்தில் ஒருங்கிணைந்த கூப்பன் குறியீடு புலம்','International phone numbers' => 'சர்வதேச தொலைபேசி எண்கள்','Gift messages and packing slips' => 'பரிசுச் செய்திகளும் பேக்கிங் சீட்டுகளும்','Local pickup and in-store collection' => 'உள்ளூர் பிக்அப் மற்றும் கடையில் சேகரிப்பு','Upgrade to PRO &mdash; 99 EUR' => 'PRO க்கு மேம்படுத்தவும் — 99 யூரோ','Fluid Checkout PRO – Bundle' => 'திரவ செக்அவுட் புரோ - பண்டில்','All PRO features + All add-ons for a special price.' => 'அனைத்து PRO அம்சங்களும் + சிறப்பு விலையில் அனைத்து துணை நிரல்களும்.','The bundle includes all add-ons we currently offer and Fluid Checkout add-ons we launch in the future.' => 'இந்தத் தொகுப்பில் நாங்கள் தற்போது வழங்கும் அனைத்து ஆட்-ஆன்களும், எதிர்காலத்தில் நாங்கள் தொடங்கும் ஃபிளூயிட் செக்அவுட் ஆட்-ஆன்களும் அடங்கும்.','226 EUR value (Save 42%)' => '226 யூரோ மதிப்பு (42% சேமிக்கவும்)','Google Address Autocomplete' => 'கூகிள் முகவரி தானியங்குநிரப்புதல்','Address Book' => 'முகவரி புத்தகம்','EU-VAT Assistant' => 'EU-VAT உதவியாளர்','All future add-ons *' => 'அனைத்து எதிர்கால துணை நிரல்களும் *','* For as long as you have an active subscription.' => '* உங்களிடம் செயலில் சந்தா இருக்கும் வரை.','Get the Bundle – Only 129 EUR' => 'தொகுப்பைப் பெறுங்கள் - 129 யூரோ மட்டுமே.','All prices shown in EUR. If there are any divergencies with the prices on our website, the offers shown on the website superseed these and will be applied.' => 'அனைத்து விலைகளும் EUR இல் காட்டப்பட்டுள்ளன. எங்கள் வலைத்தளத்தில் உள்ள விலைகளில் ஏதேனும் வேறுபாடுகள் இருந்தால், வலைத்தளத்தில் காட்டப்பட்டுள்ள சலுகைகள் இவற்றை விட அதிகமாக இருக்கும், மேலும் அவை பயன்படுத்தப்படும்.','Add-ons' => 'துணை நிரல்கள்','Enhance your checkout experience with these add-ons.' => 'இந்த துணை நிரல்களுடன் உங்கள் செக் அவுட் அனுபவத்தை மேம்படுத்தவும்.','I already have it – Hide this offer' => 'எனக்கு ஏற்கனவே இது உள்ளது - இந்த சலுகையை மறை.','Installed &mdash; Activate' => 'நிறுவப்பட்டது — செயல்படுத்து','Activated' => 'செயல்படுத்தப்பட்டது','Up to 40% less checkout fields to fill in.' => 'நிரப்ப வேண்டிய செக்அவுட் புலங்கள் 40% வரை குறைவாக இருக்கும்.','Avoid delivery delays and unsatisfied customers. Collect the <strong>correct address information</strong> from the first time they buy with you.' => 'டெலிவரி தாமதங்கள் மற்றும் திருப்தியடையாத வாடிக்கையாளர்களைத் தவிர்க்கவும். அவர்கள் உங்களிடம் முதல் முறை வாங்கும் போது <strong>சரியான முகவரித் தகவலைச்</strong> சேகரிக்கவும்.','Get this add-on &mdash; 29 EUR' => 'இந்த ஆட்-ஆனைப் பெறுங்கள் — 29 யூரோ','Address book' => 'முகவரி புத்தகம்','Multiple saved addresses for shipping and billing.' => 'ஷிப்பிங் மற்றும் பில்லிங்கிற்காக பல சேமிக்கப்பட்ட முகவரிகள்.','Let customers <strong>save multiple shipping and billing addresses</strong> on their account and choose which ones to use at checkout and cart pages.' => 'வாடிக்கையாளர்கள் தங்கள் கணக்கில் <strong>பல ஷிப்பிங் மற்றும் பில்லிங் முகவரிகளைச் சேமித்து</strong> , செக்அவுட் மற்றும் கார்ட் பக்கங்களில் எவற்றைப் பயன்படுத்த வேண்டும் என்பதைத் தேர்வுசெய்ய அனுமதிக்கவும்.','Get this add-on &mdash; 59 EUR' => 'இந்த ஆட்-ஆனைப் பெறுங்கள் — 59 யூரோ','Simplified EU-VAT validation for your store.' => 'உங்கள் கடைக்கான எளிமைப்படுத்தப்பட்ட EU-VAT சரிபார்ப்பு.','Collect and <strong>validate EU VAT numbers at the checkout page</strong>, removes tax charges on reverse charge basis and confirms customer location when needed.' => '<strong>செக்அவுட் பக்கத்தில் EU VAT எண்களைச் சேகரித்து சரிபார்க்கவும்</strong> , தலைகீழ் கட்டண அடிப்படையில் வரி கட்டணங்களை நீக்கவும் மற்றும் தேவைப்படும்போது வாடிக்கையாளர் இருப்பிடத்தை உறுதிப்படுத்தவும்.','Get this add-on &mdash; 39 EUR' => 'இந்த ஆட்-ஆனைப் பெறுங்கள் — 39 யூரோ','All add-ons are sold separately and require the <strong>Fluid Checkout Lite</strong> plugin to be installed and activated, except when noted.' => 'அனைத்து துணை நிரல்களும் தனித்தனியாக விற்கப்படுகின்றன, மேலும் குறிப்பிடப்பட்டவை தவிர, <strong>திரவ செக்அவுட் லைட்</strong> செருகுநிரலை நிறுவி செயல்படுத்த வேண்டும்.','Select an image' => 'ஒரு படத்தைத் தேர்ந்தெடுக்கவும்','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to get your license key, or <a href="%s" target="_blank">purchase a new license key</a>.' => 'உங்கள் உரிம சாவியைப் பெற <a href="https://fluidcheckout.com/account/" target="_blank">உங்கள் கணக்கில் உள்நுழையவும்</a> அல்லது <a href="%s" target="_blank">புதிய உரிம சாவியை வாங்கவும்</a> .','Valid until %s.' => '%s வரை செல்லுபடியாகும்.','Expired on %s.' => '%s அன்று காலாவதியானது.','<a href="https://fluidcheckout.com/account/" target="_blank">Log in to your account</a> to renew your license key and continue to receive updates and support.' => 'உங்கள் உரிம விசையைப் புதுப்பிக்க <a href="https://fluidcheckout.com/account/" target="_blank">உங்கள் கணக்கில் உள்நுழைந்து</a> புதுப்பிப்புகள் மற்றும் ஆதரவைத் தொடர்ந்து பெறுங்கள்.','License key cancelled.' => 'உரிமச் சாவி ரத்து செய்யப்பட்டது.','<a href="%s" target="_blank">Purchase a new license key</a> and continue to receive updates and support.' => '<a href="%s" target="_blank">புதிய உரிம விசையை வாங்கி</a> , புதுப்பிப்புகள் மற்றும் ஆதரவைத் தொடர்ந்து பெறுங்கள்.','Error: ' => 'பிழை: ','Setup & Documentation' => 'அமைப்பு &amp; ஆவணப்படுத்தல்','Great! Your checkout page is now running on Fluid Checkout.' => 'அருமை! உங்கள் செக்அவுட் பக்கம் இப்போது Fluid Checkout-இல் இயங்குகிறது.','Here are a few resources for you to get started:' => 'நீங்கள் தொடங்குவதற்கு சில ஆதாரங்கள் இங்கே:','Read the installation guide <a href="%s" target="_blank">Getting stated with Fluid Checkout</a>.' => 'நிறுவல் வழிகாட்டியைப் படியுங்கள் <a href="%s" target="_blank">திரவ செக்அவுட் மூலம் கூறப்பட்டது</a> .','Setup layout and design on the <a href="%s">checkout options</a>.' => '<a href="%s">செக்அவுட் விருப்பங்களில்</a> தளவமைப்பு மற்றும் வடிவமைப்பை அமைக்கவும்.','Check if there are any <a href="%s">integration options</a> available for other plugins you have installed.' => 'நீங்கள் நிறுவியுள்ள பிற செருகுநிரல்களுக்கு ஏதேனும் <a href="%s">ஒருங்கிணைப்பு விருப்பங்கள்</a> உள்ளதா எனச் சரிபார்க்கவும்.','Visit <a href="%s" target="_blank">our documentation</a> for more information about Fluid Checkout features.' => 'திரவ செக்அவுட் அம்சங்களைப் பற்றிய கூடுதல் தகவலுக்கு <a href="%s" target="_blank">எங்கள் ஆவணங்களைப்</a> பார்வையிடவும்.','If you ever need help, <a href="%s" target="_blank">open a support ticket</a> on our official support channel.' => 'உங்களுக்கு எப்போதாவது உதவி தேவைப்பட்டால், எங்கள் அதிகாரப்பூர்வ ஆதரவு சேனலில் <a href="%s" target="_blank">ஒரு ஆதரவு டிக்கெட்டைத் திறக்கவும்</a> .','Cart layout' => 'வண்டி அமைப்பு','Cart optimizations' => 'வண்டி மேம்படுத்தல்கள்','Enable cart page optimizations' => 'கார்ட் பக்க மேம்படுத்தல்களை இயக்கு','Display the cart page with a layout similar to the checkout page.' => 'செக்அவுட் பக்கத்தைப் போன்ற தளவமைப்போடு வண்டிப் பக்கத்தைக் காண்பி.','Header and footer' => 'தலைப்பு மற்றும் அடிக்குறிப்பு','Header and footer template' => 'தலைப்பு மற்றும் அடிக்குறிப்பு டெம்ப்ளேட்','Controls whether to use the distraction free page header and footer or keep the currently active theme\'s header and footer.' => 'கவனச்சிதறல் இல்லாத பக்க தலைப்பு மற்றும் அடிக்குறிப்பைப் பயன்படுத்துவதா அல்லது தற்போது செயலில் உள்ள கருப்பொருளின் தலைப்பு மற்றும் அடிக்குறிப்பைத் தக்கவைத்துக்கொள்வதா என்பதைக் கட்டுப்படுத்துகிறது.','Distraction free header and footer' => 'கவனச்சிதறல் இல்லாத தலைப்பு மற்றும் அடிக்குறிப்பு','Theme\'s header and footer' => 'தீமின் தலைப்பு மற்றும் அடிக்குறிப்பு','Cart elements' => 'கூடை கூறுகள்','Make the order summary stay visible on the cart page while scrolling' => 'ஸ்க்ரோல் செய்யும் போது ஆர்டர் சுருக்கம் கார்ட் பக்கத்தில் தெரியும்படி வைக்கவும்.','Coupon codes' => 'கூப்பன் குறியீடுகள்','Select position where to display the coupon codes section on the cart page. Only applicable when AJAX cart and the integrated coupon codes in the checkout page are enabled.' => 'கார்ட் பக்கத்தில் கூப்பன் குறியீடுகள் பிரிவைக் காண்பிக்க வேண்டிய இடத்தைத் தேர்ந்தெடுக்கவும். AJAX கார்ட் மற்றும் செக்அவுட் பக்கத்தில் ஒருங்கிணைந்த கூப்பன் குறியீடுகள் இயக்கப்பட்டிருக்கும் போது மட்டுமே பொருந்தும்.','Hidden' => 'மறைக்கப்பட்டது','Before the cart items section' => 'கூடை பொருட்கள் பிரிவுக்கு முன்','Inside the cart items section' => 'வண்டி பொருட்கள் பிரிவின் உள்ளே','After the cart items section' => 'கூடை பொருட்கள் பிரிவுக்குப் பிறகு','Before the shipping section' => 'கப்பல் பிரிவுக்கு முன்','After the shipping section' => 'கப்பல் பிரிவுக்குப் பிறகு','Before the order summary' => 'ஆர்டர் சுருக்கத்திற்கு முன்','Inside the order summary' => 'ஆர்டர் சுருக்கத்தின் உள்ளே','Before the order totals' => 'ஆர்டர் மொத்தமாக வருவதற்கு முன்பு','After the order totals' => 'ஆர்டர் மொத்தத்திற்குப் பிறகு','After the order summary' => 'ஆர்டர் சுருக்கத்திற்குப் பிறகு','Shipping' => 'கப்பல் போக்குவரத்து','Select position where to display the shipping costs and calculator on the cart page.' => 'வண்டி பக்கத்தில் ஷிப்பிங் செலவுகள் மற்றும் கால்குலேட்டரைக் காண்பிக்க வேண்டிய இடத்தைத் தேர்ந்தெடுக்கவும்.','Display as a cart section' => 'கூடைப் பிரிவாகக் காட்டு','Cross-sells' => 'குறுக்கு விற்பனைகள்','Select position where to display the cross-sells section on the cart page.' => 'வண்டி பக்கத்தில் குறுக்கு விற்பனைப் பிரிவைக் காண்பிக்க வேண்டிய இடத்தைத் தேர்ந்தெடுக்கவும்.','Before the cart actions' => 'வண்டி நடவடிக்கைகளுக்கு முன்','After the cart actions' => 'வண்டி நடவடிக்கைகளுக்குப் பிறகு','Layout of cross-sell items on the cart page.' => 'வண்டி பக்கத்தில் குறுக்கு விற்பனை பொருட்களின் தளவமைப்பு.','Optimized horizontal cross-sells layout' => 'உகந்த கிடைமட்ட குறுக்கு விற்பனை தளவமைப்பு','Theme\'s cross-sells layout' => 'கருப்பொருளின் குறுக்கு விற்பனை தளவமைப்பு','Number of cross-sell items to display on the cart page' => 'கூடை பக்கத்தில் காண்பிக்க வேண்டிய குறுக்கு விற்பனை பொருட்களின் எண்ணிக்கை.','Trust symbols &amp; badges' => 'நம்பிக்கை சின்னங்கள் &amp; பேட்ஜ்கள்','Add widget areas to the cart page' => 'வண்டிப் பக்கத்தில் விட்ஜெட் பகுதிகளைச் சேர்க்கவும்.','These widget areas are used to add trust symbols and trust badges on the cart page.' => 'இந்த விட்ஜெட் பகுதிகள் கார்ட் பக்கத்தில் நம்பிக்கை சின்னங்கள் மற்றும் நம்பிக்கை பேட்ஜ்களைச் சேர்க்கப் பயன்படுகின்றன.','Checkout' => 'சரிபார்','Layout &amp; design' => 'தளவமைப்பு &amp; வடிவமைப்பு','Checkout layout' => 'செக்அவுட் தளவமைப்பு','Design template' => 'வடிவமைப்பு டெம்ப்ளேட்','General styles for the checkout steps, order summary and other sections. Might also apply to other pages such as the Cart, Order Received and View Order pages.' => 'செக் அவுட் படிகள், ஆர்டர் சுருக்கம் மற்றும் பிற பிரிவுகளுக்கான பொதுவான பாணிகள். வண்டி, பெறப்பட்ட ஆர்டர் மற்றும் பார்வை ஆர்டர் பக்கங்கள் போன்ற பிற பக்கங்களுக்கும் பொருந்தக்கூடும்.','Dark mode' => 'இருண்ட பயன்முறை','Enable dark mode' => 'டார்க் பயன்முறையை இயக்கு','Header and footer templates' => 'தலைப்பு மற்றும் அடிக்குறிப்பு வார்ப்புருக்கள்','We recommend using the distraction free header and footer to avoid distractions at the checkout page. <a href="https://baymard.com/blog/cart-abandonment" target="_blank">Read the research about cart abandonment</a>.' => 'செக்அவுட் பக்கத்தில் கவனச்சிதறல்களைத் தவிர்க்க கவனச்சிதறல் இல்லாத தலைப்பு மற்றும் அடிக்குறிப்பைப் பயன்படுத்த பரிந்துரைக்கிறோம். <a href="https://baymard.com/blog/cart-abandonment" target="_blank">வண்டி கைவிடுதல் பற்றிய ஆராய்ச்சியைப் படியுங்கள்</a> .','Logo image' => 'லோகோ படம்','Choose an image to be displayed on the checkout page header. Only applies when using the distraction free header and footer.' => 'செக்அவுட் பக்க தலைப்பில் காட்டப்பட வேண்டிய படத்தைத் தேர்வுசெய்யவும். கவனச்சிதறல் இல்லாத தலைப்பு மற்றும் அடிக்குறிப்பைப் பயன்படுத்தும் போது மட்டுமே இது பொருந்தும்.','Header background color' => 'தலைப்பு பின்னணி நிறம்','Choose a background color for the checkout page header. Only applies when using the distraction free header and footer.' => 'செக்அவுட் பக்க தலைப்புக்கு பின்னணி நிறத்தைத் தேர்வுசெய்யவும். கவனச்சிதறல் இல்லாத தலைப்பு மற்றும் அடிக்குறிப்பைப் பயன்படுத்தும் போது மட்டுமே இது பொருந்தும்.','HTML color value. ie: #f3f3f3' => 'HTML வண்ண மதிப்பு. அதாவது: #f3f3f3','Page background color' => 'பக்க பின்னணி நிறம்','Choose a background color for the checkout page. Color is applied to the <em>body</em> element.' => 'செக்அவுட் பக்கத்திற்கான பின்னணி நிறத்தைத் தேர்வுசெய்யவும். <em>உள்ளடக்க</em> உறுப்புக்கு வண்ணம் பயன்படுத்தப்படுகிறது.','Footer background color' => 'அடிக்குறிப்பு பின்னணி நிறம்','Choose a background color for the checkout page footer. Only applies when using the distraction free header and footer.' => 'செக்அவுட் பக்க அடிக்குறிப்புக்கு பின்னணி நிறத்தைத் தேர்வுசெய்யவும். கவனச்சிதறல் இல்லாத தலைப்பு மற்றும் அடிக்குறிப்பைப் பயன்படுத்தும் போது மட்டுமே இது பொருந்தும்.','Checkout elements' => 'செக்அவுட் கூறுகள்','Progress bar' => 'முன்னேற்றப் பட்டி','Display the checkout progress bar when using multi-step checkout layout' => 'பல-படி செக்அவுட் அமைப்பைப் பயன்படுத்தும் போது செக்அவுட் முன்னேற்றப் பட்டியைக் காண்பி.','Make the checkout progress bar stay visible while scrolling' => 'உருட்டும்போது செக் அவுட் முன்னேற்றப் பட்டியை தெரியும்படி வைக்கவும்.','Express checkout' => 'எக்ஸ்பிரஸ் செக்அவுட்','Enable the express checkout section' => 'எக்ஸ்பிரஸ் செக்அவுட் பிரிவை இயக்கு.','Displays the express checkout section at checkout when supported payment gateways have this feature enabled.' => 'ஆதரிக்கப்படும் கட்டண நுழைவாயில்கள் இந்த அம்சத்தை இயக்கியிருக்கும் போது, செக் அவுட்டில் எக்ஸ்பிரஸ் செக் அவுட் பிரிவைக் காண்பிக்கும்.','Display express checkout buttons in one line for larger screens' => 'பெரிய திரைகளுக்கு எக்ஸ்பிரஸ் செக்அவுட் பொத்தான்களை ஒரே வரியில் காட்டவும்.','Ignore additional checkout required fields when paying with a compatible express checkout payment gateway' => 'இணக்கமான எக்ஸ்பிரஸ் செக்அவுட் கட்டண நுழைவாயில் மூலம் பணம் செலுத்தும்போது கூடுதல் செக்அவுட் தேவையான புலங்களை புறக்கணிக்கவும்.','Make the order summary stay visible while scrolling' => 'ஸ்க்ரோல் செய்யும்போது ஆர்டர் சுருக்கம் தெரியும்படி வைக்கவும்.','Choose a background color for the order summary section.' => 'ஆர்டர் சுருக்கப் பிரிவுக்கான பின்னணி வண்ணத்தைத் தேர்வுசெய்யவும்.','Highlight the order totals row in the order summary table' => 'ஆர்டர் சுருக்க அட்டவணையில் ஆர்டர் மொத்த வரிசையை முன்னிலைப்படுத்தவும்.','Most useful when the order summary section does not have a highlighted background color. Might also apply to the Cart, Order Received and View Order pages when using Fluid Checkout PRO.' => 'ஆர்டர் சுருக்கப் பிரிவில் ஹைலைட் செய்யப்பட்ட பின்னணி நிறம் இல்லாதபோது மிகவும் பயனுள்ளதாக இருக்கும். Fluid Checkout PRO ஐப் பயன்படுத்தும் போது, கார்ட், ஆர்டர் பெறப்பட்டது மற்றும் View ஆர்டர் பக்கங்களுக்கும் இது பொருந்தும்.','Action link on the order summary at checkout.' => 'செக் அவுட்டில் ஆர்டர் சுருக்கத்தில் செயல் இணைப்பு.','Edit cart link' => 'கூடை இணைப்பைத் திருத்து','Cart items count' => 'கூடை உருப்படிகளின் எண்ணிக்கை','Link to shop page' => 'கடைப் பக்கத்திற்கான இணைப்பு','Cart items' => 'கூடை பொருட்கள்','Enable options to edit cart items on the checkout page' => 'செக்அவுட் பக்கத்தில் கூடை உருப்படிகளைத் திருத்த விருப்பங்களை இயக்கவும்.','Allow customers to change product quantities or removing items directly at the checkout page.' => 'வாடிக்கையாளர்கள் தயாரிப்பு அளவுகளை மாற்றவோ அல்லது பொருட்களை நேரடியாக செக்அவுட் பக்கத்தில் அகற்றவோ அனுமதிக்கவும்.','Hide cart items errors at the checkout page' => 'செக் அவுட் பக்கத்தில் கூடை உருப்படிகளின் பிழைகளை மறைக்கவும்.','Do not display the cart items errors message at the top of the checkout page. When submitting the checkout form to complete the purchase, these error messages will always be displayed.' => 'செக்அவுட் பக்கத்தின் மேலே கூடை உருப்படிகளின் பிழைகள் செய்தியைக் காட்ட வேண்டாம். கொள்முதலை முடிக்க செக்அவுட் படிவத்தை சமர்ப்பிக்கும்போது, இந்த பிழை செய்திகள் எப்போதும் காட்டப்படும்.','Place order' => 'ஆர்டர் செய்','Define the position to display "Place order" and terms checkbox section.' => '&quot;வரிசைப்படுத்து&quot; மற்றும் சொற்கள் தேர்வுப்பெட்டிப் பகுதியைக் காண்பிக்க வேண்டிய நிலையை வரையறுக்கவும்.','Some options might not be compatible with some plugins and themes.' => 'சில விருப்பங்கள் சில செருகுநிரல்கள் மற்றும் கருப்பொருள்களுடன் இணக்கமாக இல்லாமல் இருக்கலாம்.','Below the payment section' => 'கட்டணப் பிரிவின் கீழே','Below the order summary' => 'ஆர்டர் சுருக்கம் கீழே','Both below the payment section and the order summary' => 'கட்டணப் பிரிவு மற்றும் ஆர்டர் சுருக்கம் இரண்டுக்கும் கீழே','Add widget areas to the checkout page' => 'செக்அவுட் பக்கத்தில் விட்ஜெட் பகுதிகளைச் சேர்க்கவும்.','These widget areas are used to add trust symbols and trust badges on the checkout page.' => 'இந்த விட்ஜெட் பகுதிகள், செக்அவுட் பக்கத்தில் நம்பிக்கை சின்னங்கள் மற்றும் நம்பிக்கை பேட்ஜ்களைச் சேர்க்கப் பயன்படுகின்றன.','Display checkout sidebar widgets only when viewing the last checkout step on mobile devices when using multi-step checkout layout' => 'பல-படி செக்அவுட் அமைப்பைப் பயன்படுத்தும் போது மொபைல் சாதனங்களில் கடைசி செக்அவுட் படியைப் பார்க்கும்போது மட்டுமே செக்அவுட் பக்கப்பட்டி விட்ஜெட்களைக் காண்பி.','Enable integrated coupon code section' => 'ஒருங்கிணைந்த கூப்பன் குறியீடு பிரிவை இயக்கு.','Only applicable if use of coupon codes are enabled in the WooCommerce settings.' => 'WooCommerce அமைப்புகளில் கூப்பன் குறியீடுகளின் பயன்பாடு இயக்கப்பட்டிருந்தால் மட்டுமே பொருந்தும்.','Display the coupon codes section title' => 'கூப்பன் குறியீடுகள் பிரிவு தலைப்பைக் காண்பி.','Only applicable when coupon code is displayed as a separate section on the checkout or cart pages.' => 'செக்அவுட் அல்லது கார்ட் பக்கங்களில் கூப்பன் குறியீடு தனிப் பிரிவாகக் காட்டப்படும்போது மட்டுமே பொருந்தும்.','Select position where to display the coupon codes section on the checkout page.' => 'செக்அவுட் பக்கத்தில் கூப்பன் குறியீடுகள் பகுதியை எங்கு காண்பிக்க வேண்டும் என்பதைத் தேர்ந்தெடுக்கவும்.','Before the checkout steps' => 'செக்அவுட் படிகளுக்கு முன்','As a substep before payment methods' => 'கட்டண முறைகளுக்கு முன் துணைப் படியாக','As a substep after payment methods' => 'கட்டண முறைகளுக்குப் பிறகு துணைப் படியாக','Select style of the "apply coupon" button. Only applicable when the coupon code section is displayed "Before the checkout steps" on the checkout page, or "Before the cart items section" on the cart page.' => '&quot;கூப்பனைப் பயன்படுத்து&quot; பொத்தானின் பாணியைத் தேர்ந்தெடுக்கவும். கூப்பன் குறியீடு பிரிவு &quot;செக் அவுட் படிகளுக்கு முன்&quot; அல்லது &quot;கார்ட் உருப்படிகளுக்கு முன்&quot; கார்ட் பக்கத்தில் காட்டப்படும் போது மட்டுமே பொருந்தும்.','Default button style' => 'இயல்புநிலை பொத்தான் பாணி','"Add" link button' => '&quot;சேர்&quot; இணைப்பு பொத்தான்','Checkout fields' => 'செக்அவுட் புலங்கள்','Optional fields' => 'விருப்ப புலங்கள்','Hide optional fields behind a link button' => 'இணைப்பு பொத்தானுக்குப் பின்னால் விருப்ப புலங்களை மறை.','It is recommended to keep this option checked to reduce the number of open input fields, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">read the research</a>.' => 'திறந்த உள்ளீட்டு புலங்களின் எண்ணிக்கையைக் குறைக்க இந்த விருப்பத்தைத் தேர்வுசெய்து வைத்திருப்பது பரிந்துரைக்கப்படுகிறது, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#1-address-line-2--company-name-can-safely-be-collapsed-behind-a-link" target="_blank">ஆராய்ச்சியைப் படியுங்கள்</a> .','Do not hide "Address line 2" fields behind a link button' => 'இணைப்பு பொத்தானுக்குப் பின்னால் &quot;முகவரி வரி 2&quot; புலங்களை மறைக்க வேண்டாம்.','Recommended only when most customers actually need the "Address line 2" field, or when getting the right shipping address is crucial (ie. if delivering food and other perishable products).' => 'பெரும்பாலான வாடிக்கையாளர்களுக்கு &quot;முகவரி வரி 2&quot; புலம் உண்மையில் தேவைப்படும்போது அல்லது சரியான ஷிப்பிங் முகவரியைப் பெறுவது மிக முக்கியமானதாக இருக்கும்போது (அதாவது உணவு மற்றும் பிற அழுகக்கூடிய பொருட்களை டெலிவரி செய்தால்) மட்டுமே பரிந்துரைக்கப்படுகிறது.','Display the "Add" link buttons in lowercase' => '&quot;சேர்&quot; இணைப்பு பொத்தான்களை சிறிய எழுத்துக்களில் காட்டவும்.','Make the labels of optional field "Add" link button as <code>lowercase</code> (ie. "Add phone number" instead of "Add Phone Number"). This option also affects the link buttons for coupon code fields.' => 'விருப்பப் புலமான &quot;சேர்&quot; இணைப்பு பொத்தானின் லேபிள்களை <code>lowercase</code> மாற்றவும் (அதாவது &quot;தொலைபேசி எண்ணைச் சேர்&quot; என்பதற்குப் பதிலாக &quot;தொலைபேசி எண்ணைச் சேர்&quot;). இந்த விருப்பம் கூப்பன் குறியீடு புலங்களுக்கான இணைப்பு பொத்தான்களையும் பாதிக்கிறது.','Billing address' => 'பில்லிங் முகவரி','Select position where to display the billing address section on the checkout page.' => 'செக்அவுட் பக்கத்தில் பில்லிங் முகவரிப் பகுதியைக் காண்பிக்க வேண்டிய இடத்தைத் தேர்ந்தெடுக்கவும்.','As a step before shipping' => 'அனுப்புவதற்கு முன் ஒரு படியாக','As a sub-step before the shipping address section' => 'ஷிப்பிங் முகவரிப் பிரிவுக்கு முந்தைய துணைப் படியாக','As a sub-step after the shipping address section' => 'ஷிப்பிங் முகவரிப் பிரிவுக்குப் பிறகு துணைப் படியாக','As a step after shipping' => 'அனுப்பிய பிறகு ஒரு படியாக','As a sub-step before the payment section' => 'பணம் செலுத்தும் பிரிவுக்கு முந்தைய துணைப் படியாக','Force billing and shipping addresses to the same (single section)' => 'பில்லிங் மற்றும் ஷிப்பிங் முகவரிகளை ஒரே (ஒற்றைப் பிரிவு)க்கு கட்டாயப்படுத்தவும்.','Checkbox for "Same as shipping/billing address" checked by default' => '&quot;ஷிப்பிங்/பில்லிங் முகவரியைப் போன்றது&quot; என்பதற்கான தேர்வுப்பெட்டி இயல்பாகவே தேர்ந்தெடுக்கப்பட்டது.','The checkbox "Same as shiping address" will start as checked by default when the shipping address section is displayed first. <br>The checkbox "Same as billing address" will start as checked by default when the billing address section is displayed first. <br> It is recommended to leave this option checked as to significantly reduce the number of open input fields at checkout, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">read the research</a>.' => 'ஷிப்பிங் முகவரிப் பிரிவு முதலில் காட்டப்படும்போது, &quot;ஷிப்பிங் முகவரியைப் போன்றது&quot; என்ற தேர்வுப்பெட்டி இயல்பாகவே தேர்வுசெய்யப்பட்டதாகத் தொடங்கும்.<br> பில்லிங் முகவரிப் பிரிவு முதலில் காட்டப்படும்போது, &quot;பில்லிங் முகவரியைப் போலவே&quot; என்ற தேர்வுப்பெட்டி முன்னிருப்பாகத் தேர்ந்தெடுக்கப்பட்டதாகத் தொடங்கும்.<br> செக் அவுட்டில் திறந்த உள்ளீட்டு புலங்களின் எண்ணிக்கையைக் கணிசமாகக் குறைக்க, இந்த விருப்பத்தைச் சரிபார்க்காமல் விடுவது பரிந்துரைக்கப்படுகிறது, <a href="https://baymard.com/blog/checkout-flow-average-form-fields#3-default-billing--shipping-and-hide-the-fields-entirely" target="_blank">ஆராய்ச்சியைப் படியுங்கள்</a> .','Highlight the billing address section in the checkout form' => 'செக்அவுட் படிவத்தில் பில்லிங் முகவரிப் பகுதியை முன்னிலைப்படுத்தவும்.','Shipping address' => 'அனுப்பும் முகவரி','Highlight the shipping address section in the checkout form' => 'செக்அவுட் படிவத்தில் ஷிப்பிங் முகவரிப் பகுதியை முன்னிலைப்படுத்தவும்.','Shipping methods' => 'அனுப்பும் முறைகள்','Choose in which position to display the shipping methods section.' => 'ஷிப்பிங் முறைகள் பகுதியை எந்த நிலையில் காண்பிக்க வேண்டும் என்பதைத் தேர்வுசெய்யவும்.','Before shipping address' => 'அனுப்புவதற்கு முந்தைய முகவரி','After shipping address' => 'அனுப்பிய பிறகு முகவரி','Prevent automatic selection of the first shipping method' => 'முதல் ஷிப்பிங் முறையை தானாகத் தேர்ந்தெடுப்பதைத் தடுக்கவும்.','When enabled, the first shipping method available will not be automatically selected when no other shipping method was previously selected for each shipping package. <br>This option will be automatically enabled if the option for clearing the selected shipping method is enabled for the Local Pickup feature.' => 'இயக்கப்பட்டால், ஒவ்வொரு ஷிப்பிங் தொகுப்புக்கும் முன்னர் வேறு எந்த ஷிப்பிங் முறையும் தேர்ந்தெடுக்கப்படாதபோது, கிடைக்கக்கூடிய முதல் ஷிப்பிங் முறை தானாகவே தேர்ந்தெடுக்கப்படாது.<br> தேர்ந்தெடுக்கப்பட்ட ஷிப்பிங் முறையை அழிக்கும் விருப்பம் உள்ளூர் பிக்அப் அம்சத்திற்கு இயக்கப்பட்டிருந்தால், இந்த விருப்பம் தானாகவே இயக்கப்படும்.','Local pickup' => 'உள்ளூர் பிக்அப் வசதி','Removes shipping address section when a local pickup shipping method is selected.' => 'உள்ளூர் பிக்அப் ஷிப்பிங் முறை தேர்ந்தெடுக்கப்படும்போது ஷிப்பிங் முகவரிப் பகுதியை நீக்குகிறது.','Replaces the shipping address with the pickup point location when a local pickup shipping method is selected.' => 'உள்ளூர் பிக்அப் ஷிப்பிங் முறை தேர்ந்தெடுக்கப்படும்போது, ஷிப்பிங் முகவரியை பிக்அப் புள்ளி இருப்பிடத்துடன் மாற்றுகிறது.','Show option to clear shipping methods in the pickup location substep' => 'பிக்அப் இட துணைப் படியில் ஷிப்பிங் முறைகளை அழிக்க விருப்பத்தைக் காட்டு.','Show a link button on the pickup location substep to clear the chosen shipping methods. This can be used to allow showing the shipping address section again if a local pickup method was previously selected.' => 'தேர்ந்தெடுக்கப்பட்ட ஷிப்பிங் முறைகளை அழிக்க, பிக்அப் இருப்பிட துணைப் படியில் இணைப்பு பொத்தானைக் காட்டு. உள்ளூர் பிக்அப் முறை முன்பு தேர்ந்தெடுக்கப்பட்டிருந்தால், ஷிப்பிங் முகவரிப் பிரிவை மீண்டும் காட்ட இதைப் பயன்படுத்தலாம்.','Choose which address to save as the shipping address for local pickup orders.' => 'உள்ளூர் பிக்அப் ஆர்டர்களுக்கான ஷிப்பிங் முகவரியாக எந்த முகவரியைச் சேமிக்க வேண்டும் என்பதைத் தேர்வுசெய்யவும்.','Save the selected pickup location' => 'தேர்ந்தெடுக்கப்பட்ட பிக்அப் இடத்தைச் சேமிக்கவும்','Save same as the billing address' => 'பில்லிங் முகவரியைப் போலவே சேமிக்கவும்.','Do not save any shipping address' => 'எந்த ஷிப்பிங் முகவரியையும் சேமிக்க வேண்டாம்.','Company name field for shipping address' => 'ஷிப்பிங் முகவரிக்கான நிறுவனப் பெயர் புலம்','Change visibility for the company name field for the shipping address section on the checkout form.' => 'செக் அவுட் படிவத்தில் உள்ள ஷிப்பிங் முகவரிப் பகுதிக்கான நிறுவனத்தின் பெயர் புலத்திற்கான தெரிவுநிலையை மாற்றவும்.','If field is set as "optional", which is the default visibility state, no changes will be applied to let other plugins apply any changes they need.' => 'புலம் &quot;விருப்பத்தேர்வு&quot; என அமைக்கப்பட்டால், அது இயல்புநிலை தெரிவுநிலை நிலையாக இருந்தால், மற்ற செருகுநிரல்கள் தங்களுக்குத் தேவையான மாற்றங்களைப் பயன்படுத்த அனுமதிக்க எந்த மாற்றங்களும் பயன்படுத்தப்படாது.','Hidden (remove field)' => 'மறைக்கப்பட்டது (புலத்தை அகற்று)','Optional' => 'விருப்பத்தேர்வு','Required' => 'அவசியம்','Add shipping phone field to the checkout form.' => 'செக் அவுட் படிவத்தில் ஷிப்பிங் தொலைபேசி புலத்தைச் சேர்க்கவும்.','Choose in which step to display the shipping phone field.' => 'ஷிப்பிங் தொலைபேசி புலத்தை எந்தப் படியில் காட்ட வேண்டும் என்பதைத் தேர்வுசெய்யவும்.','Contact step' => 'தொடர்பு படி','Billing phone' => 'பில்லிங் தொலைபேசி','Add billing phone field to the checkout form.' => 'செக்அவுட் படிவத்தில் பில்லிங் தொலைபேசி புலத்தைச் சேர்க்கவும்.','Choose in which step to display the billing phone field.' => 'பில்லிங் தொலைபேசி புலத்தை எந்தப் படியில் காட்ட வேண்டும் என்பதைத் தேர்வுசெய்யவும்.','Enable international phone number format and validation for phone fields' => 'தொலைபேசி புலங்களுக்கு சர்வதேச தொலைபேசி எண் வடிவமைப்பு மற்றும் சரிபார்ப்பை இயக்கு.','Format and validate phone numbers according to each country.' => 'ஒவ்வொரு நாட்டிற்கும் ஏற்ப தொலைபேசி எண்களை வடிவமைத்து சரிபார்க்கவும்.','Enable phone number validation based on country rules' => 'நாட்டு விதிகளின் அடிப்படையில் தொலைபேசி எண் சரிபார்ப்பை இயக்கு.','When disabled, the phone field validation will not check if country or area codes are valid for the country.' => 'முடக்கப்பட்டிருக்கும் போது, நாடு அல்லது பகுதி குறியீடுகள் நாட்டிற்கு செல்லுபடியாகுமா என்பதை தொலைபேசி புல சரிபார்ப்பு சரிபார்க்காது.','Only show allowed countries for shipping or billing' => 'ஷிப்பிங் அல்லது பில்லிங்கிற்கு அனுமதிக்கப்பட்ட நாடுகளை மட்டும் காட்டு.','When enabled, only the countries allowed for shipping will be available in the shipping phone field, and only countries allowed for billing will be available for the billing phone field.' => 'இயக்கப்பட்டால், ஷிப்பிங் செய்ய அனுமதிக்கப்பட்ட நாடுகள் மட்டுமே ஷிப்பிங் தொலைபேசி புலத்தில் கிடைக்கும், மேலும் பில்லிங் செய்ய அனுமதிக்கப்பட்ட நாடுகள் மட்டுமே பில்லிங் தொலைபேசி புலத்தில் கிடைக்கும்.','Show country code beside the flag' => 'கொடியின் அருகில் நாட்டின் குறியீட்டைக் காட்டு.','Show an example of a valid phone number inside phone fields' => 'தொலைபேசி புலங்களுக்குள் செல்லுபடியாகும் தொலைபேசி எண்ணின் உதாரணத்தைக் காட்டு.','Do not change placeholders' => 'ஒதுக்கிடங்களை மாற்ற வேண்டாம்.','Show if not defined' => 'வரையறுக்கப்படவில்லை என்றால் காட்டு','Always show' => 'எப்போதும் காட்டு','Order notes' => 'ஆர்டர் குறிப்புகள்','Define the visibility of the additional order notes field.' => 'கூடுதல் ஆர்டர் குறிப்புகள் புலத்தின் தெரிவுநிலையை வரையறுக்கவும்.','Gift options' => 'பரிசு விருப்பங்கள்','Display gift message and other gift options at the checkout page' => 'பரிசுச் செய்தி மற்றும் பிற பரிசு விருப்பங்களை செக்அவுட் பக்கத்தில் காண்பி.','Allow customers to add a gift message and other gift related options to the order.' => 'வாடிக்கையாளர்கள் ஆர்டரில் பரிசு செய்தி மற்றும் பிற பரிசு தொடர்பான விருப்பங்களைச் சேர்க்க அனுமதிக்கவும்.','Display the gift message fields always expanded' => 'பரிசு செய்தி புலங்களை எப்போதும் விரிவாக்கி காட்டு.','Display the gift message as part of the order details table instead of a separate section' => 'பரிசுச் செய்தியை தனிப் பிரிவிற்குப் பதிலாக ஆர்டர் விவரங்கள் அட்டவணையின் ஒரு பகுதியாகக் காட்டவும்.','This option affects the order confirmation page (thank you page) and order details on account pages, emails and packing slips.' => 'இந்த விருப்பம் ஆர்டர் உறுதிப்படுத்தல் பக்கத்தையும் (நன்றி பக்கம்) கணக்குப் பக்கங்கள், மின்னஞ்சல்கள் மற்றும் பேக்கிங் சீட்டுகளில் உள்ள ஆர்டர் விவரங்களையும் பாதிக்கிறது.','Account matching' => 'கணக்கு பொருத்தம்','Enable the account matching feature' => 'கணக்கு பொருத்த அம்சத்தை இயக்கு.','Associate the guest customer\'s orders with their existing account when an account already exists with the customer\'s contact details.' => 'வாடிக்கையாளரின் தொடர்பு விவரங்களுடன் ஒரு கணக்கு ஏற்கனவே இருக்கும்போது, விருந்தினர் வாடிக்கையாளரின் ஆர்டர்களை அவர்களின் தற்போதைய கணக்குடன் இணைக்கவும்.','Display message when an account exists with the email address provided' => 'வழங்கப்பட்ட மின்னஞ்சல் முகவரியுடன் ஒரு கணக்கு இருக்கும்போது செய்தியைக் காண்பி.','Replaces the account creation fields with a notification and option to log in. In some contexts, it might be recommended to leave this option disabled to protect the privacy of customers.' => 'கணக்கு உருவாக்கப் புலங்களை அறிவிப்பு மற்றும் உள்நுழைவதற்கான விருப்பத்துடன் மாற்றுகிறது. சில சூழல்களில், வாடிக்கையாளர்களின் தனியுரிமையைப் பாதுகாக்க இந்த விருப்பத்தை முடக்க பரிந்துரைக்கப்படலாம்.','Dashboard' => 'டாஷ்போர்டு','Integrations' => 'ஒருங்கிணைப்புகள்','No integrations available at the moment on this section. The options related to each plugin will only appear here if that plugin is activated.' => 'இந்தப் பிரிவில் தற்போது எந்த ஒருங்கிணைப்புகளும் கிடைக்கவில்லை. ஒவ்வொரு செருகுநிரலுக்கும் தொடர்புடைய விருப்பங்கள் அந்த செருகுநிரல் செயல்படுத்தப்பட்டால் மட்டுமே இங்கே தோன்றும்.','License keys' => 'உரிம விசைகள்','No license keys options available at the moment on this section. The options related to each plugin or add-on will appear here when that plugin is activated.' => 'இந்தப் பிரிவில் தற்போது உரிம விசை விருப்பங்கள் எதுவும் கிடைக்கவில்லை. ஒவ்வொரு செருகுநிரல் அல்லது துணை நிரலுக்கும் தொடர்புடைய விருப்பங்கள் அந்த செருகுநிரல் செயல்படுத்தப்படும்போது இங்கே தோன்றும்.','Visit our website for more information about our plugins and add-ons.' => 'எங்கள் செருகுநிரல்கள் மற்றும் துணை நிரல்கள் பற்றிய கூடுதல் தகவலுக்கு எங்கள் வலைத்தளத்தைப் பார்வையிடவும்.','Order pay page' => 'ஆர்டர் கட்டணப் பக்கம்','Order pay' => 'ஆர்டர் ஊதியம்','Enable order pay page optimizations' => 'ஆர்டர் கட்டணப் பக்க மேம்படுத்தல்களை இயக்கு','Changes the layout of order pay page for existing orders pending payment from the customer.' => 'வாடிக்கையாளரிடமிருந்து பணம் செலுத்தப்பட வேண்டிய நிலுவையில் உள்ள ஏற்கனவே உள்ள ஆர்டர்களுக்கான ஆர்டர் கட்டணப் பக்கத்தின் அமைப்பை மாற்றுகிறது.','Add widget areas to the order pay page' => 'ஆர்டர் கட்டணப் பக்கத்தில் விட்ஜெட் பகுதிகளைச் சேர்க்கவும்.','These widget areas are used to add trust symbols and trust badges on the order pay page.' => 'இந்த விட்ஜெட் பகுதிகள் ஆர்டர் கட்டணப் பக்கத்தில் நம்பிக்கை சின்னங்கள் மற்றும் நம்பிக்கை பேட்ஜ்களைச் சேர்க்கப் பயன்படுகின்றன.','Thank you page & order details layout' => 'நன்றி பக்கம் &amp; ஆர்டர் விவரங்கள் தளவமைப்பு','Order details optimizations' => 'ஆர்டர் விவரங்கள் மேம்படுத்தல்கள்','Enable thank you page and order details optimizations' => 'நன்றி பக்கம் மற்றும் ஆர்டர் விவரங்கள் மேம்படுத்தல்களை இயக்கு.','Changes the layout of order details on the thank you page and account pages.' => 'நன்றி பக்கம் மற்றும் கணக்குப் பக்கங்களில் ஆர்டர் விவரங்களின் அமைப்பை மாற்றுகிறது.','Enable order details customizations on email notifications' => 'மின்னஞ்சல் அறிவிப்புகளில் ஆர்டர் விவரங்கள் தனிப்பயனாக்கங்களை இயக்கு.','Display new sections and change order of sections on email notifications. Styles of order details on email notifications remain unchanged.' => 'மின்னஞ்சல் அறிவிப்புகளில் புதிய பிரிவுகளைக் காண்பிக்கவும், பிரிவுகளின் வரிசையை மாற்றவும். மின்னஞ்சல் அறிவிப்புகளில் உள்ள ஆர்டர் விவரங்களின் பாணிகள் மாறாமல் இருக்கும்.','Thank you page' => 'நன்றி பக்கம்','These options affect the thank you page, also known as order received or order confirmation pages.' => 'இந்த விருப்பங்கள் நன்றி பக்கத்தைப் பாதிக்கும், இது ஆர்டர் பெறப்பட்ட அல்லது ஆர்டர் உறுதிப்படுத்தல் பக்கங்கள் என்றும் அழைக்கப்படுகிறது.','Order details layout' => 'ஆர்டர் விவரங்களின் தளவமைப்பு','Display the order details with wide layout on the thank you page' => 'நன்றி பக்கத்தில் பரந்த தளவமைப்புடன் ஆர்டர் விவரங்களைக் காண்பி.','Add widget areas to the thank you page' => 'நன்றி பக்கத்தில் விட்ஜெட் பகுதிகளைச் சேர்க்கவும்.','These widget areas are used to add trust symbols and trust badges on the thank you page.' => 'இந்த விட்ஜெட் பகுதிகள் நன்றி பக்கத்தில் நம்பிக்கை சின்னங்கள் மற்றும் நம்பிக்கை பேட்ஜ்களைச் சேர்க்கப் பயன்படுகின்றன.','These options affect the thank you page , view order details on account pages and on email notifications.' => 'இந்த விருப்பங்கள் நன்றி பக்கத்தைப் பாதிக்கின்றன, கணக்குப் பக்கங்களிலும் மின்னஞ்சல் அறிவிப்புகளிலும் ஆர்டர் விவரங்களைப் பார்க்கின்றன.','Order actions' => 'செயல்களை ஒழுங்குபடுத்து','Choose in which position to display the order actions.' => 'வரிசை செயல்களை எந்த நிலையில் காட்ட வேண்டும் என்பதைத் தேர்வுசெய்யவும்.','Inside the order overview' => 'ஆர்டர் கண்ணோட்டத்தின் உள்ளே','After the order overview' => 'ஆர்டர் மேலோட்டப் பார்வைக்குப் பிறகு','After order sections' => 'ஆர்டர் பிரிவுகளுக்குப் பிறகு','Order status' => 'ஆர்டர் நிலை','Display the order status progress bar' => 'ஆர்டர் நிலை முன்னேற்றப் பட்டியை காட்டு','Choose in which position to display the section.' => 'பகுதியை எந்த நிலையில் காண்பிக்க வேண்டும் என்பதைத் தேர்வுசெய்யவும்.','After the order overview and payment instructions' => 'ஆர்டர் கண்ணோட்டம் மற்றும் கட்டண வழிமுறைகளுக்குப் பிறகு','Before the order items section' => 'ஆர்டர் பொருட்கள் பிரிவுக்கு முன்','Inside the order items section' => 'ஆர்டர் பொருட்கள் பிரிவின் உள்ளே','After the order items section' => 'ஆர்டர் பொருட்கள் பிரிவுக்குப் பிறகு','On the sidebar' => 'பக்கப்பட்டியில்','Order downloads' => 'பதிவிறக்கங்களை ஆர்டர் செய்','Gift message' => 'பரிசுச் செய்தி','Tools' => 'கருவிகள்','Troubleshooting' => 'பழுது நீக்கும்','Debug options' => 'பிழைத்திருத்த விருப்பங்கள்','Debug mode' => 'பிழைத்திருத்த முறை','Using debug mode affects the website performance. Only use this option while troubleshooting.' => 'பிழைத்திருத்த பயன்முறையைப் பயன்படுத்துவது வலைத்தள செயல்திறனைப் பாதிக்கிறது. பிழைத்திருத்தத்தின் போது மட்டுமே இந்த விருப்பத்தைப் பயன்படுத்தவும்.','Load unminified assets' => 'குறைக்கப்படாத சொத்துக்களை ஏற்று','Enhanced select fields' => 'மேம்படுத்தப்பட்ட தேர்ந்தெடுக்கப்பட்ட புலங்கள்','Replace <code>select2</code> dropdown components with <code>TomSelect</code>' => '<code>select2</code> கீழ்தோன்றும் கூறுகளை <code>TomSelect</code> உடன் மாற்றவும்.','TomSelect is a simpler dropdown selection component which is less prone to errors than Select2, while offering the same features that are actually used on WooCommerce checkout pages.' => 'TomSelect என்பது Select2 ஐ விட பிழைகளுக்கு குறைவான வாய்ப்புள்ள ஒரு எளிமையான கீழ்தோன்றும் தேர்வு கூறு ஆகும், அதே நேரத்தில் WooCommerce செக்அவுட் பக்கங்களில் உண்மையில் பயன்படுத்தப்படும் அதே அம்சங்களை வழங்குகிறது.','Fix automatic zoom-in on form fields' => 'படிவ புலங்களில் தானியங்கி பெரிதாக்குதலை சரிசெய்யவும்.','Set <code>font-size</code> inside form fields to 16px' => 'படிவ புலங்களுக்குள் <code>font-size</code> 16px ஆக அமைக்கவும்.','When the font size inside form fields is smaller than 16px, Safari and other browsers might automatically zoom in on mobile devices to make the text easier to read. When this option is enabled, it will set the font size for inside the form fields to 16px on pages optimized by Fluid Checkout to avoid it zooming in.' => 'படிவப் புலங்களுக்குள் உள்ள எழுத்துரு அளவு 16px ஐ விடக் குறைவாக இருக்கும்போது, உரையைப் படிக்க எளிதாக்க Safari மற்றும் பிற உலாவிகள் மொபைல் சாதனங்களில் தானாகவே பெரிதாக்கக்கூடும். இந்த விருப்பம் இயக்கப்பட்டால், படிவப் புலங்களுக்குள் உள்ள எழுத்துரு அளவை 16px ஆக அமைக்கும், இதனால் அது பெரிதாக்கப்படுவதைத் தவிர்க்க Fluid Checkout ஆல் மேம்படுத்தப்பட்ட பக்கங்களில் இருக்கும்.','The shipping destination is always set to "Default to customer shipping address" when Fluid Checkout is activated. Customers can still provide different shipping and billing addresses during checkout, an option for setting the default billing address to be the same as the shipping address is available at WooCommerce > Settings > Fluid Checkout.' => 'திரவ செக்அவுட் செயல்படுத்தப்படும்போது, ஷிப்பிங் சேருமிடம் எப்போதும் &quot;வாடிக்கையாளர் ஷிப்பிங் முகவரிக்கு இயல்புநிலை&quot; என்று அமைக்கப்படும். செக்அவுட்டின் போது வாடிக்கையாளர்கள் வெவ்வேறு ஷிப்பிங் மற்றும் பில்லிங் முகவரிகளை வழங்க முடியும், இயல்புநிலை பில்லிங் முகவரியை ஷிப்பிங் முகவரியைப் போலவே அமைப்பதற்கான விருப்பம் WooCommerce &gt; அமைப்புகள் &gt; திரவ செக்அவுட்டில் கிடைக்கிறது.','Fluid Checkout' => 'திரவ செக்அவுட்','Settings' => 'அமைப்புகள்','Support' => 'ஆதரவு','<a target="_blank" href="%s">Upgrade to PRO</a> to unlock more options.' => 'கூடுதல் விருப்பங்களைத் திறக்க <a target="_blank" href="%s">PRO க்கு மேம்படுத்தவும்</a> .','(PRO)' => '(புரோ)','(experimental)' => '(சோதனை)','This is an experimental feature and may not work as expected with all themes and plugins. Use it with caution.' => 'இது ஒரு சோதனை அம்சம், மேலும் அனைத்து தீம்கள் மற்றும் செருகுநிரல்களிலும் எதிர்பார்த்தபடி வேலை செய்யாமல் போகலாம். இதை எச்சரிக்கையுடன் பயன்படுத்தவும்.','Confirm' => 'உறுதிப்படுத்தவும்','Verify Phone Number' => 'தொலைபேசி எண்ணைச் சரிபார்க்கவும்','Please verify your phone number.' => 'உங்கள் தொலைபேசி எண்ணைச் சரிபார்க்கவும்.','The Bluehost Plugin' => 'ப்ளூஹோஸ்ட் செருகுநிரல்','Custom fields' => 'தனிப்பயன் புலங்கள்','Enable custom shipping email field' => 'தனிப்பயன் ஷிப்பிங் மின்னஞ்சல் புலத்தை இயக்கு','When enabled, the shipping email field from the Bluehost plugin will be added to the checkout form.' => 'இயக்கப்பட்டால், Bluehost செருகுநிரலில் இருந்து ஷிப்பிங் மின்னஞ்சல் புலம் செக்அவுட் படிவத்தில் சேர்க்கப்படும்.','Captcha Pro by BestWebSoft' => 'BestWebSoft வழங்கும் கேப்ட்சா ப்ரோ','Captcha position' => 'கேப்ட்சா நிலை','Define the position to display the captcha section. Some positions might not work depending on the captcha type chosen.' => 'கேப்ட்சா பிரிவைக் காண்பிக்க வேண்டிய நிலையை வரையறுக்கவும். தேர்ந்தெடுக்கப்பட்ட கேப்ட்சா வகையைப் பொறுத்து சில நிலைகள் வேலை செய்யாமல் போகலாம்.','Selecting a pickup point is required before proceeding.' => 'தொடர்வதற்கு முன் ஒரு பிக்அப் பாயிண்டைத் தேர்ந்தெடுப்பது அவசியம்.','Pickup point' => 'பிக்அப் பாயிண்ட்','Pickup point not selected yet.' => 'பிக்அப் பாயிண்ட் இன்னும் தேர்ந்தெடுக்கப்படவில்லை.','Pickup point:' => 'பிக்அப் பாயிண்ட்:','Mailchimp for WooCommerce' => 'WooCommerce க்கான Mailchimp','Subscribe checkbox' => 'குழுசேர் தேர்வுப்பெட்டி','Override the position for the subscribe checkbox on the checkout page and always display it on the contact step' => 'செக்அவுட் பக்கத்தில் சந்தா தேர்வுப்பெட்டிக்கான நிலையை மீறி, அதை எப்போதும் தொடர்பு படியில் காட்டவும்.','Before Shipping Address' => 'அனுப்புவதற்கு முந்தைய முகவரி','(originally "%s")' => '(முதலில் &quot;%s&quot;)','After Shipping Address' => 'அனுப்பிய பிறகு முகவரி','Selecting a collection point is required when shipping with FermoPoint.' => 'FermoPoint உடன் அனுப்பும்போது ஒரு சேகரிப்பு புள்ளியைத் தேர்ந்தெடுப்பது அவசியம்.','If checked informs typos in email to users. (This feature has been disabled because Fluid Checkout offers the feature. Changes to this option will not take effect)' => 'சரிபார்க்கப்பட்டால் பயனர்களுக்கு மின்னஞ்சலில் எழுத்துப்பிழைகள் தெரிவிக்கப்படும். (ஃப்ளூயிட் செக்அவுட் அம்சத்தை வழங்குவதால் இந்த அம்சம் முடக்கப்பட்டுள்ளது. இந்த விருப்பத்தில் மாற்றங்கள் நடைமுறைக்கு வராது)','Enable Mail Check: (disabled feature)' => 'அஞ்சல் சரிபார்ப்பை இயக்கு: (முடக்கப்பட்ட அம்சம்)','Carrier:' => 'கேரியர்:','Sendinblue - WooCommerce Email Marketing' => 'செண்டின்ப்ளூ - WooCommerce மின்னஞ்சல் மார்க்கெட்டிங்','Sign up checkbox position' => 'பதிவு செக்பாக்ஸ் நிலை','Move the sign up checkbox to the contact step' => 'பதிவு செக்பாக்ஸை தொடர்பு படிக்கு நகர்த்தவும்.','When enabled, the checkbox will be moved to the contact step independently of the display location defined in the Sendinblue plugin settings.' => 'இயக்கப்படும்போது, Sendinblue செருகுநிரல் அமைப்புகளில் வரையறுக்கப்பட்ட காட்சி இருப்பிடத்தைப் பொருட்படுத்தாமல், தேர்வுப்பெட்டி தொடர்பு படிநிலைக்கு நகர்த்தப்படும்.','Theme Betheme' => 'தீம் பெத்தேம்','Checkout progress' => 'செக்அவுட் முன்னேற்றம்','Output the checkout steps section from Betheme on the checkout, cart and order received pages.' => 'செக்அவுட், வண்டி மற்றும் ஆர்டர் பெறப்பட்ட பக்கங்களில் பெத்தேமில் இருந்து செக்அவுட் படிகள் பகுதியை வெளியிடவும்.','Theme Fennik' => 'தீம் ஃபெனிக்','Breadcrumbs' => 'பிரட்தூள்களில் நனைக்கப்பட்டு','Output the breadcrumbs section from the Fennik theme when using Fluid Checkout header and footer.' => 'Fluid Checkout தலைப்பு மற்றும் அடிக்குறிப்பைப் பயன்படுத்தும் போது Fennik தீமிலிருந்து பிரட்க்ரம்ப்ஸ் பகுதியை வெளியிடவும்.','Theme Go' => 'தீம் கோ','My account layout' => 'எனது கணக்கு அமைப்பு','Enable wide layout on "My account" page.' => '&quot;எனது கணக்கு&quot; பக்கத்தில் அகலமான அமைப்பை இயக்கு.','Theme Impreza' => 'தீம் இம்ப்ரெஸா','Header' => 'தலைப்பு','Spacing for site header at the checkout page (in pixels)' => 'செக்அவுட் பக்கத்தில் தள தலைப்புக்கான இடைவெளி (பிக்சல்களில்)','Only applicable when using the Impreza theme header at the checkout page.' => 'செக்அவுட் பக்கத்தில் இம்ப்ரெஸா தீம் தலைப்பைப் பயன்படுத்தும் போது மட்டுமே பொருந்தும்.','Theme Kapee' => 'தீம் கபீ','Output the checkout steps section from the Kapee theme on the checkout, cart and order received pages.' => 'செக்அவுட், வண்டி மற்றும் ஆர்டர் பெறப்பட்ட பக்கங்களில் கேபி கருப்பொருளிலிருந்து செக்அவுட் படிகள் பகுதியை வெளியிடவும்.','Theme Porto' => 'தீம் போர்டோ','Output the checkout steps section from the Porto theme on the checkout, cart and order received pages.' => 'போர்டோ தீமில் இருந்து செக்அவுட் படிகள் பகுதியை செக்அவுட், வண்டி மற்றும் ஆர்டர் பெறப்பட்ட பக்கங்களில் வெளியிடவும்.','Theme Pressmart' => 'தீம் பிரஸ்மார்ட்','Output the checkout steps section from the Pressmart theme on the checkout, cart and order received pages.' => 'பிரஸ்மார்ட் கருப்பொருளிலிருந்து செக்அவுட் படிகள் பகுதியை செக்அவுட், வண்டி மற்றும் ஆர்டர் பெறப்பட்ட பக்கங்களில் வெளியிடவும்.','Theme The Gem' => 'தீம் தி ஜெம்','Output the checkout steps section from The Gem theme when using Fluid Checkout header and footer.' => 'Fluid Checkout தலைப்பு மற்றும் அடிக்குறிப்பைப் பயன்படுத்தும் போது The Gem தீமிலிருந்து Checkout படிகள் பகுதியை வெளியிடவும்.','Theme Woodmart' => 'தீம் வுட்மார்ட்','Output the checkout steps section from the Woodmart theme when using Fluid Checkout header and footer.' => 'Fluid Checkout தலைப்பு மற்றும் அடிக்குறிப்பைப் பயன்படுத்தும் போது Woodmart கருப்பொருளிலிருந்து checkout படிகள் பகுதியை வெளியிடவும்.','Checkout options' => 'வெளியேறுதல் விருப்பங்கள்','Disable the theme checkout options' => 'தீம் செக்அவுட் விருப்பங்களை முடக்கு.','The options display product image, quantity field, remove button and link to product page added by the theme are disabled by default.' => 'தீம் மூலம் சேர்க்கப்பட்ட தயாரிப்பு படத்தைக் காண்பிக்கும் விருப்பங்கள், அளவு புலம், அகற்று பொத்தான் மற்றும் தயாரிப்பு பக்கத்திற்கான இணைப்பு ஆகியவை இயல்பாகவே முடக்கப்பட்டுள்ளன.','Theme Atomion' => 'தீம் ஆட்டமியன்','Order progress' => 'ஆர்டர் முன்னேற்றம்','Display order progress from the theme' => 'கருப்பொருளிலிருந்து ஆர்டர் முன்னேற்றத்தைக் காட்டு','Display the form field labels visible on the page' => 'பக்கத்தில் தெரியும் படிவ புல லேபிள்களைக் காண்பி.','It is recommended to keep this option enabled for a better user experience.' => 'சிறந்த பயனர் அனுபவத்திற்காக இந்த விருப்பத்தை இயக்கி வைத்திருப்பது பரிந்துரைக்கப்படுகிறது.','Theme ZK Nito' => 'தீம் ZK நிட்டோ','Add extra fields to the checkout form' => 'செக்அவுட் படிவத்தில் கூடுதல் புலங்களைச் சேர்க்கவும்.','Adds an extra shipping email and shipping phone field to the shipping address section.' => 'ஷிப்பிங் முகவரிப் பிரிவில் கூடுதல் ஷிப்பிங் மின்னஞ்சல் மற்றும் ஷிப்பிங் தொலைபேசி புலத்தைச் சேர்க்கிறது.','Go to the home page' => 'முகப்புப் பக்கத்திற்குச் செல்லவும்.','Skip to content' => 'உள்ளடக்கத்திற்குச் செல்','Create an account' => 'ஒரு கணக்கை உருவாக்கு','Log in to your account' => 'உங்கள் கணக்கில் உள்நுழையவும்','Already have an account?' => 'ஏற்கனவே ஒரு கணக்கு உள்ளதா?','Logged in as %1$s. <a href="%2$s">Log out</a>' => '%1$s ஆக உள்நுழைந்துள்ளீர்கள். <a href="%2$s">வெளியேறு.</a>','Your order has a total amount due of %s. No further payment is needed.' => 'உங்கள் ஆர்டரில் மொத்த நிலுவைத் தொகை %s ஆகும். மேலும் கட்டணம் எதுவும் தேவையில்லை.','Close and continue with checkout' => 'மூடிவிட்டு செக் அவுட்டைத் தொடரவும்','Continue' => 'தொடரவும்','Parcel terminal' => 'பார்சல் முனையம்','<p>This widget has been replaced by Fluid Checkout. </p><p>When using Fluid Checkout, the checkout page needs to use the original WooCommerce shortcode <code>[woocommerce_checkout]</code> to work properly.</p><p>For more information, visit the Fluid Checkout documentation.</p>' => '<p>இந்த விட்ஜெட், Fluid Checkout ஆல் மாற்றப்பட்டுள்ளது.</p><p> Fluid Checkout-ஐப் பயன்படுத்தும் போது, checkout பக்கம் சரியாக வேலை செய்ய அசல் WooCommerce சுருக்குக்குறியீட்டை <code>[woocommerce_checkout]</code> பயன்படுத்த வேண்டும்.</p><p> மேலும் தகவலுக்கு, திரவ செக்அவுட் ஆவணங்களைப் பார்வையிடவும்.</p>','Theme The7' => 'தீம் The7','Additional header sections' => 'கூடுதல் தலைப்புப் பிரிவுகள்','Output additional header sections if enabled in the theme settings.' => 'தீம் அமைப்புகளில் இயக்கப்பட்டிருந்தால் கூடுதல் தலைப்புப் பிரிவுகளை வெளியிடுங்கள்.','More information' => 'மேலும் தகவல்','Pick a Locker' => 'ஒரு லாக்கரைத் தேர்ந்தெடுக்கவும்','Page template' => 'பக்க டெம்ப்ளேட்','Use `Order confirmation` page template for block-based themes' => 'தொகுதி அடிப்படையிலான கருப்பொருள்களுக்கு `ஆர்டர் உறுதிப்படுத்தல்` பக்க டெம்ப்ளேட்டைப் பயன்படுத்தவும்.','Only applies when using a block-based theme.' => 'தொகுதி அடிப்படையிலான கருப்பொருளைப் பயன்படுத்தும் போது மட்டுமே பொருந்தும்.','WooCommerce Stripe Gateway' => 'WooCommerce ஸ்ட்ரைப் கேட்வே','Payment form' => 'பணம் செலுத்தும் படிவம்','Apply styles to the Stripe payment form fields' => 'ஸ்ட்ரைப் கட்டணப் படிவப் புலங்களுக்கு ஸ்டைல்களைப் பயன்படுத்துங்கள்','When enabled, Fluid Checkout will apply styles optimized for compatibility with the plugin. You may want to disable this if you are using custom styles for the Stripe payment form fields.' => 'இயக்கப்பட்டிருக்கும் போது, செருகுநிரலுடன் இணக்கத்தன்மைக்கு உகந்ததாக வடிவமைக்கப்பட்ட பாணிகளை Fluid Checkout பயன்படுத்தும். நீங்கள் Stripe கட்டண படிவ புலங்களுக்கு தனிப்பயன் பாணிகளைப் பயன்படுத்துகிறீர்கள் என்றால் இதை முடக்க விரும்பலாம்.','Theme Nyture' => 'தீம் நைச்சர்','Output the checkout steps section from the Nyture theme when using Fluid Checkout.' => 'Fluid Checkout ஐப் பயன்படுத்தும் போது Nyture கருப்பொருளிலிருந்து checkout படிகள் பகுதியை வெளியிடுங்கள்.','Maybe be forced as "required" if the billing address section is displayed after the shipping address section, and the billing phone field is set as "required. This is needed to ensure the shipping address can be copied to the billing address when that option is checked.' => 'ஷிப்பிங் முகவரிப் பிரிவுக்குப் பிறகு பில்லிங் முகவரிப் பிரிவு காட்டப்பட்டு, பில்லிங் தொலைபேசி புலம் &quot;தேவை&quot; என அமைக்கப்பட்டால், &quot;தேவை&quot; என்று கட்டாயப்படுத்தப்படலாம். அந்த விருப்பம் தேர்ந்தெடுக்கப்படும்போது ஷிப்பிங் முகவரியை பில்லிங் முகவரிக்கு நகலெடுக்க முடியும் என்பதை உறுதிப்படுத்த இது தேவைப்படுகிறது.','Maybe be forced as "required" if the billing address section is displayed after the billing address section, and the billing phone field is set as "required. This is needed to ensure the billing address can be copied to the billing address when that option is checked.' => 'பில்லிங் முகவரிப் பிரிவுக்குப் பிறகு பில்லிங் முகவரிப் பிரிவு காட்டப்பட்டு, பில்லிங் தொலைபேசி புலம் &quot;தேவை&quot; என அமைக்கப்பட்டால், &quot;தேவை&quot; என்று கட்டாயப்படுத்தப்படலாம். அந்த விருப்பம் தேர்ந்தெடுக்கப்படும்போது பில்லிங் முகவரியை பில்லிங் முகவரிக்கு நகலெடுக்க முடியும் என்பதை உறுதிப்படுத்த இது தேவைப்படுகிறது.','Allows customers to make payments for orders that are either created manually by the store admin or not completed during checkout.' => 'கடை நிர்வாகியால் கைமுறையாக உருவாக்கப்பட்ட அல்லது செக் அவுட்டின் போது முடிக்கப்படாத ஆர்டர்களுக்கு வாடிக்கையாளர்கள் பணம் செலுத்த அனுமதிக்கிறது.','The plugin WooCommerce Checkout Manager by Quadlayers is not compatible with Fluid Checkout' => 'Quadlayers இன் WooCommerce Checkout Manager செருகுநிரல், Fluid Checkout உடன் இணக்கமாக இல்லை.','Company name field for billing address' => 'பில்லிங் முகவரிக்கான நிறுவனப் பெயர் புலம்','Change visibility for the company name field for the billing address section on the checkout form.' => 'செக்அவுட் படிவத்தில் பில்லிங் முகவரிப் பகுதிக்கான நிறுவனத்தின் பெயர் புலத்திற்கான தெரிவுநிலையை மாற்றவும்.','Theme Bookshelf' => 'தீம் புத்தக அலமாரி','Output the checkout steps section from the Bookshelf theme when using Fluid Checkout.' => 'Fluid Checkout ஐப் பயன்படுத்தும் போது Bookshelf கருப்பொருளிலிருந்து Checkout படிகள் பகுதியை வெளியிடவும்.','Account password' => 'கணக்கு கடவுச்சொல்','An account will be created with the information provided at checkout when completing the order.' => 'ஆர்டரை முடிக்கும்போது செக் அவுட்டில் வழங்கப்படும் தகவலுடன் ஒரு கணக்கு உருவாக்கப்படும்.','Choose the style of the progress bar.' => 'முன்னேற்றப் பட்டியின் பாணியைத் தேர்வுசெய்யவும்.','Bars' => 'பார்கள்','Theme Ireca' => 'தீம் ஐரேகா','WooCommerce page layout' => 'WooCommerce பக்க அமைப்பு','Force wide layout on WooCommerce pages' => 'WooCommerce பக்கங்களில் பரந்த தளவமைப்பை கட்டாயப்படுத்துங்கள்.','This ensures the content section is not too narrow on WooCommerce pages including checkout, cart, order received, order pay and account pages.' => 'இது WooCommerce பக்கங்களில் உள்ளடக்கப் பிரிவு மிகவும் குறுகலாக இல்லை என்பதை உறுதி செய்கிறது, இதில் செக்அவுட், வண்டி, ஆர்டர் பெறப்பட்டது, ஆர்டர் கட்டணம் மற்றும் கணக்குப் பக்கங்கள் ஆகியவை அடங்கும்.','Button label for applying coupon codesApply' => 'விண்ணப்பிக்கவும்','Checkout step titleContact' => 'தொடர்பு','Checkout step titleShipping' => 'கப்பல் போக்குவரத்து','Checkout step titleBilling' => 'பில்லிங்','Checkout step titlePayment' => 'பணம் செலுத்துதல்','Checkout substep change link labelChange' => 'மாற்றம்','Checkout substep save link labelSave changes' => 'மாற்றங்களைச் சேமிக்கவும்','Substep review textNone.' => 'இல்லை.','Substep review field format%1$s' => '%1$s','Substep review field format: with label%2$s: %1$s' => '%2$s: %1$s','Image uploader.No image selected.' => 'எந்தப் படமும் தேர்ந்தெடுக்கப்படவில்லை.','Settings sectionCart' => 'வண்டியில்','Settings sectionOrder pay' => 'ஆர்டர் ஊதியம்','Settings sectionThank you' => 'நன்றி','Settings section titleIntegrations' => 'ஒருங்கிணைப்புகள்','Settings section titleLicense keys' => 'உரிம விசைகள்','Captcha positionBefore place order section' => 'ஆர்டர் செய்வதற்கு முன் பிரிவு','Captcha positionBefore place order button' => 'ஆர்டர் செய்வதற்கு முன் பொத்தான்','No shipping method chosen label for the order summary--' => '--','Close button aria-labelClose' => 'மூடு','Log in link label at checkout contact stepLog in' => 'உள்நுழைய','Log in separator label at for when guest checkout is disabledOr continue below' => 'அல்லது கீழே தொடரவும்','Log in separator label at for when guest checkout is disabledOr continue as a guest' => 'அல்லது விருந்தினராகத் தொடரவும்','Clear image selection on admin pages.Remove image' => 'படத்தை அகற்று',] ];